"use strict";(self.webpackChunkmyblog_2_0=self.webpackChunkmyblog_2_0||[]).push([[3639],{65081:n=>{n.exports=JSON.parse('{"blogPosts":[{"id":"/2023/02/25/\ud504\ub85c\uc81d\ud2b8\ubb38\uc81c","metadata":{"permalink":"/blog/second/2023/02/25/\ud504\ub85c\uc81d\ud2b8\ubb38\uc81c","source":"@site/second-blog/2023-02-25-\ud504\ub85c\uc81d\ud2b8\ubb38\uc81c/index.mdx","title":"\ud504\ub85c\uc81d\ud2b8 \uad00\ub9ac \ubb38\uc81c","description":"\ud504\ub85c\uc81d\ud2b8 \ubc94\uc704\uae30\uc220\uc11c\ub97c \uc124\uba85\ud55c \ub0b4\uc6a9\uc911 \uac00\uc7a5 \uac70\ub9ac\uac00 \uba3c\uac83\uc740?","date":"2023-03-04T19:00:00.000Z","formattedDate":"2023\ub144 3\uc6d4 4\uc77c","tags":[{"label":"database","permalink":"/blog/second/tags/database"},{"label":"\ud504\ub85c\uc81d\ud2b8\uad00\ub9ac","permalink":"/blog/second/tags/\ud504\ub85c\uc81d\ud2b8\uad00\ub9ac"}],"readingTime":25.88,"hasTruncateMarker":false,"authors":[{"name":"100milliongold","title":"developer","url":"https://sebastienlorber.com","imageURL":"https://avatars.githubusercontent.com/u/13652711?s=400&u=0a1092b8b1170f71ae883d098f8721c12ce39e9e&v=4","key":"100milliongold"}],"frontMatter":{"title":"\ud504\ub85c\uc81d\ud2b8 \uad00\ub9ac \ubb38\uc81c","date":"2023-03-04T19:00:00.000Z","authors":["100milliongold"],"tags":["database","\ud504\ub85c\uc81d\ud2b8\uad00\ub9ac"],"password":"1q2w3e4r"},"unlisted":false,"nextItem":{"title":"\ud504\ub85c\uc81d\ud2b8 \uad00\ub9ac","permalink":"/blog/second/2023/03/04/\ud504\ub85c\uc81d\ud2b8\uad00\ub9ac"}},"content":"\ud504\ub85c\uc81d\ud2b8 \ubc94\uc704\uae30\uc220\uc11c\ub97c \uc124\uba85\ud55c \ub0b4\uc6a9\uc911 \uac00\uc7a5 \uac70\ub9ac\uac00 \uba3c\uac83\uc740?\\n\\n1. \uc774\ud574\uad00\uacc4\uc790 \uac04\uc5d0 \ud504\ub85c\uc81d\ud2b8 \ubc94\uc704\uc5d0 \ub300\ud55c \uacf5\uac10\ub300\ub97c \ud615\uc131\\n\\n2. \ud504\ub85c\uc81d\ud2b8 \ubc94\uc704\uc640 \uc81c\ud488 \ubc94\uc704\ub97c \ud3ec\ud568\ud558\uc5ec \uc804\uccb4 \ubc94\uc704\ub97c \uae30\uc220\ud568\\n\\n3. \ud504\ub85c\uc81d\ud2b8 \ud300\uc758 \uc8fc\uc694 \uad6c\uc131\uc6d0\uc744 \uc815\ud558\uace0 \ucc45\uc784\uacfc \uad8c\ud55c \uc218\uc900\uc5d0 \ub300\ud574 \uaddc\uc815\ud568\\n\\n4. \ud504\ub85c\uc81d\ud2b8 \ubc94\uc704, \uc8fc\uc694 \uc778\ub3c4\ubb3c, \uac00\uc815, \uc81c\uc57d\uc744 \uae30\uc220\ud55c \ubb38\uc11c\\n\\n<details>\\n  <summary>\ub2f5</summary>\\n\\n    2. \ud504\ub85c\uc81d\ud2b8 \ubc94\uc704\uc640 \uc81c\ud488 \ubc94\uc704\ub97c \ud3ec\ud568\ud558\uc5ec \uc804\uccb4 \ubc94\uc704\ub97c \uae30\uc220\ud568 \uc774 \uac00\uc7a5 \uac70\ub9ac\uac00 \uba3c \uac83\uc785\ub2c8\ub2e4.\\n\\n    \ud504\ub85c\uc81d\ud2b8 \ubc94\uc704\uae30\uc220\uc11c\ub294 \ud504\ub85c\uc81d\ud2b8 \ubc94\uc704\uc5d0 \ub300\ud55c \uc774\ud574\uad00\uacc4\uc790 \uac04\uc758 \uacf5\uac10\ub300 \ud615\uc131\uc744 \uc704\ud574 \uc791\uc131\ub429\ub2c8\ub2e4. \ud504\ub85c\uc81d\ud2b8 \ud300\uc758 \uc8fc\uc694 \uad6c\uc131\uc6d0\uc744 \uc815\ud558\uace0 \ucc45\uc784\uacfc \uad8c\ud55c \uc218\uc900\uc744 \uaddc\uc815\ud558\uba70, \ud504\ub85c\uc81d\ud2b8 \ubc94\uc704, \uc8fc\uc694 \uc778\ub3c4\ubb3c, \uac00\uc815, \uc81c\uc57d\uc744 \uae30\uc220\ud569\ub2c8\ub2e4.\\n\\n    \ud558\uc9c0\ub9cc, \uc81c\ud488 \ubc94\uc704\ub97c \ud3ec\ud568\ud558\uc5ec \uc804\uccb4 \ubc94\uc704\ub97c \uae30\uc220\ud558\ub294 \uac83\uc740 \uc77c\ubc18\uc801\uc73c\ub85c \ud504\ub85c\uc81d\ud2b8 \uc2a4\ucf54\ud504 \ubb38\uc11c\uc5d0\uc11c \ub2e4\ub8e8\ub294 \ub0b4\uc6a9\uc774\ubbc0\ub85c \ud504\ub85c\uc81d\ud2b8 \ubc94\uc704\uae30\uc220\uc11c\uc640\ub294 \uac70\ub9ac\uac00 \uc788\uc2b5\ub2c8\ub2e4. \ub530\ub77c\uc11c, 2\ubc88\uc774 \uac00\uc7a5 \uac70\ub9ac\uac00 \uba3c \uac83\uc785\ub2c8\ub2e4.\\n\\n</details>\\n\\n---\\n\\n\uc18c\ud504\ud2b8\uc6e8\uc5b4 \ub178\ub825\ucd94\uc815(effort estimation)\uc740 \uc18c\ud504\ud2b8\uc6e8\uc5b4 \ud504\ub85c\uc81d\ud2b8\uc758 \uc77c\uc815\uad00\ub9ac\uc640 \uc6d0\uac00\uad00\ub9ac\ub97c \uc704\ud55c \uae30\ucd08\uc790\ub8cc\ub97c \uc81c\uacf5\ud55c\ub2e4. \uacfc\uac70 \uc2e4\uc801 \uc790\ub8cc\uc640 \uc218\ud559\uc801\uc778 \ubaa8\ub378\uc744 \uc774\uc6a9\ud558\uc5ec \ud504\ub85c\uc81d\ud2b8 \ub178\uc5ed\uc758 \uc18c\uc694\ub7c9\uc744 \uc81c\uc2dc\ud558\ub294 \ubc29\ubc95\uc73c\ub85c \uac00\uc7a5 \uc801\uc808\ud55c \uac83\uc740?\\n\\n1. \ud558\ud5a5\uc9c1\ucd94\uc815(Tom-down estimating)\\n2. \uc720\uc0ac\ucd94\uc815(Estimating by analogy)\\n3. \ubaa8\uc218\uc801 \ubaa8\ub378\ub9c1(Parametric modeling)\\n4. \uc0c1\ud5a5\uc2dd \ucd94\uc815(Botton-up estimating)\\n\\n<details>\\n  <summary>\ub2f5</summary>\\n\\n    3. \ubaa8\uc218\uc801 \ubaa8\ub378\ub9c1(Parametric modeling)\uc740 \uacfc\uac70 \ud504\ub85c\uc81d\ud2b8\uc758 \uc131\uacfc \uae30\ub85d\uc744 \ubc14\ud0d5\uc73c\ub85c \uc77c\uc815\uacfc \ube44\uc6a9 \ub4f1\uc744 \uc608\uce21\ud558\ub294 \ubc29\ubc95\uc73c\ub85c, \ud2b9\uc815 \ud504\ub85c\uc81d\ud2b8\uc758 \uc18d\uc131\uacfc \ub178\ub825 \uc0ac\uc774\uc758 \uad00\uacc4\ub97c \uc218\ud559\uc801\uc778 \ubaa8\ub378\ub85c \ud45c\ud604\ud558\uc5ec \uc801\uc6a9\ud558\ub294 \ubc29\ubc95\uc785\ub2c8\ub2e4. \uc774 \ubc29\ubc95\uc740 \uc720\uc0ac\ucd94\uc815\ubcf4\ub2e4 \ub354 \uc815\ud655\ud558\uba70, \ud558\ud5a5\uc2dd \ucd94\uc815\ubcf4\ub2e4 \ub354 \ube60\ub974\uac8c \ucd94\uc815\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4. \ub530\ub77c\uc11c, \uc18c\ud504\ud2b8\uc6e8\uc5b4 \ub178\ub825 \ucd94\uc815\uc5d0 \uac00\uc7a5 \uc801\ud569\ud55c \ubc29\ubc95 \uc911 \ud558\ub098\uc785\ub2c8\ub2e4.\\n\\n</details>\\n\\n---\\n\\n\uc77c\ubc18\uc801\uc73c\ub85c \uc81c\uc548\uc694\uccad\uc11c, \uc81c\uc548\uc11c, \uc0ac\uc5c5\uc218\ud589\uacc4\ud68d\uc11c\ub97c \uadfc\uac04\uc73c\ub85c \ud504\ub85c\uc81d\ud2b8 \uc694\uad6c\uc0ac\ud56d\uc744 \uc815\uc758\ud55c\ub2e4. \uc694\uad6c\uc0ac\ud56d \uc815\uc758 \uc2dc \uace0\ub824\ud560 \uc0ac\ud56d\uc744 \ubaa8\ub450 \ud3ec\ud568\ud558\ub294 \uac83\uc740?\\n\\n\uac00. \uc694\uad6c\uc0ac\ud56d\uc740 \uc911\uc694\uc131\uc5d0 \ub530\ub77c \uc6b0\uc120\uc21c\uc704\uac00 \ubd80\uc5ec\ub418\uc5b4\uc57c \ud55c\ub2e4.\\n\ub098. \uc694\uad6c\uc0ac\ud56d\uc740 \ud504\ub85c\uc81d\ud2b8 \uc804 \uacf5\uc815\uc5d0\uc11c \ucd94\uc801 \uac00\ub2a5\ud560\uc218 \uc788\ub3c4\ub85d \uc2dd\ubcc4\uc790\ub97c \ubd80\uc5ec\ud558\uc5ec\uc57c \ud55c\ub2e4.\\n\ub2e4. \uc694\uad6c\uc0ac\ud56d\uc740 \uad6c\uccb4\uc801\uc774\uace0, \uba85\ud655\ud558\uace0, \uc218\ub834\uc5ec\ubd80\uac00 \ud655\uc778\ub420\uc218 \uc788\uc5b4\uc57c\ud55c\ub2e4.\\n\ub77c. \uc694\uad6c\uc0ac\ud56d \ubb38\uc11c\ub294 \ube44\uc9c0\ub2c8\uc2a4 \uc694\uad6c\uc0ac\ud56d\uacfc \uc5b4\ub5bb\uac8c \uc5f0\uacb0\ub418\ub294\uc9c0 \uc124\uba85\ub418\uc5b4\uc57c \ud55c\ub2e4.\\n\\n1. \uac00,\ub098,\ub2e4\\n2. \ub098,\ub2e4,\ub77c\\n3. \uac00,\ub2e4,\ub77c\\n4. \uac00,\ub098,\ub2e4,\ub77c\\n\\n<details>\\n  <summary>\ub2f5</summary>\\n\\n    \uc815\ud655\ud55c \ub2f5\ubcc0\uc740 \uc5c6\uc9c0\ub9cc, \uc77c\ubc18\uc801\uc73c\ub85c \uc694\uad6c\uc0ac\ud56d \uc815\uc758 \uc2dc \uace0\ub824\ud574\uc57c \ud560 \uc0ac\ud56d\uc73c\ub85c\ub294 \uac00, \ub098, \ub2e4, \ub77c\uc758 \ubaa8\ub4e0 \ud56d\ubaa9\uc774 \ud3ec\ud568\ub429\ub2c8\ub2e4.\\n\\n    \uac00) \uc694\uad6c\uc0ac\ud56d\uc740 \uc911\uc694\ub3c4\uc5d0 \ub530\ub77c \uc6b0\uc120\uc21c\uc704\ub97c \ubd80\uc5ec\ud574\uc57c \ud569\ub2c8\ub2e4. \uc774\ub294 \ud504\ub85c\uc81d\ud2b8 \uc77c\uc815\uc744 \uc218\ub9bd\ud558\uace0 \ud504\ub85c\uc81d\ud2b8\uc758 \ud575\uc2ec \uae30\ub2a5\uc744 \uc6b0\uc120\uc801\uc73c\ub85c \uac1c\ubc1c\ud558\ub294 \uac83\uc5d0 \ub3c4\uc6c0\uc774 \ub429\ub2c8\ub2e4.\\n\\n    \ub098) \uc694\uad6c\uc0ac\ud56d\uc740 \ucd94\uc801 \uac00\ub2a5\ud558\ub3c4\ub85d \uace0\uc720\ud55c \uc2dd\ubcc4\uc790\ub97c \ubd80\uc5ec\ud574\uc57c \ud569\ub2c8\ub2e4. \uc774\ub294 \uc694\uad6c\uc0ac\ud56d\uc774 \ubcc0\uacbd\ub418\uac70\ub098 \uc5c5\ub370\uc774\ud2b8 \ub420 \ub54c \uc694\uad6c\uc0ac\ud56d\uc744 \uad00\ub9ac\ud558\uace0 \ucd94\uc801\ud558\ub294 \ub370 \ub3c4\uc6c0\uc774 \ub429\ub2c8\ub2e4.\\n\\n    \ub2e4) \uc694\uad6c\uc0ac\ud56d\uc740 \uad6c\uccb4\uc801\uc774\uace0 \uba85\ud655\ud574\uc57c \ud569\ub2c8\ub2e4. \uc694\uad6c\uc0ac\ud56d\uc774 \uba85\ud655\ud558\uc9c0 \uc54a\uc73c\uba74 \ud504\ub85c\uc81d\ud2b8 \uc77c\uc815\uc774 \uc9c0\uc5f0\ub420 \uc218 \uc788\uc2b5\ub2c8\ub2e4. \ub610\ud55c \uc694\uad6c\uc0ac\ud56d\uc774 \ubaa8\ud638\ud558\uba74 \uc694\uad6c\uc0ac\ud56d\uc774 \uc5b8\uc81c \ucda9\uc871\ub418\uc5c8\ub294\uc9c0 \ud310\ub2e8\ud558\uae30 \uc5b4\ub824\uc6cc\uc9d1\ub2c8\ub2e4.\\n\\n    \ub77c) \uc694\uad6c\uc0ac\ud56d \ubb38\uc11c\ub294 \ube44\uc988\ub2c8\uc2a4 \uc694\uad6c\uc0ac\ud56d\uacfc \uc5b4\ub5bb\uac8c \uc5f0\uacb0\ub418\ub294\uc9c0 \uc124\uba85\ub418\uc5b4\uc57c \ud569\ub2c8\ub2e4. \uc774\ub294 \ud504\ub85c\uc81d\ud2b8\uac00 \ube44\uc988\ub2c8\uc2a4 \ubaa9\ud45c\ub97c \ub2ec\uc131\ud558\ub294 \ub370 \ub3c4\uc6c0\uc774 \ub418\ub3c4\ub85d \uc694\uad6c\uc0ac\ud56d\uc774 \uac1c\ubc1c\ub418\uc5c8\ub294\uc9c0 \ud655\uc778\ud558\ub294 \ub370 \ub3c4\uc6c0\uc774 \ub429\ub2c8\ub2e4.\\n\\n    \ub530\ub77c\uc11c, 4. \uac00, \ub098, \ub2e4, \ub77c\uc758 \ubaa8\ub4e0 \ud56d\ubaa9\uc774 \ud3ec\ud568\ub41c \uac83\uc774 \uac00\uc7a5 \uc801\uc808\ud569\ub2c8\ub2e4.\\n\\n</details>\\n\\n---\\n\\n\ud504\ub85c\uc81d\ud2b8 \uc77c\uc815\uacc4\ud68d \uae30\ubc95\uc5d0 \uad00\ud55c \uc124\uba85\uc73c\ub85c \uac00\uc7a5 \uac70\ub9ac\uac00 \uba3c\uac83\uc740?\\n\\n1. \uc8fc\uacbd\ub85c(critical path)\uc5d0 \ud3ec\ud568\ub418\uc9c0 \uc54a\ub294 \ud65c\ub3d9\uc740 \uc5ec\uc720\uc2dc\uac04\uc774 \ubc18\ub4dc\uc2dc 0\ubcf4\ub2e4 \ud06c\ub2e4.\\n2. \uc804\uccb4 \ud504\ub85c\uc81d\ud2b8\uc758 \uc77c\uc815\uc744 \ub2e8\ucd95\ud558\ub824\uba74 \uc8fc\ud65c\ub3d9(critical activity)\uc758 \uc218\ud589\uc2dc\uac04\uc744 \ub2e8\ucd95\ud574\uc57c\ud55c\ub2e4.\\n3. \uc2dc\uac04-\ube44\uc6a9\ubaa8\ud615(time-cost model)\uc5d0\uc11c \ud504\ub85c\uc81d\ud2b8 \uc77c\uc815\uc744 \ub2e8\ucd95(crashing) \ud568\uc5d0 \ub530\ub77c \ud65c\ub3d9 \uc9c1\uc811\ube44(activity direct cost) \uac00 \uac10\uc18c\ud55c\ub2e4.\\n4. \ud65c\ub3d9\uc758 \uc18c\uc694\uc2dc\uac04\uc774 3\uc810 \ucd94\uc815\uce58(three point estimate) \ub85c \uc8fc\uc5b4\uc9c0\uba74 \ud504\ub85c\uc81d\ud2b8 \uc644\ub8cc\uc2dc\uac01\uc5d0 \ub300\ud55c \ud655\ub960 \ubd84\uc11d\uc744 \uc218\ud589\ud560\uc218 \uc788\ub2e4.\\n\\n<details>\\n  <summary>\ub2f5</summary>\\n\\n    3\ubc88\uc774 \uac00\uc7a5 \uac70\ub9ac\uac00 \uba3c \uc124\uba85\uc785\ub2c8\ub2e4. \uc2dc\uac04-\ube44\uc6a9\ubaa8\ud615\uc5d0\uc11c \ud504\ub85c\uc81d\ud2b8 \uc77c\uc815\uc744 \ub2e8\ucd95\ud558\ub294 \uac83\uc740 crashing\uc774\uba70, \uc774\uc5d0 \ub530\ub978 \ud65c\ub3d9 \uc9c1\uc811\ube44\uc758 \uc99d\uac00\uc785\ub2c8\ub2e4. \ub530\ub77c\uc11c, 3\ubc88\uc5d0\uc11c\ub294 \uc77c\uc815\uc744 \ub2e8\ucd95\ud588\uc744 \ub54c \ud65c\ub3d9 \uc9c1\uc811\ube44\uac00 \uac10\uc18c\ud55c\ub2e4\uace0 \uc798\ubabb \uc124\uba85\ud558\uace0 \uc788\uc2b5\ub2c8\ub2e4.\\n\\n</details>\\n\\n---\\n\\n\uc791\uc5c5\ubd84\ub958\uccb4\uacc4(WSB, Work Breakdown Structure)\uc5d0 \ub300\ud55c \uc124\uba85\uc73c\ub85c \uac00\uc7a5 \uac70\ub9ac\uac00 \uba3c\uac83\uc740\\n\\n1. WBS\ub294 \ud544\uc694\ud55c \uc791\uc5c5\uc744 \uc778\ub3c4\ubb3c \uc704\uc8fc\ub85c \uacc4\uce35\uc801\uc73c\ub85c \ubd84\ud560\ud55c \uac83\uc774\ub2e4.\\n2. WBS\ub294 \uc6d0\uac00\ubd84\ub958\uccb4\uacc4(CBS, Cost Breakdown Structure)\uad6c\ucd95\uc5d0 \uae30\ubcf8\uc774 \ub41c\ub2e4.\\n3. WBS \uc2dd\ubcc4\uc790(identifer)\ub294 \ucd5c\ud558\uc704 \ub2e8\uacc4\uc778 \uc791\uc5c5\ud328\ud0a4\uc9c0\uc5d0\ub9cc \ubd80\uc5ec\ud55c\ub2e4.\\n4. WBS \uc0ac\uc804\uc740 WBS\uc758 \uad6c\uc131\uc694\uc18c\ub97c \ubcf4\ub2e4 \uad6c\uccb4\uc801\uc73c\ub85c \uc124\uba85\ud558\ub294 \ubb38\uc11c\uc774\ub2e4.\\n\\n<details>\\n  <summary>\ub2f5</summary>\\n\\n    3\ubc88\uc774 \uac00\uc7a5 \uac70\ub9ac\uac00 \uba3c \uc124\uba85\uc785\ub2c8\ub2e4. WBS \uc2dd\ubcc4\uc790\ub294 \ucd5c\ud558\uc704 \ub2e8\uacc4\uc778 \uc791\uc5c5 \ud328\ud0a4\uc9c0\ubfd0\ub9cc \uc544\ub2c8\ub77c \uc0c1\uc704 \uacc4\uce35\uc758 \uc694\uc18c\ub4e4\uc5d0\ub3c4 \ubd80\uc5ec\ub420 \uc218 \uc788\uc2b5\ub2c8\ub2e4. WBS \uc2dd\ubcc4\uc790\ub294 \uc77c\ubc18\uc801\uc73c\ub85c \uacc4\uce35\uc801\uc778 \ubc88\ud638 \uccb4\uacc4\ub85c \uad6c\uc131\ub418\uba70, \uc774\ub294 \uc804\uccb4 \ud504\ub85c\uc81d\ud2b8\uc5d0 \ub300\ud55c WBS \uacc4\uce35 \uad6c\uc870\ub97c \ud45c\ud604\ud558\uae30 \uc704\ud55c \uac83\uc785\ub2c8\ub2e4.\\n\\n</details>\\n\\n---\\n\\n\ud604\uc7ac \ud504\ub85c\uc81d\ud2b8\ub97c 10\uac1c\uc6d4 \uc9c4\ud589\ud558\uba74\uc11c \uc131\uacfc\uc5d0 \uad00\ub828\ub41c \uc9c0\uc218\ub97c \uacc4\uc0b0\ud574\ubcf4\ub2c8 \ud604\uc7ac \uc2dc\uc810\uc5d0\uc11c SPI(\uc77c\uc815 \uc131\uacfc \uc9c0\uc218: Schedule Performance Index)\uac00 1.10, CPI(\uc6d0\uac00 \uc131\uacfc\uc9c0\uc218: Cost Performance Index)\uac00 0.91\uac12\uc774 \uc0b0\ucd9c\ub418\uc5c8\ub2e4. \uc774 \uc124\uba85\uc73c\ub85c \uc801\uc808\ud55c\uac83\uc740?\\n\\n1. \ud504\ub85c\uc81d\ud2b8\uac00 \uc77c\uc815\ubcf4\ub2e4 \uc55e\uc11c\uace0 \uc788\ub2e4.\\n2. \ud504\ub85c\uc81d\ud2b8\uac00 \uc608\uc0b0\uc744 \uc808\uac10\ud588\ub2e4.\\n3. \uc6d0\uac00\uac00 \ucd08\uacfc\ub418\uace0 \uc788\ub2e4.\\n4. SV(\uc77c\uc815\ucc28\uc774: Schedule Variance)\ub294 \uc74c\uc758 \uac12\uc744 \uac16\ub294\ub2e4.\\n\\n<details>\\n  <summary>\ub2f5</summary>\\n\\n    \uc8fc\uc5b4\uc9c4 \uc124\uba85\uc73c\ub85c \uc801\uc808\ud55c \uac83\uc740 \ub2e4\uc74c\uacfc \uac19\uc2b5\ub2c8\ub2e4.\\n\\n    3. \uc6d0\uac00\uac00 \ucd08\uacfc\ub418\uace0 \uc788\ub2e4.\\n        SPI \uac12\uc774 1.10\uc774\ub77c\ub294 \uac83\uc740 \ud604\uc7ac\uae4c\uc9c0 \ud504\ub85c\uc81d\ud2b8 \uc77c\uc815\ubcf4\ub2e4 10% \ub354 \ube60\ub974\uac8c \uc9c4\ud589\ub418\uace0 \uc788\ub2e4\ub294 \uac83\uc744 \uc758\ubbf8\ud569\ub2c8\ub2e4. CPI \uac12\uc774 0.91\uc774\ub77c\ub294 \uac83\uc740 \ud604\uc7ac\uae4c\uc9c0 \uc608\uc0b0\ub300\ube44 9% \ub354 \ub9ce\uc740 \ube44\uc6a9\uc774 \ub4e4\uc5b4\uac14\ub2e4\ub294 \uac83\uc744 \uc758\ubbf8\ud569\ub2c8\ub2e4. \ub530\ub77c\uc11c \uc6d0\uac00\uac00 \ucd08\uacfc\ub418\uace0 \uc788\ub2e4\ub294 \uac83\uc744 \uc54c \uc218 \uc788\uc2b5\ub2c8\ub2e4. SV \uac12\uc740 \uc8fc\uc5b4\uc9c0\uc9c0 \uc54a\uc558\uc73c\ubbc0\ub85c \uc54c \uc218 \uc5c6\uc2b5\ub2c8\ub2e4.\\n\\n</details>\\n\\n---\\n\\n\ud488\uc9c8\uad00\ub9ac \ub3c4\uad6c \uc911 \ubaa9\ud45c\uc5d0 \ub3c4\uc798\ud558\uae30 \uc704\ud55c \uc77c\ub828\uc758 \ub2e8\uacc4\uc640 \uad00\uacc4\uc18d\uc5d0\uc11c \ubaa9\ud45c\ub97c \uc774\ud574\ud558\uae30 \uc704\ud574\uc11c \uc0ac\uc6a9\ub418\ub294 \ub3c4\uad6c\ub85c \uac00\uc7a5 \uc801\uc808\ud55c \uac83\uc740?\\n\\n1. \uc5f0\uad00\uad00\uacc4\ub3c4\\n2. \ud504\ub85c\uc138\uc2a4\uacb0\uc815 \ud504\ub85c\uadf8\ub7a8\ucc28\ud2b8(PDPC)\\n3. \uce5c\ud654\ub3c4\\n4. \uacc4\ud1b5\ub3c4\\n\\n<details>\\n  <summary>\ub2f5</summary>\\n\\n    2. \ud504\ub85c\uc138\uc2a4\uacb0\uc815 \ud504\ub85c\uadf8\ub7a8\ucc28\ud2b8(PDPC)\ub294 \uc77c\ub828\uc758 \ub2e8\uacc4 \uc911 \ubaa9\ud45c\ub97c \uc774\ud574\ud558\uae30 \uc704\ud574 \uc0ac\uc6a9\ub418\ub294 \ub3c4\uad6c\uc785\ub2c8\ub2e4. PDPC\ub294 \ubaa9\ud45c\ub97c \uc774\ud574\ud558\uae30 \uc704\ud574 \uc138\ubd80\uc801\uc778 \uc694\uc18c\ub97c \ub098\ub204\uace0, \uac01 \uc694\uc18c\uac00 \uc5b4\ub5bb\uac8c \ub2ec\uc131\ub420 \uc218 \uc788\ub294\uc9c0 \ud0d0\uad6c\ud558\ub294 \uac83\uc73c\ub85c \uad6c\uc131\ub429\ub2c8\ub2e4. \uc774\ub97c \ud1b5\ud574 \ubaa9\ud45c\ub97c \ub354\uc6b1 \uba85\ud655\ud788 \uc774\ud574\ud558\uace0, \ub2ec\uc131\ud558\uae30 \uc704\ud55c \uacc4\ud68d\uc744 \uc218\ub9bd\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4.\\n\\n</details>\\n\\n---\\n\\n\ud488\uc9c8\uad00\ub9ac\uc5d0\uc11c \uc131\uacfc\ub97c \ud3c9\uacfc\ud558\uace0 \ud544\uc694\ud55c \uad8c\uace0\uc548\uc744 \uc81c\uc2dc\ud558\uae30 \uc704\ud574 \ud488\uc9c8\ud65c\ub3d9\ub4e4\uc758 \uc2e4\ud589\uacb0\uacfc\ub97c \uac10\uc2dc\ud558\uace0 \uae30\ub85d\ud558\ub294 \ud504\ub85c\uc138\uc2a4\ub97c \uc218\ud589\ud558\ub294 \ud65c\ub3d9\uc73c\ub85c \uac00\uc7a5 \uc801\uc808\ud55c \uac83\uc740\\n\\n1. \ud514\uc9c8\ud3c9\uac00 \ud65c\ub3d9\\n2. \ud488\uc9c8\ubcf4\uc99d \ud65c\ub3d9\\n3. \ud488\uc9c8\ud1b5\uc81c \ud65c\ub3d9\\n4. \ud488\uc9c8\uacc4\ud68d\uc218\ub9bc \ud65c\ub3d9\\n\\n<details>\\n  <summary>\ub2f5</summary>\\n\\n    3. \ud488\uc9c8\ud1b5\uc81c \ud65c\ub3d9\uc785\ub2c8\ub2e4.\\n\\n    \ud488\uc9c8\ud1b5\uc81c\ub294 \ud488\uc9c8\uad00\ub9ac\uc5d0\uc11c \ud488\uc9c8\ubaa9\ud45c\ub97c \ub2ec\uc131\ud558\uae30 \uc704\ud55c \ud65c\ub3d9 \uc911 \ud558\ub098\ub85c, \uc2e4\ud589\ub41c \ud488\uc9c8\ud65c\ub3d9\uc758 \uacb0\uacfc\ub97c \uac10\uc2dc\ud558\uace0 \uac80\uc0ac\ud558\uc5ec \ud488\uc9c8\ubaa9\ud45c\uc5d0 \ubd80\ud569\ub418\ub294\uc9c0\ub97c \ud655\uc778\ud558\uace0, \ud544\uc694\ud55c \uad8c\uace0\uc548\uc744 \uc81c\uc2dc\ud558\ub294 \ud504\ub85c\uc138\uc2a4\uc785\ub2c8\ub2e4. \ub530\ub77c\uc11c, \ud488\uc9c8\ud3c9\uac00, \ud488\uc9c8\ubcf4\uc99d, \ud488\uc9c8\uacc4\ud68d\uc218\ub9bd \ud65c\ub3d9\uc740 \ud488\uc9c8\uad00\ub9ac\uc758 \ub2e4\ub978 \ud504\ub85c\uc138\uc2a4\uc640 \uad00\ub828\ub41c \ud65c\ub3d9\uc785\ub2c8\ub2e4.\\n\\n</details>\\n\\n---\\n\\n\uc704\ud5d8\ubd84\uc11d\uc2dc \uac00\uc7a5 \uae30\ubcf8\uc801\uc778 \uc704\ud5d8\uc758 \uc18d\uc131\uc73c\ub85c\ub294 \uc704\ud5d8\uc774 \ubc1c\uc0dd\ud560 \ud655\ub8f0\uc778 \uc704\ud5d8\uac00\ub2a5\uc131(Risk likely-hood)\uacfc \uc704\ud5d8\uc774 \ubc1c\uc0dd\ud560 \uacbd\uc6b0\uc5d0 \ud504\ub85c\uc81d\ud2b8\uc5d0 \ubbf8\uce58\ub294 \uc601\ud5a5\uc758 \ud06c\uae30\uc778 \uc704\ud5d8 \ucda9\uaca9\uc774\ub2e4. \uc704\ud5d8 \ucda9\uaca9\uc744 \uc815\ub7c9\ud654 \ud560 \ub54c \uace0\ub824\ud574\uc57c \ud560 \uc77c\ubc18\uc801\uc778 \uc7a0\uc7ac\ube44\uc6a9\uacfc \uac00\uc7a5 \uac70\ub9ac\uac00 \uba3c\uac83\uc740?\\n\\n1. \ubb3c\uac00\uc0c1\uc2b9\uc73c\ub85c \uc778\ud55c \ucd94\uac00 \ud22c\uc785\ub418\ub294 \ube44\uc6a9\\n2. \uc77c\uc815 \uc9c0\uc5f0\uc73c\ub85c \uc778\ud55c \ube44\uc6a9\\n3. \ucd94\uac00\uc801\uc778 \ub610\ub294 \ub354 \ube44\uc2fc \uc790\uc6d0\uc744 \uc0ac\uc6a9\ud568\uc73c\ub85c\uc368 \ubc1c\uc0dd\ud558\ub294 \ube44\uc6a9\\n4. \uc2dc\uc2a4\ud15c\uc758 \ud488\uc9c8\uacfc \uae30\ub2a5\uc5d0 \ub300\ud55c \uc808\ucda9\uc73c\ub85c \uc778\ud55c \uba85\uc2dc\uc801 \ub610\ub294 \uc554\ubb35\uc801 \ube44\uc6a9\\n\\n<details>\\n  <summary>\ub2f5</summary>\\n\\n    \uac00\uc7a5 \uac70\ub9ac\uac00 \uba3c \uac83\uc740\\n\\n    1. \ubb3c\uac00\uc0c1\uc2b9\uc73c\ub85c \uc778\ud55c \ucd94\uac00 \ud22c\uc785\ub418\ub294 \ube44\uc6a9\uc785\ub2c8\ub2e4. \uc704\ud5d8 \ucda9\uaca9\uc744 \uc815\ub7c9\ud654\ud560 \ub54c \uace0\ub824\ud574\uc57c \ud560 \uc77c\ubc18\uc801\uc778 \uc7a0\uc7ac\ube44\uc6a9\uc740 \ud504\ub85c\uc81d\ud2b8 \uc77c\uc815 \uc9c0\uc5f0, \ucd94\uac00\uc801\uc778 \ub610\ub294 \ub354 \ube44\uc2fc \uc790\uc6d0\uc744 \uc0ac\uc6a9\ud568\uc73c\ub85c\uc368 \ubc1c\uc0dd\ud558\ub294 \ube44\uc6a9, \uadf8\ub9ac\uace0 \uc2dc\uc2a4\ud15c\uc758 \ud488\uc9c8\uacfc \uae30\ub2a5\uc5d0 \ub300\ud55c \uc808\ucda9\uc73c\ub85c \uc778\ud55c \uba85\uc2dc\uc801 \ub610\ub294 \uc554\ubb35\uc801 \ube44\uc6a9\uc785\ub2c8\ub2e4. \ubb3c\uac00\uc0c1\uc2b9\uc740 \uc77c\ubc18\uc801\uc73c\ub85c \ud504\ub85c\uc81d\ud2b8\uc758 \uc704\ud5d8 \ubd84\uc11d\uc5d0\uc11c \uace0\ub824\ub418\ub294 \uc601\ud5a5 \uc694\uc18c\uac00 \uc544\ub2d9\ub2c8\ub2e4.\\n\\n</details>\\n\\n---\\n\\nWBS \uad6c\uc131\uc694\uc18c\ub4e4\ub85c \uc774\ub8e8\uc5b4\uc9c4 \uc791\uc5c5\uc744 \uc644\ub8cc\ud558\uae30 \uc704\ud574 \uc2e4\uc81c \ubc1c\uc0dd\ub41c \ube44\uc6a9(\uc2e4\uc81c\uc6d0\uac00: AC)\uc740 200 \uc774\uace0 WBS\uc758 \uad6c\uc131\uc694\uc18c\ub4e4 \uc911\uc5d0\uc11c \uc2e4\uc81c \uc644\ub8cc\ub41c \uc791\uc5c5\uc5d0 \ud574\ub2f9\ud558\ub294 \uc608\uc0b0 \ud569\uacc4 \uae08\uc561 (\ud68d\ub4dd\uac00\uce58:EV) \uc740 100 \uc774\ub2e4. \ub610\ud55c WBS\uc758 \uad6c\uc131\uc694\uc18c\ub4e4\uc744 \uc9c0\uc815\ub41c \uc77c\uc815\uc2dc\uc810\uae4c\uc9c0 \uc644\ub8cc\ud558\ub3c4\ub85d \uacc4\ud68d\ub41c \uc791\uc5c5\uc5d0 \ub300\ud574 \ucc45\uc815\ub41c \uc608\uc0c1 \ud569\uacc4\uae08\uc561(\uacc4\ud68d\uac00\uce58:PV)\uc740 120\uc774\ub2e4. \uc774 \uacbd\uc6b0 \ube44\uc6a9\ucc28\uc774(Cost Variance)\ub294 \uc5bc\ub9c8\uc778\uac00?\\n\\n1. -100\\n2. -80\\n3. 20\\n4. 100\\n\\n<details>\\n  <summary>\ub2f5</summary>\\n\\n    EV(\ud68d\ub4dd\uac00\uce58) = 100, AC(\uc2e4\uc81c\uc6d0\uac00) = 200 \uc774\ubbc0\ub85c Cost Variance(CV)\ub294 \ub2e4\uc74c\uacfc \uac19\uc774 \uacc4\uc0b0\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4.\\n\\n    CV = EV - AC = 100 - 200 = -100\\n\\n    \ub530\ub77c\uc11c \uc815\ub2f5\uc740 1\ubc88\uc778 \\"-100\\"\uc785\ub2c8\ub2e4.\\n\\n</details>\\n\\n---\\n\\n\ud504\ub85c\uc81d\ud2b8 \ub9ac\uc2a4\ud06c \uad00\ub9ac\ub97c \uc704\ud55c \ud504\ub85c\uc138\uc2a4\uc758 \uc218\uc11c\ub85c \uac00\uc7a5 \uc801\uc808\ud55c\uac83\uc740\\n\\n1. \ub9ac\uc2a4\ud06c \uad00\ub9ac \uacc4\ud68d\uc218\ub9bd -> \ub9ac\uc2a4\ud06c \uc2dd\ubcc4 -> \uc815\uc131\uc801 \ub9ac\uc2a4\ud06c \ubd84\uc11d\uc218\ud589 -> \uc815\ub7c9\uc801 \ub9ac\uc2a4\ud06c \ubd84\uc11d\uc218\ud589 -> \ub9ac\uc2a4\ud06c \uacc4\ud68d\uc218\ub9bd -> \ub9ac\uc2a4\ud06c \ud1b5\uc81c\\n\\n2. \ub9ac\uc2a4\ud06c \uc2dd\ubcc4 -> \ub9ac\uc2a4\ud06c\uad00\ub9ac \uacc4\ud68d\uc218\ub9bd -> \uc815\ub7c9\uc801 \ub9ac\uc2a4\ud06c \ubd84\uc11d\uc218\ud589 -> \uc815\uc131\uc801 \ub9ac\uc2a4\ud06c \ubd84\uc11d \uc218\ud589 -> \ub9ac\uc2a4\ud06c \ub300\uc751 \uacc4\ud68d \uc218\ub9bd -> \ub9ac\uc2a4\ud06c \ud1b5\uc81c\\n\\n3. \ub9ac\uc2a4\ud06c\uad00\ub9ac \uacc4\ud68d\uc218\ub9bd -> \ub9ac\uc2a4\ud06c \ub300\uc751\uacc4\ud68d \uc218\ub9bd -> \uc815\uc131\uc801 \ub9ac\uc2a4\ud06c \ubd84\uc11d\uc218\ud589 -> \uc815\ub7c9\uc801 \ub9ac\uc2a4\ud06c \ubd84\uc11d\uc218\ud589 -> \ub9ac\uc2a4\ud06c\uc2dd\ubcc4 -> \ub9ac\uc2a4\ud06c\ud1b5\uc81c\\n\\n4. \ub9ac\uc2a4\ud06c \uc2dd\ubcc4 -> \ub9ac\uc2a4\ud06c\uad00\ub9ac \uacc4\ud68d\uc218\ub9bd -> \uc815\uc131\uc801 \ub9ac\uc2a4\ud06c \ubd84\uc11d\uc218\ud589 -> \uc815\ub7c9\uc801 \ub9ac\uc2a4\ud06c \ubd84\uc11d -> \ub9ac\uc2a4\ud06c \ub300\uc751 \uacc4\ud68d\uc218\ub9bd -> \ub9ac\uc2a4\ud06c \ub300\uc751 \uacc4\ud68d \uc218\ub9bd -> \ub9ac\uc2a4\ud06c \ud1b5\uc81c\\n\\n5. \ub9ac\uc2a4\ud06c \uc2dd\ubcc4 -> \ub9ac\uc2a4\ud06c\uad00\ub9ac \uacc4\ud68d\uc218\ub9bd -> \uc815\ub7c9\uc801 \ub9ac\uc2a4\ud06c \ubd84\uc11d\uc218\ud589 -> \uc815\uc131\uc801 \ub9ac\uc2a4\ud06c \ubd84\uc11d \uc218\ud589 -> \ub9ac\uc2a4\ud06c \ub300\uc751 \uacc4\ud68d \uc218\ub9bd -> \ub9ac\uc2a4\ud06c \ud1b5\uc81c\\n\\n<details>\\n  <summary>\ub2f5</summary>\\n\\n    \ud504\ub85c\uc81d\ud2b8 \ub9ac\uc2a4\ud06c \uad00\ub9ac\ub97c \uc704\ud55c \ud504\ub85c\uc138\uc2a4\uc758 \uc21c\uc11c\ub294 \ub2e4\uc74c\uacfc \uac19\uc2b5\ub2c8\ub2e4.\\n\\n    1. \ub9ac\uc2a4\ud06c \uc2dd\ubcc4: \uac00\ub2a5\ud55c \ubaa8\ub4e0 \ub9ac\uc2a4\ud06c \uc2dd\ubcc4\\n\\n    2. \ub9ac\uc2a4\ud06c\uad00\ub9ac \uacc4\ud68d\uc218\ub9bd: \ub9ac\uc2a4\ud06c \uad00\ub9ac \uc811\uadfc\ubc29\uc2dd, \ud65c\ub3d9 \ubc94\uc704, \ub9ac\uc2a4\ud06c \uad00\ub9ac \uc2dc\uc2a4\ud15c, \ud504\ub85c\uc138\uc2a4 \ubc0f \ub2e8\uacc4, \uc5ed\ud560 \ubc0f \ucc45\uc784 \ub4f1\uc744 \uc815\uc758\\n    3. \uc815\ub7c9\uc801 \ub9ac\uc2a4\ud06c \ubd84\uc11d: \ub9ac\uc2a4\ud06c \ubc1c\uc0dd\ud655\ub960\uacfc \uc601\ud5a5\uc758 \uc815\ub3c4\ub97c \uc22b\uc790\ub85c \uacc4\uc0b0\ud558\uc5ec, \uac01 \ub9ac\uc2a4\ud06c\uc758 \uc0c1\ub300\uc801\uc778 \uc911\uc694\ub3c4\ub97c \ud3c9\uac00\\n    4. \uc815\uc131\uc801 \ub9ac\uc2a4\ud06c \ubd84\uc11d: \ube44\uc815\ud615\uc801\uc778 \ubc29\ubc95\uc73c\ub85c, \uc8fc\uad00\uc801\uc778 \uc6b0\uc120\uc21c\uc704\ub97c \ubd80\uc5ec\ud558\uac70\ub098, \ub9ac\uc2a4\ud06c\uc5d0 \ub300\ud55c \uc778\uc2dd\uc744 \ud655\ub9bd\\n    5. \ub9ac\uc2a4\ud06c \ub300\uc751 \uacc4\ud68d \uc218\ub9bd: \ub9ac\uc2a4\ud06c \ub300\uc751 \uc804\ub7b5 \uc218\ub9bd \ubc0f \uad6c\ud604\uc744 \uc704\ud55c \uacc4\ud68d \uc218\ub9bd\\n    6. \ub9ac\uc2a4\ud06c \ud1b5\uc81c: \ub9ac\uc2a4\ud06c \ub300\uc751 \uacc4\ud68d\uc758 \ud6a8\uacfc\uc131 \uac80\ud1a0 \ubc0f \ubcc0\uacbd \uad00\ub9ac, \uc0c8\ub85c\uc6b4 \ub9ac\uc2a4\ud06c\uc758 \uc2dd\ubcc4 \ubc0f \ubd84\uc11d, \ud504\ub85c\uc81d\ud2b8 \ud300\uc6d0 \ubc0f \uc774\ud574\uad00\uacc4\uc790\uc5d0 \ub300\ud55c \uad50\uc721 \ub4f1\uc744 \ud3ec\ud568\\n\\n    \ub530\ub77c\uc11c, 2\ubc88\uc758 \ub2f5\uc774 \uac00\uc7a5 \uc801\uc808\ud569\ub2c8\ub2e4.\\n\\n</details>\\n\\n---\\n\\nPMO(Project Managerment Office)\uc758 \uae30\ub2a5\uc5d0 \ub300\ud558\uc5ec 5\uac1c \uc774\uc0c1 \uc124\uba85\ud558\uc2dc\uc624.\\n\\n<details>\\n  <summary>\ub2f5</summary>\\n\\n    PMO(Project Management Office)\ub294 \uc870\uc9c1 \ub0b4\uc5d0\uc11c \ud504\ub85c\uc81d\ud2b8 \uad00\ub9ac \uc5c5\ubb34\ub97c \uc9c0\uc6d0\ud558\uace0 \ud6a8\uc728\uc801\uc778 \ud504\ub85c\uc81d\ud2b8 \uc218\ud589\uc744 \uc704\ud55c \ud504\ub85c\uc138\uc2a4, \ubc29\ubc95, \ub3c4\uad6c \ubc0f \uae30\uc220\uc744 \uac1c\ubc1c, \uad00\ub9ac\ud558\uba70, \uc870\uc9c1\uc758 \ube44\uc988\ub2c8\uc2a4 \ubaa9\ud45c\ub97c \ub2ec\uc131\ud558\uae30 \uc704\ud574 \ud504\ub85c\uc81d\ud2b8\ub97c \uc9c0\uc6d0\ud558\ub294 \uc870\uc9c1\uc785\ub2c8\ub2e4. PMO\uc758 \uae30\ub2a5\uc740 \ub2e4\uc74c\uacfc \uac19\uc2b5\ub2c8\ub2e4.\\n\\n    1. \ud504\ub85c\uc81d\ud2b8 \uad00\ub9ac \ud504\ub85c\uc138\uc2a4 \uac1c\ubc1c: \ud504\ub85c\uc81d\ud2b8 \uad00\ub9ac \ud504\ub85c\uc138\uc2a4\ub97c \uac1c\ubc1c\ud558\uace0, \uad00\ub9ac\ud558\uae30 \uc704\ud55c \ubc29\ubc95\uacfc \ub3c4\uad6c\ub97c \uc81c\uacf5\ud569\ub2c8\ub2e4.\\n\\n    2. \ud504\ub85c\uc81d\ud2b8 \uad00\ub9ac \uc131\uc219\ub3c4 \ud3c9\uac00: \uc870\uc9c1\uc758 \ud504\ub85c\uc81d\ud2b8 \uad00\ub9ac \uc5ed\ub7c9\uc744 \ud3c9\uac00\ud558\uace0, \ud504\ub85c\uc81d\ud2b8 \uad00\ub9ac \uc131\uc219\ub3c4\ub97c \uac1c\uc120\ud558\uae30 \uc704\ud55c \uc804\ub7b5\uc744 \uc218\ub9bd\ud569\ub2c8\ub2e4.\\n\\n    3. \ud504\ub85c\uc81d\ud2b8 \uad00\ub9ac \uc9c0\uc6d0: \ud504\ub85c\uc81d\ud2b8 \uad00\ub9ac\uc790 \ubc0f \ud300\uc5d0\uac8c \ud504\ub85c\uc81d\ud2b8 \uad00\ub9ac \uc9c0\uc6d0\uc744 \uc81c\uacf5\ud558\uc5ec, \ud504\ub85c\uc81d\ud2b8\uc758 \ud6a8\uc728\uc131\uacfc \uc131\uacfc\ub97c \uac1c\uc120\ud569\ub2c8\ub2e4.\\n\\n    4. \ud504\ub85c\uc81d\ud2b8 \uc131\uacfc \uce21\uc815 \ubc0f \ubd84\uc11d: \ud504\ub85c\uc81d\ud2b8 \uc131\uacfc\ub97c \uce21\uc815\ud558\uace0 \ubd84\uc11d\ud558\uc5ec, \ud504\ub85c\uc81d\ud2b8 \uad00\ub9ac \ud504\ub85c\uc138\uc2a4\uc640 \uacb0\uacfc\ub97c \uac1c\uc120\ud558\uae30 \uc704\ud55c \ubc29\ubc95\uc744 \uc81c\uc2dc\ud569\ub2c8\ub2e4.\\n\\n    5. \ud504\ub85c\uc81d\ud2b8 \ud3ec\ud2b8\ud3f4\ub9ac\uc624 \uad00\ub9ac: \uc870\uc9c1 \ub0b4 \ubaa8\ub4e0 \ud504\ub85c\uc81d\ud2b8\ub97c \ud3ec\ud2b8\ud3f4\ub9ac\uc624\ub85c \uad00\ub9ac\ud558\uc5ec, \ud504\ub85c\uc81d\ud2b8\uc758 \ubaa9\ud45c \ub2ec\uc131\uacfc \ub9ac\uc18c\uc2a4 \ud6a8\uc728\uc131\uc744 \uadf9\ub300\ud654\ud569\ub2c8\ub2e4.\\n\\n    6. \ud504\ub85c\uc81d\ud2b8 \uacbd\uc601: \ud504\ub85c\uc81d\ud2b8\uc758 \uacbd\uc601\uacfc \uad00\ub828\ub41c \uc804\ub7b5 \ubc0f \ubc29\ubc95\uc744 \uac1c\ubc1c\ud558\uace0 \uc801\uc6a9\ud558\uc5ec, \uc870\uc9c1\uc758 \ube44\uc988\ub2c8\uc2a4 \ubaa9\ud45c \ub2ec\uc131\uc744 \uc9c0\uc6d0\ud569\ub2c8\ub2e4.\\n\\n    7. \ud504\ub85c\uc81d\ud2b8 \ub9ac\uc2a4\ud06c \uad00\ub9ac: \ud504\ub85c\uc81d\ud2b8 \ub9ac\uc2a4\ud06c\ub97c \uad00\ub9ac\ud558\uace0, \uc870\uc9c1\uc758 \uc704\ud5d8\uc744 \ucd5c\uc18c\ud654\ud569\ub2c8\ub2e4.\\n\\n    8. \uc9c0\uc2dd \uad00\ub9ac: \uc870\uc9c1 \ub0b4\uc5d0\uc11c \uc313\uc778 \ud504\ub85c\uc81d\ud2b8 \uad00\ub9ac \uc9c0\uc2dd\uc744 \uad00\ub9ac\ud558\uace0 \uacf5\uc720\ud568\uc73c\ub85c\uc368, \uc870\uc9c1 \ub0b4 \ud504\ub85c\uc81d\ud2b8 \uad00\ub9ac \uc5ed\ub7c9\uc744 \ud5a5\uc0c1\uc2dc\ud0b5\ub2c8\ub2e4.\\n\\n    9. \ud504\ub85c\uc81d\ud2b8 \uad00\ub9ac \uad50\uc721 \ubc0f \ud6c8\ub828: \ud504\ub85c\uc81d\ud2b8 \uad00\ub9ac\uc790 \ubc0f \ud300\uc6d0\ub4e4\uc5d0\uac8c \ud504\ub85c\uc81d\ud2b8 \uad00\ub9ac \uad50\uc721 \ubc0f \ud6c8\ub828\uc744 \uc81c\uacf5\ud558\uc5ec, \ud504\ub85c\uc81d\ud2b8 \uc218\ud589 \ub2a5\ub825\uc744 \ud5a5\uc0c1\uc2dc\ud0b5\ub2c8\ub2e4.\\n\\n    10. \uc870\uc9c1 \uc804\ub7b5 \uc218\ub9bd: \uc870\uc9c1\uc758 \ube44\uc988\ub2c8\uc2a4 \uc804\ub7b5\uacfc \ud504\ub85c\uc81d\ud2b8 \uad00\ub9ac \uc804\ub7b5\uc744 \uc77c\uce58\uc2dc\ud0a4\uace0, \ud504\ub85c\uc81d\ud2b8 \uc218\ud589\uc744 \ud1b5\ud574 \uc870\uc9c1\uc758 \ube44\uc988\ub2c8\\n\\n</details>\\n\\n---\\n\\n\ud504\ub85c\uc81d\ud2b8 \uad00\ub9ac \uad00\uc810\uc5d0\uc11c \uc791\uc5c5\ubd84\ud574\ub3c4(WBS)\ub780 \ubb34\uc5c7\uc774\uba70, PMI\uac00 \uc81c\uc2dc\ud558\ub294 10\uac1c \ud65c\ub3d9\uc601\uc5ed \uac01\uac01\uc5d0\uc11c \uc5b4\ub5bb\uac8c \ud65c\uc6a9\ub420 \uc218 \uc788\ub294\uc9c0 \uc124\uba85\ud558\uc2dc\uc624.\\n\\n<details>\\n  <summary>\ub2f5</summary>\\n\\n    \uc791\uc5c5\ubd84\ud574\ub3c4(WBS: Work Breakdown Structure)\ub294 \ud504\ub85c\uc81d\ud2b8\uc758 \uc804\uccb4 \uc791\uc5c5\uc744 \uc138\ubd80\uc801\uc778 \uc791\uc5c5 \ub2e8\uc704\ub85c \ub098\ub208 \uad6c\uc870\ub97c \ub9d0\ud569\ub2c8\ub2e4. \uc774\ub97c \ud1b5\ud574 \ud504\ub85c\uc81d\ud2b8\uc758 \ubc94\uc704\ub97c \uba85\ud655\ud558\uac8c \ud30c\uc545\ud558\uace0, \uc791\uc5c5\uc744 \uacc4\ud68d, \ucd94\uc801, \ud1b5\uc81c\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4.\\n\\n    PMI\uac00 \uc81c\uc2dc\ud558\ub294 10\uac1c \ud65c\ub3d9\uc601\uc5ed \uac01\uac01\uc5d0\uc11c WBS\uac00 \ud65c\uc6a9\ub420 \uc218 \uc788\ub294 \ubc29\ubc95\uc740 \ub2e4\uc74c\uacfc \uac19\uc2b5\ub2c8\ub2e4.\\n\\n    1. \ud504\ub85c\uc81d\ud2b8 \ud1b5\ud569 \uad00\ub9ac: WBS\ub294 \ud504\ub85c\uc81d\ud2b8\uc758 \ubc94\uc704\ub97c \uc815\uc758\ud558\ub294\ub370 \uc911\uc694\ud55c \uc5ed\ud560\uc744 \ud569\ub2c8\ub2e4. \ud504\ub85c\uc81d\ud2b8 \ud1b5\ud569 \uad00\ub9ac\uc5d0\uc11c\ub294 WBS\ub97c \uae30\ubc18\uc73c\ub85c \uc804\uccb4 \ud504\ub85c\uc81d\ud2b8 \uacc4\ud68d\uc744 \uc218\ub9bd\ud558\uace0 \uad00\ub9ac\ud569\ub2c8\ub2e4.\\n\\n    2. \ubc94\uc704 \uad00\ub9ac: WBS\ub294 \ud504\ub85c\uc81d\ud2b8 \ubc94\uc704\uc758 \uad6c\uc131\uc694\uc18c\ub97c \uc138\ubd84\ud654\ud558\uc5ec \uba85\ud655\ud788 \uc774\ud574\ud558\uace0, \ubc94\uc704 \ubcc0\uacbd \uc694\uccad\uc744 \ubd84\uc11d\ud558\ub294 \ub370 \ud65c\uc6a9\ub429\ub2c8\ub2e4.\\n\\n    3. \uc77c\uc815 \uad00\ub9ac: WBS\ub294 \uc77c\uc815 \uacc4\ud68d\uc758 \uae30\ubc18\uc774 \ub418\uba70, \uac01 \uc791\uc5c5\uc774 \uc644\ub8cc\ub418\uc5b4\uc57c \ud558\ub294 \uc2dc\uae30\ub97c \ud30c\uc545\ud560 \uc218 \uc788\ub3c4\ub85d \ub3c4\uc640\uc90d\ub2c8\ub2e4.\\n\\n    4. \ube44\uc6a9 \uad00\ub9ac: WBS\ub294 \uac01 \uc791\uc5c5\ubcc4 \uc608\uc0b0\uc744 \ud560\ub2f9\ud558\uace0 \ucd94\uc801\ud558\ub294 \ub370 \uc0ac\uc6a9\ub429\ub2c8\ub2e4. \uc774\ub97c \ud1b5\ud574 \ud504\ub85c\uc81d\ud2b8 \ube44\uc6a9\uc744 \ud6a8\uacfc\uc801\uc73c\ub85c \uad00\ub9ac\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4.\\n\\n    5. \ud488\uc9c8 \uad00\ub9ac: WBS\ub294 \uac01 \uc791\uc5c5\uc758 \ud488\uc9c8 \uc694\uad6c\uc0ac\ud56d\uc744 \uc2dd\ubcc4\ud558\uace0, \ud488\uc9c8 \uac80\uc99d \ubc0f \ud1b5\uc81c\ub97c \uc704\ud55c \uc808\ucc28\ub97c \uc218\ub9bd\ud558\ub294 \ub370 \uc0ac\uc6a9\ub429\ub2c8\ub2e4.\\n\\n    6. \ub9ac\uc2a4\ud06c \uad00\ub9ac: WBS\ub294 \ub9ac\uc2a4\ud06c\ub97c \uc2dd\ubcc4\ud558\uace0 \ub300\uc751 \uc804\ub7b5\uc744 \uc218\ub9bd\ud558\ub294 \ub370 \ud544\uc694\ud55c \uc791\uc5c5\uc744 \uc815\uc758\ud558\ub294 \ub370 \uc0ac\uc6a9\ub429\ub2c8\ub2e4.\\n\\n    7. \uc790\uc6d0 \uad00\ub9ac: WBS\ub294 \uac01 \uc791\uc5c5\uc5d0 \ud544\uc694\ud55c \uc778\ub825, \uc7a5\ube44, \ubb3c\uc790 \ub4f1 \uc790\uc6d0\uc758 \uc694\uad6c\uc0ac\ud56d\uc744 \ud30c\uc545\ud558\uace0, \uc790\uc6d0 \ud560\ub2f9 \ubc0f \ucd94\uc801\uc744 \uc704\ud55c \uae30\ucd08 \uc790\ub8cc\ub85c \ud65c\uc6a9\ub429\ub2c8\ub2e4.\\n\\n    8. \uc758\uc0ac\uc18c\ud1b5 \uad00\ub9ac: WBS\ub294 \ud504\ub85c\uc81d\ud2b8\uc758 \uc758\uc0ac\uc18c\ud1b5 \uacc4\ud68d\uc744 \uc218\ub9bd\ud558\ub294 \ub370 \ud544\uc694\ud55c \uc815\ubcf4\ub97c \uc81c\uacf5\ud558\uace0, \uc758\uc0ac\uc18c\ud1b5 \ud750\ub984\uc744 \ud30c\uc545\ud558\ub294 \ub370 \ud65c\uc6a9\ub429\ub2c8\ub2e4.\\n\\n    9. \uc774\ud574 \uad00\uacc4\uc790 \uad00\ub9ac: WBS\ub294 \uc774\ud574 \uad00\uacc4\uc790\uc758 \uc694\uad6c\uc0ac\ud56d\uc744 \ud30c\uc545\ud558\ub294 \ub370 \ud544\uc694\ud55c \uc815\ubcf4\ub97c \uc81c\uacf5\ud558\uace0, \uc774\ud574 \uad00\uacc4\uc790\uc640\uc758 \uc758\uc0ac\uc18c\ud1b5\uc744 \uc6d0\ud65c\ud558\uac8c \ud558\ub294 \ub370 \ud65c\uc6a9\ub429\ub2c8\ub2e4.\\n\\n    10. \uc870\ub2ec \uad00\ub9ac: WBS\ub294 \ud504\ub85c\uc81d\ud2b8\uc5d0\uc11c \ud544\uc694\ud55c \uc790\uc6d0\uacfc \uc7ac\ud654\ub97c \uc2dd\ubcc4\ud558\uace0, \uc870\ub2ec \uacc4\ud68d\uc744 \uac1c\ubc1c\ud558\ub294 \ub370 \uc0ac\uc6a9\ub429\ub2c8\ub2e4.\\n\\n</details>\\n\\n---\\n\\n\ud504\ub85c\uc81d\ud2b8 \uacc4\ud68d\uc218\ub9bd\uc744 \uc704\ud558\uc5ec \uc704\ud5d8\uad00\ub9ac\uacc4\ud68d\uc744 \uac80\ud1a0\ud558\uace0 \uc788\ub2e4.\\n\\n\uac00. \uc704\ud5d8\uad00\ub9ac \uccb4\uacc4\ub97c \uae30\uc220\ud558\uc2dc\uc624.\\n\ub098. \uc815\uc131\uc801 \uc704\ud5d8\ubd84\uc11d\uacfc \uc815\ub7c9\uc801 \uc704\ud5d8\ubd84\uc11d\uc5d0 \ub300\ud558\uc5ec \uc124\uba85\ud558\uc2dc\uc624.\\n\\n<details>\\n  <summary>\ub2f5</summary>\\n\\n    \uac00. \uc704\ud5d8\uad00\ub9ac \uccb4\uacc4\ub294 \ud504\ub85c\uc81d\ud2b8\uc5d0\uc11c \uc704\ud5d8\uc744 \ud3c9\uac00\ud558\uace0 \ucc98\ub9ac\ud558\uae30 \uc704\ud55c \uc77c\ub828\uc758 \uc808\ucc28\uc640 \ubc29\ubc95\uc744 \ub9d0\ud569\ub2c8\ub2e4. \uc77c\ubc18\uc801\uc73c\ub85c \ub2e4\uc74c\uacfc \uac19\uc740 \ub2e8\uacc4\ub97c \ud3ec\ud568\ud569\ub2c8\ub2e4.\\n\\n    1. \uc704\ud5d8\uad00\ub9ac \uacc4\ud68d \uc218\ub9bd: \ud504\ub85c\uc81d\ud2b8 \uc704\ud5d8\uc744 \uad00\ub9ac\ud558\uae30 \uc704\ud55c \ubc29\ubc95\uacfc \uc808\ucc28\ub97c \uac1c\ubc1c\ud569\ub2c8\ub2e4.\\n    2. \uc704\ud5d8 \uc2dd\ubcc4: \ud504\ub85c\uc81d\ud2b8\uc5d0\uc11c \ubc1c\uc0dd\ud560 \uc218 \uc788\ub294 \uc704\ud5d8\uc744 \uc2dd\ubcc4\ud569\ub2c8\ub2e4.\\n    3. \uc704\ud5d8 \ud3c9\uac00: \ubc1c\uc0dd \uac00\ub2a5\uc131\uacfc \uc601\ud5a5\uc758 \uc815\ub3c4\ub97c \ud3c9\uac00\ud569\ub2c8\ub2e4.\\n    4. \uc704\ud5d8 \ub300\uc751 \uacc4\ud68d \uc218\ub9bd: \uc704\ud5d8\uc744 \ucc98\ub9ac\ud558\uae30 \uc704\ud55c \uacc4\ud68d\uc744 \uc218\ub9bd\ud569\ub2c8\ub2e4.\\n    5. \uc704\ud5d8 \ubaa8\ub2c8\ud130\ub9c1 \ubc0f \uc81c\uc5b4: \uc704\ud5d8\uc744 \uac10\uc2dc\ud558\uace0 \ub300\uc751 \uacc4\ud68d\uc744 \uc870\uc815\ud569\ub2c8\ub2e4.\\n\\n    \ub098. \uc815\uc131\uc801 \uc704\ud5d8\ubd84\uc11d\uc740 \uc8fc\ub85c \uc8fc\uad00\uc801\uc778 \ud310\ub2e8\uacfc \uacbd\ud5d8\uc801 \uc9c0\uc2dd\uc744 \uc774\uc6a9\ud558\uc5ec \uc704\ud5d8\uc744 \ud3c9\uac00\ud558\ub294 \uac83\uc785\ub2c8\ub2e4. \uc8fc\uc694 \uacb0\uacfc\ubb3c\uc740 \uc704\ud5d8 \uc2dd\ubcc4, \uc704\ud5d8 \ub4f1\uae09\ud654 \ubc0f \uc704\ud5d8 \ub300\uc751 \uacc4\ud68d \uc218\ub9bd \ub4f1\uc785\ub2c8\ub2e4.\\n\\n    \ubc18\uba74\uc5d0 \uc815\ub7c9\uc801 \uc704\ud5d8\ubd84\uc11d\uc740 \uc218\ud559\uc801\uc778 \ubc29\ubc95\uc744 \uc0ac\uc6a9\ud558\uc5ec \uc704\ud5d8\uc758 \ubc1c\uc0dd \uac00\ub2a5\uc131\uacfc \uc601\ud5a5\uc758 \uc815\ub3c4\ub97c \uc218\uce58\ud654\ud558\ub294 \uac83\uc785\ub2c8\ub2e4. \uc774 \ubc29\ubc95\uc740 \uc8fc\ub85c \uacbd\uc81c\uc801\uc778 \uce21\uba74\uc5d0\uc11c \ud6a8\uacfc\uc801\uc785\ub2c8\ub2e4. \uc8fc\uc694 \uacb0\uacfc\ubb3c\uc740 \uc704\ud5d8 \ub4f1\uae09\ud654, \uc704\ud5d8 \uacc4\uc0b0 \ubc0f \ub300\uc751 \uc804\ub7b5 \uac1c\ubc1c \ub4f1\uc785\ub2c8\ub2e4.\\n\\n</details>"},{"id":"/2023/03/04/\ud504\ub85c\uc81d\ud2b8\uad00\ub9ac","metadata":{"permalink":"/blog/second/2023/03/04/\ud504\ub85c\uc81d\ud2b8\uad00\ub9ac","source":"@site/second-blog/2023-03-04-\ud504\ub85c\uc81d\ud2b8\uad00\ub9ac/index.mdx","title":"\ud504\ub85c\uc81d\ud2b8 \uad00\ub9ac","description":"\uc9c4\ud589\ub3c4","date":"2023-03-04T10:00:00.000Z","formattedDate":"2023\ub144 3\uc6d4 4\uc77c","tags":[{"label":"database","permalink":"/blog/second/tags/database"},{"label":"\ud504\ub85c\uc81d\ud2b8\uad00\ub9ac","permalink":"/blog/second/tags/\ud504\ub85c\uc81d\ud2b8\uad00\ub9ac"}],"readingTime":43.39,"hasTruncateMarker":false,"authors":[{"name":"100milliongold","title":"developer","url":"https://sebastienlorber.com","imageURL":"https://avatars.githubusercontent.com/u/13652711?s=400&u=0a1092b8b1170f71ae883d098f8721c12ce39e9e&v=4","key":"100milliongold"}],"frontMatter":{"title":"\ud504\ub85c\uc81d\ud2b8 \uad00\ub9ac","date":"2023-03-04T10:00:00.000Z","authors":["100milliongold"],"tags":["database","\ud504\ub85c\uc81d\ud2b8\uad00\ub9ac"]},"unlisted":false,"prevItem":{"title":"\ud504\ub85c\uc81d\ud2b8 \uad00\ub9ac \ubb38\uc81c","permalink":"/blog/second/2023/02/25/\ud504\ub85c\uc81d\ud2b8\ubb38\uc81c"},"nextItem":{"title":"DataBase CAP \uc774\ub860\uc774\ub780","permalink":"/blog/second/2023/03/04/cap-\uc774\ub860"}},"content":"import Drawio from \\"@theme/Drawio\\";\\nimport simpleGraph1 from \\"!!raw-loader!./mindmap.drawio\\";\\n\\n## \uc9c4\ud589\ub3c4\\n\\n<Drawio content={simpleGraph1} resize={true} />\\n\\n## 1. \ud504\ub85c\uc81d\ud2b8\uad00\ub9ac \uac1c\uc694\\n\\n- \ud504\ub85c\uc138\uc2a4: \uac00\uacf5\ud558\ub294 \uc77c\ub828\uc758 \uad00\ub9ac\\n\\n- \ud504\ub85c\uc81d\ud2b8 \uc131\uacf5\uc740 : QCD\\n\\n## PDCA\ub780?\\n\\nPDCA\ub780 \ud504\ub85c\uc138\uc2a4\uc758 \ubcc0\uacbd \uc0ac\ud56d\uc774\ub098 \ubb38\uc81c \ud574\uacb0\uc744 \uc704\ud55c \uc194\ub8e8\uc158\uc744 \ud14c\uc2a4\ud2b8\ud558\uba70 \uc9c0\uc18d\uc801\uc778 \uac1c\uc120\uc744 \uc774\uc5b4\ub098\uac00\ub294 \ub2e8\uc21c\ud558\uace0 \ubc18\ubcf5\uc801\uc778 \uad00\ub9ac \ubc29\uc2dd\uc785\ub2c8\ub2e4. \uc624\ub298\ub0a0 \ub2e4\uc591\ud55c \ubd84\uc57c\uc5d0\uc11c \uc0ac\uc6a9\ub418\ub294 \ub9ce\uc740 \ud504\ub85c\uc138\uc2a4 \ubc0f \ud488\uc9c8 \uad00\ub9ac \uc811\uadfc\ubc95\uacfc \ub9c8\ucc2c\uac00\uc9c0\ub85c PDCA\ub294 20\uc138\uae30 \uc81c\uc870\uc5c5\uc5d0\uc11c \uc0ac\uc6a9\ub418\uba74\uc11c \ub110\ub9ac \uc54c\ub824\uc84c\uc2b5\ub2c8\ub2e4. \ub2e8\uc21c\ud558\uba74\uc11c\ub3c4 \uc27d\uac8c \ub530\ub77c\ud560 \uc218 \uc788\ub294 \ud504\ub85c\uc138\uc2a4 \ub355\ubd84\uc5d0 PDCA\ub294 \uc624\ub298\ub0a0 \uc81c\uc870\uc5c5 \uc774\uc678\uc5d0\ub3c4 \ub2e4\uc591\ud55c \ubd84\uc57c\uc758 \uac1c\uc778, \ud300, \uc870\uc9c1\uc5d0\uc11c \uc0ac\uc6a9\ub418\uace0 \uc788\uc8e0.\\n\\n## PDCA\uc758 \uc720\ub798\\n\\nPDCA\ub294 W. Edwards Deming\uc758 \'\uc288\uc6cc\ud2b8 \uc0ac\uc774\ud074(Shewhart Cycle)\'\uc5d0\uc11c \uc720\ub798\ud588\uc2b5\ub2c8\ub2e4. \uc774 \uc0ac\uc774\ud074\uc758 \uc774\ub984\uc740 \ud604\ub300 \ud488\uc9c8 \uad00\ub9ac\uc758 \uc544\ubc84\uc9c0\ub85c \ubd88\ub9ac\ub294 \ud1b5\uacc4\ud559\uc790 Walter Shewhart\uc758 \uc774\ub984\uc5d0\uc11c \ub530\uc628 \uac83\uc785\ub2c8\ub2e4. \ubbf8\uad6d\uc758 \uad50\uc218\uc774\uc790 \uc5d4\uc9c0\ub2c8\uc5b4\uc600\ub358 Deming\uc740 \ud488\uc9c8 \uad00\ub9ac \uc774\ub860\uc73c\ub85c \uc804\ud6c4 \uc77c\ubcf8\uc758 \uc0b0\uc5c5 \ud504\ub85c\uc138\uc2a4\ub97c \uac1c\uc120\ud574 \uacbd\uc81c\ub97c \ubd80\ud765\uc2dc\ud0a8 \uc0ac\ub78c\uc73c\ub85c \uc720\uba85\ud569\ub2c8\ub2e4. \uc2e4\uc81c\ub85c PDCA\ub77c\ub294 \uc774\ub984\uc740 Deming\uc758 \uac15\uc758\ub97c \ub4e4\uc740 \uc0ac\ub78c\ub4e4\uc774 \uc288\uc6cc\ud2b8 \uc0ac\uc774\ud074\uc744 \'\uacc4\ud68d-\uc2e4\ud589-\ud655\uc778-\uc870\uce58(Plan-Do-Check-Act)\'\ub85c \uc815\ub9ac\ud55c \uac83\uc5d0\uc11c \uae30\uc778\ud588\uc8e0. Deming\uc740 \uc0ac\uc2e4 \'\ud655\uc778(Check)\'\ubcf4\ub2e4 \'\uc5f0\uad6c(Study)\'\ub77c\ub294 \uc6a9\uc5b4\ub97c \uc120\ud638\ud574 \uc774 \uc0ac\uc774\ud074\uc744 PDSA\ub85c \ubd88\ub800\ub294\ub370, \uadf8 \uc774\uc720\ub294 \'\uc5f0\uad6c\'\uc758 \uacbd\uc6b0 \uc5b4\ub5a4 \uc810\uc774 \ubc14\ub00c\uc5c8\ub294\uc9c0 \ub2e8\uc21c\ud558\uac8c \ud655\uc778\ub9cc \ud558\ub294 \uac83\uc774 \uc544\ub2c8\ub77c \uacb0\uacfc\ub97c \uc2ec\uce35 \ubd84\uc11d\ud558\ub294 \ub370 \uc911\uc810\uc744 \ub450\uae30 \ub54c\ubb38\uc785\ub2c8\ub2e4.\\n\\n\ud558\uc9c0\ub9cc \uc5ec\ub7ec \ubc88 \uc644\ub8cc\ud558\uace0 \ubc18\ubcf5\ud574\uc57c \ud558\ub294 \ud2b9\uc131 \ud0d3\uc5d0 \uc774 \uc811\uadfc\ubc95\uc740 \uc624\ub298\ub0a0 \ub300\uccb4\ub85c PDCA \uc0ac\uc774\ud074\ub85c \ubd88\ub9ac\uace0 \uc788\uc2b5\ub2c8\ub2e4. PDCA\uc758 \uad6c\uc870\uc640 \ub17c\ub9ac\ub294 \ub9b0 \uc81c\uc870, \uce74\uc774\uc820, 6\uc2dc\uadf8\ub9c8 \ub4f1 \ub3d9\uc2dc\ub300 \uc81c\uc870 \uae30\ubc18 \ud488\uc9c8 \uad00\ub9ac \uc811\uadfc\ubc95\uc5d0\uc11c\ub3c4 \ucc3e\uc544\ubcfc \uc218 \uc788\uc2b5\ub2c8\ub2e4.\\n\\n## \ud504\ub85c\uc81d\ud2b8 \uc2e4\ud328 \ud604\ud669 1\\n\\n\ubbf8\uad6d\uc758 1,450\uac1c \ub300\uae30\uc5c5\uc744 \ub300\uc0c1\uc73c\ub85c \uc2e4\uc2dc\ud55c \uc124\ubb38\uc870\uc0ac \uacb0\uacfc\\nIT \ud504\ub85c\uc81d\ud2b8 \uc911 \uc57d 31%\uac00 \ud504\ub85c\uc81d\ud2b8 \uc644\ub8cc \uc804 \uc774\uc288 \uc0ac\ud56d \ubc1c\uc0dd\uc73c\ub85c \ucde8\uc18c\ub418\uace0,\\n\uc644\ub8cc\ub41c \ud504\ub85c\uc81d\ud2b8\uc758 \ubc18 \uc774\uc0c1\uc774 \ucc45\uc815\ub41c \ube44\uc6a9\uc758 189% \uc774\uc0c1\uc744 \uc9c0\ucd9c\ud558\ub294 \uac83\uc73c\ub85c \ub098\ud0c0\ub0a8.\\n\ud504\ub85c\uc81d\ud2b8 \uc2e4\ud328\uc758 \uc720\ud615\uc73c\ub85c\ub294 \uc77c\uc815\uc758 \uc9c0\uc5f0, \uc608\uc0b0 \ucd08\uacfc \ubc0f \uae30\ub300\ud6a8\uacfc\uc5d0 \ub300\ud55c \ubd88\ub9cc\uc871 \uc774\uc5c8\uc73c\uba70,\\n\ub300\ub2e4\uc218\uc758 \ud504\ub85c\uc81d\ud2b8\uac00 \uc77c\uc815\uc774 \uc9c0\uc5f0\ub428.\\n\ud2b9\ud788 1\ub144 \uc774\uc0c1 \uc7a5\uae30 \ud504\ub85c\uc81d\ud2b8\uc758 \uacbd\uc6b0 35% \uc774\uc0c1\uc774 \uacc4\ud68d\ub41c \uc77c\uc815\uc758 \ub450 \ubc30 \uc774\uc0c1\uc758 \uae30\uac04\uc774 \uc18c\uc694\ub41c \uac83\uc73c\ub85c \ub098\ud0c0\ub0a8\\n\\n![1](./image1.png)\\n\\n## \ud504\ub85c\uc81d\ud2b8 \uc2e4\ud328 \uc6d0\uc778\\n\\n\ud504\ub85c\uc81d\ud2b8\uc758 \uc8fc\uc694 Risk Factor\ub294 \uc77c\uc815 \ucc28\uc9c8, \ud504\ub85c\uc81d\ud2b8 \ubc94\uc704\uc758 \ubcc0\uacbd \ubc0f \ube44\uc6a9 \ucd08\uacfc \ub4f1\uc73c\ub85c \ub098\ud0c0\ub0ac\uace0, \ucd08\uae30\uc5d0 \uc798\ubabb \uc218\ub9bd\ub41c \ud504\ub85c\uc81d\ud2b8\\n\uacc4\ud68d\uc774 \ud504\ub85c\uc81d\ud2b8 \uc804 \uae30\uac04\uc744 \ud1b5\ud558\uc5ec \ubb38\uc81c\ub97c \ubc1c\uc0dd\uc2dc\ud0a4\uac8c \ub418\ubbc0\ub85c, \ud504\ub85c\uc81d\ud2b8 \ucd08\uae30 \ub2e8\uacc4\uc758 \uc801\uc808\ud55c \uc704\ud5d8\uad00\ub9ac\uacc4\ud68d \uc218\ub9bd \ubc0f \uacac\uace0\ud55c \ud504\\n\ub85c\uc81d\ud2b8 \uacc4\ud68d \uc218\ub9bd\uc758 \uc911\uc694\uc131\uc774 \uc81c\uae30\ub428\\n\\n![2](./image2.png)\\n\\n## \ud504\ub85c\uc81d\ud2b8 \uc2e4\ud328 \uc6d0\uc778 2\\n\\n\ube44\uc988\ub2c8\uc2a4 Driver \uc5ed\uc2dc \ud504\ub85c\uc81d\ud2b8 \uc131\uacf5\uc744 \uc88c\uc6b0\ud558\ub294 \uc8fc\uc694\ud55c \uc694\uc18c\ub85c \ucda9\ubd84\ud55c \ube44\uc988\ub2c8\uc2a4 \uc694\uac74 \ubd84\uc11d \ubc0f \ud504\ub85c\uc81d\ud2b8 \uae30\uac04 \uc911 \ubc1c\uc0dd\ub418\ub294 \ube44\\n\uc988\ub2c8\uc2a4 \ubcc0\ud654\uc5d0 \ub300\ud55c \uc2e0\uc18d\ud55c \ubc18\uc601\uc774 \uc911\uc694\ud558\uba70, \ud504\ub85c\uc81d\ud2b8 \uad00\ub9ac \uc804\ubb38 \uc778\ub825\uc744 \ud1b5\ud55c \uccb4\uacc4\uc801\uc774\uace0 \uc9c0\uc18d\uc801\uc778 Monitoring\uc5d0 \ub300\ud55c \ud544\uc694\\n\uc131\uc774 \uc81c\uae30\ub418\uc5c8\uc2b5\ub2c8\ub2e4.\\n\\n![3](./image3.png)\\n\\n## \ud504\ub85c\uc81d\ud2b8 \uc2e4\ud328 \uc6d0\uc778 3\\n\\n\uc774\ub7ec\ud55c \uacb0\uacfc\ub97c \uc885\ud569\ud558\uc5ec \ubd84\uc11d\ud55c \uacb0\uacfc \ud504\ub85c\uc81d\ud2b8 \uc2e4\ud328\uc758 \uc8fc\uc694 \uc6d0\uc778\uc740\\n\\n1. \ucd08\uae30\uc5d0 \uc798\ubabb \uc218\ub9bd\ub41c \ud504\ub85c\uc81d\ud2b8 \uacc4\ud68d,\\n2. \ube44\uc988\ub2c8\uc2a4\uc5d0 \ub300\ud55c \ubc18\uc601 \ubbf8\ud761,\\n3. \ud504\ub85c\uc81d\ud2b8 \uad00\ub9ac \ubd80\uc7ac\ub85c \ub098\ud0c0\ub0ac\uc73c\uba70,\\n\\n\uc751\ub2f5\uc790\ub4e4\uc740 \ud504\ub85c\uc81d\ud2b8 \uc2e4\ud328\ub97c \ubc29\uc9c0\ud558\uae30 \uc704\ud55c \ud574\uacb0\ucc45\uc73c\ub85c \uc804\ubb38\uc801\uc774\uace0 \uccb4\uacc4\uc801\uc778 \ud504\ub85c\uc81d\ud2b8 \uad00\ub9ac\ub97c \uc81c\uc2dc\ud568\\n\\n![](./image4.png)\\n\\n## \ud504\ub85c\uc81d\ud2b8 \uc131\uacf5 \uc694\uc778\\n\\n| \uc6b0\uc120 \uc21c\uc704 | \ud504\ub85c\uc81d\ud2b8 \uc131\uacf5 \uc694\uc778                  | \uc6b0\uc120 \uc21c\uc704 | \ud504\ub85c\uc81d\ud2b8 \uc131\uacf5 \uc694\uc778           |\\n| :-------: | ----------------------------------- | :-------: | ---------------------------- |\\n|     1     | \uc801\uc6a9 \uac00\ub2a5\ud55c \uacc4\ud68d                    |    10     | \uc2e0\uc18d\ud55c \uc758\uc0ac\uacb0\uc815              |\\n|     2     | \uba85\ud655\ud55c \uae30\uc220 \uc0ac\uc591                    |    11     | \uacc4\uc57d \uc804\ub7b5                    |\\n|     3     | \uccb4\uacc4\uc801\uc778 \uc758\uc0ac\uc18c\ud1b5                   |    12     | \ud6a8\uacfc\uc801\uc778 \ud300\uc6cd                |\\n|     4     | \ud300\uc6d0\uc758 \uba85\ud655\ud55c \uc5ed\ud560\uacfc \ucc45\uc784           |    13     | H/W, S/W, \uc778\uc6d0\uc758 \uc801\uc808\ud55c \ud1b5\ud569 |\\n|     5     | \uc77c\uc815 \ucee8\ud2b8\ub864                         |    14     | \uacbd\uc81c\uc801 \ud0c0\ub2f9\uc131                |\\n|     6     | \uc804\ubb38\uac00 \ud65c\uc6a9                         |    15     | \ud6a8\uacfc\uc801\uc778 \ub9ac\ub354\uc2ed              |\\n|     7     | \uc608\uc0b0 \ucee8\ud2b8\ub864                         |    16     | \ucd5c\uace0\uacbd\uc601\uc790\uc758 \uc9c0\uc6d0            |\\n|     8     | \uba85\ud655\ud55c \ud504\ub85c\uc81d\ud2b8 \ubaa9\ud45c                |    17     | \ud6a8\uacfc\uc801\uc778 \uac08\ub4f1 \uad00\ub9ac           |\\n|     9     | \ud504\ub85c\uc81d\ud2b8 \uc694\uad6c \uc790\uaca9 \uc694\uac74\uc744 \uac16\ucd98 \uc778\ub825 |           |                              |\\n\\n- seles \uc5d0 \ub9de\uac8c \uacc4\ud68d\ud558\ub294 \uac83\uc740 \uc2e4\ud328\uc758 \uc694\uc778\uc774 \ub41c\ub2e4.\\n- <b>\\n    PM\uc740 \uc801\uc6a9\uac00\ub2a5\ud55c \uacc4\ud68d\uc744 \uac00\uc9c0\uace0 , \uba85\ud655\ud55c \uae30\uc220\uc0ac\uc591\uc744 \uac00\uc9c0\uace0 , \ud300\uc6d0\ub4e4\uacfc\\n    \uc758\uc0ac\uc18c\ud1b5\uc744 \ud574\uc57c \ud55c\ub2e4\\n  </b>\\n  - \uc911\uc694\ud55c \uc598\uae30\ub294 \ud55c\ubc88\uc774 \uc544\ub2c8\ub77c \ubc18\ubcf5\uc801\uc73c\ub85c \uc804\ub2ec\ud574\uc57c\ud55c\ub2e4.\\n\\n## PMBOK \uac1c\uc694\\n\\n- First drafted in 1983 and now in its fourth edition, the PMBOK\xae Guide provides the fundamentals of project management as they apply to a wide range of projects. (\ud504\ub85c\uc81d\ud2b8 \uad00\ub9ac\uc758 \uae30\ucd08 \uc81c\uacf5 \uac00\uc774\ub4dc)\\n\\n- It is found in top business schools, corporate training centers and on the desks of hundreds of thousands of working project managers in all industries all over the world. (\uc804\uccb4 \uc0b0\uc5c5\ubd84\uc57c, \uacbd\uc601\ub300\ud559(\uc6d0), \uae30\uc5c5\uad50\uc721\uc13c\ud130, PM\uc758 \ucc45\uc0c1)\\n\\n- The PMBOK\xae Guide is available in English and in 10 official translations. Including previous editions and official translations, over 2.5 million copies are in circulation. (\uc601\uc5b4 \uc678 10\uac1c\uad6d\uc5b4\ub85c \ubc88\uc5ed, 250\ub9cc\ubd80 \uc774\uc0c1)\\n\\n- \ud68c\uc0ac\ub0b4\uc758 \ud504\ub85c\uc81d\ud2b8\uc5d0 \ub300\ud55c \ud45c\uc900\uc744 \ub9cc\ub4e4\uc5b4\uc57c \ud55c\ub2e4. (\uc804\uc790\uc815\ubd80 \ud504\ub808\uc784\uc6cc\ud06c, \uc560\ub2c8\ud504\ub808\uc784\uc6cc\ud06c \ub4f1)\\n\\n- \uc774\ub97c \uc704\ud558\uc5ec \ubcc0\ud654\ub97c \uc704\ud55c \uacc4\ud68d\uc744 \uc218\ub9bd\ud558\uae30 \uc704\ud558\uc5ec \ucee8\uc124\ud134\ud2b8\ub97c \ud574\uc57c\ud55c\ub2e4.\\n\\n## PMBOK \uac1c\uc694\\n\\n### \u25a0 \ud504\ub85c\uc81d\ud2b8\uc758 \uc815\uc758\\n\\n- A project is a temporary endeavor undertaken to create a unique product or service.\\n- \ud2b9\uc815 \uc81c\ud488, \uc11c\ube44\uc2a4\ub97c \ub9cc\ub4e4\uae30 \uc704\ud574 \uc218\ud589\ub418\uc5b4\uc57c \ud560 \uc77c\uc2dc\uc801\uc778 \ub178\ub825\\n\\n### \u25a0 \ud504\ub85c\uc81d\ud2b8\uc758 \ud2b9\uc9d5\\n\\n- \u2022 Temporary\\n  - \uba85\ubc31\ud55c \ud504\ub85c\uc81d\ud2b8 \ucc29\uc218\uc77c\uacfc \uc644\ub8cc\uc77c\uc774 \uc874\uc7ac\\n  - \uc9e7\uc740 \uae30\uac04\ub9cc\uc744 \uc758\ubbf8\ud558\uc9c0 \uc54a\uc74c\\n  - Temporary Market Window : \uc2dc\uc7a5\uc774 \ud5c8\ub77d\ud558\ub294 \ud55c\uc815\ub41c \uc2dc\uac04 \ub0b4\uc5d0 \ud504\ub85c\uc81d\ud2b8 \uc644\uc218\\n  - Temporary Project Team : \ud504\ub85c\uc81d\ud2b8 \uc885\ub8cc \uc9c1\ud6c4 \ub300\ubd80\ubd84\uc758 \ud504\ub85c\uc81d\ud2b8 \ud300\uc740 \ud574\uccb4\ub428\\n- \u2022 Unique Product, Service, or Result\\n  - \ub300\ubd80\ubd84\uc758 \ud504\ub85c\uc81d\ud2b8\ub294 \uae30\uc874\uc5d0 \uc5c6\ub358 \uc81c\ud488\uc774\ub098 \uc11c\ube44\uc2a4\ub97c \uac1c\ubc1c\\n- \u2022 Progressive Elaboration (\uc810\uc9c4\uc801 \uad6c\uccb4\ud654)\\n  - Temporary\uc640 Unique \uac1c\ub150\uc758 \ud1b5\ud569\\n  - Progressive: \uc21c\ucc28\uc801, \ub2e8\uacc4\uc801 \uc9c4\ud589\\n  - Elaboration: \uc138\ubd80\ud56d\ubaa9\uae4c\uc9c0 \ucca0\uc800\ud558\uac8c \uc791\uc5c5\\n\\n### \u25a0 Project vs. Operation\\n\\n- \uacf5\ud1b5\uc810\\n  - Performed by People, \uc0ac\ub78c\uc774 \ud558\ub294 \uc77c\\n  - Constrained by Limited Resources, \uc790\uc6d0\uc774 \uc81c\uc57d\ub428\\n  - Planned, Executed and Controlled, \uacc4\ud68d, \uc2e4\ud589, \ud1b5\uc81c\ub428\\n- \ucc28\uc774\uc810\\n  - Project : Temporary, Unique Product or Service, \ud2b9\uc815 \uc81c\ud488, \uc11c\ube44\uc2a4\\n  - <u>Operation : On-going, Repetitive, \uc9c0\uc18d\uc801, \ubc18\ubcf5\uc801</u>\\n\\n![](./%EC%8A%A4%ED%81%AC%EB%A6%B0%EC%83%B7%202023-02-25%20%EC%98%A4%EC%A0%84%2011.24.24.png)\\n\\n### \u25a0 \uae30\ud0c0 \uad00\ub828 \uac1c\ub150\\n\\n- Programs\\n  - \ud558\ub098 \uc774\uc0c1\uc758 \ud504\ub85c\uc81d\ud2b8\uac00 \uc798 \uc9dc\uc5ec\uc9c4 Coordination\uc5d0 \ub530\ub77c \uad00\ub9ac\ub428\\n  - \uac1c\uac1c\uc758 \ud504\ub85c\uc81d\ud2b8\ub9cc\uc73c\ub85c\ub294 \uc5bb\uc744 \uc218 \uc5c6\ub294 \uc774\uc775 \ud68d\ub4dd\uc744 \ubaa9\uc801\uc73c\ub85c \ud568\\n- Subproject\\n  - \ud558\ub098\uc758 \ud504\ub85c\uc81d\ud2b8\uac00 \uad00\ub9ac \uac00\ub2a5\ud55c \uc791\uc740 \uad6c\uc131\uc694\uc18c\ub85c \ubd84\ub9ac\ub428\\n- Project Portfolio Management (SM\uc131\uc740 \ube60\uc9d0)\\n  - \ud504\ub85c\uc81d\ud2b8 \ub610\ub294 \ud504\ub85c\uadf8\ub7a8\uc5d0 \ub300\ud55c \ud22c\uc790 \uc5ec\ubd80\ub97c \uacb0\uc815\ud558\ub294\ub370 \uc788\uc5b4\uc11c \ud68c\uc0ac\uc758 \uc804\ub7b5\uacfc \uac00\uc6a9 \uc790\uc6d0\uc744 \uadfc\uac70\ub85c\ud68c\uc0ac\uc758 \uc804\uccb4 \ud504\ub85c\uc81d\ud2b8\ub97c \uad00\ub9ac\ud568\\n\\n## Project, Program, Portfolio\\n\\n| \uad6c\ubd84 | \ud504\ub85c\uc81d\ud2b8 \uad00\ub9ac                                                                 | \ud504\ub85c\uadf8\ub7a8 \uad00\ub9ac                                                                    | \ud3ec\ud2b8\ud3f4\ub9ac\uc624 \uad00\ub9ac                                                                                                                                                  |\\n| ---- | ----------------------------------------------------------------------------- | -------------------------------------------------------------------------------- | ---------------------------------------------------------------------------------------------------------------------------------------------------------------- |\\n| \ubc94\uc704 | \uace0\uc720\ud55c \uc81c\ud488, \ubaa9\ud45c\ub97c \ud55c\uc2dc\uc801 \uc0dd\uba85\uc8fc\uae30 \ub3d9\uc548 \ub2ec\uc131\ud558\ub294 \uc810\uc9c4\uc801 \uc0c1\uc138\ud654\ub97c \uac00\uc9c0\ub294 \ub178\ub825 | \ud504\ub85c\uadf8\ub7a8\uc740 \ud504\ub85c\uc81d\ud2b8\ubcf4\ub2e4 \uc0c1\ub300\uc801\uc73c\ub85c \ub354\uc6b1 \uc911\uc694\ud55c \uc774\uc775 \ubc0f \ud070 \ubc94\uc704\ub97c \uac00\uc9d0            | - \uc870\uc9c1\uc758 \uc804\ub7b5\uc801 \ubaa9\ud45c\ub4e4\uacfc \uc870\uc9c1\uc801\uc778 \ubc94\uc704\ub97c \uac00\uc9d0 - \uc6b4\uc601 \uc5c5\ubb34 \ud3ec\ud568(\uc9d1\ud569\uccb4)                                                                                           |\\n| \ubcc0\uacbd | \ud504\ub85c\uc81d\ud2b8 \uad00\ub9ac\uc790\ub294 \ubcc0\uacbd \uad00\ub9ac\ub97c \ud1b5\ud574 \uac10\uc2dc \ubc0f \ud1b5\uc81c \uc218\ud589                          | \ud504\ub85c\uadf8\ub7a8 \uad00\ub9ac\uc790\ub294 \ud504\ub85c\uadf8\ub7a8 \ub0b4/\uc678\ubd80\uc758 \ubaa8\ub4e0 \ubcc0\uacbd \uad00\ub9ac                              | \ud3ec\ud2b8\ud3f4\ub9ac\uc624 \uad00\ub9ac\uc790\ub294 \ub0b4/\uc678\ubd80 \ud658\uacbd\uc758 \uc9c0\uc18d\uc801\uc778 \uac10\uc2dc \ubc0f \ud1b5\uc81c                                                                                                         |\\n| \uacc4\ud68d | \ud504\ub85c\uc81d\ud2b8 \uad00\ub9ac\uc790\ub294 \ud504\ub85c\uc81d\ud2b8 \uc0dd\uba85\uc8fc\uae30 \ub3d9\uc548 \uc810\uc9c4\uc801 \uc0c1\uc138\ud654 \uacc4\ud68d                   | \ud504\ub85c\uadf8\ub7a8 \uad00\ub9ac\uc790\ub294 \uac01 \ucef4\ud3ec\ub10c\ud2b8 \uc218\uc900\ubcc4 \ud504\ub85c\uadf8\ub7a8 \ubc0f \uc0c1\uc138\ud654 \uacc4\ud68d \uac1c\ubc1c                | \ud3ec\ud2b8\ud3f4\ub9ac\uc624 \uad00\ub9ac\uc790\ub294 \uc885\ud569\uc801\uc778 \uae30\uc5c5 \ud3ec\ud2b8\ud3f4\ub9ac\uc624\uc5d0 \ub300\ud574 \uac01\uac01 \ud504\ub85c\uc81d\ud2b8/\ud504\ub85c\uadf8\ub7a8/\ud3ec\ud2b8\ud3f4\ub9ac\uc624 \ud504\ub85c\uc138\uc2a4\uc640 \uc758\uc0ac \uc804\ub2ec \uc0dd\uc131 \ubc0f \uc218\ud589                                          |\\n| \uad00\ub9ac | \ud504\ub85c\uc81d\ud2b8 \uad00\ub9ac\uc790\ub294 \ud504\ub85c\uc81d\ud2b8 \ubaa9\uc801\uc744 \ub2ec\uc131\ud560 \ub54c\uae4c\uc9c0 \ud504\ub85c\uc81d\ud2b8 \ud300\uc744 \uad00\ub9ac            | \ud504\ub85c\uadf8\ub7a8 \uad00\ub9ac\uc790\ub294 \ud504\ub85c\uadf8\ub7a8 \uc2a4\ud0dc\ud504\uc640 \ud504\ub85c\uc81d\ud2b8 \uad00\ub9ac\uc790\uc5d0\uac8c \ube44\uc804 \uc81c\uacf5 \ubc0f \ub9ac\ub354\uc2ed \uad00\ub9ac | \ud3ec\ud2b8\ud3f4\ub9ac\uc624 \uad00\ub9ac\uc790\ub294 \ud3ec\ud2b8\ud3f4\ub9ac\uc624 \uad00\ub9ac \uc2a4\ud0dc\ud504, \ud504\ub85c\uadf8\ub7a8 \uad00\ub9ac\uc790, \ud504\ub85c\uc81d\ud2b8 \uad00\ub9ac\uc790 \ub4f1 \uc804\uc0ac \uc885\ud569\uc801\uc778 \ud3ec\ud2b8\ud3f4\ub9ac\uc624\uc5d0 \ub300\ud558\uc5ec \ucc45\uc784\uc744 \uac00\uc9c8 \uc218 \uc788\ub3c4\ub85d \uad00\ub9ac \ubc0f \uc870\uc815 \uad00\ub9ac\ub97c \uc218\ud589 |\\n| \uc131\uacf5 | \ud504\ub85c\uc81d\ud2b8 \uc131\uacf5\uc740 \ud488\uc9c8, \uc77c\uc815 \uc900\uc218, \uc608\uc0b0 \uc900\uc218, \uace0\uac1d\ub9cc\uc871 \uc815\ub3c4\uc5d0 \uc758\ud574 \uce21\uc815\ub428       | \ud504\ub85c\uc81d\ud2b8 \uc774\uc775 \uc815\ub3c4\uc5d0 \uc758\ud574 \uc131\uacf5\uc774 \uce21\uc815\ub428                                          | \ud3ec\ud2b8\ud3f4\ub9ac\uc624\uc758 \uc218\ud589\uc73c\ub85c \uc778\ud55c \ud22c\uc790 \ub300\ube44 \ud6a8\uacfc\uc5d0 \uc758\ud574 \uce21\uc815\ub428                                                                                                          |\\n| \uac10\uc2dc | \ud504\ub85c\uc81d\ud2b8 \uad00\ub9ac\uc790\ub294 \uc81c\ud488, \uc11c\ube44\uc2a4 \ud504\ub85c\uc81d\ud2b8 \uacb0\uacfc\uc5d0 \ub300\ud574 \uac10\uc2dc \ubc0f \ud1b5\uc81c              | \ud504\ub85c\uadf8\ub7a8 \uad00\ub9ac\uc790\ub294 \ubaa9\ud45c, \uc77c\uc815, \uc608\uc0b0, \uc774\uc775\uc744 \ud504\ub85c\uadf8\ub7a8 \ucef4\ud3ec\ub10c\ud2b8\ubcc4 \uac10\uc2dc              | \ud3ec\ud2b8\ud3f4\ub9ac\uc624 \uad00\ub9ac\uc790\ub294 \uc804\ub7b5\uc801\uc778 \ubcc0\uacbd \ubc0f \uc131\uacfc\uc5d0 \ub300\ud558\uc5ec \uac10\uc2dc                                                                                                          |\\n\\n## Project \uad00\ub9ac \uc815\uc758\\n\\n### \u25a0 \ud504\ub85c\uc81d\ud2b8 \uad00\ub9ac\uc758 \uc815\uc758\\n\\n- Project management is the application of knowledge, skills, tools, and technique to project activities in order to meet or exceed stakeholder needs and expectation from a project.\\n- \ud504\ub85c\uc81d\ud2b8 \ud65c\ub3d9\uc5d0 \uc9c0\uc2dd, \uc2a4\ud0ac, \ub3c4\uad6c, \uae30\ubc95\uc744 \uc801\uc6a9\ud558\ub294 \uac83\\n- \uc774\ud574\ub2f9\uc0ac\uc790\uc758 \uc694\uad6c, \uae30\ub300\uce58\ub97c \ub9cc\uc871\uc2dc\ud0a4\uac70\ub098 \ucd08\uacfc \ub2ec\uc131\ud558\ub294 \uac83\uc744 \ubaa9\uc801\uc73c\ub85c \ud568\\n\\n### \u25a0 \ud504\ub85c\uc81d\ud2b8 \ud300\uc774 \ud1b5\uc81c\ud558\ub294 \ub300\uc0c1\\n\\n- \ubc94\uc704, \uc77c\uc815, \ube44\uc6a9, \ub9ac\uc2a4\ud06c, \ud488\uc9c8 \ub4f1\uc5d0 \ub300\ud55c \uc0c1\ucda9\uc801 \uc694\uad6c\uc0ac\ud56d\\n- \uc11c\ub85c \ub2e4\ub978 Needs\uc640 \uae30\ub300\uc218\uc900\uc744 \uac00\uc9c4 Stakeholder\ub4e4\\n- \uc694\uad6c\uc0ac\ud56d\uacfc \uace0\uac1d \uae30\ub300\uc0ac\ud56d\uc758 \ucda9\uc871\\n\\n## Project \uacc4\ud68d Vs \uc2e4\uc81c\\n\\n### \u25a0 \ud504\ub85c\uc81d\ud2b8 \uacc4\ud68d\\n\\n- \u2022 \ud504\ub85c\uc81d\ud2b8 \uc9c4\ud589 \uc0c1\ud669\uc744 \uc608\uc0c1\ud558\uc5ec, \uc608\uc0c1\ub41c \uc2dc\uc810\uc5d0 \uc0ac\ub78c\ub4e4\uc774 \uc218\ud589\ud574\uc57c \ud558\ub294 \uc791\uc5c5\ub4e4\uc744 \uacc4\ud68d\ud55c \ubaa8\ub378\\n\\n### \u25a0 \ud504\ub85c\uc81d\ud2b8 \uc2e4\ud589\\n\\n- \u2022 \ud504\ub85c\uc81d\ud2b8\ub97c \uc2e4\uc81c\uc0c1\ud669\uc5d0\uc11c \uc0ac\ub78c\ub4e4\uc774 \uc218\ud589\ud558\ub294 \uac83\uc784\\n\\n### \u25a0 \ud504\ub85c\uc81d\ud2b8 \uacc4\ud68d\uacfc \uc2e4\uc81c \uc0c1\ud669 \ube44\uad50\\n\\n| \uacc4\ud68d          | \uc2e4\uc81c\uc0c1\ud669                 |\\n| ------------- | ------------------------ |\\n| \ub17c\ub9ac\uc801 \uc9c4\ud589   | \ube44\ub17c\ub9ac\uc801 \uc9c4\ud589            |\\n| \uc77c\uad00\uc131        | \ub3cc\ubc1c\uc0c1\ud669 \ubc1c\uc0dd, \ube44 \uc77c\uad00\uc131 |\\n| \uc798 \uc815\ub9ac\ub428     | \ubcf5\uc7a1\ud55c \uc0c1\ud669\uc758 \ub3d9\uc2dc\ubc1c\uc0dd   |\\n| \ub2e8\uc21c\ud654\ub41c \ubaa8\ub378 | Fuzzy (\uc544\uc218\ub77c\uc7a5)         |\\n\\n## \ud504\ub85c\uc81d\ud2b8\uad00\ub9ac \uc218\uba85\uc8fc\uae30\\n\\n- \ube44\uc6a9\uc744 \uc544\ub084\ub824\ub2e4\uac00 \ud504\ub85c\uc81d\ud2b8\uc5d0 \uc2e4\ud328\ud558\ub294 \uacbd\uc6b0\uac00 \ubc1c\uc0dd\ud55c\ub2e4.\\n\\n![](./image3.png)\\n\\n![](./image1.png)\\n\\n![](./image2.png)\\n\\n## \uc26c\ub294\uc2dc\uac04\\n\\n- \ub9ac\ub354\uc2ed\uc740 \ub530\ub77c\uc11c \uc774\ub4dd\uc774 \uc0dd\uae34\ub2e4\ub294 \ud310\ub2e8\uc774 \uc788\uc73c\uba74 \ub530\ub97c\uac83\uc774\ub2e4.\\n- \uc598\uae30\ub97c \ud558\uace0 \uc8fc\ub294\uac8c \uc788\uc744\uacbd\uc6b0\\n\\n## \ud504\ub85c\uc81d\ud2b8\uad00\ub9ac \uc218\uba85\uc8fc\uae30 \ud2b9\uc9d5\\n\\n### Project Phase\uc758 \ud2b9\uc9d5\\n\\n1. \ud504\ub85c\uc81d\ud2b8\uc758 \ud1b5\uc81c \uad00\ub9ac\ub97c \uc6a9\uc774\ud558\uac8c \ud558\uae30 \uc704\ud574 \uc791\uc740 \ub2e8\uc704\uc758 Phase\ub85c \ub098\ub204\uc5b4 \uc218\ud589\\n2. \uac01 Phase\uc758 \ub9c8\uc9c0\ub9c9 \ub2e8\uacc4\uc5d0\uc11c\ub294 \ud558\ub098 \ub610\ub294 \ub2e4\uc218\uc758 Deliverables (Tangible, Verifiable Work\\n   Product)\uac00 \uc874\uc7ac\\n3. \uac01 Phase\uc758 \uc885\ub8cc\ub2e8\uacc4\uc5d0\uc11c\ub294 \uc624\ub958\ub97c \uc218\uc815\ud558\uace0 \ub2e4\uc74c Phase\ub85c\uc758 \uc9c4\ud589 \uc5ec\ubd80\ub97c \ud310\ub2e8\\n\\n### Project Life Cycle\uc5d0 \ub530\ub978 \ubcc0\ud654\\n\\n![](./image5.png)\\n\\n## \u2b50\ufe0f\u2b50\ufe0f\u2b50\ufe0f\u2b50\ufe0f 10\ub300 \ud504\ub85c\uc81d\ud2b8 \uad00\ub9ac \ub300\uc0c1 \uc601\uc5ed(KA\\\\*)\\n\\n![](./1.png)\\n\\n- \uc774\ud574\uad00\uacc4\uc790\uc758 \uacf5\uc870 : \uc790\uc6d0 \uad00\ub9ac, \uc758\uc0ac\uc18c\ud1b5 \uad00\ub9ac , \uc774\ud574\uad00\uacc4\uc790 \uad00\ub9ac , \uc870\ub2ec\uad00\ub9ac\\n\\n  - \uc774\ud574\uad00\uacc4\uc790 : \ud611\ub825\uc0ac, \uac1c\ubc1c\uc790 \ub4f1\ub4f1\\n  - \uc774\ud574\uad00\ub9ac\ub97c \uc758\ud558\uc5ec : \uc758\uc0ac\uc18c\ud1b5\\n\\n- \ud1b5\ud569\uad00\ub9ac (\uc0bc\uc911\uad00\ub9ac) : \ubc94\uc704 , \uc77c\uc815 , \uad8c\uac00\\n\\n## 10\ub300 \ud504\ub85c\uc81d\ud2b8\uad00\ub9ac \ub300\uc0c1 \uc601\uc5ed \ub0b4\uc6a9\\n\\n![](./%EC%8A%A4%ED%81%AC%EB%A6%B0%EC%83%B7%202023-02-25%20%EC%98%A4%EC%A0%84%2011.54.27.png)\\n\\n## Project \uad00\ub9ac Cube\\n\\n![](./%EC%8A%A4%ED%81%AC%EB%A6%B0%EC%83%B7%202023-02-25%20%EC%98%A4%ED%9B%84%2012.19.20.png)\\n\\n## \ud504\ub85c\uc138\uc2a4 \uadf8\ub8f9 \uac1c\uc694\\n\\n### 1. \ud504\ub85c\uc138\uc2a4\uc758 \uc815\uc758\\n\\n- \u201cA Process is a series of actions (that transform inputs to outputs) bringing about expected results.\u201d \ud2b9\uc815\uacb0\uacfc\ub97c \ub2ec\uc131\ud558\uae30 \uc704\ud574 \ud544\uc694\ud55c \uc77c\ub828\uc758 \ud65c\ub3d9(\uc785\ub825\ubb3c\uc744 \ucd9c\ub825\ubb3c\ub85c \ubc14\uafc8)\\n\\n### 2. \ud504\ub85c\uc138\uc2a4 \uadf8\ub8f9\uc758 \ubd84\ub958\\n\\n1. Initiating Processes\\n   - \u2022 \ud504\ub85c\uc81d\ud2b8\ub098 \uac01 Stage\uc758 \uc2dc\uc791\uc774\ub098 \ub2e4\uc74c \ub2e8\uacc4\ub85c\uc758 \uc9c4\ud589\uc744 \uc2b9\uc778\ud568\\n2. Planning Processes\\n   - \u2022 \ud504\ub85c\uc81d\ud2b8 \ubaa9\uc801\uc744 \uc815\uc758\ud558\uace0 \uadf8 \ubaa9\uc801\uc744 \ub2ec\uc131\ud558\uae30 \uc704\ud55c \ub300\uc548\uc744 \ud0d0\uc0c9\ud558\uace0 \uacc4\ud68d\uc744 \uc218\ub9bd\ud568\\n3. Executing Processes\\n   - \u2022 \uc218\ub9bd\ub41c \uacc4\ud68d\uc744 \uc774\ud589\ud558\uae30 \uc704\ud55c \uc790\uc6d0\uc744 \ubc30\ubd84\ud558\uace0 \uc2e4\uc81c \uc791\uc5c5\uc744 \uc218\ud589\ud568\\n4. Controlling Processes\\n   - \u2022 \uacc4\uc18d\uc801\uc778 \ubaa8\ub2c8\ud130\ub9c1\uc744 \ud1b5\ud574 \uacc4\ud68d\uacfc \uc2e4\uc81c\uc758 \ucc28\uc774\ub97c \ud30c\uc545\ud558\uace0 \uc801\uc808\ud55c \uad50\uc815 \ud65c\ub3d9\uc744 \uc218\ud589\ud568\\n5. Closing Processes\\n   - \u2022 \ud504\ub85c\uc81d\ud2b8\ub098 \uac01 \ub2e8\uacc4\uc758 \uacb0\uacfc\uc5d0 \ub300\ud55c \uace0\uac1d \uc2b9\uc778\uc744 \ud68d\ub4dd\ud568\\n\\n## \ub2e8\uacc4, \ud504\ub85c\uc81d\ud2b8\uc0c1\uc758 \uadf8\ub8f9\uac04 \uc0c1\ud638\uc791\uc6a9\\n\\n![](./%EC%8A%A4%ED%81%AC%EB%A6%B0%EC%83%B7%202023-02-25%20%EC%98%A4%ED%9B%84%2012.21.47.png)\\n\\n---\\n\\n# PM(Project Manager)\\n\\n## PM(Project Manager) \uc5ed\ud560\uacfc \ucc45\uc784\\n\\n- \u26ab PM (Project Manager ) : \ud504\ub85c\uc81d\ud2b8 \ucd1d\uad04 \uad00\ub9ac\uc790\\n\\n![](./%EC%8A%A4%ED%81%AC%EB%A6%B0%EC%83%B7%202023-02-25%20%EC%98%A4%ED%9B%84%2012.29.29.png)\\n\\n- \ud504\ub85c\uc81d\ud2b8\uc758 \uc131\uacf5\uc744 \uc704\ud574\uc11c\ub294 \uace0\uac1d\uc758 \ub9c8\uc74c\uc744 \uc6c0\uc9c1\uc5b4\uc57c \ud55c\ub2e4.\\n\\n## \uc774\ud574\uad00\uacc4\uc790\ub4e4\\n\\n1. \ud504\ub85c\uc81d\ud2b8\uc5d0 \uc801\uadf9\uc801\uc73c\ub85c \ucc38\uc5ec\ud558\uac70\ub098 \ub610\ub294 \ud504\ub85c\uc81d\ud2b8\uc758 \uacb0\uacfc\uc5d0 \uc774\ud574 \uad00\uacc4\uac00 \uc788\ub294 \uac1c\uc778\uc774\ub098 \ubd80\uc11c\ub97c \ucd1d\uce6d\ud568\\n2. \ubaa8\ub4e0 Stakeholder\ub4e4\uc758 \uc694\uad6c\uc0ac\ud56d\uc740 \ud504\ub85c\uc81d\ud2b8 \ucd08\uae30\uc5d0 \ud655\uc815\ub418\uc5b4\uc57c \ud558\uba70 \ud504\ub85c\uc81d\ud2b8 \uc9c4\ud589 \uc911\uc5d0 \uc801\uc808\ud788 \uad00\ub9ac\ub418\uc5b4\uc57c \ud568\\n3. \uc8fc\uc694 Stakeholder\uc758 \uc608\\n   - \u2022 \uc790\uc0ac \ubc0f \uace0\uac1d\uc0ac\uc758 \ud504\ub85c\uc81d\ud2b8 \uad00\ub9ac\uc790\\n   - \u2022 \ud504\ub85c\uc81d\ud2b8\uc758 \uc0b0\ucd9c\ubb3c\uc744 \uc9c1\uc811 \uc0ac\uc6a9\ud560 \uace0\uac1d\uc0ac\uc758 \uac1c\uc778\uc774\ub098 \ubd80\uc11c, Key man\\n   - \u2022 \uc2e4\uc81c \ud504\ub85c\uc81d\ud2b8\ub97c \uc218\ud589\ud560 \uc791\uc5c5 \uc870\uc9c1, \ud300\uc6d0, \uadf8\ub8f9\\n   - \u2022 \ud504\ub85c\uc81d\ud2b8\uc5d0 \ub300\ud55c \uc7ac\uc815\uc9c0\uc6d0 \uacb0\uc815\uad8c\uc774\ub098 \uc758\uc0ac \uacb0\uc815\uad8c\uc744 \uac00\uc9c0\uace0 \uc788\ub294 \uc0ac\ub78c, \uc2a4\ud3f0\uc11c\\n4. \uae30\ud0c0 Stakeholders\\n   - \u2022 \ub0b4\ubd80 / \uc678\ubd80, \ud504\ub85c\uc81d\ud2b8 Owners, \uc7ac\uc815\uc9c0\uc6d0\uc790, \ud611\ub825\uc5c5\uccb4 \uc778\uc6d0, \ud300\uc6d0\uc758 \uac00\uc871, \uc815\ubd80\uae30\uad00, \ubbf8\ub514\uc5b4 \ub9e4\uccb4, \uad00\ub828 \uc555\ub825\ub2e8\uccb4, \uc0ac\ud68c \uc804\ubc18 \ub4f1\\n\\n![](./%EC%8A%A4%ED%81%AC%EB%A6%B0%EC%83%B7%202023-02-25%20%EC%98%A4%ED%9B%84%2012.31.43.png)\\n\\n## \uc870\uc9c1\uccb4\uacc4\\n\\n1. \uc870\uc9c1\uc758 \uc2dc\uc2a4\ud15c\\n   - \u2022 \uc870\uc9c1\uc774 \ubcf4\uc720\ud558\uace0 \uc788\ub294 \ubcf4\uc0c1\uccb4\uacc4 \ub4f1\uc758 \uace0\uc720\ud55c \uc870\uc9c1 \uc2dc\uc2a4\ud15c\uc740 \ud504\ub85c\uc81d\ud2b8\uc5d0 \uc601\ud5a5\uc744 \uc90c\\n2. \uc870\uc9c1\uc758 \ubb38\ud654\uc640 \uc2a4\ud0c0\uc77c\\n   - \u2022 \uc870\uc9c1\uc758 \uacf5\uc720\uac00\uce58, \uae30\uc900, \ubbff\uc74c, \uc815\ucc45, \uae30\ub300 \ub4f1 \ud68c\uc0ac\uac00 \ubcf4\uc720\ud55c \uae30\uc5c5 \ubb38\ud654 \ub4f1\uc740 \ud504\ub85c\uc81d\ud2b8 \uc218\ud589 \ubc29\uc2dd\uacfc \uc758\uc0ac\uc18c\ud1b5\uc5d0 \uc601\ud5a5\uc744 \uc90c\\n3. \uc870\uc9c1\uad6c\uc870\\n   - \u2022 Functional, Projectized, Matrix \uc870\uc9c1 \ub4f1\uc758 \uac01 \uc870\uc9c1 \uad6c\uc870\uc5d0 \ub530\ub77c \uc11c\ub85c \ub2e4\ub978 \uc7a5\ub2e8\uc810\uc744 \uac00\uc9c0\uace0 \uc788\uc73c\ubbc0\ub85c \ud504\ub85c\uc81d\ud2b8\uc758 \uc131\uaca9\uc5d0 \ub9de\ub294 \uc870\uc9c1 \uad6c\uc870\ub97c \uc120\ud0dd\ud558\uc5ec \ud300\uc744 \uad6c\uc131\ud574\uc57c \ud568\\n\\n![](./%EC%8A%A4%ED%81%AC%EB%A6%B0%EC%83%B7%202023-02-25%20%EC%98%A4%ED%9B%84%2012.33.26.png)\\n\\n## \uad00\ub9ac\ud1b5\uc81c \uc2a4\ud0ac\\n\\n![](./%EC%8A%A4%ED%81%AC%EB%A6%B0%EC%83%B7%202023-02-25%20%EC%98%A4%ED%9B%84%2012.36.04.png)\\n\\n- \ubc1c\uc8fc\ucc98\uc640 \uc704\uc5d0\uc11c \ub098\uc628 \uc598\uae30\ub97c \uc798\uc870\uc728\ud558\uace0 \ubcf4\uace0\ud574\uc57c\ud55c\ub2e4.\\n\\n## \uc0ac\ud68c, \uacbd\uc81c, \ud658\uacbd\\n\\n- \ucc28\uc138\ub300 \ud504\ub85c\uc81d\ud2b8 \uc2e4\ud328\uc694\uc778\uc740 \uc778\ub825\ud22c\uc785\uc774 \uc798\ubabb\ub418\uc5b4\uc11c \uadf8\ub7f0\uac83\\n\\n1. \ud45c\uc900\\n   \u2022 \uad8c\uc704 \uc788\ub294 \uae30\uad00\uc774 \uacf5\ud1b5\uc801 \ubc18\ubcf5\uc801 \uc0ac\uc6a9\uc744 \ubaa9\uc801\uc73c\ub85c \uc2b9\uc778\ud55c \ubb38\uc11c\uc778 \ud45c\uc900\uc740 \ud504\ub85c\uc81d\ud2b8\uc5d0 \uac15\uc81c\uc801\uc778 \ub8f0 \ub610\ub294 \uac00\uc774\ub4dc\ub77c\uc778\uc774 \ub428\\n2. \uaddc\uc81c\\n   \u2022 \uaddc\uc81c \uad8c\ud55c\uc774 \uc788\ub294 \uc815\ubd80 \ub2e8\uccb4 \ub4f1\uc774 \uacb0\uc815\ud55c \uc81c\ud488, \uc11c\ube44\uc2a4, \ud504\ub85c\uc138\uc2a4\uc5d0 \ub300\ud55c \uac15\uc81c\uc801\uc778 \uc870\ud56d\uc73c\ub85c \ud504\ub85c\uc81d\ud2b8 \uc218\ud589\uc2dc \uc81c\uc57d\uc0ac\ud56d\uc73c\ub85c \uc791\uc6a9\ud568\\n3. \uad6d\uc81c\ud654 \uad00\ub828 \ud658\uacbd\\n   \u2022 \uc0ac\uc5c5\uc758 \uc601\uc5ed\uc774 \uad6d\uc678\ub85c \ud655\ub300\ub41c \ud574\uc678 \ud504\ub85c\uc81d\ud2b8\ub098 \uc678\uad6d \uc5c5\uccb4\uc640\uc758 \ud569\uc791 \ud504\ub85c\uc81d\ud2b8\uc758 \uacbd\uc6b0 \uc2dc\ucc28, \uad6d\uc81c \ubc95\ub839, \uad6d\uacbd\uc77c \ubc0f \uc5c5\ubb34 \uc2dc\uac04 \ucc28\uc774, \uc7a5\uac70\ub9ac \uc5ec\ud589, \uc815\uce58\uc801 \uc0c1\ud669 \ub4f1\\n4. \ubb38\ud654\uc801 \ucc28\uc774\\n   \u2022 \uace0\uac1d \ub610\ub294 \ud504\ub85c\uc81d\ud2b8 \ud300\uc6d0\uc758 \uc815\uce58, \uacbd\uc81c, \uc724\ub9ac, \uc885\uad50 \ub4f1 \uc804\ubc18\uc801\uc778 \uc0dd\ud65c\uc591\uc2dd\uc774\ub098 \uc758\uc2dd \uad6c\uc870 \ub4f1\uc758 \ucc28\uc774\\n5. \uc0ac\ud68c/\uacbd\uc81c/\ud658\uacbd\uc801 \uc9c0\uc18d \uac00\ub2a5\uc131\\n   \u2022 \ud504\ub85c\uc81d\ud2b8\uc758 \uc9c0\uc18d\uc801 \uc218\ud589\uc5d0 \uae0d\uc815\uc801 \ub610\ub294 \ubd80\uc815\uc801\uc778 \uc601\ud5a5\uc744 \uc904 \uc218 \uc788\ub294 \uc0ac\ud68c, \uacbd\uc81c, \ud658\uacbd\uc801 \uc694\uc18c(\uc774\uc775 \ub2e8\uccb4, \ud658\uacbd \ub2e8\uccb4\uc758 \ud65c\ub3d9\ub4f1)\\n\\n# PMO(Project Management Office)\\n\\n## \uc815\uc758, \ud544\uc694\uc131\\n\\n- \ub2e4\uc218\uc758 \ud504\ub85c\uc81d\ud2b8\ub4e4\uc744 \uad00\ub9ac\\n\\n### \u26ab PMO\uc758 \uc815\uc758\\n\\n- IT \ud504\ub85c\uc81d\ud2b8\uc758 \ud6a8\uc728\uc131 \ubc0f \uc131\uacf5\ub960\uc744 \ub192\uc774\uace0 \uc870\uc9c1\ucc28\uc6d0\uc5d0\uc11c \ud504\ub85c\uc81d\ud2b8\ud3ec\ud2b8\ud3f4\ub9ac\uc624 \uad00\ub9ac\ub97c \uc704\ud558\uc5ec \ud504\ub85c\uc81d\ud2b8\ub97c \uc9c0\uc18d\uc801\uc73c\ub85c \ubaa8\ub2c8\ud130\ub9c1, \ud504\ub85c\uc138\uc2a4 \ubc0f \ubc29\ubc95\ub860 \ud45c\uc900\ud654, \uae30\uc220\uc801 Support \ub4f1\uc744 \uc218\ud589\ud558\ub294 \ud504\ub85c\uc81d\ud2b8\uad00\ub9ac \uc870\uc9c1\\n- \ub2e4\uc218 IT \ud504\ub85c\uc81d\ud2b8\ub97c \uc804\uc0ac\ucc28\uc6d0\uc5d0\uc11c \ud1b5\ud569 \uad00\ub9ac\ud558\ub294 \uc804\ub2f4\uc870\uc9c1\\n- PM/PL(Project Manager/Project Leader)\uc5d0\uac8c \ud504\ub85c\uc81d\ud2b8\uad00\ub9ac \uad50\uc721\uc744 \uc2e4\uc2dc\ud558\uace0, \ud504\ub85c\uc81d\ud2b8\uc5d0 \ub300\ud55c\uc77c\uc815\uc774\ud589, \uc131\uacfc \ucd94\uc801\ub4f1\uc758 \uc5c5\ubb34\ub97c \uc804\uc0ac\ucc28\uc6d0\uc5d0\uc11c \uad00\ub9ac\uac10\ub3c5\\n\\n### \u26ab PMO\uc758 \ud544\uc694\uc131\\n\\n- \ud504\ub85c\uc81d\ud2b8\ub4e4\uc5d0 \ub300\ud55c **\uc885\ud569\uc801 \uad00\ub9ac \uc804\ub7b5** \uc218\ub9bd\\n- \uac1c\uc778\uc801 \uc9c0\uc2dd, \uc5ed\ub7c9\uc744 **\uc870\uc9c1\uc758 \uc5ed\ub7c9\uc73c\ub85c \ud655\ub300**\\n- \ud504\ub85c\uc81d\ud2b8 \uc218\ud589\uc758 \uacb0\uacfc\uc5d0 \ub300\ud55c **\uc131\uacf5\ub960 \ud5a5\uc0c1**\\n- \ud504\ub85c\uc81d\ud2b8\uc758 **\uacc4\ud68d, \uc218\ud589, \ud3c9\uac00**\uc5d0 \uc774\ub974\ub294 **\ud504\ub85c\uc138\uc2a4 \ud45c\uc900\ud654** -> \uc608\uce21 \uacc4\uc0b0\uc758 \ud544\uc694\uc131\\n- \ud504\ub85c\uc81d\ud2b8 \uc774\uc288, \ubb38\uc81c \ud574\uacb0 \uc704\ud55c \uc804\ub2f4 \uc804\ubb38\uac00 \uc870\uc9c1 \ud544\uc694\\n\\n## \ud544\uc694\uc131\\n\\n\ud604\uc7ac IT \ud658\uacbd\uc744 \ubc18\uc601\ud55c PMO \ub3c4\uc785\uc758 \ud544\uc694\uc131\uc740 \ud06c\uac8c \ud504\ub85c\uc81d\ud2b8 \uaddc\ubaa8\uc758 \uac70\ub300\ud654 \ubc0f \uc2dc\uc2a4\ud15c\uac04 \ud1b5\ud569 \uadf8\ub9ac\uace0 \uc0c1\uc2dc \uac10\uc2dc \uccb4\uc81c \uc694\uad6c\uc784\\n\\n![](./%EC%8A%A4%ED%81%AC%EB%A6%B0%EC%83%B7%202023-02-25%20%EC%98%A4%ED%9B%84%202.11.18.png)\\n\\n- \ud544\ub0b4\uc639\ubc18\uc601\ud55c PMO \ub3c4\uc785\uc758 \ud544\uc694\uc131\uc740\\n\\nIT \ud658\uacbd\uc744 \ubc18\uc601\ud55c PMO \ub3c4\uc785\uc758 \ud544\uc694\uc131\uc740 \ud06c\uac8c \ud504\ub85c\uc81d\ud2b8 \uaddc\ubaa8\uc758 \uac70\ub300\ud654 \ubc0f \uc2dc\uc2a4\ud15c\uac04 \ud1b5\ud569 \uadf8\ub9ac\uace0 \uc0c1\uc2dc \uac10\uc2dc \uccb4\uc81c \uc694\uad6c\uc784\\n\\n## PMO Framework\\n\\n![](./%EC%8A%A4%ED%81%AC%EB%A6%B0%EC%83%B7%202023-02-25%20%EC%98%A4%ED%9B%84%202.33.28.png)\\n\\n# KA(Knowledge Area)\\n\\n## \ud504\ub85c\uc81d\ud2b8 \ud1b5\ud569\uad00\ub9ac \uac1c\uc694\\n\\n\ud504\ub85c\uc81d\ud2b8\uad00\ub9ac \ud504\ub85c\uc138\uc2a4 \uadf8\ub8f9\uc5d0 \uc18d\ud558\ub294 \ub2e4\uc591\ud55c \ud504\ub85c\uc138\uc2a4\uc640 \ud504\ub85c\uc81d\ud2b8\uad00\ub9ac \ud65c\ub3d9\ub4e4\uc744 \uc2dd\ubcc4, \uc815\uc758, \uacb0\ud569, \ud1b5\ud569 \ubc0f \uc870\uc815\ud558\ub294 \ub370 \ud544\uc694\ud55c \ud504\ub85c\uc138\uc2a4\\n\uc640 \ud65c\ub3d9. \uc790\uc6d0 \ud560\ub2f9 \uc120\ud0dd, \uc0c1\ucda9\ud558\ub294 \ubaa9\ud45c\uc640 \ub300\uc548\ub4e4 \uac04 \uade0\ud615 \uc720\uc9c0, \ud504\ub85c\uc81d\ud2b8\uad00\ub9ac \uc9c0\uc2dd\uc601\uc5ed\ub4e4 \uac04 \uc0c1\ud638\uc758\uc874\uad00\uacc4 \uad00\ub9ac \ub4f1\uc744 \ud3ec\ud568\\n\\n1. \ud504\ub85c\uc81d\ud2b8\ud5cc\uc7a5 \uc218\ub9bd\\n   \ud504\ub85c\uc81d\ud2b8\uc758 \ucc44\ud0dd\uc744 \uacf5\uc2dd\uc801\uc73c\ub85c \uc2b9\uc778\ud558\uace0 \ud504\ub85c\uc81d\ud2b8 \uad00\ub9ac\uc790\uc5d0\uac8c \uc870\uc9c1\uc758 \uc790\uc6d0\uc744 \ud504\ub85c\uc81d\ud2b8 \ud65c\ub3d9\uc5d0 \ud22c\uc785\ud560 \uc218 \uc788\ub294 \uad8c\ud55c\uc744 \ubd80\uc5ec\ud558\ub294 \ub0b4\uc6a9\uc758\\n   \ubb38\uc11c\ub97c \uac1c\ubc1c\ud558\ub294 \ud504\ub85c\uc138\uc2a4\\n2. \ud504\ub85c\uc81d\ud2b8\uad00\ub9ac \uacc4\ud68d\uc11c \uac1c\ubc1c\\n   \ubaa8\ub4e0 \ubcf4\uc870 \uacc4\ud68d\uc11c\ub97c \uc815\uc758, \uc791\uc131 \ubc0f \uc870\uc728\ud558\uc5ec \ud558\ub098\uc758 \uc885\ud569\uc801\uc778 \ud504\ub85c\uc81d\ud2b8\uad00\ub9ac \uacc4\ud68d\uc11c\uc5d0 \ud1b5\ud569\ud558\ub294 \ud504\ub85c\uc138\uc2a4. \ud504\ub85c\uc81d\ud2b8\uc758 \ud1b5\ud569 \uae30\uc900\uc120 \ubc0f\\n   \ubcf4\uc870 \uacc4\ud68d\uc11c\uac00 \ud504\ub85c\uc81d\ud2b8\uad00\ub9ac \uacc4\ud68d\uc11c \ub0b4\uc5d0 \ud3ec\ud568\ub420 \uc218\ub3c4 \uc788\ub2e4.\\n3. \ud504\ub85c\uc81d\ud2b8\uc791\uc5c5 \uc9c0\uc2dc \ubc0f \uc2e4\ud589\\n   \ud504\ub85c\uc81d\ud2b8 \ubaa9\ud45c\ub97c \ub2ec\uc131\ud558\uae30 \uc704\ud574 \ud504\ub85c\uc81d\ud2b8\uad00\ub9ac \uacc4\ud68d\uc11c\uc5d0 \uc815\uc758\ub41c \uc791\uc5c5\uc744 \uc9c0\ub3c4 \ubc0f \uc218\ud589\ud558\uace0, \uc2b9\uc778\ub41c \ubcc0\uacbd \uc0ac\ud56d\uc744 \uc774\ud589\ud558\ub294 \ud504\ub85c\uc138\uc2a4\\n4. \ud504\ub85c\uc81d\ud2b8 \uc9c0\uc2dd \uad00\ub9ac\\n   \uae30\uc874 \uc9c0\uc2dd\uc744 \uc0ac\uc6a9\ud558\uace0 \uc0c8\ub85c\uc6b4 \uc9c0\uc2dd\uc744 \ub9cc\ub4e4\uc5b4 \ud504\ub85c\uc81d\ud2b8 \ubaa9\ud45c\ub97c \ub2ec\uc131\ud558\uace0 \uc870\uc9c1 \ud559\uc2b5\uc5d0 \uae30\uc5ec\ud558\ub294 \ud504\ub85c\uc138\uc2a4.\\n5. \ud504\ub85c\uc81d\ud2b8\uc791\uc5c5 \uac10\uc2dc \ubc0f \ud1b5\uc81c\\n   \ud504\ub85c\uc81d\ud2b8\uad00\ub9ac \uacc4\ud68d\uc11c\uc5d0 \uc815\uc758\ub41c \uc131\uacfc \ubaa9\ud45c\ub97c \ub2ec\uc131\ud558\uae30 \uc704\ud574 \ud504\ub85c\uc81d\ud2b8 \uc9c4\ucc99\uc744 \ucd94\uc801 \ubc0f \uac80\ud1a0\ud558\uace0 \ubcf4\uace0\ud558\ub294 \ud504\ub85c\uc138\uc2a4\\n6. \ud1b5\ud569 \ubcc0\uacbd\ud1b5\uc81c \uc218\ud589\\n   \ubaa8\ub4e0 \ubcc0\uacbd\uc694\uccad\uc744 \uac80\ud1a0\ud558\uace0, \ubcc0\uacbd\uc0ac\ud56d\uc744 \uc2b9\uc778\ud558\uace0, \uc778\ub3c4\ubb3c, \uc870\uc9c1 \ud504\ub85c\uc138\uc2a4 \uc790\uc0b0, \ud504\ub85c\uc81d\ud2b8 \ubb38\uc11c, \ud504\ub85c\uc81d\ud2b8\uad00\ub9ac \uacc4\ud68d\uc11c\uc5d0 \ub300\ud55c \ubcc0\uacbd\uc0ac\ud56d\uc744\\n   \uad00\ub9ac\ud558\uace0, \ubcc0\uacbd\uc0ac\ud56d \uc870\uce58\uacb0\uacfc\ub97c \uc804\ub2ec\ud558\ub294 \ud504\ub85c\uc138\uc2a4\\n7. \ud504\ub85c\uc81d\ud2b8 \ub610\ub294 \ub2e8\uacc4 \uc885\ub8cc\\n   \ud504\ub85c\uc81d\ud2b8\ub098 \ub2e8\uacc4\ub97c \uacf5\uc2dd\uc801\uc73c\ub85c \uc644\ub8cc\ud558\uae30 \uc704\ud574 \ud504\ub85c\uc81d\ud2b8\uad00\ub9ac \ud504\ub85c\uc138\uc2a4 \uadf8\ub8f9\uc5d0 \uc18d\ud55c \ubaa8\ub4e0 \ud65c\ub3d9\uc744 \uc885\uacb0\ud558\ub294 \ud504\ub85c\uc138\uc2a4\\n\\n## \ud504\ub85c\uc81d\ud2b8 \ud1b5\ud569\uad00\ub9ac \ud1b5\ud569\uad00\ub9ac, \ud1b5\ud569 \ubcc0\uacbd\ud1b5\uc81c \uc218\ud589\\n\\n![](<./img%20(1).png>)\\n\\n## \ud504\ub85c\uc81d\ud2b8 \ubc94\uc704\uad00\ub9ac \uac1c\uc694\\n\\n\ud504\ub85c\uc81d\ud2b8\ub97c \uc131\uacf5\uc801\uc73c\ub85c \uc644\ub8cc\ud558\ub294 \ub370 \ud544\uc694\ud55c \uc791\uc5c5\ub9cc\uc744 \ube60\uc9d0\uc5c6\uc774 \ud504\ub85c\uc81d\ud2b8\uc5d0 \ud3ec\ud568\uc2dc\ud0a4\uae30 \uc704\ud574 \uc218\ud589\ud558\ub294\\n\ud504\ub85c\uc138\uc2a4 \ud3ec\ud568. \ud504\ub85c\uc81d\ud2b8 \ubc94\uc704\uad00\ub9ac\uc5d0\uc11c \uc8fc\ub41c \uacfc\uc81c\ub294 \ud504\ub85c\uc81d\ud2b8\uc5d0 \ud3ec\ud568\ub418\ub294 \uac83\uacfc \uadf8\ub807\uc9c0 \uc54a\uc740 \uac83\uc744 \uc815\uc758\\n\ud558\uace0 \ud1b5\uc81c\ud558\ub294 \uc77c\\n\\n1. \ubc94\uc704\uad00\ub9ac \uacc4\ud68d\uc218\ub9bd\\n   \ud504\ub85c\uc81d\ud2b8 \ubc94\uc704\ub97c \uc815\uc758\ud558\uace0 \ud655\uc778 \ubc0f \ud1b5\uc81c\ud558\ub294 \ubc29\ubc95\uc744 \uae30\uc220\ud55c \ubc94\uc704\uad00\ub9ac \uacc4\ud68d\uc11c\ub97c \uc791\uc131\ud558\ub294 \ud504\ub85c\uc138\uc2a4\\n2. \uc694\uad6c\uc0ac\ud56d \uc218\uc9d1\\n   \ud504\ub85c\uc81d\ud2b8 \ubaa9\ud45c \ub2ec\uc131\uc5d0 \ud544\uc694\ud55c \uc694\uad6c\uc0ac\ud56d\uacfc \uc774\ud574\uad00\uacc4\uc790\uc758 \uc694\uad6c\ub97c \ud310\ubcc4\ud558\uace0, \ubb38\uc11c\ud654\ud558\uba70 \uad00\ub9ac\ud558\ub294 \ud504\ub85c\uc138\uc2a4\\n3. \ubc94\uc704\uc815\uc758\\n   \ud504\ub85c\uc81d\ud2b8\uc640 \uc81c\ud488\uc5d0 \ub300\ud55c \uc0c1\uc138\ud55c \uc124\uba85\uc744 \uac1c\ubc1c\ud558\ub294 \ud504\ub85c\uc138\uc2a4\\n4. \uc791\uc5c5\ubd84\ub958\uccb4\uacc4(WBS) \uc791\uc131\\n   \ud504\ub85c\uc81d\ud2b8 \uc778\ub3c4\ubb3c\uacfc \ud504\ub85c\uc81d\ud2b8\uc791\uc5c5\uc744 \uc791\uace0 \ub354 \uad00\ub9ac\ud558\uae30 \uac04\ud3b8\ud55c \uad6c\uc131\uc694\uc18c\ub85c \uc138\ubd84\ud558\ub294 \ud504\ub85c\uc138\uc2a4\\n5. \ubc94\uc704\uac80\uc218\\n   \uc644\ub8cc\ub41c \ud504\ub85c\uc81d\ud2b8 \uc778\ub3c4\ubb3c\uc758 \uc778\uc218\ub97c \uacf5\uc2dd\ud654\ud558\ub294 \ud504\ub85c\uc138\uc2a4\\n6. \ubc94\uc704\ud1b5\uc81c\\n   \ud504\ub85c\uc81d\ud2b8 \ubc0f \uc81c\ud488\ubc94\uc704\uc758 \uc0c1\ud0dc\ub97c \uac10\uc2dc\ud558\uace0 \ubc94\uc704 \uae30\uc900\uc120\uc5d0 \ub300\ud55c \ubcc0\uacbd\uc744 \uad00\ub9ac\ud558\ub294 \ud504\ub85c\uc138\uc2a4\\n\\n## \ud504\ub85c\uc81d\ud2b8 \ubc94\uc704\uad00\ub9ac WBS \uc720\ud615\\n\\n![](<./%EC%9E%91%EC%97%85%ED%8C%A8%ED%82%A4%EC%A7%80%20%EC%88%98%EC%A4%80%EA%B9%8C%EC%A7%80%20%EB%B6%84%ED%95%A0%EB%90%9C%20%EC%9E%91%EC%97%85%EB%B6%84%EB%A5%98%EC%B2%B4%EA%B3%84(WBS)%20%EC%83%98%ED%94%8C.png>)\\n\\n## \ub2e8\uacc4\ubcc4\ub85c \uad6c\uc131\ud55c \uc791\uc5c5\ubd84\ub958\uccb4\uacc4(WBS) \uc0d8\ud50c\\n\\n![](<./%EB%8B%A8%EA%B3%84%EB%B3%84%EB%A1%9C%20%EA%B5%AC%EC%84%B1%ED%95%9C%20%EC%9E%91%EC%97%85%EB%B6%84%EB%A5%98%EC%B2%B4%EA%B3%84(WBS)%20%EC%83%98%ED%94%8C.png>)\\n\\n## \uc8fc\uc694 \uc778\ub3c4\ubb3c\uc774 \ud45c\uc2dc\ub41c \uc791\uc5c5\ubd84\ub958\uccb4\uacc4(WBS) \uc0d8\ud50c\\n\\n![](<./%EC%A3%BC%EC%9A%94%20%EC%9D%B8%EB%8F%84%EB%AC%BC%EC%9D%B4%20%ED%91%9C%EC%8B%9C%EB%90%9C%20%EC%9E%91%EC%97%85%EB%B6%84%EB%A5%98%EC%B2%B4%EA%B3%84(WBS)%20%EC%83%98%ED%94%8C.png>)\\n\\n## \ud504\ub85c\uc81d\ud2b8 \ubc94\uc704\uad00\ub9ac \ubc94\uc704\uae30\uc900\uc120\\n\\n- \ubc94\uc704 \uae30\uc900\uc120\\n  \u2013 \ubc94\uc704 \uae30\uc220\uc11c, WBS \ubc0f \uad00\ub828 WBS \uc0ac\uc804\uc758 \uc2b9\uc778 \ub41c \ubc84\uc804\\n  \u2013 \uacf5\uc2dd \ubcc0\uacbd \uc81c\uc5b4 \uc808\ucc28\ub97c \ud1b5\ud574\uc11c\ub9cc \ubcc0\uacbd \ub420 \uc218 \uc788\uc73c\uba70 \ube44\uad50\ub97c \uc704\ud55c \uae30\uc900\uc73c\ub85c \uc0ac\uc6a9\\n  \u2013 \ud504\ub85c\uc81d\ud2b8 \uad00\ub9ac \uacc4\ud68d\uc758 \uad6c\uc131 \uc694\uc18c\\n\\n- \ubc94\uc704 \uae30\uc900\uc120\uc758 \uad6c\uc131 \uc694\uc18c\\n\\n  1. \ud504\ub85c\uc81d\ud2b8 \ubc94\uc704 \uae30\uc220\uc11c\\n     \u2013 \ud504\ub85c\uc81d\ud2b8 \ubc94\uc704, \uc8fc\uc694 \uc0b0\ucd9c\ubb3c, \uac00\uc815 \ubc0f \uc81c\uc57d\uc5d0 \ub300\ud55c \uc124\uba85\uc11c\\n  2. \uc791\uc5c5\ubd84\ub958\uccb4\uacc4(WBS)\\n     \u2013 \ud504\ub85c\uc81d\ud2b8 \ubaa9\ud45c\ub97c \ub2ec\uc131\ud558\uace0 \ud544\uc694\ud55c \uacb0\uacfc\ubb3c\uc744 \uc791\uc131\ud558\uae30 \uc704\ud574 \ud504\ub85c\uc81d\ud2b8 \ud300\uc774 \uc218\ud589\ud574\uc57c \ud558\ub294 \uc804\uccb4 \uc791\uc5c5 \ubc94\uc704\\n     \ub97c \uacc4\uce35\uc801\uc73c\ub85c \ubd84\ud574 \ud55c \uac83\\n     \u2013 WBS\uc758 \uac01 \ud558\uc704 \uc218\uc900\uc740 \ud504\ub85c\uc81d\ud2b8 \uc791\uc5c5\uc5d0 \ub300\ud55c \uc0c1\uc138\ud55c \uc815\uc758\uc784\\n  3. \uc791\uc5c5 \ud328\ud0a4\uc9c0(Work Package)\\n     \u2013 WBS\uc758 \uac00\uc7a5 \ub0ae\uc740 \uc218\uc900\uc73c\ub85c \uace0\uc720 \uc2dd\ubcc4\uc790\uac00 \ubd80\uc5ec\ub428\\n     \u2013 \uace0\uc720 \uc2dd\ubcc4\uc790\ub294 \ube44\uc6a9, \uc77c\uc815 \ubc0f \uc790\uc6d0 \uc815\ubcf4\uc758 \uacc4\uce35\uc801 \ud569\uacc4\ub97c \uc704\ud55c \uad6c\uc870\ub97c \uc81c\uacf5\\n     \u2013 \uac01 \uc791\uc5c5 \ud328\ud0a4\uc9c0\ub294 \ud1b5\uc81c \uacc4\uc815\uc758 \uc77c\ubd80. \ud1b5\uc81c \uacc4\uc815\uc740 \ub450 \uac1c \uc774\uc0c1\uc758 \uc791\uc5c5 \ud328\ud0a4\uc9c0\uac00 \uc874\uc7ac \uac00\ub2a5\\n  4. \uacc4\ud68d \ud328\ud0a4\uc9c0 (Planning Package)\\n     \u2013 \ud1b5\uc81c \uacc4\uc815 \uc544\ub798 \ubc0f \uc791\uc5c5 \ud328\ud0a4\uc9c0 \uc704\uc5d0 \uc788\ub294 WBS \uad6c\uc131 \uc694\uc18c\\n     \u2013 \uc54c\ub824\uc9c4 \uc791\uc5c5 \ub0b4\uc6a9\uc774\uc9c0\ub9cc \uc790\uc138\ud55c \uc77c\uc815 \ud65c\ub3d9\uc740 \uc5c6\ub294 TASK\\n\\n- \uc791\uc5c5\ubd84\ub958\uccb4\uacc4(WBS) \uc0ac\uc804\\n  WBS\uc758 \uac01 \uad6c\uc131\uc694\uc18c\uc640 \uad00\ub828\ub41c \uc0c1\uc138\ud55c \uc778\ub3c4\ubb3c, \ud65c\ub3d9, \uc77c\uc815\uc815\ubcf4\ub97c \uc81c\uacf5\\n  - WBS \ucf54\ub4dc \uc2dd\ubcc4\uc790\\n  - \uc791\uc5c5 \uc124\uba85\\n  - \uac00\uc815\uc0ac\ud56d \ubc0f \uc81c\uc57d\uc0ac\ud56d\\n  - \ub2f4\ub2f9 \uc870\uc9c1\\n  - \uc77c\uc815 \ub9c8\uc77c\uc2a4\ud1a4\\n  - \uc5f0\uad00\ub41c \uc77c\uc815\ud65c\ub3d9\\n  - \ud544\uc694\ud55c \uc790\uc6d0\\n  - \uc6d0\uac00 \uc0b0\uc815\uce58\\n  - \ud488\uc9c8 \uc694\uad6c\uc0ac\ud56d\\n  - \uc778\uc218\uae30\uc900\\n  - \uae30\uc220 \ucc38\uace0 \ubb38\ud5cc\\n  - \ud611\uc57d \uc815\ubcf4\\n\\n## \ud504\ub85c\uc81d\ud2b8 \uc77c\uc815\uad00\ub9ac \uac1c\uc694 (PMBOK 6th\ubcc0\uacbd\ucd94\uac00 : Project Time Management \u2192 Project Schedule Management)\\n\\n\ud504\ub85c\uc81d\ud2b8\ub97c \uc801\uc2dc\uc5d0 \uc644\ub8cc\ud558\ub294 \ub370 \ud544\uc694\ud55c \ud504\ub85c\uc138\uc2a4\\n\\n1. \uc77c\uc815\uad00\ub9ac \uacc4\ud68d\uc218\ub9bd\\n   \ud504\ub85c\uc81d\ud2b8 \uc77c\uc815\uc758 \uae30\ud68d, \uac1c\ubc1c, \uad00\ub9ac, \uc2e4\ud589 \ubc0f \ud1b5\uc81c\uc5d0 \ud544\uc694\ud55c \uc815\ucc45\uacfc \uc808\ucc28, \ubb38\uc11c\ud654 \uae30\uc900\uc744 \uc218\ub9bd\ud558\ub294 \ud504\ub85c\uc138\uc2a4\\n2. \ud65c\ub3d9\uc815\uc758\\n   \ud504\ub85c\uc81d\ud2b8 \uc778\ub3c4\ubb3c\uc744 \uc0dd\uc0b0\ud558\uae30 \uc704\ud574 \uc218\ud589\ud560 \uad00\ub828 \ud65c\ub3d9\ub4e4\uc744 \uc2dd\ubcc4\ud558\uace0 \ubb38\uc11c\ud654\ud558\ub294 \ud504\ub85c\uc138\uc2a4\\n3. \ud65c\ub3d9\uc21c\uc11c \ubc30\uc5f4\\n   \ud504\ub85c\uc81d\ud2b8 \ud65c\ub3d9 \uc0ac\uc774\uc758 \uad00\uacc4\ub97c \uc2dd\ubcc4\ud558\uc5ec \ubb38\uc11c\ud654\ud558\ub294 \ud504\ub85c\uc138\uc2a4\\n\\n   ~~\ud65c\ub3d9\uc790\uc6d0 \uc0b0\uc815~~\\n   ~~\uac01 \ud65c\ub3d9\uc744 \uc218\ud589\ud558\ub294 \ub370 \ud544\uc694\ud55c \uc790\uc7ac, \uc778\uc801 \uc790\uc6d0, \uc7a5\ube44 \ub610\ub294 \uacf5\uae09\ud488\uc758 \uc885\ub958\uc640 \uc218\ub7c9\uc744 \uc0b0\uc815\ud558\ub294 \ud504\ub85c\uc138\uc2a4.~~\\n\\n4. \ud65c\ub3d9\uae30\uac04 \uc0b0\uc815\\n   \uc0b0\uc815\ub41c \uc790\uc6d0\uc73c\ub85c \uac1c\ubcc4 \ud65c\ub3d9\uc744 \uc644\ub8cc\ud558\ub294 \ub370 \ud544\uc694\ud55c \ucd1d \uc791\uc5c5 \uae30\uac04 \ub2e8\uc704 \uc218\ub97c \uc0b0\uc815\ud558\ub294 \ud504\ub85c\uc138\uc2a4\\n5. \uc77c\uc815\uac1c\ubc1c\\n   \ud65c\ub3d9 \uc21c\uc11c, \uae30\uac04, \uc790\uc6d0 \uc694\uad6c\uc0ac\ud56d \ubc0f \uc77c\uc815 \uc81c\uc57d\uc744 \ubd84\uc11d\ud558\uc5ec \ud504\ub85c\uc81d\ud2b8 \uc77c\uc815 \ubaa8\ub378\uc744 \uc0dd\uc131\ud558\ub294 \ud504\ub85c\uc138\uc2a4\\n6. \uc77c\uc815\ud1b5\uc81c\\n   \uacc4\ud68d\uc744 \ub2ec\uc131\ud558\uae30 \uc704\ud574 \ud504\ub85c\uc81d\ud2b8 \ud65c\ub3d9\uc758 \uc0c1\ud0dc\ub97c \uac10\uc2dc\ud558\uba74\uc11c \ud504\ub85c\uc81d\ud2b8\uc758 \uc9c4\ud589\uc744 \uac31\uc2e0\ud558\uace0 \uc77c\uc815 \uae30\uc900\uc120\uc5d0 \ub300\ud55c \ubcc0\uacbd\uc744\\n   \uad00\ub9ac\ud558\ub294 \ud504\ub85c\uc138\uc2a4\\n\\n## \ud504\ub85c\uc81d\ud2b8 \uc77c\uc815\uad00\ub9ac PDM(Precedence Diagramming Method)\\n\\n\ud65c\ub3d9\uc744 \ub178\ub4dc(\uc5f0\uacb0\uc810)\ub85c \ud45c\uc2dc\ud558\uace0 \ud55c \uac00\uc9c0 \uc774\uc0c1\uc758 \ub17c\ub9ac\uc801 \uad00\uacc4\ub85c \uc5f0\uacb0\ud558\uc5ec \uc218\ud589 \uc21c\uc11c\ub97c \ubcf4\uc5ec\uc8fc\ub294 \uc77c\uc815\uad00\ub9ac\uae30\ubc95\\n\ub178\ub4dc \ud45c\uae30 \ud654\uc0b4\ud45c(AON)\ub294 \uc120\ud6c4\ud589 \uad00\uacc4\ub3c4(PDM)\ub97c \ud45c\uc2dc\ud558\ub294 \ubc29\ubc95\uc73c\ub85c \uc77c\ubc18\uc801\uc73c\ub85c \uc0ac\uc6a9\ub428\\nPDM\uc758 \uc758\uc874\uad00\uacc4 (\ub610\ub294 \ub17c\ub9ac\uc801 \uad00\uacc4) 4\uac00\uc9c0\\n\\n- \uc885\ub8cc-\uc2dc\uc791\uad00\uacc4(FS)\\n  - \uc120\ud589\ud65c\ub3d9\uc774 \uc644\ub8cc\ub418\uae30 \uc804\uc5d0\ub294 \ud6c4\ud589\ud65c\ub3d9\uc744 \uc2dc\uc791\ud560 \uc218 \uc5c6\ub294 \ub17c\ub9ac\uc801 \uad00\uacc4\\n  - \uc608\ub97c \ub4e4\uc5b4, \ub2ec\ub9ac\uae30 \uc2dc\ud569(\uc120\ud589\ud65c\ub3d9)\uc774 \ub05d\ub0a0 \ub54c\uae4c\uc9c0 \uc2dc\uc0c1\uc2dd(\ud6c4\ud589\ud65c\ub3d9)\uc744 \uc2dc\uc791\ud560 \uc218 \uc5c6\uc74c\\n- \uc885\ub8cc-\uc885\ub8cc\uad00\uacc4(FF)\\n  - \uc120\ud589\ud65c\ub3d9\uc774 \uc644\ub8cc\ub418\uae30 \uc804\uc5d0\ub294 \ud6c4\ud589\ud65c\ub3d9\uc744 \uc644\ub8cc\ud560 \uc218 \uc5c6\ub294 \ub17c\ub9ac\uc801 \uad00\uacc4\\n  - \uc608\ub97c \ub4e4\uc5b4, \ubb38\uc11c\uc791\uc131(\uc120\ud589\ud65c\ub3d9)\uc774 \ub05d\ub098\uc57c \ubb38\uc11c\ud3b8\uc9d1(\ud6c4\ud589\ud65c\ub3d9)\uc744 \ub9c8\uce60 \uc218 \uc788\uc74c\\n- \uc2dc\uc791-\uc2dc\uc791\uad00\uacc4(SS)\\n  - \uc120\ud589\ud65c\ub3d9\uc774 \uac1c\uc2dc\ub418\uae30 \uc804\uc5d0\ub294 \ud6c4\ud589\ud65c\ub3d9\uc744 \uac1c\uc2dc\ud560 \uc218 \uc5c6\ub294 \ub17c\ub9ac\uc801 \uad00\uacc4\\n  - \uc608\ub97c \ub4e4\uc5b4, \uae30\ucd08\uacf5\uc0ac \ucf58\ud06c\ub9ac\ud2b8 \uc791\uc5c5(\uc120\ud589\ud65c\ub3d9)\uc774 \uc2dc\uc791\ub420 \ub54c\uae4c\uc9c0 \ud45c\uba74 \uace0\ub974\uae30 \ucf58\ud2b8\ub9ac\ud2b8 \uc791\uc5c5(\ud6c4\ud589\ud65c\ub3d9)\uc744\\n    \uc2dc\uc791\ud560 \uc218 \uc5c6\uc74c\\n- \uc2dc\uc791-\uc885\ub8cc\uad00\uacc4(SF)\\n\\n  - \uc120\ud589\ud65c\ub3d9\uc774 \uc2dc\uc791\ub418\uae30 \uc804\uc5d0\ub294 \ud6c4\ud589\ud65c\ub3d9\uc744 \uc644\ub8cc\ud560 \uc218 \uc5c6\ub294 \ub17c\ub9ac\uc801 \uad00\uacc4\\n  - \uc608\ub97c \ub4e4\uc5b4, 2\uad50\ub300\uc870 \uacbd\ube44\uadfc\ubb34(\uc120\ud589\ud65c\ub3d9)\uac00 \uc2dc\uc791\ub420 \ub54c\uae4c\uc9c0 1\uad50\ub300\uc870 \uacbd\ube44\uadfc\ubb34(\ud6c4\ud589\ud65c\ub3d9)\ub97c \ub9c8\uce60 \uc218 \uc5c6\uc74c\\n\\n- \uc758\ubb34\uc801 \uc758\uc874\uad00\uacc4 (\uacbd\uc131\ub17c\ub9ac \ub610\ub294 \uacbd\uc131 \uc758\uc874\uad00\uacc4)\\n  \u2013 \ubc95\uc801\uc73c\ub85c \ub610\ub294 \uacc4\uc57d\uc5d0 \ub530\ub77c \uc694\uad6c\ub418\uac70\ub098 \uc791\uc5c5\uc758 \uc131\uaca9\uc0c1 \ub0b4\uc7ac\ub41c \uc758\uc874\uad00\uacc4\ub85c \ub300\uac1c \ubb3c\ub9ac\uc801 \uc81c\ud55c\uc774 \ub530\ub984\\n  \u2013 \uc608\ub97c \ub4e4\uc5b4 \uac74\uc124\ud504\ub85c\uc81d\ud2b8\uc5d0\uc11c\ub294 \uae30\ucd08\uacf5\uc0ac\uac00 \ub05d\ub0a0 \ub54c\uae4c\uc9c0 \uc9c0\uc0c1 \uac74\ubb3c\uc744 \uc138\uc6b8 \uc218 \uc5c6\uace0, \uc804\uc790\uc81c\ud488 \ud504\ub85c\uc81d\ud2b8\uc5d0\uc11c\ub294\\n  \ud504\ub85c\ud1a0\ud0c0\uc785\uc744 \uc81c\uc791\ud574\uc57c\ub9cc \ud14c\uc2a4\ud2b8\ud560 \uc218 \uc788\uc74c\\n  \u2013 \uae30\uc220\uc801 \uc758\uc874\uad00\uacc4\ub294 \uc758\ubb34\uc801 \uad00\uacc4\uac00 \uc544\ub2d0 \uc218 \uc788\uc74c.\\n- \uc784\uc758\uc801 \uc758\uc874\uad00\uacc4(\uc6b0\uc120\ub17c\ub9ac, \uc120\ud638\ub17c\ub9ac \ub610\ub294 \uc5f0\uc131\ub17c\ub9ac\uad00\uacc4)\\n  \u2013 \uc5ec\ub7ec \uac00\uc9c0 \uc21c\uc11c\uac00 \uac00\ub2a5\ud558\uc9c0\ub9cc, \ud2b9\uc815 \uc751\uc6a9\ubd84\uc57c\uc758 \ubaa8\ubc94 \uc0ac\ub840 \uc9c0\uc2dd\uc5d0 \ub530\ub77c \ub610\ub294 \ud2b9\uc815\ud55c \uc21c\uc11c\uac00 \ubc14\ub78c\uc9c1\ud55c \ud504\ub85c\uc81d\ud2b8\uc758\\n  \uc77c\ubd80 \ud2b9\uc218\ud55c \uc0c1\ud669\uc5d0 \ub530\ub77c \uc124\uc815\\n  \u2013 \uc784\uc758\uc758 \ucd1d \uc5ec\uc720\uac12\uc744 \uc0dd\uc131\ud558\uace0 \ub098\uc911\uc5d0 \uc77c\uc815\uacc4\ud68d \uc0ac\ud56d\uc744 \uc81c\ud55c\ud560 \uc218 \uc788\uae30 \ub54c\ubb38\uc5d0 \ucca0\uc800\ud788 \ubb38\uc11c\ud654\ud574\uc57c \ud568\\n  \u2013 \uacf5\uc815\uc911\ucca9 \ub2e8\ucd95\ubc95\uc744 \uc0ac\uc6a9\ud560 \ub54c \uc774\ub7ec\ud55c \uc784\uc758\uc801 \uc758\uc874\uad00\uacc4\ub97c \uac80\ud1a0\ud558\uc5ec \uc218\uc815 \ub610\ub294 \uc81c\uac70\ud560 \uac83\uc744 \uace0\ub824\ud574\uc57c \ud568\\n- \uc678\ubd80\uc801 \uc758\uc874\uad00\uacc4\\n  \u2013 \ud504\ub85c\uc81d\ud2b8 \ud65c\ub3d9\uacfc \ud504\ub85c\uc81d\ud2b8 \uc774\uc678 \ud65c\ub3d9 \uc0ac\uc774\uc758 \uad00\uacc4\uac00 \ud3ec\ud568\ub428\\n  \u2013 \ubcf4\ud1b5 \ud504\ub85c\uc81d\ud2b8 \ud300\uc758 \ud1b5\uc81c\uad8c \ubc16\uc5d0 \uc788\uc74c\\n  \u2013 \uc608\ub97c \ub4e4\uc5b4, \uc18c\ud504\ud2b8\uc6e8\uc5b4 \ud504\ub85c\uc81d\ud2b8\uc5d0\uc11c \ud14c\uc2a4\ud2b8 \ud65c\ub3d9\uc740 \uc678\ubd80 \uacf5\uae09\uc5c5\uccb4\uac00 \ud558\ub4dc\uc6e8\uc5b4\ub97c \ub0a9\ud488\ud574\uc57c \uc218\ud589\ud560 \uc218 \uc788\uace0,\\n  \uac74\uc124 \ud504\ub85c\uc81d\ud2b8\uc5d0\uc11c \ubd80\uc9c0\uc870\uc131 \uacf5\uc0ac\uc5d0 \uc55e\uc11c \uc815\ubd80\uc758 \ud658\uacbd\uacf5\uccad\ud68c\ub97c \uac1c\ucd5c\ud574\uc57c \ud560 \uc218 \uc788\uc74c\\n- \ub0b4\ubd80\uc801 \uc758\uc874\uad00\uacc4\\n  \u2013 \ud504\ub85c\uc81d\ud2b8 \ud65c\ub3d9 \uac04 \uc120\u2022\ud6c4\ud589 \uad00\uacc4\ub97c \ud3ec\ud568\ud558\uba70, \uc77c\ubc18\uc801\uc73c\ub85c \ud504\ub85c\uc81d\ud2b8 \ud300\uc758 \ud1b5\uc81c\uad8c \uc548\uc5d0 \uc788\uc74c\\n  \u2013 \uc608\ub97c \ub4e4\uc5b4 \ud504\ub85c\uc81d\ud2b8 \ud300\uc5d0\uc11c \uae30\uacc4\uac00 \uc870\ub9bd\ub418\uae30 \uc804\uc5d0\ub294 \uae30\uacc4\ub97c \ud14c\uc2a4\ud2b8\ud560 \uc218 \uc5c6\ub294 \uacbd\uc6b0\uc784\\n\\n## \ud504\ub85c\uc81d\ud2b8 \uc77c\uc815\uad00\ub9ac PDM \uc608\uc2dc\\n\\n![](./%EC%8A%A4%ED%81%AC%EB%A6%B0%EC%83%B7%202023-02-25%20%EC%98%A4%ED%9B%84%204.02.44.png)\\n\\n## \ud504\ub85c\uc81d\ud2b8 \uc77c\uc815\uad00\ub9ac 3\uc810 \uc0b0\uc815\ubc95\\n\\n\ud504\ub85c\uadf8\ub7a8\ud3c9\uac00 \ubc0f \uac80\ud1a0\uae30\ubc95(Program Evaluation and Review Technique, PERT)\uc5d0\uc11c \uc720\ub798\\nPERT\uc5d0\uc11c\ub294 \ub2e4\uc74c \uc138 \uac00\uc9c0 \uc0b0\uc815\uce58\ub97c \uc0ac\uc6a9\ud558\uc5ec \ud65c\ub3d9\uae30\uac04\uc758 \ub300\ub7b5\uc801 \ubc94\uc704\ub97c \uc815\uc758\\n\\n- \ucd5c\ube48\uce58(tM)\\n\\n  - \ud560\ub2f9 \uac00\ub2a5\ud55c \uc790\uc6d0, \uc790\uc6d0\ubcc4 \uc0dd\uc0b0\uc131, \ud65c\ub3d9\uc5d0 \uc2e4\uc9c8\uc801\uc778 \uac00\uc6a9 \uae30\ub300\uce58, \ub2e4\ub978 \ud56d\ubaa9\uacfc \uc758\uc874\uad00\uacc4, \uacf5\uae09 \uc911\ub2e8\uc744 \uace0\ub824\ud558\uc5ec \uc0b0\uc815\ud55c \ud65c\ub3d9\uae30\uac04\\n\\n- \ub099\uad00\uce58(tO)\\n\\n  - \ucd5c\uc0c1\uc758 \ud65c\ub3d9 \uc2dc\ub098\ub9ac\uc624\uc5d0 \ub300\ud55c \ubd84\uc11d\uc5d0 \uadfc\uac70\ud55c \ud65c\ub3d9\uae30\uac04\\n\\n- \ube44\uad00\uce58(tP)\\n\\n  - \ucd5c\uc545\uc758 \ud65c\ub3d9 \uc2dc\ub098\ub9ac\uc624\uc5d0 \ub300\ud55c \ubd84\uc11d\uc5d0 \uadfc\uac70\ud55c \ud65c\ub3d9\uae30\uac04\\n    \uc138 \uac00\uc9c0 \uc0b0\uc815\uce58 \ubc94\uc704 \ub0b4\uc5d0\uc11c \uac00\uc815\ud55c \uac12 \ubd84\ud3ec\uc5d0 \ub530\ub77c \uacf5\uc2dd\uc744 \uc0ac\uc6a9\ud558\uc5ec\\n    \uc608\uc0c1\uae30\uac04, tE\ub97c \uacc4\uc0b0\ud560 \uc218 \uc788\ub2e4.\\n    \ud754\ud788 \uc0ac\uc6a9\ub418\ub294 \ub450 \uac00\uc9c0 \uacf5\uc2dd\\n\\n- \uc0bc\uac01\ubd84\ud3ec\\n\\n  $tE = (tO + tM + tP) / 3$\\n\\n- \ubca0\ud0c0\ubd84\ud3ec(\uae30\uc874 PERT \uae30\ubc95\uc5d0\uc11c \ud30c\uc0dd)\\n\\n  $tE = (tO + 4tM + tP) / 6$\\n\\n## \ud504\ub85c\uc81d\ud2b8 \uc77c\uc815\uad00\ub9ac Critical Path Method\\n\\n- \uc8fc\uacf5\uc815\ubc95(CPM)\\n\\n  \u2013 \uc804\uccb4 \ud504\ub85c\uc81d\ud2b8\uc5d0\uc11c \uac00\uc7a5 \uae34 \uacbd\ub85c\ub97c \ub098\ud0c0\ub0b4\ub294 \uc77c\ub828\uc758 \ud65c\ub3d9 \uc21c\uc11c\ub85c, \uac00\ub2a5\ud55c \ucd5c\ub2e8 \ud504\ub85c\uc81d\ud2b8 \uae30\uac04\uc744 \uacb0\uc815\ud558\ub294 \ubc29\ubc95\\n  \u2013 \uc0b0\ucd9c\ub41c \ube60\ub978 \uac1c\uc2dc\uc77c(ES)\uacfc \ube60\ub978 \uc885\ub8cc\uc77c(EF), \ub2a6\uc740 \uac1c\uc2dc\uc77c(LS)\uacfc \ub2a6\uc740 \uc885\ub8cc\uc77c(LF)\uc774 \ubc18\ub4dc\uc2dc \ud504\ub85c\uc81d\ud2b8 \uc77c\uc815\uc774 \ub418\uc9c0\ub294\\n  \uc54a\uc74c\\n  \u2013 \uadf8\ubcf4\ub2e4\ub294 \ud65c\ub3d9\uae30\uac04, \ub17c\ub9ac\uc801 \uad00\uacc4, \uc120\ub3c4, \uc9c0\uc5f0 \ubc0f \uae30\ud0c0 \ud655\uc778\ub41c \uc81c\uc57d\uc5d0 \ub300\ud574 \uc77c\uc815 \ubaa8\ub378\uc5d0 \uc785\ub825\ub41c \ubaa8\uc218\ub97c \uc0ac\uc6a9\ud558\uc5ec\\n  \ud65c\ub3d9\uc744 \uc2e4\ud589\ud560 \uc218 \uc788\ub294 \uae30\uac04\uc744 \ub098\ud0c0\ub0c4\\n\\n- \ucd1d\uc5ec\uc720(Total Float)\\n\\n  \u2013 \uc5b4\ub5a4 \ub124\ud2b8\uc6cc\ud06c \uacbd\ub85c\uc5d0\uc11c\ub4e0 \ud504\ub85c\uc81d\ud2b8 \uc885\ub8cc\uc77c\uc744 \uc5f0\uae30\ud558\uac70\ub098 \uc77c\uc815 \uc81c\uc57d\uc744 \uc704\ubc18\ud558\ub294 \uc77c \uc5c6\uc774 \ube60\ub978 \uac1c\uc2dc\uc77c\ub85c\ubd80\ud130 \uc77c\uc815\\n  \ud65c\ub3d9\uc744 \uc9c0\uc5f0 \ub610\ub294 \uc5f0\uae30\ud560 \uc218 \uc788\ub294 \uae30\uac04\\n  \u2013 CPM \uc8fc\uacf5\uc815 \uacbd\ub85c\ub294 \uc8fc\uacf5\uc815 \uacbd\ub85c\uc0c1\uc5d0\uc11c \ucd1d \uc5ec\uc720 \uac12\uc774 0\uc778 \uac83\uc73c\ub85c \uad6c\ubd84\\n  \u2013 \uc8fc\uacf5\uc815 \uacbd\ub85c\uc758 \ucd1d \uc5ec\uc720\ub294 \uc801\uc6a9\ub41c \uc81c\uc57d\uc5d0 \ub530\ub77c \uc591\uc218, 0 \ub610\ub294 \uc74c\uc218 \uac12\\n  \u2013 \uc804\uc9c4\uacc4\uc0b0\uc73c\ub85c \uc0b0\ucd9c\ub41c \ube60\ub978 \uc885\ub8cc\uc77c\ubcf4\ub2e4 \ub2a6\uc740 \uc77c\uc815 \uc81c\uc57d\uc73c\ub85c\ubd80\ud130 \ud6c4\uc9c4\uacc4\uc0b0\uc744 \uc218\ud589\ud560 \ub54c \ucd1d \uc5ec\uc720 \uac12\uc774 \uc591\uc218\\n  \u2013 \uae30\uac04\uacfc \ub17c\ub9ac\ub85c \uc778\ud574 \ub2a6\uc740 \uac1c\uc2dc\uc77c\uacfc \ub2a6\uc740 \uc885\ub8cc\uc77c\uc5d0 \ub300\ud55c \uc81c\uc57d\uc744 \uc704\ubc18\ud558\uac8c \ub420 \uacbd\uc6b0 \ucd1d \uc5ec\uc720 \uac12\uc740 \uc74c\uc218\\n  \u2013 \uc900 \uc8fc\uacf5\uc815 \uacbd\ub85c\uac00 \uc5ec\ub7ec \uac1c \uc788\uc744 \uc218 \uc788\uc74c\\n  \u2013 \ucd1d \uc5ec\uc720 \uac12\uc774 0 \ub610\ub294 \uc591\uc218\uc778 \ub124\ud2b8\uc6cc\ud06c \uacbd\ub85c\ub97c \uc0dd\uc131\ud558\uae30 \uc704\ud574 \ud65c\ub3d9\uae30\uac04(\uc790\uc6d0\uc740 \ub9ce\uace0 \ubc94\uc704\ub294 \uc801\uc740 \uacbd\uc6b0), \ub17c\ub9ac\uc801 \uad00\\n  \uacc4(\uc784\uc758\uc801 \uad00\uacc4\ub85c \uc2dc\uc791\ud558\ub294 \uacbd\uc6b0), \uc120\ub3c4 \ubc0f \uc9c0\uc5f0 \ub610\ub294 \uae30\ud0c0 \uc77c\uc815 \uc81c\uc57d\uc5d0 \ub300\ud55c \uc870\uc815\uc774 \ud544\uc694\ud560 \uc218 \uc788\uc74c\\n\\n- \uc790\uc720\uc5ec\uc720(Free Float)\\n\\n  \u2013 \ucd1d \uc5ec\uc720\ub97c \uacc4\uc0b0\ud55c \ud6c4\uc5d0\ub294 \u201c\uc790\uc720\uc5ec\uc720\u201d\ub3c4 \uc0b0\ucd9c\\n  \u2013 \ud6c4\ud589\ud65c\ub3d9\uc758 \ube60\ub978 \uac1c\uc2dc\uc77c\uc744 \uc5f0\uae30\ud558\uac70\ub098 \uc77c\uc815 \uc81c\uc57d\uc744 \uc704\ubc18\ud558\ub294 \uc77c \uc5c6\uc774 \uc77c\uc815\ud65c\ub3d9\uc744 \uc9c0\uc5f0\ud560 \uc218 \uc788\ub294 \uae30\uac04\\n  \u2013 \uc608\ub97c \ub4e4\uc5b4, \uadf8\ub9bc\uc5d0\uc11c \ud65c\ub3d9 B\uc758 \uc790\uc720\uc5ec\uc720(FF)\ub294 5\uc77c\\n\\n## \ud504\ub85c\uc81d\ud2b8 \uc77c\uc815\uad00\ub9ac Critical Path Method\\n\\n![](./%EC%8A%A4%ED%81%AC%EB%A6%B0%EC%83%B7%202023-02-25%20%EC%98%A4%ED%9B%84%204.36.34.png)\\n\\n- \uc8fc\uacf5\uc815\uc5f0\uc1c4\ubc95(CCM)\\n\\n  \u2013 \ud504\ub85c\uc81d\ud2b8\ud300\uc5d0\uc11c \ud55c\uc815\ub41c \uc790\uc6d0\uacfc \ud504\ub85c\uc81d\ud2b8 \ubd88\ud655\uc2e4\uc131\uc744 \uace0\ub824\ud558\uc5ec \ud504\ub85c\uc81d\ud2b8 \uc77c\uc815\uacbd\ub85c\uc5d0 \uc644\ucda9\uc744 \ub458 \uc218 \uc788\ub294 \uc77c\uc815\uacc4\ud68d\\n  \ubc29\ubc95\\n\\n  \u2013 \uc8fc\uacf5\uc815\ubc95(CPM)\uc73c\ub85c \uacb0\uc815\ub41c \uc8fc\uacf5\uc815 \uacbd\ub85c\uc5d0 \uc790\uc6d0\ud560\ub2f9, \uc790\uc6d0\ucd5c\uc801\ud654, \uc790\uc6d0\ud3c9\uc900\ud654, \ud65c\ub3d9\uae30\uac04 \ubd88\ud655\uc2e4\uc131\uc774 \ubbf8\uce58\ub294 \uc601\ud5a5\\n  \uc744 \uace0\ub824\\n\\n  \u2013 \uc644\ucda9(Buffer)\uacfc \uc644\ucda9\uad00\ub9ac(Buffer Management)\ub77c\ub294 \uac1c\ub150 \ub3c4\uc785\\n\\n  \u2013 \uc548\uc804 \uc5ec\uc720, \ub17c\ub9ac\uc801 \uad00\uacc4 \ubc0f \uc790\uc6d0 \uac00\uc6a9\uc131\uc744 \ud3ec\ud568\ud558\uc9c0 \uc54a\ub294 \ud65c\ub3d9\ub4e4\uc5d0\ub2e4\uac00 \ud504\ub85c\uc81d\ud2b8 \uc77c\uc815 \uacbd\ub85c\uc758 \uc9c0\uc815\ub41c \uc9c0\uc810\uc5d0\uc11c \uc218\\n  \uc9d1 \ub41c \uc548\uc804\uc5ec\uc720\ub97c \ud1b5\uacc4\uc801\uc73c\ub85c \uc0b0\uc815\ud55c \ubc84\ud37c\uc640 \ud568\uaed8 \uc0ac\uc6a9\\n\\n  \u2013 \uc790\uc6d0\uc774 \uc81c\uc57d\ub41c \uc8fc\uacf5\uc815 \uacbd\ub85c\uac00 \u201c\uc8fc\uacf5\uc815 \uc5f0\uc1c4\u201d\\n\\n  \u2013 \uac01 \uc644\ucda9\uc758 \ud06c\uae30\ub294 \ud574\ub2f9 \uc644\ucda9\uae4c\uc9c0 \uc5f0\uacb0\ub41c \uc885\uc18d \ud65c\ub3d9 \uc5f0\uc1c4\uc758 \uae30\uac04 \ubd88\ud655\uc2e4\uc131\uc744 \uace0\ub824\ud558\uc5ec \uacb0\uc815\ud574\uc57c \ud568\\n\\n  \u2013 \ub124\ud2b8\uc6cc\ud06c \uacbd\ub85c\uc758 \ucd1d \uc5ec\uc720\ub97c \uad00\ub9ac\ud558\ub294 \ub300\uc2e0 \ud65c\ub3d9 \uc5f0\uc1c4\uc758 \uc794\uc5ec \uae30\uac04 \ub300\ube44 \uc794\uc5ec \uc644\ucda9 \uae30\uac04\uc744 \uad00\ub9ac\ud558\ub294 \ub370 \uc8fc\ub825\ud568\\n\\n- \ud504\ub85c\uc81d\ud2b8 \uc644\ucda9(Buffer)\\n\\n  \u2013 \ubd88\ud655\uc2e4\uc131\uc744 \uad00\ub9ac\ud558\uae30 \uc704\ud574 \uc791\uc5c5 \uc774\uc678\uc758 \uc77c\uc815\ud65c\ub3d9\uc778 \uae30\uac04 \uc644\ucda9\uc744 \ucd94\uac00\\n\\n  \u2013 \uc8fc\uacf5\uc815 \uc5f0\uc1c4\uc758 \ub05d\uc5d0 \ucd94\uac00\ub41c \uc644\ucda9\uc73c\ub85c \ubaa9\ud45c \uc885\ub8cc\uc77c\uc774 \uc8fc\uacf5\uc815 \uc5f0\uc1c4\uc5d0\uc11c \ubc97\uc5b4\ub098\uc9c0 \uc54a\ub3c4\ub85d \ubcf4\ud638\\n\\n- \uba39\uc784 \uc644\ucda9(Buffer)\\n\\n  \u2013 \uc8fc\uacf5\uc815 \uc5f0\uc1c4\uc5d0 \uc18d\ud558\uc9c0 \uc54a\uc740 \uc885\uc18d \ud65c\ub3d9 \uc5f0\uc1c4\uac00 \uc8fc\uacf5\uc815 \uc5f0\uc1c4\uc5d0 \uc8fc\uc785\ub418\ub294 \uac01 \uc9c0\uc810\uc5d0 \ucd94\uac00\\n\\n  \u2013 \uba39\uc784\uc644\ucda9\uc740 \uc8fc\uacf5\uc815 \uc5f0\uc1c4\uac00 \uba39\uc784 \uc5f0\uc1c4\uc5d0\uc11c \ubc97\uc5b4\ub098\uc9c0 \uc54a\ub3c4\ub85d \ubcf4\ud638"},{"id":"/2023/03/04/cap-\uc774\ub860","metadata":{"permalink":"/blog/second/2023/03/04/cap-\uc774\ub860","source":"@site/second-blog/2023-03-04-cap-\uc774\ub860/index.mdx","title":"DataBase CAP \uc774\ub860\uc774\ub780","description":"Consistency, Availability, Partition tolerance","date":"2023-03-04T01:18:58.000Z","formattedDate":"2023\ub144 3\uc6d4 4\uc77c","tags":[{"label":"database","permalink":"/blog/second/tags/database"},{"label":"\uc815\ubcf4\uad00\ub9ac","permalink":"/blog/second/tags/\uc815\ubcf4\uad00\ub9ac"}],"readingTime":1.225,"hasTruncateMarker":false,"authors":[{"name":"100milliongold","title":"developer","url":"https://sebastienlorber.com","imageURL":"https://avatars.githubusercontent.com/u/13652711?s=400&u=0a1092b8b1170f71ae883d098f8721c12ce39e9e&v=4","key":"100milliongold"}],"frontMatter":{"title":"DataBase CAP \uc774\ub860\uc774\ub780","date":"2023-03-04T01:18:58.000Z","authors":["100milliongold"],"tags":["database","\uc815\ubcf4\uad00\ub9ac"]},"unlisted":false,"prevItem":{"title":"\ud504\ub85c\uc81d\ud2b8 \uad00\ub9ac","permalink":"/blog/second/2023/03/04/\ud504\ub85c\uc81d\ud2b8\uad00\ub9ac"},"nextItem":{"title":"DataBase \uac1c\uc694 9","permalink":"/blog/second/2023/02/24/DB\uac1c\uc6949"}},"content":"## Consistency, Availability, Partition tolerance\\n\\nC, A, P \uc911 2\uac1c\ub97c \uc120\ud0dd\ud574\uc57c \ud55c\ub2e4. (3\uac00\uc9c0 \ubaa8\ub450 \ub9cc\uc871\ud560 \uc21c \uc5c6\uc74c)\\n\\n![](./image.png)\\n\\n- \uc77c\uad00\uc131(Consistency)\\n  - \ubaa8\ub4e0 \ub178\ub4dc\ub4e4\uc740 \uac19\uc740 \uc2dc\uac04\uc5d0 \ub3d9\uc77c\ud55c \ud56d\ubaa9\uc5d0 \ub300\ud558\uc5ec \uac19\uc740 \ub0b4\uc6a9\uc758 \ub370\uc774\ud130\ub97c \uc0ac\uc6a9\uc790\uc5d0\uac8c \ubcf4\uc5ec\uc900\ub2e4.\\n- \uac00\uc6a9\uc131(Availability)\\n  - \ubaa8\ub4e0 \uc0ac\uc6a9\uc790\ub4e4\uc774 \uc77d\uae30 \ubc0f \uc4f0\uae30\uac00 \uac00\ub2a5\ud574\uc57c \ud558\uba70, \uba87\uba87 \ub178\ub4dc\uc758 \uc7a5\uc560 \uc2dc\uc5d0\ub3c4 \ub2e4\ub978 \ub178\ub4dc\uc5d0 \uc601\ud5a5\uc744 \ubbf8\uce58\uba74 \uc548\ub41c\ub2e4.\\n- \ubd84\ud560\ub0b4\uc131(Partition tolerance)\\n  - \uba54\uc2dc\uc9c0 \uc804\ub2ec\uc774 \uc2e4\ud328\ud558\uac70\ub098 \uc2dc\uc2a4\ud15c \uc77c\ubd80\uac00 \ub9dd\uac00\uc838\ub3c4 \uc2dc\uc2a4\ud15c\uc774 \uacc4\uc18d \ub3d9\uc791\ud560 \uc218 \uc788\uc5b4\uc57c \ud55c\ub2e4.\\n\\n## CAP \uc120\ud0dd \uc720\ud615\\n\\n| \ubd84\ub958 | \uc124\uba85                                    | \uc608\uc2dc                       |\\n| ---- | --------------------------------------- | -------------------------- |\\n| C+A  | \uba54\uc2dc\uc9c0 \uc190\uc2e4\uc744 \ubc29\uc9c0\ud558\ub294 \uac15\ud55c \uc2e0\ub8b0\ud615      | \uc77c\ubc18\uc801 RDBMS<br/>\uc624\ub77c\ud074 \ub4f1 |\\n| C+P  | \ub370\uc774\ud130 \ubcf4\ub2e4\ub294 \uc131\ub2a5\uc774 \uc911\uc694\ud55c \ud37c\ud3ec\uba3c\uc2a4\ud615  | Big Table<br/>MongoDB      |\\n| A+P  | \uc77c\uad00\uc131\uc744 \ud06c\uac8c \uc694\ud558\uc9c0 \uc54a\ub294 \uc11c\ube44\uc2a4\uc5d0 \uc801\ud569 | CouchDB<br/>Cassandra      |"},{"id":"/2023/02/24/DB\uac1c\uc6949","metadata":{"permalink":"/blog/second/2023/02/24/DB\uac1c\uc6949","source":"@site/second-blog/2023-02-24-DB\uac1c\uc6949/index.mdx","title":"DataBase \uac1c\uc694 9","description":"\uc815\uaddc\ud654(Normalization)\uc758 \uc815\uc758","date":"2023-02-24T22:50:00.000Z","formattedDate":"2023\ub144 2\uc6d4 24\uc77c","tags":[{"label":"database","permalink":"/blog/second/tags/database"},{"label":"\uc815\ubcf4\uad00\ub9ac","permalink":"/blog/second/tags/\uc815\ubcf4\uad00\ub9ac"}],"readingTime":1.81,"hasTruncateMarker":false,"authors":[{"name":"100milliongold","title":"developer","url":"https://sebastienlorber.com","imageURL":"https://avatars.githubusercontent.com/u/13652711?s=400&u=0a1092b8b1170f71ae883d098f8721c12ce39e9e&v=4","key":"100milliongold"}],"frontMatter":{"title":"DataBase \uac1c\uc694 9","date":"2023-02-24T22:50:00.000Z","authors":["100milliongold"],"tags":["database","\uc815\ubcf4\uad00\ub9ac"],"password":"1q2w3e4r"},"unlisted":false,"prevItem":{"title":"DataBase CAP \uc774\ub860\uc774\ub780","permalink":"/blog/second/2023/03/04/cap-\uc774\ub860"},"nextItem":{"title":"\uc815\ubcf4\ubcf4\uc548 \ubb38\uc81c","permalink":"/blog/second/2023/02/19/\uc815\ubcf4\ubcf4\uc548\ubb38\uc81c"}},"content":"import Drawio from \\"@theme/Drawio\\";\\nimport simpleGraph1 from \\"!!raw-loader!./chart1.drawio\\";\\n\\n## \uc815\uaddc\ud654(Normalization)\uc758 \uc815\uc758\\n\\n- \uc774\uc0c1\ud604\uc0c1\uc744 \uc57c\uae30\ud558\ub294 \uc18d\uc131\uac04\uc758 \uc885\uc18d\uad00\uacc4\ub97c \uc81c\uac70\ud558\uae30 \uc704\ud574 \ub9b4\ub808\uc774\uc158\uc744 \uc791\uc740 \uc5ec\ub7ec \ub9b4\ub808\uc774\uc158\uc73c\ub85c <b style={{ color: \\"red\\" }}>\ubb34\uc190\uc2e4 \ubd84\ud574(Lossless Decomposition)</b> \ud558\ub294 \uacfc\uc815\\n\\n- \ub370\uc774\ud130 \uc77c\uad00\uc131 , \ub370\uc774\ud130 \uc911\ubcf5\uc758 \ucd5c\uc18c\ud654, \ub370\uc774\ud130 \uad6c\uc870\uc758 \uc548\uc804\uc131\uc744 \ucd5c\ub300\ud55c \ud655\ubcf4\ud558\uae30 \uc704\ud55c \ubc29\ubc95\\n\\n## \uc815\uaddc\ud654\uc758 \ud544\uc694\uc131\\n\\n- \uc911\ubcf5\uc5d0 \ub530\ub978 \uac31\uc2e0\uc774\uc0c1 \ud604\uc0c1\uc758 \uc81c\uac70\ub85c \uc77c\uad00\uc131 \uc720\uc9c0(\uac31\uc2e0\uc774\uc0c1 , \uc0bd\uc785\uc774\uc0c1, \uc0ad\uc81c\uc774\uc0c1 \ud604\uc0c1\uc81c\uac70)\\n- \uc790\ub8cc\uad6c\uc870\uc758 \uc548\uc804\uc131 \ud655\ubcf4 , \uc790\ub8cc \ubd88\uc77c\uce58\uc131 \ucd5c\uc18c\ud654\\n- \ud6a8\uc728\uc801\uc778 \uac80\uc0c9\uc774 \uac00\ub2a5\\n- \uc911\ubcf5\uc758 \uc81c\uac70\ub85c \uc778\ud55c \uc800\uc7a5\uacf5\uac04\uc758 \ucd5c\uc18c\ud654\\n- \ub370\uc774\ud130 \uc2e0\uaddc \ubc1c\uc0dd\uc2dc DB \uc7ac\uad6c\uc131\uc758 \ud544\uc694\uc131\uc744 \uac10\uc18c (\uc720\uc5f0\ud55c \uad6c\uc870)\\n\\n## \uc815\uaddc\ud654 \uad00\ub828 \uc6d0\ub9ac\\n\\n| \uad6c\ubd84                                   | \ub0b4\uc6a9                                                                                                                           |\\n| -------------------------------------- | ------------------------------------------------------------------------------------------------------------------------------ |\\n| \ubb34\uc190\uc2e4 \ubd84\ud574 (Lossless Decomposition)   | - \ud558\ub098 \uc774\uc0c1\uc758 \ub9b4\ub808\uc774\uc158\uc744 \ub450 \uac1c \uc774\uc0c1\uc758 \ub9b4\ub808\uc774\uc158\uc73c\ub85c \ubd84\ud574 \ud588\uc744\ub54c \ubd84\ud574\ud55c \ub9b4\ub808\uc774\uc158\uc744 \uc870\uc778\ud558\uc5ec \ub9b4\ub808\uc774\uc158\uc758 \uc815\ubcf4\ub97c \uc0dd\uc131\ud560\uc218 \uc788\uc5b4\uc57c \ud568 |\\n| \ud568\uc218\uc801 \uc885\uc18d\uc131 (Functional Dependency ) | - \ub9b4\ub808\uc774\uc158\uc758 \ud55c \uc18d\uc131 X \uac00 \ub2e4\ub978\uc18d\uc131 Y \ub97c \uacb0\uc815 \uc9c0\uc744\ub54c Y\ub294 X\uc758 \ud568\uc218\uc801 \uc73c\ub85c \uc885\uc18d\ub428 <br/> - $X \u2192 Y$ (X\ub294 \uacb0\uc815\uc790 Y\ub294 \uc885\uc18d\uc790)         |\\n\\n## \uc815\uaddc\ud654(Normalization)\uc758 \uc808\ucc28\\n\\n<Drawio content={simpleGraph1} resize={true} />"},{"id":"/2023/02/19/\uc815\ubcf4\ubcf4\uc548\ubb38\uc81c","metadata":{"permalink":"/blog/second/2023/02/19/\uc815\ubcf4\ubcf4\uc548\ubb38\uc81c","source":"@site/second-blog/2023-02-19-\uc815\ubcf4\ubcf4\uc548\ubb38\uc81c/index.mdx","title":"\uc815\ubcf4\ubcf4\uc548 \ubb38\uc81c","description":"1","date":"2023-02-19T23:10:00.000Z","formattedDate":"2023\ub144 2\uc6d4 19\uc77c","tags":[{"label":"\uc815\ubcf4\ubcf4\uc548","permalink":"/blog/second/tags/\uc815\ubcf4\ubcf4\uc548"},{"label":"\uc815\ubcf4\uad00\ub9ac","permalink":"/blog/second/tags/\uc815\ubcf4\uad00\ub9ac"}],"readingTime":17.825,"hasTruncateMarker":false,"authors":[{"name":"100milliongold","title":"developer","url":"https://sebastienlorber.com","imageURL":"https://avatars.githubusercontent.com/u/13652711?s=400&u=0a1092b8b1170f71ae883d098f8721c12ce39e9e&v=4","key":"100milliongold"}],"frontMatter":{"title":"\uc815\ubcf4\ubcf4\uc548 \ubb38\uc81c","date":"2023-02-19T23:10:00.000Z","authors":["100milliongold"],"tags":["\uc815\ubcf4\ubcf4\uc548","\uc815\ubcf4\uad00\ub9ac"],"password":"1q2w3e4r"},"unlisted":false,"prevItem":{"title":"DataBase \uac1c\uc694 9","permalink":"/blog/second/2023/02/24/DB\uac1c\uc6949"},"nextItem":{"title":"\uc815\ubcf4\ubcf4\uc548 \uae30\ucd08 1","permalink":"/blog/second/2023/02/18/\uc815\ubcf4\ubcf4\uc548\uae30\ucd081"}},"content":"## 1\\n\\nSSL \uc5d0\uad00\ud55c \uc124\uba85\uc911 \ud2c0\ub9b0\uac83\uc740\\n\\n1.  SSL \ub808\ucf54\ub4dc \ud504\ub85c\ud1a0\ucf5c\uc740 \uae30\ubc00\uc131\uacfc \uba54\uc138\uc9c0 \ubb34\uacb0\uc131, \ubd80\uc778 \ubc29\uc9c0 \uc11c\ube44\uc2a4\ub97c \uc81c\uacf5\ud55c\ub2e4.\\n2.  SSL \ub808\ucf54\ub4dc \ud504\ub85c\ud1a0\ucf5c\uc740 \ub2e8\ud3b8\ud654, \uc555\ucd95 , \uba54\uc138\uc9c0 \uc778\uc99d \ucf54\ub4dc \uacc4\uc0b0, \uc554\ud638\ud654\uc758 \uc21c\uc11c\ub85c \uc218\ud589\ub41c\ub2e4.\\n3.  SSL\uc740 TCP\ub97c \uc774\uc6a9\ud558\uc5ec \uc2e0\ub8b0\ud560\uc218 \uc788\ub294 \uc885\ub2e8-\ub300\uc885\ub2e8 \ubcf4\uc548 \uc11c\ube44\uc2a4\ub97c \uc81c\uacf5\ud558\uae30 \uc704\ud574 \uc124\uacc4\ub418\uc5c8\ub2e4.\\n4.  \uacbd\uace0(Alert) \ud504\ub85c\ud1a0\ucf5c\uc740 SSL \uad00\ub828 \uacbd\uace0\ub97c \uc804\ub2ec\ud558\uae30 \uc704\ud574 \uc0ac\uc6a9\ub41c\ub2e4.\\n\\n<details>\\n  <summary>\ub2f5</summary>\\n\\n    2\ubc88\uc774 \ud2c0\ub9b0 \uc124\uba85\uc785\ub2c8\ub2e4.\\n\\n    SSL \ub808\ucf54\ub4dc \ud504\ub85c\ud1a0\ucf5c\uc740 \uc555\ucd95, \ub2e8\ud3b8\ud654, \uba54\uc2dc\uc9c0 \uc778\uc99d \ucf54\ub4dc(MAC) \uacc4\uc0b0, \uc554\ud638\ud654 \uc21c\uc11c\ub85c \uc218\ud589\ub429\ub2c8\ub2e4. MAC \uacc4\uc0b0 \ub2e8\uacc4\ub294 \uc778\uc99d\uacfc \ubb34\uacb0\uc131 \ubcf4\ud638\ub97c \uc704\ud574 \ud544\uc694\ud55c \ub2e8\uacc4\uc785\ub2c8\ub2e4. \ub2e8\ud3b8\ud654\ub294 SSL \ub808\ucf54\ub4dc \ud504\ub85c\ud1a0\ucf5c\uc5d0\uc11c \uc218\ud589\ud558\ub294 \uac83\uc774 \uc544\ub2c8\ub77c TCP\uc5d0\uc11c \uc218\ud589\ub429\ub2c8\ub2e4.\\n\\n</details>\\n\\n---\\n\\n## 2\\n\\n\ud3ec\ub80c\uc2dd\uc744 \ud1b5\ud574 \ud68d\ub4dd\ud55c \uc99d\uac70\ub294 \ubc95\uc801\uc778 \ud6a8\ub825\uc744 \uac00\uc838\uc57c\ud560 \ud544\uc694\uac00 \uc788\ub2e4. \ud3ec\ub80c\uc2dd\uc758 \uae30\ubcf8\uc6d0\uce59\uc5d0 \ub300\ud55c \uc124\uba85\uc911 \uac00\uc7a5 \uac70\ub9ac\uac00 \uba3c\uac83\uc740\\n\\n1. \uc7ac\ud604\uc758 \uc6d0\uce59: \uc99d\uac70\ub97c \ubcf5\uad6c\ud558\ub294 \uacfc\uc815\uc5d0\uc11c \ub611\uac19\uc740 \ud658\uacbd\uc5d0\uc11c \uac19\uc740 \uacb0\uacfc\uac00 \ub098\uc624\ub3c4\ub85d \uc7ac\ud604\ud560\uc218 \uc788\uc5b4\uc57c\ud568.\\n2. \uc2e0\uc18d\uc131\uc758 \uc6d0\uce59: \uc2dc\uc2a4\ud15c\uc548\uc758 \ub514\uc2a4\ud06c \ub610\ub294 \uba54\ubaa8\ub9ac \uc815\ubcf4\uac00 \ud718\ubc1c\ub418\uae30 \uc804\uc5d0 \ube60\ub974\uac8c \ud68d\ub4dd\ud558\uc5ec\uc57c \ud568.\\n3. \uc815\ub2f9\uc131\uc758 \uc6d0\uce59: \ubaa8\ub4e0\uc99d\uac70\ub294 \uc801\ubc95\ud55c \uc808\ucc28\ub97c \uac70\uccd0\uc11c \ud68d\ub4dd\ud558\uc5ec\uc57c \ud568.\\n4. \ubb34\uacb0\uc131\uc758 \uc6d0\uce59: \uc99d\uac70\uc758 \uc774\uc1a1/\ubd84\uc11d/\ubcf4\uad00/\ubc95\uc815 \uc81c\ucd9c \uc774\ub77c\ub294 \uc77c\ub828\uc758 \uacfc\uc815\uc5d0 \ub300\ud55c \ucd94\uc801\uc774 \uac00\ub2a5\ud574\uc57c\ud568\\n\\n<details>\\n  <summary>\ub2f5</summary>\\n  \\n    \uc124\uba85\uc911 \uac00\uc7a5 \uac70\ub9ac\uac00 \uba3c \uac83\uc740 4\ubc88\uc778 \\"\ubb34\uacb0\uc131\uc758 \uc6d0\uce59: \uc99d\uac70\uc758 \uc774\uc1a1/\ubd84\uc11d/\ubcf4\uad00/\ubc95\uc815 \uc81c\ucd9c \uc774\ub77c\ub294 \uc77c\ub828\uc758 \uacfc\uc815\uc5d0 \ub300\ud55c \ucd94\uc801\uc774 \uac00\ub2a5\ud574\uc57c\ud568\\"\uc785\ub2c8\ub2e4.\\n\\n    \ud3ec\ub80c\uc2dd\uc758 \uae30\ubcf8 \uc6d0\uce59 \uc911 \ubb34\uacb0\uc131\uc758 \uc6d0\uce59\uc740 \uc99d\uac70\uac00 \uc190\uc0c1\ub418\uc9c0 \uc54a\ub3c4\ub85d, \uc99d\uac70\uc758 \ubcc0\uc870 \ubc0f \ud30c\uc190\uc744 \ub9c9\ub294 \uac83\uc744 \uc758\ubbf8\ud569\ub2c8\ub2e4. \ub530\ub77c\uc11c 4\ubc88 \uc124\uba85\uc740 \ub9de\uc2b5\ub2c8\ub2e4.\\n\\n    \ub2e4\ub978 \uc6d0\uce59\uc5d0 \ub300\ud55c \uc124\uba85\uc740 \ub2e4\uc74c\uacfc \uac19\uc2b5\ub2c8\ub2e4.\\n\\n    1. \uc7ac\ud604\uc758 \uc6d0\uce59: \uc99d\uac70\ub97c \ubcf5\uad6c\ud558\ub294 \uacfc\uc815\uc5d0\uc11c \ub611\uac19\uc740 \ud658\uacbd\uc5d0\uc11c \uac19\uc740 \uacb0\uacfc\uac00 \ub098\uc624\ub3c4\ub85d \uc7ac\ud604\ud560 \uc218 \uc788\uc5b4\uc57c \ud568.\\n    2. \uc2e0\uc18d\uc131\uc758 \uc6d0\uce59: \uc2dc\uc2a4\ud15c \uc548\uc758 \ub514\uc2a4\ud06c \ub610\ub294 \uba54\ubaa8\ub9ac \uc815\ubcf4\uac00 \ud718\ubc1c\ub418\uae30 \uc804\uc5d0 \ube60\ub974\uac8c \ud68d\ub4dd\ud558\uc5ec\uc57c \ud568.\\n    3. \uc815\ub2f9\uc131\uc758 \uc6d0\uce59: \ubaa8\ub4e0 \uc99d\uac70\ub294 \uc801\ubc95\ud55c \uc808\ucc28\ub97c \uac70\uccd0\uc11c \ud68d\ub4dd\ud558\uc5ec\uc57c \ud568.\\n\\n</details>\\n\\n---\\n\\n## 3\\n\\n\ub2e4\uc74c \uc0dd\uccb4\uc778\uc2dd\uc758 \uc815\ud655\ub3c4\ub97c \uce21\uc815\ud558\ub294 \ub9e4\uac8c\ubcc0\uc218 \uc911 \uc778\uc2dd\ub418\uc5b4\uc11c\ub294 \uc548\ub420\uc0ac\ub78c\uc774 \uc5bc\ub9c8\ub098 \uc790\uc8fc \uc2dc\uc2a4\ud15c\uc5d0 \uc758\ud574\uc11c \uc778\uc2dd\ub418\ub294\uc9c0\ub97c \ub098\ud0c0\ub0b4\ub294 \uac83\uc740\\n\\n1. \uc815\uc0c1 \uac70\ubd80\uc728\\n2. \ubd80\uc815 \uac70\ubd80\uc728\\n3. \uc815\uc0c1 \ud5c8\uc6a9\uc728\\n4. \ubd80\uc815 \ud5c8\uc6a9\uc728\\n\\n<details>\\n  <summary>\ub2f5</summary>\\n  \\n    \ub2e4\uc74c \uc0dd\uccb4\uc778\uc2dd\uc758 \uc815\ud655\ub3c4\ub97c \uce21\uc815\ud558\ub294 \ub9e4\uac1c\ubcc0\uc218 \uc911 \uc778\uc2dd\ub418\uc5b4\uc11c\ub294 \uc548\ub420 \uc0ac\ub78c\uc774 \uc5bc\ub9c8\ub098 \uc790\uc8fc \uc2dc\uc2a4\ud15c\uc5d0 \uc758\ud574\uc11c \uc778\uc2dd\ub418\ub294\uc9c0\ub97c \ub098\ud0c0\ub0b4\ub294 \uac83\uc740 \\"\ubd80\uc815 \ud5c8\uc6a9\uc728(False Acceptance Rate, FAR)\\"\uc785\ub2c8\ub2e4.\\n\\n</details>\\n\\n---\\n\\n## 4\\n\\n\ub2e4\uc74c \uc124\uba85\uacfc \uac00\uc7a5 \uad00\ub828\uc774 \uc788\ub294 \uac83\uc740\\n\\n\uae30\uc5c5\uc5d0 \ubb3c\ub9ac\uc801 \uc124\ube44, \uc804\ub825, \ub09c\ubc29, \ud1b5\ud48d \ubc0f \uc628\ub3c4 \uc870\uc808\uae30 \ub4f1\uc744 \uc81c\uacf5\ud558\ub294 \ubc31\uc5c5\uc124\ube44\ub85c \uc870\uc791\uc774 \uc774\ub97c \uc774\uc6a9\ud560 \uacbd\uc6b0 \ub2e4\ub978 \ubc31\uc5c5 \uc124\ube44\ubcf4\ub2e4 \ube44\uc6a9\uc774 \uc801\uac8c \ub4dc\ub294 \uc7a5\uc810\uc744 \uac00\uc9c4\ub2e4.\\n\\n1. \ucf5c\ub4dc \uc0ac\uc774\ud2b8\\n2. IDC\\n3. \ud56b \uc0ac\uc774\ud2b8\\n4. \uae30\uc5c5\uac04 \uc0ac\uc774\ud2b8 \uacf5\uc720\\n\\n<details>\\n  <summary>\ub2f5</summary>\\n\\n    \ucf5c\ub4dc \uc0ac\uc774\ud2b8(Cold Site)\uc640 \uad00\ub828\uc774 \uc788\uc2b5\ub2c8\ub2e4.\\n\\n    \ucf5c\ub4dc \uc0ac\uc774\ud2b8\ub294 \uc7ac\ud574 \ubc1c\uc0dd\uc2dc \uc2e0\uc18d\ud558\uac8c \uc5c5\ubb34\ub97c \ubcf5\uad6c\ud558\uae30 \uc704\ud574 \ubb3c\ub9ac\uc801\uc778 \uc2dc\uc124, \uc804\ub825, \ub09c\ubc29, \ud1b5\ud48d \ubc0f \uc628\ub3c4 \uc870\uc808\uae30 \ub4f1\uc774 \uc124\uce58\ub418\uc5b4 \uc788\uc9c0\ub9cc \uc2e4\uc81c \uc6b4\uc6a9\uc911\uc778 \uc2dc\uc2a4\ud15c\uc740 \uc5c6\ub294 \uc0c1\ud0dc\ub97c \ub9d0\ud569\ub2c8\ub2e4. \uc774\ub97c \uc774\uc6a9\ud558\uba74 \ub2e4\ub978 \ubc31\uc5c5 \uc124\ube44\ubcf4\ub2e4\ub294 \ube44\uc6a9\uc774 \uc801\uac8c \ub4e4\uc9c0\ub9cc, \uc5c5\ubb34\ub97c \ubcf5\uad6c\ud558\uae30 \uc704\ud574 \uc2dc\uc2a4\ud15c\uc744 \uad6c\ucd95\ud574\uc57c \ud558\ubbc0\ub85c \uae30\uac04\uc774 \uac78\ub9ac\ub294 \ub2e8\uc810\uc774 \uc788\uc2b5\ub2c8\ub2e4. IDC(\ub370\uc774\ud130 \uc13c\ud130)\ub294 \uae30\uc5c5\uc774\ub098 \ub2e8\uccb4\uc5d0\uc11c \ubcf4\uc720\ud558\uace0 \uc788\ub294 \uc11c\ubc84\uc640 \uc2a4\ud1a0\ub9ac\uc9c0 \ub4f1 IT\uc790\uc6d0\uc744 \uc548\uc815\uc801\uc73c\ub85c \ubcf4\uad00\xb7\uc6b4\uc601\ud560 \uc218 \uc788\ub294 \uc2dc\uc124\uc774\uba70, \ud56b \uc0ac\uc774\ud2b8\ub294 \uc2e4\uc81c\ub85c \uc5c5\ubb34\ub97c \uc6b4\uc601 \uc911\uc778 \uc2dc\uc2a4\ud15c\uc744 \ub300\uccb4\ud558\uae30 \uc704\ud55c \ubc31\uc5c5 \uc2dc\uc2a4\ud15c\uc73c\ub85c, \uacf5\uac04, \uc2dc\uc124, \uc2dc\uc2a4\ud15c \ub4f1 \ubaa8\ub4e0 \uac83\uc774 \uc900\ube44\ub418\uc5b4 \uc788\uc5b4 \ub300\ub7b5 24\uc2dc\uac04 \ub0b4\uc5d0 \uc5c5\ubb34\ub97c \ubcf5\uad6c\ud560 \uc218 \uc788\ub294 \uc2dc\uc2a4\ud15c\uc785\ub2c8\ub2e4. \uae30\uc5c5\uac04 \uc0ac\uc774\ud2b8 \uacf5\uc720\ub294 \uc5ec\ub7ec \uae30\uc5c5\uc774\ub098 \ub2e8\uccb4\ub4e4\uc774 \uacf5\ub3d9\uc73c\ub85c IT \uc790\uc6d0\uc744 \uc6b4\uc601\ud558\ub294 \ubc29\uc2dd\uc744 \ub9d0\ud569\ub2c8\ub2e4.\\n\\n</details>\\n\\n---\\n\\n## 5\\n\\n\ub2e4\uc74c\uc911 TCP \ud504\ub85c\ud1a0\ucf5c\uc758 3-way handshake \ucde8\uc57d\uc810\uc744 \uc774\uc6a9\ud558\ub294 \uacf5\uaca9\uc740 \uc5b4\ub290\uac83\uc778\uac00\\n\\n1. Fraggle\\n2. Session Jijacking\\n3. ARP Spoofing\\n4. DNS Spofing\\n\\n<details>\\n  <summary>\ub2f5</summary>\\n\\n    TCP \ud504\ub85c\ud1a0\ucf5c\uc758 3-way handshake \ucde8\uc57d\uc810\uc744 \uc774\uc6a9\ud558\ub294 \uacf5\uaca9\uc740 \'2. Session Hijacking\'\uc785\ub2c8\ub2e4. \uc774 \uacf5\uaca9\uc740 \uacf5\uaca9\uc790\uac00 \ud1b5\uc2e0 \uc911\uc778 \ub450 \ud638\uc2a4\ud2b8 \uc0ac\uc774\uc758 \uc5f0\uacb0\uc744 \uac00\ub85c\ucc44\uc5b4, \uc5f0\uacb0\uc744 \uc774\uc5b4\uac00\ub294 \uac83\uc73c\ub85c \uac00\uc7a5\ud558\uace0 \ub370\uc774\ud130\ub97c \ubcc0\uacbd\ud558\uac70\ub098 \ub3c4\uc6a9\ud560 \uc218 \uc788\ub294 \uacf5\uaca9\uc785\ub2c8\ub2e4.\\n\\n</details>\\n\\n---\\n\\n## 6\\n\\nsql injection \uacf5\uaca9\uc5d0 \ub300\ud55c \uc124\uba85\uc73c\ub85c \uac00\uc7a5 \uac70\ub9ac\uac00 \uba3c\uac83\uc740\\n\\n1. \uc6f9 \uc5b4\ud50c\ub9ac\ucf00\uc774\uc158\uc774 \uc0ac\uc6a9\uc790 \uc785\ub825\uac12\uc5d0 \ub300\ud55c \uac80\uc99d\uc744 \ud558\uc9c0 \uc54a\ub294 \ucde8\uc57d\uc810\uc744 \uc774\uc6a9\ud55c \uacf5\uaca9\uc774\ub2e4.\\n2. \uac8c\uc2dc\ud310\uc5d0 \uc545\uc131\uc2a4\ud06c\ub9bd\ud2b8\ub97c \uc0bd\uc785\ud55c\ub2e4.\\n3. \uc6f9 \ube0c\ub77c\uc6b0\uc800\ub97c \ud1b5\ud574 \uc784\uc758\uc758 sql \ubb38\uc7a5\uc744 \uc0bd\uc785\ud558\uc5ec \uc5d0\ub7ec\ub97c \ub178\ucd9c\ud558\uac70\ub098 \uc778\uac00\ub418\uc9c0 \uc54a\uc740 \ub370\uc774\ud130\ubca0\uc774\uc2a4\uc758 \ub370\uc774\ud130\ub97c \uc870\ud68c\ud560\uc218 \uc788\ub2e4.\\n4. ids\uc5d0 \uc0ac\uc6a9\uc790 \uc815\uc758 \ud328\ud134\uc744 \uc0bd\uc785\ud558\uc5ec sql injection\uc744 \ud0d0\uc9c0\ud560\uc218 \uc788\ub2e4.\\n\\n<details>\\n  <summary>\ub2f5</summary>\\n  \\n    4 \ubc88\\n  \\n    ids\uc5d0 \uc0ac\uc6a9\uc790 \uc815\uc758 \ud328\ud134\uc744 \uc0bd\uc785\ud558\uc5ec sql injection\uc744 \ud0d0\uc9c0\ud560\uc218 \uc788\ub2e4\ub294 \uac83\uc774 \uac00\uc7a5 \uac70\ub9ac\uac00 \uba3c \uac83\uc785\ub2c8\ub2e4. SQL Injection \uacf5\uaca9\uc744 \ud0d0\uc9c0\ud558\uae30 \uc704\ud574\uc11c\ub294 \ud2b9\uc815 \ud328\ud134\uc774 \uc544\ub2cc, \uc6f9 \uc5b4\ud50c\ub9ac\ucf00\uc774\uc158\uc5d0\uc11c \ubc1c\uc0dd\ud558\ub294 \ucffc\ub9ac\ubb38 \uc790\uccb4\uc758 \uad6c\uc870\uc801\uc778 \ubb38\uc81c\ub97c \ud0d0\uc9c0\ud558\ub294 \ubc29\ubc95\uc774 \uc77c\ubc18\uc801\uc73c\ub85c \uc0ac\uc6a9\ub429\ub2c8\ub2e4. \ud328\ud134 \uae30\ubc18\uc758 \ud0d0\uc9c0\ub294 \ucde8\uc57d\uc810\uc774 \ubc1c\uacac\ub41c \uacbd\uc6b0, \uc774\ubbf8 \uacf5\uaca9\uc774 \uc131\uacf5\ud55c \ud6c4\uc5d0 \ud0d0\uc9c0\ud560 \uc218 \uc788\uae30 \ub54c\ubb38\uc5d0 \uc801\uadf9\uc801\uc778 \ub300\uc751\uc774 \uc5b4\ub835\uc2b5\ub2c8\ub2e4.\\n\\n</details>\\n\\n---\\n\\n## 7\\n\\n\uc545\uc131\uc2a4\ud06c\ub9bd\ud2b8 \ud30c\uc77c\uc744 \uc5c5\ub85c\ub4dc\ud558\uc5ec \uacf5\uaca9\ud558\ub294 \uac83\uc744 \uc608\ubc29\ud558\uae30 \uc704\ud55c java \uc0c1\uc5d0\uc11c \uc548\uc804\ud55c \ucf54\ub529\uae30\ubc95\uacfc \uac70\ub9ac\uac00 \uba3c\uac83\uc744 \ubaa8\ub450 \uace0\ub974\uc2dc\uc624 (2\uac1c\uc120\ud0dd)\\n\\n1. \uc5c5\ub85c\ub4dc \ud558\ub294 \ud30c\uc77c \ud0c0\uc785\uacfc \ud06c\uae30\ub97c \uc81c\ud55c\ud558\uace0 \uc5c5\ub85c\ub4dc \ub514\ub809\ud1a0\ub9ac\ub97c \uc6f9 \uc11c\ubc84 \ub3c4\ud050\uba3c\ud2b8 \ub0b4\ubd80\uc5d0 \uc124\uc815\\n2. preparedStatement \ud074\ub798\uc2a4\ub97c \uc0ac\uc6a9\ud558\uc5ec \ud30c\uc77c\uc774 \uc2e4\ud589\uac00\ub2a5\ud55c \uba85\ub839\uc5b4\ub85c \ubcc0\uacbd\ub418\ub294\uac83\uc744 \ubc29\uc9c0\\n3. \ud654\uc774\ud2b8\ub9ac\uc2a4\ud2b8\ubc29\uc2dd\uc73c\ub85c \ud5c8\uc6a9\ub41c \ud655\uc7a5\uc790\ub9cc \uc5c5\ub85c\ub4dc\ub97c \ud5c8\uc6a9\ud558\uace0 \ud655\uc7a5\uc790\ub294 \ub300\uc18c\ubb38\uc790 \uad6c\ubd84\uc5c6\uc774 \ucc98\ub9ac\\n4. \uacf5\uaca9\uc790\uc758 \uc6f9\uc744 \ud1b5\ud55c \uc9c1\uc811 \uc811\uadfc\uc744 \ucc28\ub2e8\ud558\uace0 \ud30c\uc77c \uc2e4\ud589\uc5ec\ubd80\ub97c \uc124\uc815\ud560\uc218 \uc788\ub294 \uacbd\uc6b0\uc5d0 \uc2e4\ud589 \uc18d\uc131\uc744 \uc81c\uac70\\n\\n<details>\\n  <summary>\ub2f5</summary>\\n\\n    1\ubc88\uacfc 4\ubc88\uc774 \uac70\ub9ac\uac00 \uba3c \ub2f5\uc548\uc785\ub2c8\ub2e4.\\n\\n    1\ubc88\uc740 \ud30c\uc77c \ud0c0\uc785\uacfc \ud06c\uae30\ub97c \uc81c\ud55c\ud558\ub294 \uac83\uc740 \ubcf4\uc548\uc0c1 \uc88b\uc740 \ubc29\ubc95\uc774\uc9c0\ub9cc, \uc5c5\ub85c\ub4dc \ub514\ub809\ud1a0\ub9ac\ub97c \uc6f9 \uc11c\ubc84 \ub3c4\ud050\uba3c\ud2b8 \ub0b4\ubd80\uc5d0 \uc124\uc815\ud558\ub294 \uac83\uc740 \uc704\ud5d8\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4. \uc5c5\ub85c\ub4dc\ub41c \ud30c\uc77c\uc774 \uc6f9 \uc11c\ubc84\uc5d0\uc11c \uc2e4\ud589 \uac00\ub2a5\ud55c \uc2a4\ud06c\ub9bd\ud2b8\uc77c \uacbd\uc6b0, \uc5c5\ub85c\ub4dc\ub41c \ud30c\uc77c\uc774 \uc2e4\ud589\ub418\uc5b4 \ubcf4\uc548\uc0c1 \ubb38\uc81c\uac00 \ubc1c\uc0dd\ud560 \uc218 \uc788\uae30 \ub54c\ubb38\uc785\ub2c8\ub2e4.\\n\\n    4\ubc88\uc740 \uacf5\uaca9\uc790\uc758 \uc9c1\uc811 \uc811\uadfc \ucc28\ub2e8\uacfc \uc2e4\ud589 \uc18d\uc131 \uc81c\uac70\ub294 \ud30c\uc77c\uc5d0 \ub300\ud55c \ubcf4\uc548\uc744 \uac15\ud654\ud558\ub294 \uc88b\uc740 \ubc29\ubc95\uc785\ub2c8\ub2e4. \uadf8\ub7ec\ub098, \uc774 \ubc29\ubc95\ub9cc\uc73c\ub85c\ub294 \uc545\uc131\uc2a4\ud06c\ub9bd\ud2b8 \ud30c\uc77c\uc744 \uc5c5\ub85c\ub4dc\ud558\uc5ec \uacf5\uaca9\ud558\ub294 \uac83\uc744 \uc608\ubc29\ud560 \uc218 \uc5c6\uc2b5\ub2c8\ub2e4.\\n\\n</details>\\n\\n---\\n\\n## 8\\n\\n\ub2e4\uc74c \uc124\uba85\uc5d0 \uac00\uc7a5 \uc801\uc808\ud55c \uc811\uadfc \ud1b5\uc81c \ubc29\ubc95\uc740\\n\\n\uae30\uc874\uc5d0 \uc811\uadfc\ud1b5\uc81c\uc5d0\uc11c\ub294 \ubaa8\ub4e0 \uc0ac\uc6a9\uc790 \uac1c\uac1c\uc778\uc758 \uc811\uadfc\ud1b5\uc81c \uaddc\uce59\uc744 \ubcf4\uc720\ud558\uc600\uc9c0\ub9cc \uc774\uac83\uc774 \ud6a8\uc728\uc801\uc774\uc9c0 \ubabb\ud560 \ubfd0\ub9cc \uc544\ub2c8\ub77c \ub54c\ub85c \ubcf4\uc548 \uc0ac\uace0\uc758 \uc6d0\uc778\uc774 \ub418\uc5c8\ub2e4 \ub530\ub77c\uc11c \ubcf4\uc548 \uad00\ub9ac\uc790\ub294 \uc2dc\uc2a4\ud15c\uc5d0 \uc811\uadfc\ud558\ub294 \uc0ac\uc6a9\uc790\ub4e4\uc744 \uc9c1\ucc45 \ubc0f \ubcf4\uc9c1\uc744 \uae30\ubc18\uc73c\ub85c \ud1b5\uc81c \ud558\uace0\uc790 \ud55c\ub2e4.\\n\\n1. \uc784\uc758\uc801 \uc811\uadfc\ud1b5\uc81c\\n2. \uac15\uc81c\uc801 \uc811\uadfc\ud1b5\uc81c\\n3. \uc5ed\ud65c\uae30\ubc18 \uc811\uadfc\ud1b5\uc81c\\n4. \ub2e4\uacc4\uce35 \uc811\uadfc\ud1b5\uc81c\\n\\n<details>\\n  <summary>\ub2f5</summary>\\n  \uc5ed\ud560\uae30\ubc18 \uc811\uadfc\ud1b5\uc81c (Role-based Access Control, RBAC)\\n\\n</details>\\n\\n---\\n\\n## 9\\n\\n\ud06c\ub85c\uc2a4 \uc0ac\uc774\ud2b8 \uc2a4\ud06c\ub9bd\ud2b8(XSS) \uc5d0\ub300\ud558\uc5ec \uc124\uba85\ud558\uc2dc\uc694\\n\\n<details>\\n  <summary>\ub2f5</summary>\\n\\n    \ud06c\ub85c\uc2a4 \uc0ac\uc774\ud2b8 \uc2a4\ud06c\ub9bd\ud2b8(XSS)\ub294 \uc6f9 \uc5b4\ud50c\ub9ac\ucf00\uc774\uc158 \ucde8\uc57d\uc810 \uc911 \ud558\ub098\ub85c, \uacf5\uaca9\uc790\uac00 \uc545\uc131 \uc2a4\ud06c\ub9bd\ud2b8\ub97c \uc0bd\uc785\ud558\uc5ec \uc6f9\uc0ac\uc774\ud2b8 \uce21\uc5d0\uc11c \uc774\ub97c \uc2e4\ud589\ud558\ub3c4\ub85d \uc720\ub3c4\ud568\uc73c\ub85c\uc368 \uacf5\uaca9\uc790\uac00 \uc758\ub3c4\ud55c \ub300\ub85c \ub3d9\uc791\ud558\uac8c \ub9cc\ub4dc\ub294 \uacf5\uaca9 \uae30\ubc95\uc785\ub2c8\ub2e4.\\n\\n    \uc77c\ubc18\uc801\uc73c\ub85c, XSS\ub294 \uc6f9\uc0ac\uc774\ud2b8\uc5d0 \uc785\ub825\ub41c \uac12\uc744 \uac80\uc99d\ud558\uc9c0 \uc54a\uac70\ub098, \uac80\uc99d\uc744 \ud558\ub418 \uc801\uc808\ud55c \uc5d0\uc2a4\ucf00\uc774\ud504 \ucc98\ub9ac\ub97c \ud558\uc9c0 \uc54a\ub294 \uacbd\uc6b0 \ubc1c\uc0dd\ud569\ub2c8\ub2e4. \uc774\ub97c \ud1b5\ud574 \uacf5\uaca9\uc790\ub294 \ud574\ucee4\uac00 \uc791\uc131\ud55c \uc2a4\ud06c\ub9bd\ud2b8\ub97c \uc774\uc6a9\ud574, \ub2e4\ub978 \uc0ac\uc6a9\uc790\ub4e4\uc758 \ucfe0\ud0a4 \uc815\ubcf4, \uc138\uc158ID, \ub610\ub294 \ub2e4\ub978 \ubbfc\uac10\ud55c \uc815\ubcf4\ub97c \ud0c8\ucde8\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4.\\n\\n    XSS \uacf5\uaca9\uc740 Reflected XSS, Stored XSS, DOM-based XSS \ub4f1\uc73c\ub85c \ub098\ub269\ub2c8\ub2e4. Reflected XSS\ub294 \uc0ac\uc6a9\uc790\uc758 \uc785\ub825\uac12\uc744 \uc11c\ubc84\uc5d0\uc11c \ucc98\ub9ac\ud55c \uacb0\uacfc\ub97c \ub3cc\ub824\uc904 \ub54c \ubc1c\uc0dd\ud558\ub294 \uacf5\uaca9\uc774\uba70, Stored XSS\ub294 \uc11c\ubc84\uc5d0 \uc545\uc131 \uc2a4\ud06c\ub9bd\ud2b8\ub97c \uc800\uc7a5\ud574 \uc0ac\uc6a9\uc790\uac00 \uc811\uadfc\ud560 \ub54c \uc2e4\ud589\ub418\ub294 \uacf5\uaca9\uc785\ub2c8\ub2e4. DOM-based XSS\ub294 HTML \ubb38\uc11c \uac1d\uccb4 \ubaa8\ub378(DOM)\uc744 \uc870\uc791\ud558\uc5ec \uacf5\uaca9\uc744 \uc218\ud589\ud569\ub2c8\ub2e4.\\n\\n    XSS \uacf5\uaca9\uc744 \ubc29\uc9c0\ud558\uae30 \uc704\ud574\uc11c\ub294 \uc785\ub825\uac12 \uac80\uc99d\uacfc \uc801\uc808\ud55c \uc5d0\uc2a4\ucf00\uc774\ud504 \ucc98\ub9ac\uac00 \ud544\uc694\ud569\ub2c8\ub2e4. \ub610\ud55c, \ucfe0\ud0a4\ub97c HttpOnly \uc18d\uc131\uc73c\ub85c \uc124\uc815\ud558\uace0, \ubcf4\uc548 \ucde8\uc57d\uc810 \uc2a4\uce90\ub108\uc640 \uc6f9 \ubc29\ud654\ubcbd \ub4f1\uc758 \ubcf4\uc548\uc7a5\uce58\ub97c \uc774\uc6a9\ud558\uc5ec \ubc29\uc5b4\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4.\\n\\n</details>\\n\\n---\\n\\n## 10\\n\\n\ub514\uc9c0\ud138 \ud3ec\ub80c\uc2dd \uc124\uba85\\n\\n<details>\\n  <summary>\ub2f5</summary>\\n\\n    \ub514\uc9c0\ud138 \ud3ec\ub80c\uc2dd\uc740 \ucef4\ud4e8\ud130 \uc2dc\uc2a4\ud15c, \ub514\uc9c0\ud138 \uc800\uc7a5 \ub9e4\uccb4, \ub124\ud2b8\uc6cc\ud06c \ub4f1\uc5d0\uc11c \ubc1c\uc0dd\ud55c \ubc94\uc8c4\ub098 \uc0ac\uac74\uc758 \uc99d\uac70\ub97c \uc218\uc9d1, \ubd84\uc11d\ud558\uace0 \ubcf5\uc6d0\ud558\ub294 \uacfc\uc815\uc744 \ub9d0\ud569\ub2c8\ub2e4.\\n\\n    \ub514\uc9c0\ud138 \ud3ec\ub80c\uc2dd \uc804\ubb38\uac00\ub294 \ubc94\uc8c4\ub098 \uc0ac\uac74\uc5d0 \ub300\ud55c \uc99d\uac70\ub97c \uc218\uc9d1\ud558\uace0, \uc774\ub97c \ubcf4\uc874\ud558\uace0 \ubd84\uc11d\ud558\uc5ec \ubc94\uc778\uc774\ub098 \uc0ac\uac74\uc758 \uc9c4\uc2e4\uc744 \ud30c\uc545\ud558\ub294 \uc5ed\ud560\uc744 \uc218\ud589\ud569\ub2c8\ub2e4. \uc774\ub97c \uc704\ud574 \ub514\uc9c0\ud138 \uc800\uc7a5 \ub9e4\uccb4\ub098 \uae30\uae30\uc5d0\uc11c \uc0ad\uc81c\ub41c \ud30c\uc77c, \uc0ac\uc6a9\uc790\uc758 \ud65c\ub3d9 \ub0b4\uc5ed, \ub124\ud2b8\uc6cc\ud06c \ud328\ud0b7 \ub4f1 \ub2e4\uc591\ud55c \uc815\ubcf4\ub97c \ucd94\ucd9c\ud558\uace0 \ubd84\uc11d\ud558\ub294 \uae30\uc220\uc744 \uc0ac\uc6a9\ud569\ub2c8\ub2e4.\\n\\n    \ub514\uc9c0\ud138 \ud3ec\ub80c\uc2dd\uc740 \ucef4\ud4e8\ud130 \ubc94\uc8c4\ub098 \uc778\ud130\ub137 \ubc94\uc8c4, \uae30\uc5c5 \ub0b4\ubd80 \uc720\ucd9c, \uc9c0\uc2dd\uc7ac\uc0b0\uad8c \uce68\ud574, \uc0ac\uc0dd\ud65c \uce68\ud574, \uc545\uc131\ucf54\ub4dc \ubc0f \uc0ac\uc774\ubc84 \uacf5\uaca9\uacfc \uac19\uc740 \ub2e4\uc591\ud55c \ubc94\uc8c4\ub098 \uc0ac\uac74\uc5d0 \uc801\uc6a9\ub429\ub2c8\ub2e4. \uc774\ub97c \ud1b5\ud574 \ubc94\uc778\uc758 \ub3c4\ub09c\ud55c \ub370\uc774\ud130\ub098 \uc0ad\uc81c\ub41c \uc815\ubcf4, \uc545\uc131\ucf54\ub4dc\uac00 \uce68\ud22c\ud55c \uacbd\ub85c \ub4f1\uc744 \ud30c\uc545\ud558\uace0, \ubc94\uc8c4\ub098 \uc0ac\uac74\uc758 \uac00\ud574\uc790\ub098 \uc6d0\uc778\uc744 \ucc3e\uc544\ub0b4\ub294 \ub370 \ub3c4\uc6c0\uc744 \uc90d\ub2c8\ub2e4.\\n\\n</details>\\n\\n---\\n\\n## 11\\n\\n\uc0dd\uccb4\uc778\uc2dd\uae30\ubc95\uc758 \uac1c\ub150 \ubc0f \uad6c\ud604 \uae30\ubc95\ub4e4\uc758 \ud2b9\uc9d5\uc5d0 \ub300\ud558\uc5ec \uc124\uba85\ud558\uc2dc\uc624\\n\\n<details>\\n  <summary>\ub2f5</summary>\\n\\n    \uc0dd\uccb4\uc778\uc2dd \uae30\uc220\uc774\ub780 \uc0dd\uccb4 \ud2b9\uc9d5\uc744 \uce21\uc815\ud558\uace0 \uc778\uc2dd\ud558\ub294 \uae30\uc220\ub85c, \ud328\uc2a4\uc6cc\ub4dc\ub098 \uce74\ub4dc\ud0a4\uc640 \uac19\uc740 \uae30\uc874\uc758 \uc778\uc99d \ubc29\uc2dd\ubcf4\ub2e4 \ub192\uc740 \ubcf4\uc548\uc131\uacfc \ud3b8\uc758\uc131\uc744 \uc81c\uacf5\ud569\ub2c8\ub2e4. \ub2e4\uc591\ud55c \uc0dd\uccb4 \ud2b9\uc9d5\uc744 \uc774\uc6a9\ud55c \uc778\uc2dd \uae30\uc220\uc774 \uac1c\ubc1c\ub418\uc5b4 \uc654\uc73c\uba70, \ub300\ud45c\uc801\uc778 \uad6c\ud604 \uae30\ubc95\ub4e4\uc740 \ub2e4\uc74c\uacfc \uac19\uc2b5\ub2c8\ub2e4.\\n\\n    1. \uc9c0\ubb38 \uc778\uc2dd\\n        - \uc0ac\uc6a9\uc790\uc758 \uc190\uac00\ub77d\uc5d0 \uc788\ub294 \uc9c0\ubb38\uc758 \ubb34\ub2ac, \uc120\uba85\ub3c4, \uc9c8\uac10 \ub4f1\uc758 \uace0\uc720\ud55c \ud2b9\uc131\uc744 \uc774\uc6a9\ud574 \uc778\uc99d\ud558\ub294 \uae30\uc220\\n        - \uace0\uc720\uc131\uc774 \ub192\uc544 \ud3b8\ub9ac\ud558\uac8c \uc0ac\uc6a9\ud560 \uc218 \uc788\uc73c\ub098, \uc2e0\ub8b0\uc131\uc774 \ub2e4\uc18c \ub5a8\uc5b4\uc9c8 \uc218 \uc788\uc74c\\n    2. \ud64d\ucc44 \uc778\uc2dd\\n        - \uc0ac\uc6a9\uc790\uc758 \ub208\ub3d9\uc790 \ub0b4\ubd80\uc758 \ud64d\ucc44 \ud328\ud134\uc744 \uc774\uc6a9\ud574 \uc778\uc99d\ud558\ub294 \uae30\uc220\\n        - \ub192\uc740 \uc2e0\ub8b0\uc131\uc744 \uc81c\uacf5\ud558\uc9c0\ub9cc, \ubcf5\uc7a1\ud55c \uc778\uc2dd\uacfc\uc815\uacfc \uc778\uc2dd \uc2dc\uac04\uc774 \uc624\ub798 \uac78\ub9b0\ub2e4\ub294 \ub2e8\uc810\uc774 \uc788\uc74c\\n    3. \uc5bc\uad74 \uc778\uc2dd\\n        - \uc0ac\uc6a9\uc790\uc758 \uc5bc\uad74 \ubaa8\uc591, \ud06c\uae30, \uc724\uacfd \ub4f1\uc744 \uc774\uc6a9\ud574 \uc778\uc99d\ud558\ub294 \uae30\uc220\\n        - \ube60\ub978 \uc778\uc2dd\uc18d\ub3c4\uc640 \uc0ac\uc6a9\uc790\uc758 \uc778\uc2dd\uacfc \uad00\ub828\ub41c \ud3b8\uc758\uc131\uc774 \ub192\uc73c\ub098, \uc870\uba85\uc774\ub098 \ud658\uacbd \ubcc0\ud654 \ub4f1\uc5d0 \ub530\ub77c \uc131\ub2a5\uc774 \ud06c\uac8c \ubcc0\ub3d9\ub420 \uc218 \uc788\uc74c\\n    4. \uc74c\uc131 \uc778\uc2dd\\n        - \uc0ac\uc6a9\uc790\uc758 \uc74c\uc131 \ud1a4, \ubc1c\uc74c, \uc5b5\uc591 \ub4f1\uc744 \uc774\uc6a9\ud574 \uc778\uc99d\ud558\ub294 \uae30\uc220\\n        - \uc5b8\uc5b4\ub098 \ubc1c\uc74c\uc5d0 \ub530\ub77c \uc778\uc2dd\ub960\uc774 \ubcc0\ud560 \uc218 \uc788\uc73c\uba70, \ud2b9\uc815 \ud658\uacbd\uc5d0\uc11c\ub294 \uc778\uc2dd\uc774 \uc81c\ub300\ub85c \uc774\ub8e8\uc5b4\uc9c0\uc9c0 \uc54a\uc744 \uc218 \uc788\uc74c\\n    5. \uc815\ub9e5 \uc778\uc2dd\\n        - \uc0ac\uc6a9\uc790\uc758 \uc190\uac00\ub77d\uc774\ub098 \ud314\uc758 \uc815\ub9e5 \ubb34\ub2ac\ub97c \uc774\uc6a9\ud574 \uc778\uc99d\ud558\ub294 \uae30\uc220\\n        - \uc0dd\uccb4 \uc778\uc2dd \uae30\uc220 \uc911\uc5d0\uc11c \uac00\uc7a5 \ub192\uc740 \uc2e0\ub8b0\uc131\uc744 \uc81c\uacf5\ud558\ub098, \ub300\uc0c1 \ubd80\uc704\ub97c \uc9c1\uc811 \uc13c\uc11c\uc5d0 \ub300\uc57c \ud558\uae30 \ub54c\ubb38\uc5d0 \uc0ac\uc6a9\uc790\uc758 \ud3b8\uc758\uc131\uc774 \ub2e4\uc18c \ub5a8\uc5b4\uc9c8 \uc218 \uc788\uc74c\\n    6. \uc2ec\uc7a5 \ubc15\ub3d9 \uc778\uc2dd\\n        - \uc0ac\uc6a9\uc790\uc758 \uc2ec\uc7a5 \ubc15\ub3d9\uc744 \uce21\uc815\ud558\uc5ec \uc778\uc99d\ud558\ub294 \uae30\uc220\\n        - \ub9e4\uc6b0 \ub192\uc740 \uc2e0\ub8b0\uc131\uc744 \uc81c\uacf5\ud558\uc9c0\ub9cc, \uc13c\uc11c\uc758 \uc815\ud655\ub3c4\uc640 \uc0ac\uc6a9\uc790\uc758 \uac74\uac15\uc0c1\ud0dc \ub4f1\uc5d0 \ub530\ub77c \uc778\uc2dd\ub960\uc774 \ud06c\uac8c \ubcc0\ub3d9\ub420 - \uc218 \uc788\uc74c\\n    \uc0dd\uccb4 \uc778\uc2dd \uae30\uc220\uc740 \ub2e4\uc591\ud55c \ud2b9\uc131\uc744 \uc774\uc6a9\ud558\uc5ec \uad6c\ud604\ub429\ub2c8\ub2e4.\\n\\n</details>"},{"id":"/2023/02/18/\uc815\ubcf4\ubcf4\uc548\uae30\ucd081","metadata":{"permalink":"/blog/second/2023/02/18/\uc815\ubcf4\ubcf4\uc548\uae30\ucd081","source":"@site/second-blog/2023-02-18-\uc815\ubcf4\ubcf4\uc548\uae30\ucd081/index.mdx","title":"\uc815\ubcf4\ubcf4\uc548 \uae30\ucd08 1","description":"\uc815\ubcf4","date":"2023-02-18T10:10:00.000Z","formattedDate":"2023\ub144 2\uc6d4 18\uc77c","tags":[{"label":"\uc815\ubcf4\ubcf4\uc548","permalink":"/blog/second/tags/\uc815\ubcf4\ubcf4\uc548"},{"label":"\uc815\ubcf4\uad00\ub9ac","permalink":"/blog/second/tags/\uc815\ubcf4\uad00\ub9ac"}],"readingTime":13.565,"hasTruncateMarker":false,"authors":[{"name":"100milliongold","title":"developer","url":"https://sebastienlorber.com","imageURL":"https://avatars.githubusercontent.com/u/13652711?s=400&u=0a1092b8b1170f71ae883d098f8721c12ce39e9e&v=4","key":"100milliongold"}],"frontMatter":{"title":"\uc815\ubcf4\ubcf4\uc548 \uae30\ucd08 1","date":"2023-02-18T10:10:00.000Z","authors":["100milliongold"],"tags":["\uc815\ubcf4\ubcf4\uc548","\uc815\ubcf4\uad00\ub9ac"],"password":"1q2w3e4r"},"unlisted":false,"prevItem":{"title":"\uc815\ubcf4\ubcf4\uc548 \ubb38\uc81c","permalink":"/blog/second/2023/02/19/\uc815\ubcf4\ubcf4\uc548\ubb38\uc81c"},"nextItem":{"title":"DataBase \ubb38\uc81c\ud480\uc774","permalink":"/blog/second/2023/02/17/DB\ubb38\uc81c"}},"content":"import Drawio from \\"@theme/Drawio\\";\\nimport simpleGraph1 from \\"!!raw-loader!./\uc815\ubcf4\ubcf4\uc548FW.drawio\\";\\n\\n## \uc815\ubcf4\\n\\n- \uad50\uc2dc\ud615 : 13 \ubb38\uc81c\\n- \uc11c\uc220\ud615 : 6\ubb38\uc81c\\n\\n  - \uc2e4\ubb34 \uc0ac\ub840\ub3c4 \uc81c\uc2dc\ub428, \uc65c \ud544\uc694\ud55c\uc9c0, \uc138\ubd80\uc0ac\ud56d, \uc99d\ube59\uc5d0\\n  - \ubb38\uc81c\uc810 -> \ud574\uacb0\ucc45\uc744 \uc81c\uc2dc \ud558\ub294 \ubb38\uc81c\\n  - \ubc95\ub960 : \ub370\uc774\ud130 \uc0c1\ubc95, \uac1c\uc778\uc815\ubcf4 \ubcf4\ud638\ubc95, \uc815\ubcf4\ud1b5\uc2e0\ub9dd\ubc95, \uc2e0\uc6a9\uc815\ubcf4 \uad00\ub9ac\ubc95\\n\\n    - \uaddc\uc81c , \uadfc\uac70 \uac00\ub418\ub294 \uae30\uc900\uc774 \ubc95\uc5d0 \uc758\uc874\ud558\uae30 \ub54c\ubb38\uc5d0 \uc54c\uc544\uc57c\ud568\\n    - \ud45c\uc900\uc774 \ub428 : ISO 27001 , ISMS-P\\n\\n  - ISMS-P \uc778\uc99d\uae30\uc900 \uc548\ub0b4\uc11c \uc815\ub3c5\\n  - 1\ud398\uc774\uc9c0\uc5d0 7\ubd84\uc815\ub3c4 \uc2dc\uac04\uc774 \uc18c\uc694\ub428\\n  - \uc7a5\uae30\uc801\uc778 \uc2f8\uc6c0\uc774 \ub428 (\ub9c8\ub77c\ud1a4)\\n\\n- \uc9d1\uc5d0 \uac00\uc11c \ud560\uc77c 5\ub144\uce58 \uae30\ucd9c\ubb38\uc81c\ub97c \ubd84\uc11d\ud574\uc57c \ud574\uc57c\ub428\\n  - \ubc29\ud5a5\uc131\uc744 \uc7a1\uace0 \uc120\ud0dd\uacfc \uc9d1\uc911\uc744 \ud560\uc218 \uc788\ub3c4\ub85d \ud55c\ub2e4.\\n- 1\uad50\uc2dc \ubb38\uc81c\uc77c\ub54c \ucd9c\uc81c\uc790\uac00 \uae30\uc220\uc0ac\uc778\uc9c0, \uad50\uc218\uc778\uc778\uc9c0 \ud30c\uc545\uc744 \ud55c\ub2e4.\\n\\n  - 2,3,4 \uad50\uc2dc\uc5d0 \uac19\uc740 \ud328\ud134\uc73c\ub85c \ucd9c\uc81c \uac00\ub2a5\uc131\uc774 \uc788\uc74c\\n\\n- \ucd9c\uadfc \ub54c\ub9c8\ub2e4 \ud1a0\ud53d\uc744 \uc5f0\uc2b5\ud574\uc57c \ud55c\ub2e4.\\n- \ub2f5\uc548 \uc81c\ucd9c\uc2dc \uc624\ub2f5 \uc218\uc815\uc2dc \ud0c8\ub77d\uc774 \uac00\ub2a5\uc131\uc774 \uc788\uc74c\\n\\n## \uc815\ubcf4\ubcf4\uc548\uc758 \uac1c\ub150\\n\\n- \uc678\ubd80\uc758 \uc545\uc758\uc801\uc778 \ud574\ud0b9\uc774\ub098 \ub0b4\ubd80\uc758 \ubd88\ubc95\uc801 \uc815\ubcf4 \uc720\ucd9c\uc2dc\ub3c4\ub85c \ubd80\ud130 \uc870\uc9c1\uacfc \uace0\uac1d\uc758 \uc815\ubcf4\uc790\uc0b0\uc744 \ubcf4\ud638\ud558\uae30 \uc704\ud55c \uad00\ub9ac\uc801, \ubb3c\ub9ac\uc801, \uae30\uc220\uc801 \ub300\uc751 \uccb4\uacc4\\n\\n  - \uc815\uc758 \uc5f0\uc2b5\\n    - 5W 1H : ~ \ubaa9\uc801 ~ \uae30\uc220\uc744 \uc774\uc6a9 ~ \uc885\uacb0\uba85\uc0ac (PK)\\n    - **X ~ \ud558\ub294 \uac83 \ubb38\uc7a5\uc740 \uc4f0\uba74\uc548\ub41c\ub2e4** : \uc810\uc218 \uc548\uc90c\\n\\n- \uc704\ud5d8\uc744 \uc218\uc6a9 \uac00\ub2a5\ud55c \uc218\uc900\uc73c\ub85c \uac10\uc18c\uc2dc\ud0a4\ub294 \uc77c\ub828\uc758 \uc870\uce58 Risk = (Asset) $X$ (vulnerability) $X$ (Threat) \uc704\ud5d8 = \uc601\ud5a5\ub3c4 \\\\* \ud655\ub8f0 \\\\* \ucde8\uc57d\\n  - \ub9ac\uc2a4\ud06c\ub294 \uc790\uc0b0\uc774\ub77c\uace0\ub3c4 \ud55c\ub2e4. \uc601\ud5a5\ub3c4\ub294 \ucde8\uc57d\uc810\\n\\n| \uad6c\ubd84                       | \uc124\uba85                                                                                                                                                                                                         | \ud575\uc2ec\uc694\uc18c          |\\n| -------------------------- | ------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------ | ----------------- |\\n| \uae30\ubc00\uc131 (Confidentiality)   | \ub0b4\uc678\ub85c \ubd80\ud130 \ubd88\ubc95\uc801 \uc815\ubcf4 \uc720\uce28\uc758 \ubc29\uc9c0 \ud574\ub2f9 \uc815\ubcf4\uc5d0 \ub300\ud55c \uad8c\ud55c\uc774 \ubd80\uc5ec\ub41c \uc790\ub4e4\ub9cc\uc774 \uc811\uadfc\uac00\ub2a5\ud558\ub3c4\ub85d \ubcf4\uc7a5                                                                                                              | \uc554\ud638\ud654, \uc811\uadfc \ud1b5\uc81c |\\n| \ubb34\uacb0\uc131 (Integrity)         | \uc815\ubcf4\uc758 \uc704\ubcc0\uc870 \ubc0f \ud30c\uad34\ub97c \uc608\ubc29\ud558\uace0 \ubc29\uc9c0 \uc6d0\ucc9c \ub370\uc774\ud130\ub098 \uc0ac\uc6a9\uc790\uac00 \uc6d0\ud558\ub294 \uc815\ubcf4\ub098 \uc2dc\uc2a4\ud15c\uc5d0 \uc801\uc2dc\uc5d0 \uc811\uadfc \ubcf4\uc7a5 <br/> \uc11c\ubc84\uc5d0 \ub85c\uadf8\ub97c \uc801\uc7ac\uc2dc \uc704\ubcc0\uc870 \ubc29\uc9c0\ub97c \uc704\ud558\uc5ec \ubcc4\ub3c4\uc758 \uc800\uc7a5\uc18c \ub610\ub294 \ud5e4\uc2dc\uac12\uc744 \ubd80\uc5ec\ud558\uc5ec \ubb34\uacb0\uc131\uc744 \ubcf4\uc7a5\ud55c\ub2e4. | \ubcf4\uc548\uad00\uc81c, \uc774\uc911\ud654  |\\n| \uac00\uc6a9\uc131 (Availability)      | \ud574\ud0b9\uc73c\ub85c \uc548\ud55c \uc2dc\uc2a4\ud15c \ub3d9\uc791 \ubd88\ub2a5 \uc608\ubc29 \uc778\uac00\ub41c \uc0ac\uc6a9\uc790\uac00 \uc6d0\ud558\ub294 \uc815\ubcf4\ub098 \uc2dc\uc2a4\ud15c\uc774 \uc801\uc2dc\uc5d0 \uc811\uadfc \ubcf4\uc7a5                                                                                                                  | \ubcf4\uc548\uad00\uc81c, \uc774\uc911\ud654  |\\n| \uc778\uc99d (Authentication)      | \uc815\ubcf4\ub098 \uc2dc\uc2a4\ud15c\uc5d0 \uc811\uadfc\ud558\ub294 \uc0ac\uc6a9\uc790\uac00 \uc815\ub2f9\ud568\uc744 \ud655\uc778 \uc54c\uace0 \uc788\ub294 \uac83, \uac00\uc9c0\uace0 \uc788\ub294\uac83 , \uc2e0\uccb4\uc758 \uc77c\ubd80(\uc9c0\ubb38)                                                                                                              | PKI, \uc0dd\uccb4\uc778\uc99d     |\\n| \ubd80\uc778\ubc29\uc9c0 (Non-Repudiation) | \ud589\uc704\ub97c \ubd80\uc778\ud558\ub294 \uac83\uc744 \ubd09\uc1c4                                                                                                                                                                                    | \uc804\uc790 \uc11c\uba85         |\\n\\n- \uc0ac\ub840\ub97c \uc801\uc2dc\ud558\uba74 \uc88b\uc74c\\n\\n- **\uae30\uc5c5\uacfc \uad6d\uac00\uc758 \uc9c0\uc18d\uac00\ub2a5 \uacbd\uc601\uc744 \uc704\ud574\uc11c\ub294 \uc815\ubcf4\ubcf4\ud638\ub294 \ud544\uc218\uc774\uba70, \uc815\ubcf4\uacf5\ud559\uae30\uc220\uc0ac\uc758 \uac00\uc7a5 \uc911\uc694\ud55c \uc784\ubb34\uc784**\\n\\n## \uc815\ubcf4\ubcf4\uc548FW\\n\\n<Drawio content={simpleGraph1} resize={true} />\\n\\n- \uc815\ubcf4\ubcf4\uc548\uc740 \uc790\uc0b0\uc758 \ucde8\uc57d\uc810\uc744 \ucd5c\uc18c\ud654\ud558\uace0 \ub0b4\uc678\ubd80\uc758 \uc704\ud611\uc73c\ub85c \ubd80\ud130 \ubcf4\ud638\ud558\uae30 \uc704\ud55c \uccb4\uacc4\ub97c \ub9d0\ud568\\n\\n\\n## OWASP 10 \ub300 \ucde8\uc57d\uc810\\n\\n- OWASP\\n  - \uac1c\uc778\uacfc \uae30\uc5c5\uc774 Application \uc744 \uc2e0\ub8b0\uc131 \uc788\uac8c \uac1c\ubc1c, \uad6c\ub9e4, \uc720\uc9c0\uac00\ub2a5\ud558\ub3c4\ub85d \ubcf4\uc7a5\ud558\uae30 \uc704\ud55c \uac1c\ubc29\uc801 \ucee4\ubba4\ub2c8\ud2f0\\n  - \uc8fc\uae30\uc801\uc73c\ub85c \uc6f9 \ucde8\uc57d\uc810\uc744 \ubc1c\ud45c\ud558\uace0 \uc788\uc73c\uba70 2010 \ub144\uc5d0 \uc774\uc5b4 \ucd5c\uadfc 2012 Top 10 \ucde8\uc57d\uc810\uc744 \ubc1c\ud45c\\n- OWASP \uc758 \uc704\ud611 \ud3c9\uac00 \ubc29\ubc95\\n\\n  <table>\\n    <thead>\\n      <tr>\\n        <th>\uc704\ud611\uc6d0</th>\\n        <th>\uacf5\uaca9\uacbd\ub85c</th>\\n        <th>\uacf5\uaca9\uc774 \uc804\ud30c\ub41c \uc815\ub3c4</th>\\n        <th>\ucde8\uc57d\uc810 \ud0d0\uc9c0 \uc6a9\uc774\ub3c4</th>\\n        <th>\uae30\uc220\uc801 \uacb0\ud568</th>\\n        <th>\ube44\uc9c0\ub2c8\uc2a4\uc801 \uc601\ud5a5</th>\\n      </tr>\\n    </thead>\\n    <tbody>\\n      <tr>\\n        <td rowSpan=\\"3\\"> ? </td>\\n        <td>\uc26c\uc6c0</td>\\n        <td>\ub110\ub9ac \uc804\ud30c\ub428</td>\\n        <td>\uc26c\uc6c0</td>\\n        <td>\uc2ec\uac01</td>\\n        <td rowSpan=\\"3\\"> \ud604\uc5c5 \uc5c5\ubb34 \ubd84\uc11d Biz Impact Analysis</td>\\n      </tr>\\n      <tr>\\n        <td>\ubcf4\ud1b5</td>\\n        <td>\ubcf4\ud1b5</td>\\n        <td>\ubcf4\ud1b5</td>\\n        <td>\ubcf4\ud1b5</td>\\n      </tr>\\n      <tr>\\n        <td>\uc5ec\ub7ec\uc6c0</td>\\n        <td>\ub4dc\ubb3c\uac8c \uc804\ud30c\ub428</td>\\n        <td>\uc5b4\ub824\uc6c0</td>\\n        <td>\uc801\uc74c</td>\\n      </tr>\\n    </tbody>\\n  </table>\\n\\n- \uac01\uac01\uc758 \ud658\uacbd\uacfc \ube44\uc9c0\ub2c8\uc2a4\uc801 \ud2cd\uc131\uc740 \uad00\ub828\ub2f9\uc0ac\uc790\ub4e4\ub9cc \uc54c\uc218 \uc788\uc73c\ubbc0\ub85c, \uc704\ud611 \ud3c9\uac00\ub294 \uac01\uc790\uc758 \ubaab\uc784\\n\\n## OWASP Top 10 \ucde8\uc57d\uc810 (2013 -> 2017 -> 2021)\\n\\n| OWASP 2013                            | \u27a1\ufe0f  | OWASP 2017                         |\u27a1\ufe0f| OWASP 2021                         |\\n| ------------------------------------- | --- | ----------------------------------|--- | ---------------------------------- |\\n| 1. \uc778\uc81d\uc158                             | \u27a1\ufe0f  | 1. \uc778\uc81d\uc158                          |\u2198\ufe0f| \ucde8\uc57d\ud55c \uc811\uadfc\ud1b5\uc81c                     |\\n| 2. \ucde8\uc57d\uc810 \uc778\uc99d\uacfc \uc138\uc158 \uad00\ub9ac            |  \u27a1\ufe0f    | \ucde8\uc57d\ud55c \uc778\uc99d                        |\u2198\ufe0f| \ubbfc\uac10\ud55c \ub370\uc774\ud130 \ub178\ucd9c                          |\\n| 3. \ud06c\ub85c\uc2a4 \uc0ac\uc774\ud2b8 \uc2a4\ud06c\ub9bd\ud305             |   \u2198\ufe0f  | \ubbfc\uac10\ud55c \ub370\uc774\ud130 \ub178\ucd9c                 |\u2197\ufe0f  | \uc778\uc81d\uc158                             |\\n| 4. \uc548\uc804\ud558\uc9c0 \uc54a\uc740 \uc9c1\uc811 \uac8d\uccb4 \ucc38\uc870       |  \uff35   | XML \uc678\ubd80\uac8c\uccb4 (XXE)                 |\u2198\ufe0f| \ube44\ubcf4\ud638 \uc124\uacc4 (new)                       |\\n| 5. \uc798\ubabb\ub41c \ubcf4\uc548 \uad6c\uc131                   | \u2198\ufe0f    | \ucde8\uc57d\ud55c \uc811\uadfc\ud1b5\uc81c                    | \u2197\ufe0f|\uc798\ubabb\ub41c \ubcf4\uc548 \uad6c\uc131                      |\\n| 6.\ubbfc\uac10\ud55c \ub370\uc774\ud130 \uc720\uce0c                  |  \u2197\ufe0f   | \uc798\ubabb\ub41c \ubcf4\uc548 \uad6c\uc131                   | \u2197\ufe0f|\uc54c\ub824\uc9c4 \ucde8\uc57d\uc810\uc774 \uc788\ub294 \uad6c\uc131\uc694\uc18c \uc0ac\uc6a9 |\\n| 7. \uae30\ub2a5 \uc218\uc900\uc758 \uc811\uadfc \ud1b5\uc81c \ub204\ub77d         |   \uff35  | \ud06c\ub85c\uc2a4 \uc0ac\uc774\ud2b8 \uc2a4\ud06c\ub9bd\ud305             | \u2197\ufe0f| \ucde8\uc57d\ud55c \uc778\uc99d              |\\n| 8.\ud06c\ub85c\uc2a4 \uc0ac\uc774\ud2b8 \uc694\uccad \ubcc0\uc870             |   \ud83c\udd47  | \uc548\uc804\ud558\uc9c0 \uc54a\uc740 \uc5ed \uc9c1\ub82c\ud654            | \uff35|\uc18c\ud504\ud2b8\uc6e8\uc5b4 \ubc0f \ub370\uc774\ud130 \ubb34\uacb0\uc131 \ubb38\uc81c   |\\n| 9.\uc54c\ub824\uc9c4 \ucde8\uc57d\uc810\uc774 \uc788\ub294 \uad6c\uc131\uc694\uc18c \uc0ac\uc6a9  |  \u27a1\ufe0f   | \uc54c\ub824\uc9c4 \ucde8\uc57d\uc810\uc774 \uc788\ub294 \uad6c\uc131\uc694\uc18c \uc0ac\uc6a9 | \u2198\ufe0f |\ubd88\ucda9\ubd84\ud55c \ub85c\uae45 \ubc0f \ubaa8\ub2c8\ud130\ub9c1         |\\n| 10.\uac80\uc99d\ub418\uc9c0 \uc54a\uc740 \ub9ac\ub2e4\uc774\ub809\ud2b8 \ubc0f \ud3ec\uc6cc\ub4dc |   \ud83c\udd47  | \ubd88\ucda9\ubd84\ud55c \ub85c\uae45 \ubc0f \ubaa8\ub2c8\ud130\ub9c1          | \u2197\ufe0f|\uc11c\ubc84\uce21 \uc694\uccad \uc704\uc870(SSRF) (new)            |\\n\\n## \uc815\ubcf4 \ubcf4\ud638 \ub300\uc0c1 (\uc790\uc0b0)\\n\\n<table>\\n  <tbody>\\n    <tr>\\n      <th rowspan=\\"2\\">\uc815\ubcf4\ubcf4\uc548\ub300\uc0c1</th>\\n      <th>\ub370\uc774\ud130 \uc800\uc7a5\uc7a5\uce58</th>\\n      <th>\ud638\uc2a4\ud2b8 \ucef4\ud4e8\ud130 \uc751\uc6a9\ud504\ub85c\uadf8\ub7a8</th>\\n      <th>\uc720\uc120\ub9dd \ubb34\uc120\ub9dd \uc704\uc131\ud1b5\uc2e0\ub9dd</th>\\n      <th>\uad50\ud658\uae30 \ub77c\uc6b0\ud130</th>\\n      <th>\uc804\ud654\uae30, Fax, Pc, W/S</th>\\n      <th>\uce74\ub4dc</th>\\n    </tr>\\n    <tr>\\n      <td><img src=\\"./test1.jpeg\\" /><br /><img src=\\"./cassette.jpeg\\" /></td>\\n      <td><img src=\\"./My_Opera_Server_Back.jpg\\" /></td>\\n      <td><img src=\\"./\u110b\u1171\u1109\u1165\u11bc.jpeg\\" /></td>\\n      <td><img src=\\"./250x250.crop.jpg\\" /></td>\\n      <td><img src=\\"./phon1.jpeg\\" /><br /><img src=\\"./img.png\\" /></td>\\n      <td><img src=\\"./\u110f\u1161\u1103\u1173.webp\\" /></td>\\n    </tr>\\n    <tr>\\n      <td>\uc704\ud611\uc694\uc18c</td>\\n      <td>\uc0ad\uc81c,\ubcf5\uc0ac,\uc218\uc815</td>\\n      <td>OS \ucde8\uc57d\uc810, \uc11c\ube44\uc2a4\uac70\ubd80, \ubc14\uc774\ub7ec\uc2a4</td>\\n      <td>\ub3c4\uccad, \uc704\ubcc0\uc870</td>\\n      <td>\ud504\ub85c\ud1a0\ucf5c \ucde8\uc57d\uc810, \ud2b8\ub798\ud53d \ud3ed\uc8fc</td>\\n      <td>\uc2e0\ubd84\uc704\uc7a5</td>\\n      <td>\uc2e0\ubd84\uc704\uc7a5, \uce74\ub4dc\ubcf5\uc81c</td>\\n    </tr>\\n    <tr>\\n      <td>\uae30\uc220\uc801 \ub300\ucc45</td>\\n      <td>\uc811\uadfc\uc81c\uc5b4 \uae30\uc220,Secure DBMS</td>\\n      <td>\uc0ac\uc6a9\uc790\uc778\uc99d, \ucde8\uc57d\uc810 \uc9c4\ub2e8, \uc804\uc790\uc11c\uba85, \ubc14\uc774\ub7ec\uc2a4 \ubc29\uc9c0, Secure OS</td>\\n      <td>\ube44\ubc00\ud0a4 \uc554\ud638, \uacf5\uac1c\ud0a4 \uc554\ud638, \ud574\uc26c\ud568\uc218, VPN</td>\\n      <td>\ucde8\uc57d\uc131 \uc9c4\ub2e8, Secure \ub77c\uc6b0\ud130</td>\\n      <td>\uc0ac\uc6a9\uc790 \uc778\uc99d, \uc0dd\uccb4\uc778\uc99d, \ud328\uc2a4\uc6cc\ub4dc, \uc2a4\ud06c\ub9b0\uc138\uc774\ube0c,PC\ubcf4\uc548</td>\\n      <td>\uc0ac\uc6a9\uc790 \uc778\uc99d, \uace0\uc18d\uc554\ud638\uce69</td>\\n    </tr>\\n   \\n  </tbody>\\n</table>\\n\\n## \uc815\ubcf4 \ubcf4\uc548 \uc704\ud611\\n\\n- \uc815\ubcf4\ubcf4\uc548 \uc704\ud611\uc740 \uc804\ub7b5\uc801\uc774\uace0, \uc9c0\ub2a5\ud654 \ub418\uc5b4 \uac00\uace0 \uc788\uc74c\\n\\n  - \uc804\ub7b5\uc801\\n\\n    - \ubbfc\ucca9\ud558\uac8c \uacf5\uaca9 : Zero Day Attack\\n    - \ub2e4\uc591\ud55c \uc218\ub2e8 \ud65c\uc6a9 : \uc545\uc131\ucf54\ub4dc, \ud53c\uc2f1, \ud30c\ubc0d, \uc2a4\ud338 (\uc0ac\ud68c\uacf5\ud559)\\n    - \uc120\uc758\uc758 \uacf5\uaca9\uc790 / \ud53c\ud574\uc790 : Ddos\\n\\n  - \uc9c0\ub2a5\ud654\\n\\n## \uc815\ubcf4\ubcf4\ud638 \ud1b5\uc81c \ud65c\uc6a9\\n\\n1. \uc815\ubcf4\ubcf4\ud638 \ud1b5\uc81c \uc815\uc758\\n   - \uc704\ud5d8\uc744 \uac10\uc18c\uc2dc\ud0a4\uae30 \uc704\ud55c \uc815\ucc45, \uc808\ucc28, \uae30\uc220, \uc870\uc9c1 \uad6c\uc131 \ub4f1 \uc77c\ub828\uc758 \ud65c\ub3d9\\n2. \uc815\ubcf4\ubcf4\ud638 \ud1b5\uc81c \uc720\ud615\\n\\n| \uc720\ud615                                | \uc124\uba85                                                                     | \uc608\uc2dc                                       |\\n| ----------------------------------- | ------------------------------------------------------------------------ | ------------------------------------------ |\\n| \uc608\ubc29\ud1b5\uc81c (4\uac00\uc9c0\uc911 \ube44\uc6a9\uc774 \uc801\uc740 \ubd80\ubd84) | -\ubb38\uc81c\uac00 \ubc1c\uc0dd\ud558\uae30 \uc804\uc5d0 \ud0d0\uc9c0 <br/> - \uc624\ub958, \ub204\ub77d \ud639\uc740 \uc545\uc758\uc801 \ud589\uc704 \ubc1c\uc0dd \uc608\ubc29 | - \uc790\uaca9\uc744 \uac16\ucd98 \uc9c1\uc6d0\ub9cc \uace0\uc6a9 \ubc0f \uc9c1\ubb34 \ubd84\ub9ac     |\\n| \uc801\ubc1c\ud1b5\uc81c                            | \ubc1c\uc0dd\ud55c \uc624\ub958, \ub204\ub77d,\ub610\ub294 \uc545\uc758\uc801 \ud589\uc704 \ucc38\uc790\ud558\uace0 \ubcf4\uace0                         | \ubaa8\ub2c8\ud130\ub9c1 (\ubaa8\ub2c8\ud130\ub9c1 \uc2dc\ub098\ub9ac\uc624\uac00 \ud544\uc694)        |\\n| \uad50\uc815/\ubcf5\uc6d0\ud1b5\uc81c                       | -\ubb38\uc81c \uc6d0\uc778\uc744 \ud30c\uc545\ud558\uc5ec \ubb38\uc81c\ub85c \ubd80\ud130 \ubc1c\uc0dd\ub418\ub294 \uc624\ub958 \uace0\uc815                     | - BCP, \ubc31\uc5c5                                |\\n| \uc800\uc9c0 \ud1b5\uc81c                           | -\uc704\ud5d8\uc744 \ub2e8\ub0e0\ud558\ub3c4\ub85d \ud558\ub294 \uc2dc\uc2a4\ud15c \ub3c4\uad6c \uc808\ucc28                                 | -\ubc95/\uc81c\ub3c4 , \ubaa8\uc758\ud6c8\ub828 <br/>-IPX, \ud544\ud130\ub9c1, DLP |\\n\\n- \uc804\uccb4\uc801\uc778 \ud1b5\uc81c \ube44\uc6a9\uc744 \ucd5c\uc18c\ud654 \ud558\uae30 \uc704\ud55c \uc804\ub7b5 \uc218\ub9bd \uc911\uc6a9\\n\\n## CC \uc778\uc99d\\n\\n1. ISO 15408 CC\uc778\uc99d\uc758 \uac1c\ub150\\n\\n   - \uad6d\uac00\ub9c8\ub2e4 \uc11c\ub85c \ub2e4\ub978 \uc815\ubcf4\ubcf4\ud638\uc2dc\uc2a4\ud15c \ud3c9\uac00 \uae30\uc900\uc744 \uc5f0\ub3d9\ud558\uace0 \ud3c9\uac00\uacb0\uacfc\ub97c \uc0c1\ud638 \uc778\uc99d\ud558\uae30 \uc704\ud574 \uc81c\uc815\ub41c \ud3c9\uac00\uae30\uc900\\n   - \uad6d\uc81c\ud45c\uc900\uc73c\ub85c 1999\ub144 6\uc6d4 \uc2b9\uc778\ub428\\n\\n2. \ubaa9\uc801\\n\\n   - \uc815\ubcf4\ubcf4\ud638 \uc2dc\uc2a4\ud15c \ubcf4\uc548\ub4f1\uae09 \ud3c9\uac00\uc5d0 \uc2e0\ub8b0\uc131 \ubd80\uc5ec\\n   - \ud604\uc874\ud558\ub294 \ucce5\uac00\uae30\uc900\uacfc \uc870\ud654\ub97c \ud1b5\ud574 \ud3c9\uac00\uacb0\uacfc\uc758 \uc0c1\ud638 \uc778\uc815\\n   - \uc815\ubcf4\ubcf4\ud638 \uc2dc\uc2a4\ud15c\uc758 \uc218\ucd9c\uc785\uc5d0 \uc18c\uc694\ub418\ub294 \uc778\uc99d \ube44\uc6a9 \uc808\uac10\uc73c\ub85c \uad6d\uc81c \uc720\ud1b5 \ucd94\uc9c4\\n\\n3. CC\uc758 \uad6c\uc870\\n\\n   | 1\ubd80. \uc18c\uac1c\ubc0f \uc77c\ubc18\ubaa8\ub378    | - IT \ubcf4\uc548\uc131 \ud3c9\uac00\uc758 \uc6d0\uce59\uacfc \uac1c\ub150 \uc815\uc758 |\\n   | ----------------------- | ----------------------------------- |\\n   | 2\ubd80. \ubcf4\uc548 \uae30\ub2a5 \uc694\uad6c\uc0ac\ud56d |                                     |\\n   | 3\ubd80. \ubcf4\uc99d \uc694\uad6c\uc0ac\ud56d      |                                     |\\n\\n4. CC \uc778\uc99d\uc758 \ud575\uc2ec PR \uc640 ST\\n\\n   | \uad6c\ubd84   | PP  | ST  |\\n   | ------ | --- | --- |\\n   | \uac1c\ub150   |     |     |\\n   | \ubaa9\uc801   |     |     |\\n   | \ub3c5\ub9bd\uc131 |     |     |\\n   | \uc801\uc6a9\uc131 |     |     |\\n   | \uad00\uacc4\uc131 |     |     |\\n   | \uc644\uc804\uc131 |     |     |\\n   | \uc608\uc2dc   |     |     |\\n\\n- PP,ST, TOE \uc5d0 \ub300\ud55c \ub2e8\uc5b4\uac00 \uaf2d \ub4e4\uc5b4 \uac00\uc57c \ud55c\ub2e4.\\n\\n5. CC\uc778\uc99d \uacfc\uc815\\n\\n   | \ub2e8\uacc4 | \uba85\uce6d                         | \uc608\uc2dc                                                      |\\n   | ---- | ---------------------------- | --------------------------------------------------------- |\\n   | EAL1 | - \uae30\ub2a5\uc801 \uc2dc\ud5d8                | - \uae30\ub2a5 \uba85\uc138\uc11c, \uc124\uacc4\uc11c                                     |\\n   | EAL2 | - \uad6c\uc870\uc801 \uc2dc\ud5d8                | - \uae30\ubcf8\uc124\uacc4\uc11c, \uae30\ub2a5\uc2dc\ud5d8\uc11c, \ucde8\uc57d\uc810\ubd84\uc11d\uc11c                    |\\n   | EAL3 | - \ubc29\ubc95\ub860\uc801 \uc2dc\ud5d8, \uc810\uac80        | - \uc0dd\uba85\uc8fc\uae30 \uc9c0\uc6d0, \uac1c\ubc1c \ubcf4\uc548 , \uc624\uc6a9 \ubd84\uc11d\uc11c                  |\\n   | EAL4 | - \ubc29\ubc95\ub860\uc801 \uc124\uacc4, \uc2dc\ud5d8, \uc810\uac80  | - \uc0c1\uc138\uc124\uac8c\uc11c, \ubcf4\uc548\uc815\ucc45\uc11c, \uc77c\ubd80 \uc18c\uc2a4\ucf54\ub4dc, \uc0c1\uc138\uc2dc\ud5d8\uc11c       |\\n   | EAL5 | - \uc900\uc815\ud615\uc801 \uc124\uacc4, \uc2dc\ud5d8        | - \ubcf4\uc548 \uae30\ub2a5 / \uc804\uccb4 \uc18c\uc2a4\ucf54\ub4dc. \uad6c\uc870 \uba85\uc138\uc11c, \uc740\ub2c9\ucc44\ub110 \ubd84\uc11d\uc11c |\\n   | EAL6 | - \uc900\uc815\ud615\uc801 \uac80\uc99d\ub41c \uc124\uacc4, \uc2dc\ud5d8 | - \ubcf4\uc548 \uae30\ub2a5 / \uc804\uccb4 \uc18c\uc2a4\ucf54\ub4dc. \uad6c\uc870 \uba85\uc138\uc11c, \uc740\ub2c9\ucc44\ub110 \ubd84\uc11d\uc11c |\\n   | EAL7 | - \uc815\ud615\uc801 \uac80\uc99d                | - \ubcf4\uc548 \uae30\ub2a5 / \uc804\uccb4 \uc18c\uc2a4\ucf54\ub4dc. \uad6c\uc870 \uba85\uc138\uc11c, \uc740\ub2c9\ucc44\ub110 \ubd84\uc11d\uc11c |\\n\\n## ISO 27001\\n\\n1. ISO/IEC 27001 \uc815\uc758\\n\\n   - \uad6d\uc81c\ud45c\uc900\ud654 \uae30\uad6c\uc5d0\uc11c \uc815\ubcf4\ubcf4\ud638\uad00\ub9ac\ub97c \uc704\ud574 \uc81c\uc7a5\ud55c \ud45c\uc900\ud654\ub41c \uc2e4\ubb34\uaddc\uc57d\\n\\n2. \uc815\ubcf4\ubcf4\ud638\uad00\ub9ac\uccb4\uacc4(ISMS-P) \uad6d\uc81c\ud45c\uc900\uc758 \ud544\uc694\uc131\\n   - \uc815\ubcf4\ubcf4\ud638\ub300\ucc45 \uac15\ud654 : \ud06c\uace0 \ubcf5\uc7a1\ud55c \uc870\uc9c1\uc5d0 \ub300\ud55c \uccec\uacc4\uc801\uc778 \uc811\uadfc\ubc29\ubc95 \ubbf8\ube44\\n\\n## sso\\n\\n4. sso \uc778\uc99d \ubaa8\ub378\\n\\n- \uc778\uc99d\ub300\ud589\\n- \uc778\uc99d \uc815\ubcf4 \uc804\ub2ec\\n\\n5. sso \uc758 \ud55c\uacc4\uc815\\n\\n- \ubcf4\uc548\uce21\uba74\\n  - \ud55c\ubc88\uc758 \ud574\ud0b9\uc73c\ub85c \ubaa8\ub4e0 \uc560\ud50c\ub9ac\ucf00\uc774\uc158 \uc811\uadfc\uac00\ub2a5\\n- \uc811\uadfc\uc81c\uc5b4 \uce21\uba74\\n  - \uc778\uc99d\ub41c \uc0ac\uc6a9\uc790\uac00 \ud560\ub2f9\ub41c \uae30\ub290\ub9cc \uc0ac\uc6a9\ud560\uc218 \uc788\ub3c4\ub85d\\n- \uad00\ub9ac \uce21\uba74\\n  - \uc0ac\uc6a9\uc790 \uad00\ub9ac\uae30\ub2a5, \uad8c\ud55c \uc704\uc784 \ubc0f \ubcc0\uacbd\uc5d0 \ub300\ud55c \ucc98\ub9ac \ubd88\uac00\\n\\n## SAML\\n\\n1. SAML\\n\\n- \ud074\ub77c\uc774\uc5b8\ud2b8\uc758 \uc778\uc99d\uc815\ubcf4 \uc18d\uc131\uc815\ubcf4 \uc778\uac00\uc815\ubcf4\ub97c \uae30\uc220\ud558\uc5ec \uc6f9 \ube0c\ub77c\uc6b0\uc800\uc5d0\uc11c SSO \ub97c \uad6c\ud604\ud558\ub294 XML \uae30\ubc18\uc758 \uc5b8\uc5b4\\n\\n2. SAML \uad6c\uc131\\n\\n### AAA\\n\\n1. AAA \uc758 \ud504\ub85c\ud1a0\ucf5c\uc758 \uac1c\ub150\\n\\n- \uc720\ubb34\uc120 \ud658\uacbd\uc5d0\uc11c \uc778\uc99d. \uad8c\uc7a5"},{"id":"/2023/02/17/DB\ubb38\uc81c","metadata":{"permalink":"/blog/second/2023/02/17/DB\ubb38\uc81c","source":"@site/second-blog/2023-02-17-DB\ubb38\uc81c/index.mdx","title":"DataBase \ubb38\uc81c\ud480\uc774","description":"1\ubc88","date":"2023-02-17T12:23:00.000Z","formattedDate":"2023\ub144 2\uc6d4 17\uc77c","tags":[{"label":"database","permalink":"/blog/second/tags/database"},{"label":"\uc815\ubcf4\uad00\ub9ac","permalink":"/blog/second/tags/\uc815\ubcf4\uad00\ub9ac"}],"readingTime":18.59,"hasTruncateMarker":false,"authors":[{"name":"100milliongold","title":"developer","url":"https://sebastienlorber.com","imageURL":"https://avatars.githubusercontent.com/u/13652711?s=400&u=0a1092b8b1170f71ae883d098f8721c12ce39e9e&v=4","key":"100milliongold"}],"frontMatter":{"title":"DataBase \ubb38\uc81c\ud480\uc774","date":"2023-02-17T12:23:00.000Z","authors":["100milliongold"],"tags":["database","\uc815\ubcf4\uad00\ub9ac"],"password":"1q2w3e4r"},"unlisted":false,"prevItem":{"title":"\uc815\ubcf4\ubcf4\uc548 \uae30\ucd08 1","permalink":"/blog/second/2023/02/18/\uc815\ubcf4\ubcf4\uc548\uae30\ucd081"},"nextItem":{"title":"DataBase \uac1c\uc694 8","permalink":"/blog/second/2023/02/17/DB\uac1c\uc6948"}},"content":"## 1\ubc88\\n\\n\ud68c\ubcf5\uae30\ubc95\uc5d0 \ub300\ud55c \uc124\uba85\uc73c\ub85c \uac00\uc7a5 \uac70\ub9ac\uac00 \uba3c\uac83\uc740?\\n\\n1. \uc9c0\uc5f0 \uac31\uc2e0 \ud68c\ubcf5 \uae30\ubc95\uc740 \ud2b8\ub79c\uc7ad\uc158\uc774 \ubd80\ubd84 \uc644\ub8cc\ub420\ub54c \uae4c\uc9c0 \ub370\uc774\ud130\ubca0\uc774\uc2a4\uc758 \uc2e4\uc81c\uc801\uc778 \uac31\uc2e0\uc744 \uc9c0\uc5f0\uc2dc\ud0a8\ub2e4.\\n\\n2. \uc9c0\uc5f0 \uac31\uc2e0 \ud68c\ubcf5 \uae30\ubc95\uc740 undo/redo \ud68c\ubcf5 \uc54c\uace0\ub9ac\uc998\uc744 \uc774\uc6a9\ud558\uc5ec \ud68c\ubcf5\ud55c\ub2e4.\\n\\n3. \uc989\uc2dc\uac31\uc2e0(immediate update) \ud68c\ubcf5 \uae30\ubc95\uc740 \ud2b8\ub79c\uc7ad\uc158\uc774 \uac31\uc2e0 \uba85\ub839\uc744 \ub0b4\ub9ac\uba74 \ud2b8\ub79c\uc7ad\uc158\uc774 \uc644\ub8cc\ub418\uae30 \uc804\uc5d0 \ub370\uc774\ud130\ub97c \uc989\uc2dc \uac31\uc2e0\ud55c\ub2e4.\\n\\n4. \uadf8\ub9bc\uc790 \ud398\uc774\uc9c0(shadow page) \ud68c\ubcf5\uae30\ubc95\uc740 redo \uc5f0\uc0b0\uc774 \ud544\uc694\uc5c6\uae30 \ub54c\ubb38\uc5d0 \uc7a5\uc560\ub85c \ubd80\ud130 \ud68c\ubcf5\uc791\uc5c5\uc774 \uc2e0\uc18d\ud558\ub2e4.\\n\\n> \uc8fc\uc5b4\uc9c4 \uc124\uba85 \uc911 \ud68c\ubcf5 \uae30\ubc95\uc5d0 \ub300\ud55c \uc124\uba85\uc774 \uc544\ub2cc \ud56d\ubaa9\uc740 2\ubc88\uc785\ub2c8\ub2e4. \uc9c0\uc5f0 \uac31\uc2e0 \ud68c\ubcf5 \uae30\ubc95\uc740 undo/redo \ud68c\ubcf5 \uc54c\uace0\ub9ac\uc998\uc744 \uc774\uc6a9\ud558\uc5ec \ud68c\ubcf5\ud558\uc9c0\ub9cc, 2\ubc88 \ud56d\ubaa9\uc740 undo/redo \ud68c\ubcf5 \uc54c\uace0\ub9ac\uc998\uc5d0 \ub300\ud55c \uc124\uba85\uc73c\ub85c \ubb38\uc81c\uc640 \uad00\ub828\uc774 \uc5c6\uc2b5\ub2c8\ub2e4. \ub530\ub77c\uc11c 2\ubc88\uc774 \uac00\uc7a5 \uac70\ub9ac\uac00 \uba3c \ud56d\ubaa9\uc785\ub2c8\ub2e4.\\n\\n---\\n\\n## 2\ubc88\\n\\n2\ub2e8\uacc4 \ub85c\ud0b9 \uaddc\uc57d(2phase locking protocol)\uc758 \ub85c\ud06c \uc804\ud658 (\ud639\uc740 \ub85c\ud06c \ubcc0\ud658)\uc5d0 \ub300\ud55c \uc124\uba85\uc911 \uc633\uc740\uac83\uc740?\\n\\n1. \ud56d\ubaa9 X\uc5d0 read_lock(X) \ub97c \uc218\ud589\ud558\uc5ec \uacf5\uc720 \ub85c\ud06c\ub97c \ud68d\ub4dd \ud55c\ud6c4 \ub098\uc911\uc5d0 write_lock(X)\ub97c \uc218\ud589\ud558\uc5ec \ubca0\ud0c0\uc801 \ub85c\ud06c\ub85c \ub85c\ud06c\ub97c \uc0c1\uc2b9(upgrade)\uc2dc\ud0ac\uc218 \uc788\ub2e4.\\n\\n2. \ud56d\ubaa9 X\uc5d0 write_lock(X) \ub97c \uc218\ud589\ud558\uc5ec \ubca0\ud0c0\uc801 \ub85c\ud06c\ub97c \ud68d\ub4dd\ud55c\ud6c4 \ub098\uc911\uc5d0 read_lock(X)\ub97c \uc218\ud589\ud558\uc5ec \uacf5\uc720 \ub85c\ud06c\ub85c \ub85c\ud06c\ub97c \uc0c1\uc2b9\uc2dc\ud0ac\uc218 \uc788\ub2e4.\\n\\n3. \ud56d\ubaa9 X\uc5d0 read_lock(X) \ub97c \uc218\ud589\ud558\uc5ec \uacf5\uc720 \ub85c\ud06c\ub97c \ud68d\ub4dd\ud55c \ud6c4 \ub098\uc911\uc5d0 write_lock(X) \ub97c \uc218\ud589\ud558\uc5ec \ubca0\ud0c0\uc803 \ub85c\ud06c\ub85c \ub85c\ud06c\ub97c \ud558\uac15\uc2dc\ud0ac\uc218 \uc788\ub2e4.\\n\\n4. \ud56d\ubaa9 X\uc5d0 read_lock(X) \ub97c \uc218\ud589\ud558\uc5ec \ubca0\ud0c0\uc801 \ub85c\ud06c\ub97c \ud68d\ub4dd\ud55c \ud6c4 \ub098\uc911\uc5d0 wrrite_lock(X)\ub97c \uc218\ud589\ud558\uc5ec \uacf5\uc720 \ub85c\ud06c\ub85c \ub85c\ud06c\ub97c \ud558\uac15\uc2dc\ud0ac\uc218 \uc788\ub2e4.\\n\\n> 2\ub2e8\uacc4 \ub85c\ud0b9 \uaddc\uc57d(2-phase locking protocol)\uc758 \ub85c\ud06c \uc804\ud658(\ud639\uc740 \ub85c\ud06c \ubcc0\ud658)\uc5d0 \ub300\ud55c \uc124\uba85 \uc911 \uc633\uc740 \uac83\uc740 1\ubc88\uc785\ub2c8\ub2e4. 2\ub2e8\uacc4 \ub85c\ud0b9 \uaddc\uc57d\uc5d0\uc11c\ub294 \ud2b8\ub79c\uc7ad\uc158\uc774 \uc5bb\uc740 \ub85c\ud06c(\uc7a0\uae08)\uc744 \ud574\uc81c\ud558\uae30 \uc804\uc5d0 \ubaa8\ub4e0 \ub85c\ud06c\ub97c \ud68d\ub4dd\ud55c \ud6c4 \ubc18\ub4dc\uc2dc \ubaa8\ub4e0 \ub85c\ud06c\ub97c \ud55c \ubc88\uc5d0 \ud574\uc81c\ud574\uc57c \ud569\ub2c8\ub2e4. \ub530\ub77c\uc11c 1\ub2e8\uacc4\uc5d0\uc11c\ub294 read_lock(X)\ub97c \uc218\ud589\ud558\uc5ec \uacf5\uc720 \ub85c\ud06c\ub97c \ud68d\ub4dd\ud558\uace0, \ub098\uc911\uc5d0 2\ub2e8\uacc4\uc5d0\uc11c write_lock(X)\ub97c \uc218\ud589\ud558\uc5ec \ubca0\ud0c0\uc801 \ub85c\ud06c\ub85c \ub85c\ud06c\ub97c \uc0c1\uc2b9\uc2dc\ud0ac \uc218 \uc788\uc2b5\ub2c8\ub2e4. 2\ubc88, 3\ubc88, 4\ubc88\uc740 \ubaa8\ub450 \uc633\uc9c0 \uc54a\uc740 \uc124\uba85\uc785\ub2c8\ub2e4. 2\ubc88\uc740 \uc774\ubbf8 \ubca0\ud0c0\uc801 \ub85c\ud06c\ub97c \ud68d\ub4dd\ud55c \ud6c4\uc5d0 \uacf5\uc720 \ub85c\ud06c\ub85c \ubcc0\ud658\ud558\ub294 \uac83\uc740 \ubd88\uac00\ub2a5\ud569\ub2c8\ub2e4. 3\ubc88\uc740 \uc774\ubbf8 \uacf5\uc720 \ub85c\ud06c\ub97c \ud68d\ub4dd\ud55c \ud6c4\uc5d0 \ubca0\ud0c0\uc801 \ub85c\ud06c\ub85c \ubcc0\ud658\ud558\ub294 \uac83\ub3c4 \ubd88\uac00\ub2a5\ud569\ub2c8\ub2e4. 4\ubc88\uc740 read_lock(X)\ub97c \ud68d\ub4dd\ud55c \ud6c4\uc5d0 write_lock(X)\ub85c \ub85c\ud06c\ub97c \uc0c1\uc2b9\uc2dc\ud0a4\ub294 \uac83\uc740 \uac00\ub2a5\ud558\uc9c0\ub9cc, \uadf8 \ub2e4\uc74c\uc5d0 \uacf5\uc720 \ub85c\ud06c\ub85c \ud558\uac15\uc2dc\ud0a4\ub294 \uac83\uc740 \ubd88\uac00\ub2a5\ud569\ub2c8\ub2e4.\\n\\n---\\n\\n## 3\ubc88\\n\\n\ub370\uc774\ud130\ubca0\uc774\uc2a4 \uc2dc\uc2a4\ud15c\uc758 \ub370\uc774\ud130 \ub3c5\ub9bd\uc131\uc5d0 \ub300\ud55c \uc124\uba85\uc911 \uc633\uc9c0 \uc54a\ub294 \uac83\uc740?\\n\\n1.  ANSI 3\ub2e8\uacc4 \uc2a4\ud0a4\ub9c8 \uc544\ud0a4\ud14d\uccd0\ub294 \ub0b4\ubd80\ub2e8\uacc4(\ub0b4\ubd80\uc2a4\ud0a4\ub9c8), \uac1c\ub150\ub2e8\uacc4(\uac1c\ub150\uc2a4\ud0a4\ub9c8), \uc678\ubd80\ub2e8\uacc4(\uc678\ubd80\uc2a4\ud0a4\ub9c8)\ub85c \ub098\ub208\ub2e4. 3\ub2e8\uacc4 \uc2a4\ud0a4\ub9c8\uc758 \ubaa9\uc801\uc740 \ubb3c\ub9ac\uc801 \ub370\uc774\ud130\ubca0\uc774\uc2a4 \ub85c\ubd80\ud130 \uc0ac\uc6a9\uc790\uc758 \uc751\uc6a9\uc744 \ubd84\ub9ac\uc2dc\ud0a4\ub294 \uac83\uc774\ub2e4.\\n\\n2.  \uc678\ubd80\uc2a4\ud0a4\ub9c8\ub294 \ub370\uc774\ud130\ubca0\uc774\uc2a4\uc758 \ubb3c\ub9ac\uc801 \uc800\uc7a5\uad6c\uc870\ub97c, \uac1c\ub150\uc2a4\ud0a4\ub9c8\ub294 \ud2b9\uc815 \uc0ac\uc6a9\uc790 \uadf8\ub8f9\uc774 \uad00\uc2ec\uc744 \uac16\ub294 \ub370\uc774\ud130\ubca0\uc774\uc2a4 \ubd80\ubd84\uc744, \ub0b4\ubd80\uc2a4\ud0a4\ub9c8\ub294 \ubaa8\ub4e0 \uc0ac\uc6a9\uc790\ub97c \uc704\ud55c \uc804\uccb4 \ub370\uc774\ud130\ubca0\uc774\uc2a4 \uad6c\uc870\ub97c \uae30\uc220\ud55c\ub2e4.\\n\\n3.  \ubb3c\uc9c0\uc801 \ub370\uc774\ud130 \ub3c5\ub9bd\uc131\uc740 \uac1c\ub150\uc2a4\ud0a4\ub9c8\ub97c \ubcc0\uacbd\ud558\uc9c0 \uc54a\uc73c\uba74\uc11c \ub0b4\ubd80\uc2a4\ud0a4\ub9c8\ub97c \ubcc0\uacbd\ud560\uc218 \uc788\uc74c\uc744 \ub9d0\ud55c\ub2e4. \ub0b4\ubd80 \uc2a4\ud0a4\ub9c8\uc758 \uc811\uadfc \uad6c\uc870 \ubcc0\uacbd\uc774\ub098, \ud30c\uc77c \uc7ac\uad6c\uc131\uc774 \uac1c\ub150 \uc2a4\ud0a4\ub9c8\uc5d0 \uac00\ub2a5\ud55c \ud55c \uc601\ud5a5\uc744 \uc8fc\uc9c0 \uc54a\ub3c4\ub85d \ud55c\ub2e4.\\n\\n4.  \ub17c\ub9ac\uc801 \ub370\uc774\ud130 \ub3c5\ub9bd\uc131\uc740 \uc678\ubd80\uc2a4\ud0a4\ub9c8\ub97c \ubcc0\uacbd\ud558\uc9c0 \uc54a\uc73c\uba74\uc11c \uac1c\ub150\uc2a4\ud0a4\ub9c8\ub97c \ubcc0\uacbd\ud558\ub294 \uac83\uc744 \ub9d0\ud55c\ub2e4. \uac1c\ub150\uc2a4\ud0a4\ub9c8\uc758 \ub370\uc774\ud130\ubca0\uc774\uc2a4 \ud655\uc7a5, \uc81c\uc57d\uc870\uac74 \uac31\uc2e0 \ub4f1 \ubcc0\uacbd\uc774 \uc678\ubd80\uc2a4\ud0a4\ub9c8\uc5d0 \uac00\ub2a5\ud55c \ud55c \uc601\ud5a5\uc744 \uc8fc\uc9c0 \uc54a\ub3c4\ub85d \ud55c\ub2e4.\\n\\n> 1\ubc88 \uc124\uba85\uc774 \uc633\uc9c0 \uc54a\uc2b5\ub2c8\ub2e4. ANSI 3\ub2e8\uacc4 \uc2a4\ud0a4\ub9c8 \uc544\ud0a4\ud14d\uccd0\uc758 \ubaa9\uc801\uc740 \ub17c\ub9ac\uc801 \ub370\uc774\ud130 \ub3c5\ub9bd\uc131\uc744 \uc81c\uacf5\ud558\uae30 \uc704\ud55c \uac83\uc785\ub2c8\ub2e4. \ub17c\ub9ac\uc801 \ub370\uc774\ud130 \ub3c5\ub9bd\uc131\uc740 \uac1c\ub150\uc2a4\ud0a4\ub9c8\ub97c \ubcc0\uacbd\ud558\uc9c0 \uc54a\uc73c\uba74\uc11c \uc678\ubd80\uc2a4\ud0a4\ub9c8\ub97c \ubcc0\uacbd\ud560\uc218 \uc788\uc74c\uc744 \ub9d0\ud569\ub2c8\ub2e4.\\n\\n---\\n\\n## 4\ubc88\\n\\n\ubc84\ud074\ub9ac \ub300\ud559\uc758 Eric Brewer \uad50\uc218\uac00 \ubc1c\ud45c\ud55c CAP \uc774\ub860\uc740 \ubd84\uc0b0 \ucef4\ud4e8\ud305 \ud658\uacbd\uc758 \uc138 \uac00\uc9c0 \ud2b9\uc9d5\uc744 \uc815\uc758 \ud558\uace0 \uc788\ub294\ub370, NoSQL\uc740 \uc774\uc911 \ud55c \ub450 \uac00\uc9c0 \ud2b9\uc9d5\uc744 \ud3ec\uae30\ud558\ub294 \ub300\uc2e0 \uace0\uc131\ub2a5, \ud655\uc7a5\uc131 \ub4f1\uc758 \uc9c0\uc6d0\uc5d0 \ucd08\uc810\uc744 \ub9de\ucd94\uace0 \uc788\ub2e4. \uc5ec\uae30\uc11c CAP \uc774\ub860\uc758 \uc138 \uac00\uc9c0 \ud2b9\uc9d5\uc744 \ubc14\ub974\uac8c \uae30\uc220\ud55c \uac83\uc740?\\n\\n1. C-Consistency, A-Atomicity, P-Persistency\\n2. C-Consistency, A-Atomicity, P-Partition Tolerance\\n3. C-Consistency, A-Availability, P-Partition Tolerance\\n4. C-Consistency, A-Availability, P-Persistency\\n\\n> C-Consistency, A-Availability, P-Partition Tolerance\\n\\n---\\n\\n## 5\ubc88\\n\\n\ub370\uc774\ud130 \ud488\uc9c8\uc5d0 \uad00\ud55c \ub2e4\uc74c \uc124\uba85 \uc911 \uc633\uc9c0 \uc54a\uc740 \uac83\uc740?\\n\\n1. \ub370\uc774\ud130 \ud488\uc9c8\uc740 \uc870\uc9c1\uc758 \ubaa9\uc801 \ub2ec\uc131\uc744 \uc704\ud558\uc5ec \uad00\ub9ac\ub418\ub294 \ub370\uc774\ud130\uac00 \uc774\uc6a9\uc790\uc758 \ub9cc\uc871\uc744 \ucd1d\uc871\uc2dc\ud0ac \uc218 \uc788\ub294 \uc218\uc900\uc744 \ub9d0\ud55c\ub2e4.\\n2. \ub370\uc774\ud130 \ud488\uc9c8 \uad00\ub9ac\ub294 \uc870\uc9c1\uc774 \uc6b4\uc601\ud558\ub294 \uc815\ubcf4\uc2dc\uc2a4\ud15c\uc744 \ud65c\uc6a9\ud558\ub294 \uc774\uc6a9\uc790\uc758 \uae30\ub300\ub97c \ub9cc\uc871\uc2dc\ud0a4\uae30 \uc704\ud558\uc5ec \uc218\ud589\ud558\ub294 \ub370\uc774\ud130 \uad00\ub9ac \ud65c\ub3d9\uc774\ub2e4.\\n3. \ub370\uc774\ud130 \ud488\uc9c8 \uc9c4\ub2e8\uc758 \uc885\ub958 \uc911 \ub370\uc774\ud130 \uad6c\uc870 \uc9c4\ub2e8\uc740 \ub370\uc774\ud130 \uad00\ub9ac \ud504\ub85c\uc138\uc2a4\ub97c \ubd84\uc11d\ud558\uc5ec \ubb38\uc81c\uc810\uc744 \ub3c4\ucd9c\ud558\uace0 \uc774\ub97c \uac1c\uc120\ud560 \uc218 \uc788\ub294 \ud575\uc2ec \uc5c5\ubb34 \ud504\ub85c\uc138\uc2a4\ub97c \ucd5c\uc801\ud654\ud55c\ub2e4.\\n4. \ub370\uc774\ud130 \ud488\uc9c8 \uc9c4\ub2e8\uc744 \uc704\ud55c \ub370\uc774\ud130 \ud504\ub85c\ud30c\uc77c\ub9c1\uc740 \ub370\uc774\ud130\uc758 \uc911\uc694 \uc815\ubcf4\uc640 \ud1b5\uacc4 \uac12\uc744 \uc218\uc9d1\ud558\ub294 \uc815\ubcf4 \ubd84\uc11d \uae30\ubc95\uc774\ub2e4.\\n\\n3\ubc88\uc774 \uc633\uc9c0 \uc54a\uc740 \uc124\uba85\uc785\ub2c8\ub2e4.\\n\\n\ub370\uc774\ud130 \ud488\uc9c8\uc5d0 \uad00\ud55c \uc124\uba85\uc740 \ub2e4\uc74c\uacfc \uac19\uc2b5\ub2c8\ub2e4.\\n\\n1. \ub370\uc774\ud130 \ud488\uc9c8\uc740 \uc870\uc9c1\uc758 \ubaa9\uc801 \ub2ec\uc131\uc744 \uc704\ud558\uc5ec \uad00\ub9ac\ub418\ub294 \ub370\uc774\ud130\uac00 \uc774\uc6a9\uc790\uc758 \ub9cc\uc871\uc744 \ucd1d\uc871\uc2dc\ud0ac \uc218 \uc788\ub294 \uc218\uc900\uc744 \ub9d0\ud569\ub2c8\ub2e4.\\n2. \ub370\uc774\ud130 \ud488\uc9c8 \uad00\ub9ac\ub294 \uc870\uc9c1\uc774 \uc6b4\uc601\ud558\ub294 \uc815\ubcf4\uc2dc\uc2a4\ud15c\uc744 \ud65c\uc6a9\ud558\ub294 \uc774\uc6a9\uc790\uc758 \uae30\ub300\ub97c \ub9cc\uc871\uc2dc\ud0a4\uae30 \uc704\ud558\uc5ec \uc218\ud589\ud558\ub294 \ub370\uc774\ud130 \uad00\ub9ac \ud65c\ub3d9\uc785\ub2c8\ub2e4.\\n3. \ub370\uc774\ud130 \ud488\uc9c8 \uc9c4\ub2e8\uc758 \uc885\ub958 \uc911 \ub370\uc774\ud130 \uad6c\uc870 \uc9c4\ub2e8\uc740 \ub370\uc774\ud130 \uad6c\uc870 \uc790\uccb4\uc5d0 \ub300\ud55c \uc9c4\ub2e8\uc73c\ub85c, \ub370\uc774\ud130 \uad6c\uc870\uac00 \uc815\ud655\ud558\uace0 \uc77c\uad00\uc131\uc774 \uc788\ub294\uc9c0 \ub4f1\uc744 \ubd84\uc11d\ud569\ub2c8\ub2e4.\\n4. \ub370\uc774\ud130 \ud488\uc9c8 \uc9c4\ub2e8\uc744 \uc704\ud55c \ub370\uc774\ud130 \ud504\ub85c\ud30c\uc77c\ub9c1\uc740 \ub370\uc774\ud130\uc758 \ud2b9\uc131\uc744 \ubd84\uc11d\ud558\uc5ec \uc911\ubcf5\uac12, \uacb0\uce21\uac12, \uc774\uc0c1\uce58 \ub4f1\uc758 \ubb38\uc81c\uc810\uc744 \ud30c\uc545\ud558\uace0 \uac1c\uc120\ud560 \uc218 \uc788\ub294 \ubc29\uc548\uc744 \ub3c4\ucd9c\ud558\ub294 \uc815\ubcf4 \ubd84\uc11d \uae30\ubc95\uc785\ub2c8\ub2e4.\\n\\n\ub530\ub77c\uc11c, 3\ubc88\uc758 \uc124\uba85\uc740 \ub370\uc774\ud130 \uad6c\uc870 \uc9c4\ub2e8\uacfc \ub370\uc774\ud130 \uad00\ub9ac \ud504\ub85c\uc138\uc2a4 \ucd5c\uc801\ud654\ub97c \ud63c\ub3d9\ud55c \uac83\uc73c\ub85c \uc633\uc9c0 \uc54a\uc2b5\ub2c8\ub2e4.\\n\\n---\\n\\n## 6\ubc88\\n\\n\ub2e4\uc74c\uc740 \ub370\uc774\ud130\ubca0\uc774\uc2a4 \uc2dc\uc2a4\ud15c\uc758 3-\uce35 \uad6c\uc870(3-tier architecture)\uc5d0 \ub300\ud55c \uc124\uba85\uc774\ub2e4. \uac00 \uc640 \ub098\uc5d0 \ub4e4\uc5b4\uac08 \uc6a9\uc5b4\ub85c \uac00\uc7a5 \uc801\ud569\ud55c\uac83\uc740?\\n\\n3-\uce35\uad6c\uc870\ub294 2-\uce35 \uad6c\uc870(2-tier architecture)\ub97c \uad6c\uc131\ud558\ub294 \ud074\ub77c\uc774\uc5b8\ud2b8\uc640 \ub370\uc774\ud130\ubca0\uc774\uc2a4 \uc11c\ubc84 \uc0ac\uc774\uc5d0 \uc911\uac04 \ub2e8\uacc4\ub97c \ud558\ub098 \ub354 \ucd94\uac00\ud55c \uac83\uc774\ub2e4. \uc911\uac04 \ub2e8\uacc4\ub294 \uc77c\ubc18\uc801\uc73c\ub85c (\uac00)\ub77c \ubd80\ub974\uba70 (\ub098)\uc744 \ucc98\ub9ac\ud55c\ub2e4.\\n\\n1. \uac00: \uc751\uc6a9 (\ud504\ub85c\uadf8\ub7a8) \uc11c\ubc84 , \ub098 : \ud568\uc218\ud615 \ud504\ub85c\uadf8\ub7a8\\n2. \uac00: \uc751\uc6a9 (\ud504\ub85c\uadf8\ub7a8) \uc11c\ubc84 , \ub098 : \ube44\uc9c0\ub2c8\uc2a4 \ub85c\uc9c1\\n3. \uac00: \ud074\ub77c\uc6b0\ub4dc (\ud504\ub85c\uadf8\ub7a8) \uc11c\ubc84, \ub098: \ud568\uc218\ud615 \ud504\ub85c\uadf8\ub7a8\\n4. \uac00: \ud074\ub77c\uc6b0\ub4dc (\ud504\ub85c\uadf8\ub7a8) \uc11c\ubc84, \ub098: \ube44\uc9c0\ub2c8\uc2a4 \ub85c\uc9c1\\n\\n> 3-\uce35 \uad6c\uc870\ub294 2-\uce35 \uad6c\uc870\ub97c \uad6c\uc131\ud558\ub294 \ud074\ub77c\uc774\uc5b8\ud2b8\uc640 \ub370\uc774\ud130\ubca0\uc774\uc2a4 \uc11c\ubc84 \uc0ac\uc774\uc5d0 \uc911\uac04 \ub2e8\uacc4\ub97c \ud558\ub098 \ub354 \ucd94\uac00\ud55c \uac83\uc774\uba70, \uc77c\ubc18\uc801\uc73c\ub85c \uac00: \uc751\uc6a9 (\ud504\ub85c\uadf8\ub7a8) \uc11c\ubc84, \ub098: \ube44\uc9c0\ub2c8\uc2a4 \ub85c\uc9c1\uc744 \ucc98\ub9ac\ud569\ub2c8\ub2e4. \ub530\ub77c\uc11c \uc815\ub2f5\uc740 \\"2. \uac00: \uc751\uc6a9 (\ud504\ub85c\uadf8\ub7a8) \uc11c\ubc84, \ub098: \ube44\uc9c0\ub2c8\uc2a4 \ub85c\uc9c1\\"\uc785\ub2c8\ub2e4.\\n\\n---\\n\\n## 7\ubc88\\n\\n\ub099\uad00\uc801 \ub3d9\uc2dc\uc801 \uc81c\uc5b4 (optimistic concurrency control) \uc5d0\uc11c \uac01 \ud2b8\ub79c\uc81d\uc158\uc740 \uc138 \uac00\uc9c0 \ub2e8\uacc4\ub85c \uc2e4\ud589\ub418\ub294\ub370, \uadf8 \ub2e8 \uacc4\ub97c \uc21c\uc11c\ub300\ub85c \ubc14\ub974\uac8c \ub098\uc5f4\ud55c \uac83\uc740?\\n\\n1. \uc77d\uae30 \ub2e8\uacc4(read phase) - \uac80\uc99d \ub2e8\uacc4(validation phase) - \uc4f0\uae30 \ub2e8\uacc4(write phase)\\n2. \uac31\uc2e0 \ub2e8\uacc4(update phase) - \uac80\uc99d \ub2e8\uacc4 (validation phase) - \uc885\ub8cc \ub2e8\uacc4(commit phase)\\n3. \uac31\uc2e0 \ub2e8\uacc4(update phase) - \uc885\ub8cc \ub2e8\uacc4(commit phase) - \uac80\uc99d \ub2e8\uacc4 (validation phase)\\n4. \uc77d\uae30 \ub2e8\uacc4(read phase) - \uc4f0\uae30 \ub2e8\uacc4(write phase) - \uac80\uc99d \ub2e8\uacc4(validation phase)\\n\\n> \ub099\uad00\uc801 \ub3d9\uc2dc\uc131 \uc81c\uc5b4(Optimistic Concurrency Control)\uc5d0\uc11c \uac01 \ud2b8\ub79c\uc7ad\uc158\uc740 \uc138 \uac00\uc9c0 \ub2e8\uacc4\ub85c \uc2e4\ud589\ub429\ub2c8\ub2e4. \uccab \ubc88\uc9f8\ub294 \uc77d\uae30 \ub2e8\uacc4(read phase)\uc774\uba70, \uac01 \ud2b8\ub79c\uc7ad\uc158\uc740 \ub370\uc774\ud130\ub97c \uc77d\uace0 \ub85c\uceec \ubcf5\uc0ac\ubcf8\uc744 \ub9cc\ub4ed\ub2c8\ub2e4. \ub450 \ubc88\uc9f8\ub294 \uac31\uc2e0 \ub2e8\uacc4(update phase)\uc774\uba70, \uac01 \ud2b8\ub79c\uc7ad\uc158\uc740 \ub85c\uceec \ubcf5\uc0ac\ubcf8\uc744 \uc0ac\uc6a9\ud558\uc5ec \uc791\uc5c5\uc744 \uc218\ud589\ud569\ub2c8\ub2e4. \uc138 \ubc88\uc9f8\ub294 \uac80\uc99d \ub2e8\uacc4(validation phase)\uc774\uba70, \uac01 \ud2b8\ub79c\uc7ad\uc158\uc740 \uc791\uc5c5\uc774 \ub2e4\ub978 \ud2b8\ub79c\uc7ad\uc158\uc5d0 \uc758\ud574 \ubcc0\uacbd\ub418\uc9c0 \uc54a\uc558\ub294\uc9c0 \ud655\uc778\ud569\ub2c8\ub2e4. \ub530\ub77c\uc11c \uc815\ub2f5\uc740 1. \uc77d\uae30 \ub2e8\uacc4(read phase) - \uac80\uc99d \ub2e8\uacc4(validation phase) - \uc4f0\uae30 \ub2e8\uacc4(write phase)\uc785\ub2c8\ub2e4.\\n\\n---\\n\\n## 8\ubc88\\n\\n\ub370\uc774\ud130 \ubaa8\ub378\ub9c1\uc758 4\ub2e8\uacc4\uc5d0 \ub300\ud558\uc5ec \uc124\uba85\ud558\uc2dc\uc624.\\n\\n\ub370\uc774\ud130 \ubaa8\ub378\ub9c1\uc740 \ud604\uc2e4 \uc138\uacc4\uc758 \uac1c\uccb4(Entity)\uc640 \uac1c\uccb4 \uac04\uc758 \uad00\uacc4(Relationship)\ub97c \ub370\uc774\ud130\ubca0\uc774\uc2a4 \uad6c\uc870\ub85c \ud45c\ud604\ud558\ub294 \uacfc\uc815\uc785\ub2c8\ub2e4. \ub370\uc774\ud130 \ubaa8\ub378\ub9c1\uc758 \uc8fc\uc694 \ub2e8\uacc4\ub294 \ub2e4\uc74c\uacfc \uac19\uc2b5\ub2c8\ub2e4.\\n\\n1. \uc694\uad6c\uc0ac\ud56d \uc218\uc9d1\uacfc \ubd84\uc11d (Requirements Gathering and Analysis) \ub2e8\uacc4: \uc2dc\uc2a4\ud15c\uc774\ub098 \uc560\ud50c\ub9ac\ucf00\uc774\uc158\uc5d0\uc11c \ud544\uc694\ub85c \ud558\ub294 \ub370\uc774\ud130 \uc694\uad6c\uc0ac\ud56d\uc744 \uc218\uc9d1\ud558\uace0 \ubd84\uc11d\ud558\uc5ec \uba85\ud655\ud558\uace0 \uc644\uc804\ud55c \uc694\uad6c\uc0ac\ud56d\uc744 \ub3c4\ucd9c\ud569\ub2c8\ub2e4.\\n\\n2. \uac1c\ub150\uc801 \ubaa8\ub378\ub9c1 (Conceptual Modeling) \ub2e8\uacc4: \uc694\uad6c\uc0ac\ud56d \uc218\uc9d1 \ub2e8\uacc4\uc5d0\uc11c \uc5bb\uc740 \uac1c\uccb4(Entity)\uc640 \uad00\uacc4(Relationship)\ub97c \uc774\uc6a9\ud558\uc5ec \uac1c\ub150\uc801 \ubaa8\ub378\uc744 \uc124\uacc4\ud569\ub2c8\ub2e4. \uc774 \ub2e8\uacc4\uc5d0\uc11c\ub294 \uc5d4\ud2f0\ud2f0(Entity)\uc640 \uc5d4\ud2f0\ud2f0 \uac04\uc758 \uad00\uacc4\ub97c ER(Entity Relationship) \ub2e4\uc774\uc5b4\uadf8\ub7a8\uc73c\ub85c \ud45c\ud604\ud569\ub2c8\ub2e4.\\n\\n3. \ub17c\ub9ac\uc801 \ubaa8\ub378\ub9c1 (Logical Modeling) \ub2e8\uacc4: \uac1c\ub150\uc801 \ubaa8\ub378\ub9c1 \ub2e8\uacc4\uc5d0\uc11c \ub9cc\ub4e0 ER \ub2e4\uc774\uc5b4\uadf8\ub7a8\uc744 \ubc14\ud0d5\uc73c\ub85c \ud14c\uc774\ube14(Table)\uacfc \uad00\uacc4\ub97c \uc124\uacc4\ud569\ub2c8\ub2e4. \uc774 \ub2e8\uacc4\uc5d0\uc11c\ub294 \ud14c\uc774\ube14 \uc815\uc758\uc11c \ubc0f \uad00\uacc4 \uc815\uc758\uc11c \ub4f1\uc758 \ubb38\uc11c\ub97c \uc791\uc131\ud558\uace0, ER \ub2e4\uc774\uc5b4\uadf8\ub7a8\uc744 \ub17c\ub9ac\uc801 \ubaa8\ub378\ub85c \ubcc0\ud658\ud558\uc5ec ERD(Entity Relationship Diagram)\ub97c \ub9cc\ub4ed\ub2c8\ub2e4.\\n\\n4. \ubb3c\ub9ac\uc801 \ubaa8\ub378\ub9c1 (Physical Modeling) \ub2e8\uacc4: \ub17c\ub9ac\uc801 \ubaa8\ub378\ub9c1 \ub2e8\uacc4\uc5d0\uc11c \ub9cc\ub4e0 ERD\ub97c \ubc14\ud0d5\uc73c\ub85c \uc2e4\uc81c \ub370\uc774\ud130\ubca0\uc774\uc2a4 \uc2dc\uc2a4\ud15c\uc5d0\uc11c \uc0ac\uc6a9\ud560 \uc218 \uc788\ub294 \ubb3c\ub9ac\uc801 \uc2a4\ud0a4\ub9c8\ub97c \uc124\uacc4\ud569\ub2c8\ub2e4. \uc774 \ub2e8\uacc4\uc5d0\uc11c\ub294 \ud14c\uc774\ube14 \uc0dd\uc131 \uc2a4\ud06c\ub9bd\ud2b8, \uc778\ub371\uc2a4 \uc0dd\uc131 \uc2a4\ud06c\ub9bd\ud2b8, \uc81c\uc57d\uc870\uac74 \uc0dd\uc131 \uc2a4\ud06c\ub9bd\ud2b8 \ub4f1\uc744 \uc791\uc131\ud569\ub2c8\ub2e4. \ub610\ud55c, \uc131\ub2a5\uacfc \ubcf4\uc548 \ub4f1\uc744 \uace0\ub824\ud558\uc5ec \ubb3c\ub9ac\uc801 \ubaa8\ub378\uc744 \ucd5c\uc801\ud654\ud569\ub2c8\ub2e4.\\n\\n---\\n\\n## 9\ubc88\\n\\nDB\uc774\uc0c1\ud604\uc0c1(Anomaly) \uc5d0 \ub300\ud558\uc5ec \uc124\uba85 \ud558\uc2dc\uc624\\n\\nDB\uc774\uc0c1\ud604\uc0c1(Anomaly)\uc774\ub780, \ub370\uc774\ud130\ubca0\uc774\uc2a4 \uc124\uacc4\uac00 \ube44\uc815\uaddc\ud654(normalization)\ub418\uc5b4 \uc788\uc9c0 \uc54a\uac70\ub098, \ud14c\uc774\ube14 \uad6c\uc870\ub098 \uad00\uacc4 \uc124\uc815 \ub4f1\uc774 \uc798\ubabb\ub41c \uacbd\uc6b0 \ubc1c\uc0dd\ud560 \uc218 \uc788\ub294 \ubb38\uc81c\uc785\ub2c8\ub2e4. \uc774\uc0c1\ud604\uc0c1\uc740 \ub370\uc774\ud130\ubca0\uc774\uc2a4\uc758 \uc77c\uad00\uc131, \uc815\ud655\uc131, \ubb34\uacb0\uc131 \ub4f1\uc744 \ud574\uce58\uae30 \ub54c\ubb38\uc5d0 \ub370\uc774\ud130\ubca0\uc774\uc2a4\uc758 \ud6a8\uc728\uc131\uacfc \uc548\uc804\uc131\uc5d0 \uc601\ud5a5\uc744 \ubbf8\uce60 \uc218 \uc788\uc2b5\ub2c8\ub2e4.\\n\\n\uc8fc\uc694\ud55c \uc774\uc0c1\ud604\uc0c1\uc73c\ub85c\ub294 \ub2e4\uc74c\uacfc \uac19\uc740 \uac83\uc774 \uc788\uc2b5\ub2c8\ub2e4.\\n\\n- \uc0bd\uc785 \uc774\uc0c1 (Insertion Anomaly) : \uc0c8\ub85c\uc6b4 \ub370\uc774\ud130\ub97c \uc0bd\uc785\ud560 \ub54c, \ubc18\ub4dc\uc2dc \ub2e4\ub978 \uc18d\uc131\ub4e4\uacfc \ud568\uaed8 \uc0bd\uc785\ud574\uc57c \ud558\ub294 \uacbd\uc6b0\uac00 \uc788\uc5b4, \uc0bd\uc785 \uc2dc \ubd88\ud544\uc694\ud55c \ub370\uc774\ud130\uac00 \uc911\ubcf5\ub420 \uc218 \uc788\uace0, \uc774\ub85c \uc778\ud574 \uacf5\uac04 \ub0ad\ube44\uc640 \ub370\uc774\ud130 \ubd88\uc77c\uce58 \ubb38\uc81c\uac00 \ubc1c\uc0dd\ud569\ub2c8\ub2e4.\\n- \uc0ad\uc81c \uc774\uc0c1 (Deletion Anomaly) : \ud2b9\uc815 \ub370\uc774\ud130\ub97c \uc0ad\uc81c\ud560 \ub54c, \uadf8 \ub370\uc774\ud130\uc640 \uad00\ub828\ub41c \ub2e4\ub978 \ub370\uc774\ud130\ub3c4 \ud568\uaed8 \uc0ad\uc81c\ub418\ub294 \ubb38\uc81c\uac00 \ubc1c\uc0dd\ud569\ub2c8\ub2e4.\\n- \uac31\uc2e0 \uc774\uc0c1 (Update Anomaly) : \ub370\uc774\ud130 \uc911 \uc77c\ubd80\ub97c \uc218\uc815\ud558\ub824 \ud560 \ub54c, \uc77c\ubd80 \ub370\uc774\ud130\ub9cc \uc218\uc815\ub418\uc5b4 \ub370\uc774\ud130 \ubd88\uc77c\uce58 \ubb38\uc81c\uac00 \ubc1c\uc0dd\ud569\ub2c8\ub2e4.\\n\\n\uc774\ub7ec\ud55c \uc774\uc0c1\ud604\uc0c1\uc744 \ubc29\uc9c0\ud558\uae30 \uc704\ud574\uc11c\ub294 \ub370\uc774\ud130\ubca0\uc774\uc2a4 \uc124\uacc4 \ub2e8\uacc4\uc5d0\uc11c \uc815\uaddc\ud654(normalization) \uacfc\uc815\uc744 \uac70\uccd0\uc57c \ud569\ub2c8\ub2e4. \uc815\uaddc\ud654\ub294 \uc911\ubcf5\uc744 \ucd5c\uc18c\ud654\ud558\uace0, \ub370\uc774\ud130\uc758 \uc77c\uad00\uc131\uacfc \ubb34\uacb0\uc131\uc744 \ubcf4\uc7a5\ud558\uae30 \uc704\ud55c \uacfc\uc815\uc73c\ub85c, \ub370\uc774\ud130\ub97c \uc801\uc808\ud55c \ub17c\ub9ac\uc801 \uad6c\uc870\ub85c \ubd84\ud574\ud558\ub294 \uc791\uc5c5\uc785\ub2c8\ub2e4.\\n\\n---\\n\\n## 10\ubc88\\n\\nDB \uc635\ud2f0\ub9c8\uc774\uc800 (DB Optimizer: DB \uc9c8\uc758 \ucd5c\uc801\ud654\uae30)\\n\\n- DB \uc635\ud2f0\ub9c8\uc774\uc800\ub294 \ub370\uc774\ud130\ubca0\uc774\uc2a4 \uc2dc\uc2a4\ud15c\uc5d0\uc11c \uc0ac\uc6a9\uc790\uac00 \uc785\ub825\ud55c SQL \ucffc\ub9ac\ub97c \uac00\uc7a5 \ube60\ub974\uace0 \ud6a8\uc728\uc801\uc778 \ubc29\uc2dd\uc73c\ub85c \ucc98\ub9ac\ud558\uae30 \uc704\ud574 \uc2e4\ud589 \uacc4\ud68d\uc744 \ucd5c\uc801\ud654\ud558\ub294 \uc5ed\ud560\uc744 \ud569\ub2c8\ub2e4. \uc989, \uc0ac\uc6a9\uc790\uac00 \uc694\uccad\ud55c \ub370\uc774\ud130\uc5d0 \ub300\ud574 \uac00\uc7a5 \ud6a8\uc728\uc801\uc73c\ub85c \uc811\uadfc\ud560 \uc218 \uc788\ub294 \ubc29\ubc95\uc744 \ucc3e\uc544\ub0b4\ub294 \uac83\uc785\ub2c8\ub2e4.\\n\\n- DB \uc635\ud2f0\ub9c8\uc774\uc800\ub294 \ub370\uc774\ud130\ubca0\uc774\uc2a4 \uc2dc\uc2a4\ud15c\uc758 \ub0b4\ubd80 \ub370\uc774\ud130 \uad6c\uc870, \uc778\ub371\uc2a4, \ud1b5\uacc4 \uc815\ubcf4, \ud558\ub4dc\uc6e8\uc5b4 \uc790\uc6d0 \ub4f1\uc744 \uace0\ub824\ud558\uc5ec \uc5ec\ub7ec \uc2e4\ud589 \uacc4\ud68d\uc744 \uc0dd\uc131\ud558\uace0, \uadf8 \uc911\uc5d0\uc11c \uac00\uc7a5 \ud6a8\uc728\uc801\uc778 \uc2e4\ud589 \uacc4\ud68d\uc744 \uc120\ud0dd\ud569\ub2c8\ub2e4. \uc774\ub97c \uc704\ud574 DB \uc635\ud2f0\ub9c8\uc774\uc800\ub294 SQL \ucffc\ub9ac\uc758 \uc870\uc778, \ud544\ud130\ub9c1, \uc815\ub82c \ub4f1\uc758 \uc5f0\uc0b0 \ube44\uc6a9\uc744 \uacc4\uc0b0\ud558\uace0, \uc778\ub371\uc2a4\ub97c \uc0ac\uc6a9\ud560 \uc218 \uc788\ub294 \uacbd\uc6b0\uc640 \uc804\uccb4 \ud14c\uc774\ube14 \uc2a4\uce94\uc744 \uc218\ud589\ud574\uc57c \ud558\ub294 \uacbd\uc6b0\ub97c \ud310\ub2e8\ud558\uc5ec \uc801\uc808\ud55c \uc2e4\ud589 \uacc4\ud68d\uc744 \uc120\ud0dd\ud569\ub2c8\ub2e4.\\n\\n- DB \uc635\ud2f0\ub9c8\uc774\uc800\ub294 \ub370\uc774\ud130\ubca0\uc774\uc2a4 \uc2dc\uc2a4\ud15c\uc758 \uc131\ub2a5\uc5d0 \ud070 \uc601\ud5a5\uc744 \ubbf8\uce58\uae30 \ub54c\ubb38\uc5d0, \ucd5c\uc2e0 \uae30\uc220\uacfc \uc54c\uace0\ub9ac\uc998\uc744 \uc801\uc6a9\ud558\uc5ec \uc2e4\ud589 \uacc4\ud68d\uc744 \ucd5c\uc801\ud654\ud558\ub294 \uac83\uc774 \uc911\uc694\ud569\ub2c8\ub2e4. \ub610\ud55c, DB \uc635\ud2f0\ub9c8\uc774\uc800\ub294 \ub370\uc774\ud130\ubca0\uc774\uc2a4 \uc2dc\uc2a4\ud15c\uc758 \uc131\ub2a5\uc744 \ucd5c\uc801\ud654\ud558\uae30 \uc704\ud574 \uc815\uae30\uc801\uc73c\ub85c \uc218\ud589\ub418\ub294 \ud1b5\uacc4 \uc815\ubcf4 \uc218\uc9d1 \uc791\uc5c5\uacfc \uac19\uc740 \uad00\ub9ac \uc791\uc5c5\ub3c4 \uc218\ud589\ud569\ub2c8\ub2e4."},{"id":"/2023/02/17/DB\uac1c\uc6948","metadata":{"permalink":"/blog/second/2023/02/17/DB\uac1c\uc6948","source":"@site/second-blog/2023-02-17-DB\uac1c\uc6948/index.mdx","title":"DataBase \uac1c\uc694 8","description":"\ubb3c\ub9ac\uc801 \ub370\uc774\ud130 \ubaa8\ub378\ub9c1\uc758 \uc815\uc758","date":"2023-02-17T10:23:00.000Z","formattedDate":"2023\ub144 2\uc6d4 17\uc77c","tags":[{"label":"database","permalink":"/blog/second/tags/database"},{"label":"\uc815\ubcf4\uad00\ub9ac","permalink":"/blog/second/tags/\uc815\ubcf4\uad00\ub9ac"}],"readingTime":8.605,"hasTruncateMarker":false,"authors":[{"name":"100milliongold","title":"developer","url":"https://sebastienlorber.com","imageURL":"https://avatars.githubusercontent.com/u/13652711?s=400&u=0a1092b8b1170f71ae883d098f8721c12ce39e9e&v=4","key":"100milliongold"}],"frontMatter":{"title":"DataBase \uac1c\uc694 8","date":"2023-02-17T10:23:00.000Z","authors":["100milliongold"],"tags":["database","\uc815\ubcf4\uad00\ub9ac"],"password":"1q2w3e4r"},"unlisted":false,"prevItem":{"title":"DataBase \ubb38\uc81c\ud480\uc774","permalink":"/blog/second/2023/02/17/DB\ubb38\uc81c"},"nextItem":{"title":"DataBase \uac1c\uc694 6","permalink":"/blog/second/2023/02/16/DB\uac1c\uc6946"}},"content":"import Drawio from \\"@theme/Drawio\\";\\nimport simpleGraph1 from \\"!!raw-loader!./chart1.drawio\\";\\nimport simpleGraph2 from \\"!!raw-loader!./chart2.drawio\\";\\nimport simpleGraph3 from \\"!!raw-loader!./chart3.drawio\\";\\nimport simpleGraph4 from \\"!!raw-loader!./chart4.drawio\\";\\n\\n## \ubb3c\ub9ac\uc801 \ub370\uc774\ud130 \ubaa8\ub378\ub9c1\uc758 \uc815\uc758\\n\\n- \ub17c\ub9ac \ub370\uc774\ud130 \ubaa8\ub378\uc744 \ud2c0\uc815 \ub370\uc774\ud130\ubca0\uc774\uc2a4\ub85c \uc124\uacc4\ud568\uc73c\ub85c\uc368 \uc0dd\uc131\ub41c \ub370\uc774\ud130\ub97c \uc800\uc7a5\ud560\uc218 \uc788\ub294 \ubb3c\ub9ac\uc801\uc778 \uc2a4\ud0a4\ub9c8\ub97c \ub9cc\ub4dc\ub294 \uc77c\ub828\uc758 \uacfc\uc815\\n\\n## \ubb3c\ub9ac \ub370\uc774\ud130 \ubaa8\ub378\ub9c1\uc758 \ubaa9\uc801 \ubc0f \ud6a8\uacfc\\n\\n- \uc0ac\uc804\uc801\uc73c\ub85c \uc791\uc131\ub41c \ub17c\ub9ac \ub370\uc774\ud130 \ubaa8\ub378\uc744 \uac01\uac01\uc758 \uad00\uacc4\ud615 DBMS \uc758 \ud2b9\uc131, \uae30\ub2a5 , \uc131\ub2a5 \ub4f1\uc744 \uace0\ub824\ud568\\n- \ub370\uc774\ud130\uc758 \uc800\uc7a5\uacf5\uac04 , \ub370\uc774\ud130\uc758 \ubd84\uc0b0, \ub370\uc774\ud130\uc758 \uc800\uc7a5 \ubc29\ubc95\ub4f1\uc744 \uace0\ub824\ud568\\n- \uc798 \uc791\uc131\ub41c \ubb3c\ub9ac \ub370\uc774\ud130 \ubaa8\ub378\uc740 \ub370\uc774\ud130 \ubca0\uc774\uc2a4 \uc6b4\uc6a9 \uc131\ub2a5\uc744 \ud06c\uac8c \ud5a5\uc0c1 \uc2dc\ud0ac\uc218 \uc788\uc74c\\n\\n## \ubb3c\ub9ac\uc801 \ubaa8\ub378\ub9c1\uc758 \uc8fc\uc694 Activity\\n\\n|        \uc8fc\uc694 Task         | \uc124\uba85                                                                                                                 |\\n| :----------------------: | :------------------------------------------------------------------------------------------------------------------- |\\n| 1. \ubb3c\ub9ac\uc694\uc18c \uc870\uc0ac \ubc0f \ubd84\uc11d | DBMS\uc758 \uc885\ub958 \ubc0f \ubc84\uc804, \uc6b4\uc601\uccb4\uc81c\uc5d0 \ub300\ud55c \ubd84\uc11d                                                                            |\\n|       2. \ubc18 \uc84d\uaddc\ud654       | \uc815\uaddc\ud654\ub41c Entity type, \uc18d\uc131, \uad00\uacc4\ub97c \uc2dc\uc2a4\ud15c\uc758 \uc131\ub2a5 \ud5a5\uc0c1, \uac1c\ubc1c\uacfc \uc6b4\uc6a9\uc744 \ub2e8\uc21c\ud654\ud558\uae30 \uc704\ub798 \ub370\uc774\ud130 \ubaa8\ub378\uc744 \ud1b5\ud569\ud558\ub294 \ud504\ub85c\uc138\uc2a4 |\\n|  3.\ub17c\ub9ac-\ubb3c\ub9ac \ubaa8\ub378 \ubcc0\ud658   | \ub17c\ub9ac \uc601\uc601\uacfc \ubb3c\ub9ac \uc601\uc5ed\uc744 \ubcf4\ub294 \uc2dc\uac01\uc774 \ub2e4\ub974\ubbc0\ub85c \ub17c\ub9ac \ubaa8\ub378\uc744 \ubb3c\ub9ac \ubaa8\ub378\ub85c \ubcc0\ud658\ud574\uc57c\ud568                                      |\\n\\n## \ub17c\ub9ac\uc801 \ubaa8\ub378\ub9c1\uacfc\uc758 \uc6a9\uc5b4 \ube44\uad50\\n\\n| \ubd84\uc11d                          | \uc124\uacc4                               |\\n| :---------------------------- | :--------------------------------- |\\n| \ub17c\ub9ac \ub370\uc774\ud130 \ubaa8\ub378\ub9c1 (ER Model) | \ubb3c\ub9ac \ub370\uc774\ud130 \ubaa8\ub378\ub9c1(Physical Model) |\\n| Entity                        | Table                              |\\n| Attribute                     | Column                             |\\n| Primary UID                   | Primary Key                        |\\n| Identifer                     | Key                                |\\n| Relationship                  | Foreign Key                        |\\n|                               | Table space, Index space           |\\n\\n## \uc774\uc0c1\ud604\uc0c1(Anomaly) \uc758 \uc815\uc758 \u2b50\ufe0f\u2b50\ufe0f\u2b50\ufe0f\\n\\n- \uc774\uc0c1\ud604\uc0c1(Anomaly) \uc758 \uc815\uc758\\n\\n  - \ub9b4\ub808\uc774\uc158\uc758 <b style={{color:\\"blue\\"}}>\ub370\uc774\ud130 \uc911\ubcf5 \uad00\ub9ac\ub85c</b> \uc778\ud574 \ub370\uc774\ud130\ub97c \uc870\uc791\ud560\ub54c \ubc1c\uc0dd\ud558\ub294 <b style={{color:\\"blue\\"}}>\uc608\uae30\uce58 \ubabb\ud55c \uacb0\uacfc\ub97c \ucd08\ub798</b>\ud558\ub294 \ube44 \ud569\ub9ac\uc801 \ud604\uc0c1\\n\\n- \uc774\uc0c1\ud604\uc0c1(Anomaly)\uc758 \uc885\ub958\\n\\n  |                  \uc885\ub958                   | \uc124\uba85                                                                                                          |  Anomaly  |\\n  | :-------------------------------------: | :------------------------------------------------------------------------------------------------------------ | :-------: |\\n  | <b style={{color:\\"blue\\"}}>\uc0bd\uc785 \uc774\uc0c1</b> | \ub9b4\ub808\uc774\uc158\ub0b4\uc5d0 \ud558\ub098\uc758 \ud29c\ud50c\uc744 \uc0bd\uc785\ud558\ub824\uace0 \ud560\ub54c \ubd88\ud544\uc694\ud558\uace0 \uc6d0\ud558\uc9c0 \uc54a\ub294 \ub370\uc774\ud130\uae4c\uc9c0 \uc0bd\uc785\ud574\uc57c \ud558\ub294 \ud604\uc0c1               | Insertion |\\n  | <b style={{color:\\"blue\\"}}>\uc0ad\uc81c \uc774\uc0c1</b> | \ub9b4\ub808\uc774\uc158\ub0b4\uc758 \ud558\ub098\uc758 \ud29c\ud50c\uc744 \uc0ad\uc81c\ud558\ub824\uace0 \ud560\ub54c \uc720\uc9c0\ub418\uc5b4\uc57c \ud560 \uc815\ubcf4\uae4c\uc9c0 \uc0ad\uc81c\ub418\ub294 \uc5f0\uc1c4 \uc0ad\uc81c(Triggered Deletion) \ud604\uc0c1 | Deletion  |\\n  | <b style={{color:\\"blue\\"}}>\uac31\uc2e0 \uc774\uc0c1</b> | \uc911\ubcf5\ub41c \ud29c\ube14 \uc911\uc5d0\uc11c \uc77c\ubd80 \ud29c\ud50c\uc758 \uc18d\uc131\uc744 \ubcc0\uacbd\uc2dc\ud0a4\ub824\uace0 \ud560 \ub54c \uc815\ubcf4\uc758 \ubaa8\uc21c\uc131\uc774 \ubc1c\uc0dd\ud558\ub294 \ud604\uc0c1                        |  Update   |\\n\\n## \uc774\uc0c1\ud604\uc0c1\uc758 \uc0ac\ub840\\n\\n| \ud559\ubc88 | \uc9c0\ub3c4 \uad50\uc218 |  \ud559\uacfc  | \uacfc\ubaa9\ubc88\ud638 | \uc131\uc801 |\\n| :--: | :-------: | :----: | :------: | :--: |\\n| 100  |  \uae40\uacbd\uc544   | \ucef4\ud4e8\ud130 |    DB    |  A   |\\n| 100  |  \uae40\uacbd\uc544   | \ucef4\ud4e8\ud130 |    OS    |  B   |\\n| 101  |  \uad8c\ud604\uc815   |  \uc804\uc790  |    CA    |  A   |\\n| 102  |  \uae40\uacbd\uc544   | \ucef4\ud4e8\ud130 |    DB    |  C   |\\n| 102  |  \uae40\uacbd\uc544   | \ucef4\ud4e8\ud130 |    OS    |  A   |\\n| 102  |  \uae40\uacbd\uc544   | \ucef4\ud4e8\ud130 |    SA    |  A   |\\n| 103  |  \uc815\ub3d9\uc601   | \ucef4\ud4e8\ud130 |    DB    |  C   |\\n| 103  |  \uc815\ub3d9\uc601   | \ucef4\ud4e8\ud130 |    OS    |  A   |\\n| 103  |  \uc815\ub3d9\uc601   | \ucef4\ud4e8\ud130 |    SA    |  A   |\\n| 103  |  \uc815\ub3d9\uc601   | \ucef4\ud4e8\ud130 |    IT    |  B   |\\n\\n- \uc0bd\uc785\uc774\uc0c1 : \ud2b9\uc815 \uacfc\ubaa9\uc774 \uac1c\uc124\ub418\uc5c8\uc74c\uc5d0\ub3c4 \ubd88\uad6c\ud558\uace0, \uc9c0\ub3c4\uad50\uc218\uac00 \uc5c6\uc5b4\uc11c \ub4f1\ub85d\uc744 \ubabb\ud558\ub294 \uacbd\uc6b0\\n- \uc0ad\uc81c\uc774\uc0c1: \ud559\ubc88\uc774 101\ubc88\uc740 \uacfc\ubaa9 \ub4f1\ub85d\uc744 \ucde8\uc18c\ud558\uba74 \ud559\uacfc, \uc9c0\ub3c4\uad50\uc218 \uc815\ubcf4\ub3c4 \uc0ad\uc81c\\n- \uac31\uc2e0\uc774\uc0c1: \ud559\ubc88\uc774 103\uc778 \uc0ad\uc0dd\uc758 \uc9c0\ub3c4\uad50\uc218\uac00 \ubcc0\uacbd\ub418\uba74 4\uac1c\uc758 \ud29c\ud50c \ubcc0\uacbd\\n\\n## \uc774\uc0c1 \ud604\uc0c1\uc758 \uc6d0\uc778 \ubc0f \ud574\uacb0\\n\\n- \uc18d\uc131\ub4e4 \uac04\uc5d0 <b style={{color:\\"blue\\"}}>\uc885\uc18d\uc131(Dependency)</b> \ubd84\uc11d\uc744 \ud1b5\ud574 \uae30\ubcf8\uc801\uc73c\ub85c \ud558\ub098\uc758 \uc885\uc18d\uc131\uc774 \ud558\ub098\uc758 \ub9b4\ub808\uc774\uc158\uc73c\ub85c \ud45c\ud604\ub418\ub3c4\ub85d <b style={{color:\\"blue\\"}}>\ubb34\uc190\uc2e4 \ubd84\ud574(Decomposition)</b> \ud558\uba74 \ub428\\n\\n## \ud568\uc218\uc801 \uc885\uc18d\uc131(Functional Dependency)\uc758 \uac1c\ub150\\n\\n- \ub370\uc774\ud130 \ub4e4\uc774 \uc5b4\ub5a4 \uae30\uc900 \uac12\uc5d0 \uc758\ud574 \ud5a5\uc0c1 \uc885\uc18d\ub418\ub294 \ud604\uc0c1\\n- \ub9b4\ub808\uc774\uc158\uc758 \ud55c \uc18d\uc131 X \uac00 \ub2e4\ub978 \uc18d\uc131 Y\ub97c \uacb0\uc815\uc9c0\uc744\ub54c Y\ub294 X\uc5d0 \ud568\uc218\uc801\uc73c\ub85c \uc885\uc18d\ub418\uc5c8\ub2e4\uace0 \ud558\uba70 R.X \u2192 R.Y \uc640 \uac19\uc774 \ud45c\ud604\\n- X \u2192 Y \uc77c\uacbd\uc6b0 , X\ub294 \uacb0\uc815\uc790(Determination) Y\ub294 \uc885\uc18d\uc790(Dependent)\ub85c \ub9dd\uba85\\n\\n<Drawio content={simpleGraph1} resize={true} />\\n\\n## \ud568\uc218\uc801 \uc885\uc18d\uc131\uc758 \uc885\ub958\\n\\n<table>\\n  <thead>\\n    <tr>\\n      <th width=\\"40%\\">\uc720\ud615</th>\\n      <th>\uc608\uc2dc</th>\\n    </tr>\\n  </thead>\\n  <tbody>\\n    <tr>\\n      <td>\\n        - <b style={{ color: \\"blue\\" }}>\uc644\uc804</b> \ud568\uc218\uc801 \uc885\uc18d\uc131\\n        <br /> [\ud559\ubc88,\uacfc\ubaa9\ubc88\ud638] \u2192 \uc131\uc801 <br />\\n        (\ud559\ubc88, \uacfc\ubaa9\ubc88\ud638\ub97c \uc54c\uc544\uc57c \uc131\uc801 \ud655\uc778\uac00\ub2a5) <br />\\n        <br />\\n        - \ubd80\ubd84 \ud568\uc218\uc801 \uc885\uc18d\uc131\\n        <br /> \ud559\ubc88 \u2192 \ud559\uacfc <br />\\n        (\ud559\ubc88 \ud639\uc740 \uacfc\ubaa9\ubc88\ud638 \ub9cc \uc54c\uc544\ub3c4 \ud559\uacfc \ud655\uc778\uac00\ub2a5)\\n      </td>\\n      <td>\\n        <Drawio content={simpleGraph2} resize={true} />\\n      </td>\\n    </tr>\\n    <tr>\\n      <td>\\n        <b style={{ color: \\"blue\\" }}>\uc774\ud589</b> \ud568\uc218\uc801 \uc885\uc18d\uc131:\\n        <br /> \ud559\ubc88 \u2192 \uc9c0\ub3c4\uad50\uc218 <br /> \uc9c0\ub3c4\uad50\uc218 \u2192 \ud559\uacfc <br /> \ud559\ubc88 \u2192 \ud559\uacfc <br /> <br />\\n        (\ud559\ubc88\uc774 \uc9c0\ub3c4\uad50\uc218\ub97c \uacb0\uc815\uc9c0\uc744\uc218 \uc788\uace0 \uc9c0\ub3c4\uad50\uc218\uac00 \ud559\uacfc\ub97c \uacb0\uc815\uc9c0\uc744\uc218 \uc788\uc73c\uba74\\n        \ud559\ubc88\uc774 \ud559\uacfc\ub97c \uacb0\uc815 \uc9c0\uc744\uc218 \uc788\uc74c)\\n      </td>\\n      <td>\\n        <Drawio content={simpleGraph3} resize={true} />\\n      </td>\\n    </tr>\\n    <tr>\\n      <td>\\n        - <b style={{ color: \\"blue\\" }}>\uacb0\uc815\uc790</b> \ud568\uc218\uc801 \uc885\uc18d\uc131:\\n        <br />\\n        \uad50\uc218 \u2192 \uacfc\ubaa9\\n        <br />\\n        <br />\\n        (\uad50\uc218\uac00 \ud0a4\uac00 \uc544\ub2d8\uc5d0\ub3c4 \ubd88\uad6c\ud558\uace0 \uacfc\ubaa9\uc758 \uacb0\uc815\uc790 \uc5ed\ud65c\uc744 \ud568)\\n      </td>\\n      <td>\\n        <Drawio content={simpleGraph4} resize={true} />\\n      </td>\\n    </tr>\\n  </tbody>\\n</table>\\n\\n## \ud568\uc218\uc801 \uc885\uc18d\uc131 \ucd94\ub860 \uaddc\uce59 (\uc554\uc2a4\ud2b8\ub871\uc758 \uacf5\ub9ac(Axiom))\\n\\n| \ucd94\ub860\uaddc\uce59    | \ub0b4\uc6a9                                                                                                                                                                                                              |\\n| ----------- | ----------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------- |\\n| \uc7ac\uadc0 \uaddc\uce59   | - Reflexivify Rule <br/> - $Y \\\\subseteq X$ \uc774\uba74 $X \\\\rightarrow\\tY$ \uc774\ub2e4 : Y \uac00 X\uc5d0 \ud3ec\ud568 \ub418\uc5b4 \uc788\uc73c\uba74 X \ub294 Y\uc758 \uacb0\uc815\uc790 \uc774\ub2e4.                                                                                           |\\n| \uc99d\uac00 \uaddc\uce59   | - Augmentation Rule <br/> - $X \\\\rightarrow Y$ \uc774\uba74 $XZ \\\\rightarrow\\tYZ$ \uc774\ub2e4 : X \uac00 Y\ub97c \uacb0\uc815 \uc9c0\uc744\ub54c X \uac00 \uc99d\uac00 \ub418\uc5b4\ub3c4 \ub3d9\uc77c\ud558\ub2e4.                                                                                      |\\n| \uc774\ud589 \uaddc\uce59   | - Transitive Rule <br/> - $X \\\\rightarrow Y$ \uc774\uace0 $Y \\\\rightarrow Z$ \uc774\uba74 $X \\\\rightarrow Z$ \uc774\ub2e4 : X \uac00 Y\ub97c \uacb0\uc815 \uc9c0\uc744\uc218 \uc788\uace0 Y \uac00 Z\ub97c \uacb0\uc815 \uc9c0\uc744\uc218 \uc788\uc744\ub54c X \ub294 Z\ub97c \uacb0\uc815 \uc9c0\uc744\uc218 \uc788\ub2e4.                                 |\\n| \uc5f0\ud569 \uaddc\uce59   | - Union Rule <br/> - $X \\\\rightarrow Y$ \uc774\uace0 $X \\\\rightarrow Z$ \uc774\uba74 $X \\\\rightarrow\\tYZ$ \uc774\ub2e4 : X \uac00 Y\ub97c \uacb0\uc815 \uc9c0\uc744\uc218 \uc788\uace0 X \uac00 Z\ub97c \uacb0\uc815 \uc9c0\uc744\uc218 \uc788\uc744\ub54c X \ub294 \uc5f0\ud569\ub41c Y\uc640Z\ub97c \uacb0\uc815 \uc9d3\ub294\ub2e4.                                 |\\n| \ubd84\ud574 \uaddc\uce59   | - Decomposition Rule <br/> - $X \\\\rightarrow YZ$ \uc774\uba74 $X \\\\rightarrow\\tY$ \uc774\uace0 $X \\\\rightarrow\\tZ$ \uc774\ub2e4 : X \ub294 \uc5f0\ud569\ub41c Y\uc640Z\ub97c \uacb0\uc815 \uc9c0\uc744\ub54c X \uac00 Y\ub97c \uacb0\uc815 \uc9c0\uc744\uc218 \uc788\uace0 X \uac00 Z\ub97c \uacb0\uc815 \uc9c0\uc744\uc218 \uc788\ub2e4.                            |\\n| \uac00\uc774\ud589 \uaddc\uce59 | - Pseuco Transitive Rule <br/> - $X \\\\rightarrow Y$ \uc774\uace0 $WY \\\\rightarrow Z$ \uc774\uba74 $WX \\\\rightarrow\\tZ$ \uc774\ub2e4 : X \uac00 Y\ub97c \uacb0\uc815 \uc9c0\uc744\uc218 \uc788\uace0 \uc5f0\ud569\ub41c W \uc640 Y \uac00 Z\ub97c \uacb0\uc815 \uc9c0\uc744\uc218 \uc788\uc744\ub54c \uc5f0\ud569\ub41c W \uc640 X \ub294 Z\ub97c \uacb0\uc815 \uc9c0\uc744\uc218 \uc788\ub2e4. |\\n\\n- \uc7ac\uadc0 \uaddc\uce59, \uc99d\uac00 \uaddc\uce59, \uc774\ud589 \uaddc\uce59 : \uae30\ubcf8 \uaddc\uce59\\n- \uc5f0\ud569 \uaddc\uce59, \ubd84\ud574 \uaddc\uce59, \uac00\uc774\ud589 \uaddc\uce59 : \ud655\uc7a5 \uaddc\uce59"},{"id":"/2023/02/16/DB\uac1c\uc6946","metadata":{"permalink":"/blog/second/2023/02/16/DB\uac1c\uc6946","source":"@site/second-blog/2023-02-16-DB\uac1c\uc6946/index.mdx","title":"DataBase \uac1c\uc694 6","description":"\ub370\uc774\ud130 \ubaa8\ub378\ub9c1\uc758 \uc815\uc758","date":"2023-02-16T22:16:00.000Z","formattedDate":"2023\ub144 2\uc6d4 16\uc77c","tags":[{"label":"database","permalink":"/blog/second/tags/database"},{"label":"\uc815\ubcf4\uad00\ub9ac","permalink":"/blog/second/tags/\uc815\ubcf4\uad00\ub9ac"},{"label":"\ub370\uc774\ud130 \ubaa8\ub378\ub9c1","permalink":"/blog/second/tags/\ub370\uc774\ud130-\ubaa8\ub378\ub9c1"}],"readingTime":6.16,"hasTruncateMarker":false,"authors":[{"name":"100milliongold","title":"developer","url":"https://sebastienlorber.com","imageURL":"https://avatars.githubusercontent.com/u/13652711?s=400&u=0a1092b8b1170f71ae883d098f8721c12ce39e9e&v=4","key":"100milliongold"}],"frontMatter":{"title":"DataBase \uac1c\uc694 6","date":"2023-02-16T22:16:00.000Z","authors":["100milliongold"],"tags":["database","\uc815\ubcf4\uad00\ub9ac","\ub370\uc774\ud130 \ubaa8\ub378\ub9c1"],"password":"1q2w3e4r"},"unlisted":false,"prevItem":{"title":"DataBase \uac1c\uc694 8","permalink":"/blog/second/2023/02/17/DB\uac1c\uc6948"},"nextItem":{"title":"DataBase \uac1c\uc694 7","permalink":"/blog/second/2023/02/16/DB\uac1c\uc6947"}},"content":"import Drawio from \\"@theme/Drawio\\";\\nimport simpleGraph1 from \\"!!raw-loader!./chart1.drawio\\";\\nimport simpleGraph2 from \\"!!raw-loader!./chart2.drawio\\";\\n\\n## \ub370\uc774\ud130 \ubaa8\ub378\ub9c1\uc758 \uc815\uc758\\n\\n- \ud604\uc2e4\uc138\uacc4\uc758 \uc5c5\ubb34\uc801\uc778 \ud504\ub85c\uc138\uc2a4\ub97c \ucd94\uc0c1\ud654\ud558\uc5ec \ubb3c\ub9ac\uc801\uc73c\ub85c \ub370\uc774\ud130\ubca0\uc774\uc2a4\ud654 \ud558\uae30\uc704\ud55c \uacfc\uc815\uc73c\ub85c \uc2e4\uccb4\uc640 \uad00\uacc4\ub97c \uc911\uc2ec\uc73c\ub85c \uccb4\uacc4\uc801\uc73c\ub85c \ud45c\ud604\ud558\uace0 \ubb38\uc11c\ud654\ud558\ub294 \uae30\ubc95\\n- \uc815\ubcf4 \uc2dc\uc2a4\ud15c\uc758 \uc911\uc2ec\uc744 \ub370\uc774\ud130\uc758 \uad00\uc810\uc5d0\uc11c \uc811\uadfc\ud558\ub294 \ub370\uc774\ud130 \uc911\uc2ec\uc758 \ubd84\uc11d\uae30\ubc95\\n\\n---\\n\\n## \ub370\uc774\ud130 \ubaa8\ub378\ub9c1\uc758 \uc8fc\uc694 \ud2b9\uc131\\n\\n- \uac00\uba85\ubb38\ucd94\ud1b5\uc774\\n\\n|  \uad6c\ubd84  | \ub0b4\uc6a9                                                         |\\n| :----: | :----------------------------------------------------------- |\\n| \uac00\uc2dc\ud654 | \uc2dc\uc2a4\ud15c\uc744 \ud604\uc7ac \ub610\ub294 TO-BE \ubaa8\uc2b5\uc73c\ub85c \uc2dc\uac01\uc801\uc73c\ub85c \ud45c\ud604            |\\n| \uba85\uc138\ud654 | \uc2dc\uc2a4\ud15c\uc758 \uad6c\uc870\ud654 \ud589\ub3d9\uc744 \uba85\uc138\ud654 \ud560\uc218 \uc788\uc74c                      |\\n| \ubb38\uc11c\ud654 | \uc815\ubcf4 \uc2dc\uc2a4\ud15c \uc694\uad6c \uc0ac\ud56d\uc5d0 \ud544\uc694\ud55c \ub370\uc774\ud130\ub97c \ubb38\uc11c\ud654 \ud568            |\\n| \ucd94\uc0c1\ud654 | \ubcf5\uc7a1\ud55c \ud604\uc2e4 \uc138\uacc4\ub97c \ub2e8\uc21c\ud654 \ud558\uc5ec \uc774\ud574\uac00 \uac00\ub2a5\ud558\ub3c4\ub85d \ud568          |\\n| \ud1b5\ud569\uc131 | \uc5b4\ud50c\ub9ac\ucf00\uc774\uc158\uacfc \ub370\uc774\ud130\uc758 \ud1b5\ud569\uc774 \uac00\ub2a5\ud568                        |\\n| \uc774\ud574\uc131 | \uac1c\ubc1c\uc790\ub4e4\uc758 \uc2dc\uc2a4\ud15c\uc5d0 \ub300\ud55c \uc774\ud574\ub97c \ub192\uc774\uba70 \uc758\uc0ac\uc18c\ud1b5\uc744 \uc6d0\ud65c\ud788 \ud568. |\\n\\n---\\n\\n## \ub370\uc774\ud130 \ubaa8\ub378\ub9c1\uc758 \ub2e8\uacc4\\n\\n<table style={{ border: \\"0px\\", width: \\"100%\\", backgroundColor: \\"#fff\\" }}>\\n  <colgroup>\\n    <col width=\\"10%\\" />\\n    <col width=\\"90%\\" />\\n  </colgroup>\\n  <thead>\\n  <tr>\\n    <th>\ubaa8\ub378\ub9c1</th>\\n    <th>\uc124\uba85</th>\\n  </tr>\\n  \\n  </thead>\\n  <tbody>\\n  \\n  <tr>\\n    <td>\\n        <b>\uc694\uac1c\ub17c\uc2a4\ubb3c\uc751\ubcf4</b>\\n      <Drawio content={simpleGraph1} resize={true} />\\n    </td>\\n    <td>\\n      <table border=\\"0\\">\\n      <thead>\\n        <tr>\\n          <th>\ub2e8\uacc4</th>\\n          <th>\uc124\uba85</th>\\n        </tr>\\n      </thead>\\n        <tbody>\\n        <tr>\\n          <td>\\n            <b style={{ color: \\"blue\\" }}>\uac1c\ub150\uc801 \uc124\uacc4</b>\\n          </td>\\n          <td>\\n            - \ub370\uc774\ud130\ubca0\uc774\uc2a4\uc5d0 \uc800\uc7a5\ub420 \ub370\uc774\ud130\uc640 \uc774 \ub370\uc774\ud130\uac00 \uc900\uc218\ud574\uc57c \ud558\ub294\\n            \uc81c\uc57d\uc870\uac74\ub4e4\uc744 \uace0 \uc218\uc900\uc73c\ub85c \uae30\uc220\ud558\uae30 \uc704\ud574 \uc0ac\uc6a9 <br /> - \uac1c\ub150\uc801 \uc2a4\ud0a4\ub9c8\ub780\\n            DBMS \ud074\ub798\uc2a4\ub098 \ud2b9\uc815 DBMS\uc640 \ub3c5\ub9bd\uc801\uc778, \ub370\uc774\ud130\ubca0\uc774\uc2a4 \uad6c\uc870\uc5d0 \ub300\ud55c \uc0c1\uc758\\n            \uc218\uc900\uc5d0\uc11c\uc758 \uae30\uc220\uc744 \uc758\ubbf8\ud568\\n            <br /> - \uac1c\ub150\uc801 \ubaa8\ub378\uc774\ub780 \uac1c\ub150\uc801\uc73c\ub85c \uc2a4\ud0a4\ub9c8\ub97c \uae30\uc220\ud558\ub294\ub370 \uc0ac\uc6a9\ud558\ub294 \uc5b8\uc5b4\\n            <br /> - \uc774 \ub2e8\uacc4\ub294{\\" \\"}\\n            <b style={{ color: \\"blue\\" }}>\uc8fc\ub85c ER \ubaa8\ub378\uc744 \uc774\uc6a9\ud558\uc5ec \uc218\ud589\ub428</b>\\n          </td>\\n        </tr>\\n        <tr>\\n          <td>\\n            <b style={{ color: \\"blue\\" }}>\ub17c\ub9ac\uc801 \uc124\uacc4</b>\\n          </td>\\n          <td>\\n            - \ub370\uc774\ud130\ubca0\uc774\uc2a4 \uc124\uacc4\ub97c \uad6c\ud604\ud558\uae30 \uc704\ud574 \uc120\uc815\ub41c DBMS \ud074\ub798\uc2a4\uc758 \ub370\uc774\ud130\\n            \ubaa8\ub378\uc5d0 \ub5a0\u314f\ub978 \ub17c\ub9ac\uc801 \uc2a4\ud0a4\ub9c8 \uc0dd\uc131. \uc608\ub85c RDBMS\ub294{\\" \\"}\\n            <b style={{ color: \\"blue\\" }}>\\n              ER \uac1c\ub150 \uc2a4\ud0a4\ub9c8\ub97c \uad00\uacc4 \ub370\uc774\ud130\ubca0\uc774\uc2a4 \uc2a4\ud0a4\ub9c8\ub85c \ubcc0\ud658\\n            </b>\\n            <br /> - \ud2b9\uc815 DBMS\uc5d0 \uc885\uc18d\ub418\uc9c0 \uc54a\uc74c(\uc608:RDBMS\ub294 \ubaa8\ub450 \uad00\uacc4\ud615 \ubaa8\ub378 \uc0ac\uc6a9\ud568)\\n            <br /> - \ub17c\ub9ac\uc801 \ubaa8\ub378\uc740 \ub17c\ub9ac \uc2a4\ud0a4\ub9c8\ub97c \uae30\uc220\ud558\uae30 \uc704\ud574 \uc0ac\uc6a9\ub418\ub294 \uc5b8\uc5b4\\n          </td>\\n        </tr>\\n        <tr>\\n          <td>\\n            <b style={{ color: \\"blue\\" }}>\uc2a4\ud0a4\ub9c8 \uc815\uc81c</b>\\n          </td>\\n          <td>\\n            \uad00\uacc4 \ub370\uc774\ud130\ubca0\uc774\uc2a4 \uc2a4\ud0a4\ub9c8\uc5d0 \uc788\ub294 \ub9b4\ub808\uc774\uc158\uc744 \ubd84\uc11d\ud558\uc5ec \uc7a0\uc7ac\uc801\uc778\\n            \ubb38\uc81c\uc810\uc744 \ud30c\uc54c\ud558\uace0 \uc815\uc81c(\\n            <b style={{ color: \\"blue\\" }}>\uc815\uaddc\ud654 \uc774\ub860 \uc801\uc6a9</b>)\\n          </td>\\n        </tr>\\n        <tr>\\n          <td>\ubb3c\ub9ac\uc801 \uc124\uacc4</td>\\n          <td>\\n            - \ub370\uc774\ud130\ub97c \ud6a8\uacfc\uc801\uc73c\ub85c \uc811\uadfc\ud558\uae30 \uc704\ud574 \uc0ac\uc6a9\ud558\ub294{\\" \\"}\\n            <b style={{ color: \\"blue\\" }}>\\n              \uc800\uc7a5\uad6c\uc870\uc640 \uc811\uadfc\ubc29\ubc95\uc744 \uae30\uc220, \uc6d0\ud558\ub294 \uc131\ub2a5 \uae30\uc900\uc5d0 \ub9de\ub3c4\ub85d\\n            </b>{\\" \\"}\\n            \ub354 \uc815\uc81c\\n            <br /> - \uc989\uc815 DBMS \uc5d0 \uc885\uc18d\uc801\uc774\uba70, \ubcf4\ub2e4 \ud5a5\uc0c1\ub41c \ud6a8\uacfc\ub8f0 \uc704\ud574 \ubb3c\ub9ac\uc801 \uc124\uacc4\\n            \uc2dc \uc120\ud0dd\ud55c \uacb0\uc815\uc774 \ub17c\ub9ac\uc801 \uc2a4\ud0a4\ub9c8 \uad6c\uc870\uc5d0 \uc601\ud589\uc744 \ubbf8\uce60\uc218 \uc788\uc74c\\n          </td>\\n        </tr>\\n        <tr>\\n          <td>\uc751\uc6a9 \ubc0f \ubcf4\uc548\uc124\uacc4</td>\\n          <td>\\n            \uc751\uc6a9\uc5d0 \uad00\uacc4\ub418\ub294 \uac8c\uccb4\ub4e4(\uc0ac\uc6a9\uc790, \uc0ac\uc6a9\uc790 \uadf8\ub8f9\ub4f1)\uc758 \uc811\uadfc \uad8c\ud55c \uc815\uc758\\n          </td>\\n        </tr>\\n        </tbody>\\n      </table>\\n    </td>\\n\\n  </tr>\\n  </tbody>\\n</table>\\n\\n---\\n\\n## \uac1c\ub150\uc801 \ub370\uc774\ud130 \ubaa8\ub378\ub9c1\uc758 \uc815\uc758\\n\\n- \ud574\ub2f9 \uc870\uc9c1\uc758 \uc5c5\ubb34 \uc694\uac74\uc744 \ucda9\uc871\ud558\uae30 \uc704\ud574\uc11c \uc8fc\uc81c\uc601\uc5ed\uacfc \ud575\uc2ec \ub370\uc774\ud130 \uc9d1\ud569, \ud575\uc2ec \ub370\uc774\ud130 \uc9d1\ud569\uac04\uc758 \uad00\uacc4\ub97c \uc815\uc758\ud558\ub294 \uc0c1\uc704 \uc218\uc900\uc758 \uac1c\ub7b5\uc801 \ub370\uc774\ud130 \uc124\uacc4 \uc791\uc5c5\\n- \ud2b9\uc815 DBMS\ub098 \ud558\ub4dc\uc6e8\uc5b4 \uc99d\uc5d0 \ub3c5\ub9bd\uc801\uc778 \uac1c\ub150\uc801 \uc2a4\ud0a4\ub9c8\ub97c \uae30\uc220\ud558\ub294 \uacfc\uc815\\n\\n## \uac1c\ub150\uc801 \ub370\uc774\ud130 \ubaa8\ub378\ub9c1\uc758 \ud2b9\uc9d5\\n\\n- \uc0ac\uc6a9\uc790 \uc5c5\ubb34\uc801\uc778 \uc694\uac74 \uc911\uc2ec\\n- \uc0ac\uc6a9\uc790 \uc911\uc2ec\uc758 \uc6a9\uc5b4 \uc0ac\uc6a9\\n- \ud2b9\uc815 DBMS \ub098 \ud558\ub4dc\uc6e8\uc5b4 \ub4f1\uc5d0 \ub3c5\ub9bd\uc801\\n- ***\\n\\n  ER(Entity-Relationship) Diagram, EER(Extended-ER)\\n  Diagram \uc73c\ub85c \ud45c\ud604\ub428\\n\\n  ***\\n\\n- EER : \ucef4\ud4e8\ud130 \uacfc\ud559\uc5d0\uc11c \ud5a5\uc0c1\ub41c \uac1c\uccb4 \uad00\uacc4 ( EER ) \ubaa8\ub378(\ub610\ub294 \ud655\uc7a5\ub41c \uac1c\uccb4 \uad00\uacc4 \ubaa8\ub378)\uc740 \ub370\uc774\ud130\ubca0\uc774\uc2a4 \uc124\uacc4\uc5d0 \uc0ac\uc6a9\ub418\ub294 \uc6d0\ub798 \uac1c\uccb4 \uad00\uacc4 (ER) \ubaa8\ub378\uc5d0 \ub300\ud55c \ud655\uc7a5\uc744 \ud1b5\ud569\ud558\ub294 \uc0c1\uc704 \uc218\uc900 \ub610\ub294 \uac1c\ub150\uc801 \ub370\uc774\ud130 \ubaa8\ub378\uc785\ub2c8\ub2e4 . <br/>\\n  \uc5d4\uc9c0\ub2c8\uc5b4\ub9c1 \uc124\uacc4 \ubc0f \uc81c\uc870( CAD / CAM ), \ud1b5\uc2e0 , \ubcf5\uc7a1\ud55c \uc18c\ud504\ud2b8\uc6e8\uc5b4 \uc2dc\uc2a4\ud15c \ubc0f \uc9c0\ub9ac \uc815\ubcf4 \uc2dc\uc2a4\ud15c (GIS) \uacfc \uac19\uc740 \ubcf4\ub2e4 \ubcf5\uc7a1\ud55c \ub370\uc774\ud130\ubca0\uc774\uc2a4\uc5d0\uc11c \ubc1c\uacac\ub418\ub294 \uc18d\uc131 \ubc0f \uc81c\uc57d \uc870\uac74\uc744 \ubcf4\ub2e4 \uc815\ud655\ud558\uac8c \ubc18\uc601\ud558\ub3c4\ub85d \uac1c\ubc1c\ub418\uc5c8\uc2b5\ub2c8\ub2e4 .\\n\\n---\\n\\n## \uac1c\ub150\uc801 \ub370\uc774\ud130 \ubaa8\ub378\ub9c1 \uc808\ucc28\\n\\n![](./image.jpg)\\n\\n---\\n\\n## \uac1c\ub150\uc801 \ubaa8\ub378\ub9c1 \uc694\uc18c\\n\\n<Drawio content={simpleGraph2} resize={true} />"},{"id":"/2023/02/16/DB\uac1c\uc6947","metadata":{"permalink":"/blog/second/2023/02/16/DB\uac1c\uc6947","source":"@site/second-blog/2023-02-16-DB\uac1c\uc6947/index.mdx","title":"DataBase \uac1c\uc694 7","description":"E-R(Entity-Relation) \ubaa8\ub378\uc758 \uc815\uc758","date":"2023-02-16T11:42:00.000Z","formattedDate":"2023\ub144 2\uc6d4 16\uc77c","tags":[{"label":"database","permalink":"/blog/second/tags/database"},{"label":"\uc815\ubcf4\uad00\ub9ac","permalink":"/blog/second/tags/\uc815\ubcf4\uad00\ub9ac"}],"readingTime":11.665,"hasTruncateMarker":false,"authors":[{"name":"100milliongold","title":"developer","url":"https://sebastienlorber.com","imageURL":"https://avatars.githubusercontent.com/u/13652711?s=400&u=0a1092b8b1170f71ae883d098f8721c12ce39e9e&v=4","key":"100milliongold"}],"frontMatter":{"title":"DataBase \uac1c\uc694 7","date":"2023-02-16T11:42:00.000Z","authors":["100milliongold"],"tags":["database","\uc815\ubcf4\uad00\ub9ac"],"password":"1q2w3e4r"},"unlisted":false,"prevItem":{"title":"DataBase \uac1c\uc694 6","permalink":"/blog/second/2023/02/16/DB\uac1c\uc6946"},"nextItem":{"title":"DataBase \uac1c\uc694 5","permalink":"/blog/second/2023/02/16/DB\uac1c\uc6945"}},"content":"import Drawio from \\"@theme/Drawio\\";\\nimport simpleGraph1 from \\"!!raw-loader!./chart1.drawio\\";\\nimport simpleGraph2 from \\"!!raw-loader!./chart2.drawio\\";\\nimport simpleGraph3 from \\"!!raw-loader!./chart3.drawio\\";\\nimport simpleGraph4 from \\"!!raw-loader!./chart4.drawio\\";\\n\\n## E-R(Entity-Relation) \ubaa8\ub378\uc758 \uc815\uc758\\n\\n- \ub370\uc774\ud130 \ubca0\uc774\uc2a4\uc758 \uac1c\ub150\uc801 \uc124\uacc4\ub97c \uc704\ud574 \uac00\uc7a5 \ub110\ub9ac \uc0ac\uc6a9\ub418\uba70, \uc2e4 \uc138\uacc4\uc758 \ub370\uc774\ud130\ub97c \uac8c\uccb4\ub4e4\uacfc \uadf8\ub4e4\uac04\uc758 \uad00\uacc4\uc5d0 \uc758\ud558\uc5ec \ubb18\uc0ac\ud558\ub294 \uae30\ubc95\\n- 1976\ub144 Peter Chan \uc5d0 \uc758\ud574 \uc18c\uac1c\ub41c \ud6c4\ub85c \ub354\uc6b1 \uc77c\ubc18\ud654 \ub418\uc5c8\uc74c\\n- \uac8c\uccb4(entity), \uad00\uacc4(relationship), \uc18d\uc131(attributes)\uc758 \uac1c\ub150\uc744 \ub3c4\uc785\ud558\uc5ec \ud604\uc2e4\uc138\uacc4\ub97c \uac1c\ub150\uc801\uc73c\ub85c \ud45c\ud604\ud558\ub294 \ubaa8\ub378\\n\\n## E-R \ubaa8\ub378\uc758 \ud2b9\uc9d5\\n\\n- \ud2b9\uc815 DB \uad6c\uc870 \ub3c5\ub9bd\uc801\\n- \ubd84\uc11d\uac00, \uac1c\ubc1c\uc790, \uc0ac\uc6a9\uc790\uc5d0\uac8c \uacf5\ud1b5\ub41c \uc2dc\uac01 \uc81c\uc2dc\\n- \uc758\uc0ac\uc18c\ud1b5 \ub3c4\uad6c \ud65c\uc6a9\\n\\n---\\n\\n## E-R \ubaa8\ub378\uc758 \uad6c\uc131\uc694\uc18c\\n\\n<table>\\n  <thead>\\n    <tr>\\n      <th>\uad6c\uc131\uc694\uc18c</th>\\n      <th>\ud45c\ud604</th>\\n      <th>\uc124\uba85</th>\\n    </tr>\\n  </thead>\\n  <tbody>\\n    <tr>\\n      <th>\uac8c\uccb4</th>\\n      <td></td>\\n      <td>\ud604\uc2e4 \uc138\uacc4 \ud074\ub798\uc2a4 \ud45c\ud604, \uc2e4 \uc138\uacc4 \uc874\uc7ac \uc2e4\uccb4</td>\\n    </tr>\\n    <tr>\\n      <th>\uad00\uacc4</th>\\n      <td></td>\\n      <td>\uac8c\uccb4\uc640 \uac8c\uccb4\uac04 \uc5f0\uad00\uc131</td>\\n    </tr>\\n    <tr>\\n      <th>\uc18d\uc131</th>\\n      <td></td>\\n      <td>\uac8c\uccb4 \ub610\ub294 \uad00\uacc4\uc758 \uae30\ubcf8\uc801 \uc131\uc9c8</td>\\n    </tr>\\n    <tr>\\n      <th>\ub9c1\ud06c</th>\\n      <td></td>\\n      <td>\uac8c\uccb4\ud0c0\uc785\uacfc \uc18d\uc131\uc744 \uc5f0\uacb0</td>\\n    </tr>\\n    <tr>\\n      <th>\ub808\uc774\ube14</th>\\n      <td></td>\\n      <td>\uad00\uac8c\uc640 \uc0ac\uc0c1, \uc6d0\uc18c \uc218\ub97c \ud45c\ud604 \uc77c\ub300\uc77c, \uc77c\ub300\ub2e4, \ub2e4\ub300\ub2e4</td>\\n    </tr>\\n  </tbody>\\n</table>\\n\\n![](./img1.png)\\n![](./img2.png)\\n\\n---\\n\\n## E-R \ubaa8\ub378\uc758 \ud45c\uae30\ubc95\\n\\n- IE(Information Engineering): ERWin\\n\\n<Drawio content={simpleGraph1} resize={true} />\\n\\n- Rechard Baker(Case Method) : DA#, Oracle Designer\\n\\n\ucd9c\ucc98 : https://cafe.naver.com/dbian\\n\\n![](./img3.png)\\n\\n![](./img4.png)\\n\\n---\\n\\n## E-R \ubaa8\ub378\ub9c1 \ub2e8\uacc4\\n\\n<Drawio content={simpleGraph2} resize={true} />\\n\\n## E-R \ubaa8\ub378\uc758 \ud55c\uacc4\uc131\\n\\n- \ub2e8\uc21c\ud558\uace0 \uc77c\ubc18\uc801 \ubaa8\ub378\uc5d0 \uc801\ud569\\n- GIS, Mutimedia, \uac8d\uccb4\uc9c0\ud5a5 \uac19\uc740 \uc751\uc6a9 \ubd84\uc57c\uc5d0\uc11c\ub294 \ud45c\ud604\uc5d0 \ud55c\uacc4\uac00 \uc874\uc7ac\\n- \uc7ac\uc0ac\uc6a9, \uc0c1\uc18d , \ud655\uc7a5\uc131\uc758 \uac1c\ub150\uc774 \uc5c6\uc74c\\n\\n## E-R \ubaa8\ub378\uc758 \ud55c\uacc4 \uadf9\ubcf5\uacfc \ubc1c\uc804\\n\\n- \ud55c\uacc4\uc810 \uadf9\ubcf5\uc744 \uc704\ud574 EER \uc0ac\uc6a9\\n  - Super\uc640 Sub type\uc758 \uad00\uacc4\ub97c \ud1b5\ud55c \uc77c\ubc18\ud654, \uc9d1\ub2e8\ud654, \ud2b9\uc218\ud654, \uc0c1\uc18d\uc758 \uac1c\ub150 \ub4f1\uc7a5\\n- \uac8d\uccb4\uc9c0\ud5a5 \ubaa8\ub378\ub9c1 \uae30\ubc95 \ub3c4\uc785 \ubc0f ERD \uac1c\ub150\uc758 \ud655\uc7a5 \ud65c\uc6a9\uc774 \ud544\uc694\\n- \uba40\ud2f0\ubbf8\ub514\uc5b4 DB, OODB \ub4f1\uc740 \ud544\uc218 \ubd88\uac00\uacb0\ud55c DB \ucd94\uc138\\n- OO(Object Oriented) Data \ubaa8\ub378\uc758 \ubcf8\uaca9\ud654 \uc608\uc0c1\\n- RDB \uc5d0\uc11c \ucc98\ub9ac\ud558\uae30 \ud798\ub4e0 CAD/CAM, \uba40\ud2f0\ubbf8\ub514\uc5b4 \ubd84\uc57c\ub97c \ubc1c\ud310\uc73c\ub85c \uc131\uc7a5 \uc608\uc0c1\\n\\n## EER(Enhanced ER) \uc815\uc758\\n\\n- \ubcf5\uc7a1\ud55c \ud604\uc2e4 \uc138\uacc4\ub97c \ud45c\ud604\ud558\ub294\ub370 \uc788\uc5b4\uc11c E-R \ubaa8\ub378\uc758 \ud55c\uacc4\ub97c \uadf9\ubcf5\ud558\uae30 \uc704\ud574 \ud2b9\uc218\ud654, \uc77c\ubc18\ud654, \uc9d1\ub2e8\ud654 \ub4f1\uc758 \uac1c\ub150\uc744 \ucd94\uac00\ud55c \ubaa8\ub378\\n\\n- EER \uc758 \ud575\uc2ec \uac1c\ub150\\n\\n|          \uad6c\uc131\uc694\uc18c           | \uc8fc\uc694 \ub0b4\uc6a9                                                                                                                                                       |\\n| :-------------------------: | --------------------------------------------------------------------------------------------------------------------------------------------------------------- |\\n| \ud2b9\uc218\ud654<br/>(Specialization) | - \ud558\ub098\uc758 \uac8c\uccb4(Super type)\ub97c \uba87 \uac1c\uc758 Sub type \uc73c\ub85c \ubd84\ub9ac<br/> - \uc0c1\uc704\uc5d0\uc11c \ud558\uc704 \ud074\ub798\uc2a4\ub97c \ubd84\ub958\ud558\ub294 \ud558\ud5a5\uc2dd(Top-down) \uac1c\ub150<br/> - \uc0c1\uc704 \uac1c\uccb4\uc758 \uc18d\uc131\uc744 \uc0c1\uc18d\ud568(IS-A \uad00\uacc4) |\\n| \uc77c\ubc18\ud654<br/>(Generalization) | - \uba87 \uac1c\uc758 \uac1c\uccb4\uc9d1\ud569\uc744 \ud569\ud574\uc11c \uc0c1\uc704\uc758 \ud55c\uac1c\uccb4\ub85c \ud1b5\ud569<br/> - \ud558\uc704\uc5d0\uc11c \uc0c1\uc704 \ud074\ub798\uc2a4\ub85c \uc77c\ubc18\ud654\ub77c\ub294 \uc0c1\ud5a5\uc2dd(Bottom-up) \uac1c\ub150<br/> - \ud2b9\uc218\ud654\uc758 \uc5ed\ubc29\ud5a5(IS-A \uad00\uacc4)              |\\n|  \uc9d1\ub2e8\ud654<br/>(Aggregation)   | - \ud2b9\uc218\ud654(Specialzation)\uc2dc \uc0c1\uc704 \uac1c\uccb4\uc758 \uac1c\ub150\uc744 \ud558\uc704 \uac1c\uccb4\uac00 \uc0c1\uc18d<br/> - \ub2e8\uc77c\uc0c1\uc18d/\ub2e4\uc911\uc0c1\uc18d                                                                          |\\n|    \uc0c1\uc18d<br/>(Inhertance)    | - \ub2e8\uc704 \uac1c\uccb4\ub4e4\uc744 \ud558\ub098\ub85c \ubb36\uc5b4 \uc0c1\uc704 \ub808\ubca8\uc758 \ubcf5\ud569 \uac1c\uccb4\ub97c \uad6c\uc131<br/> - \uc9d1\ub2e8\ud654 \uac1c\ub150\uc744 \uc774\uc6a9\ud560 \uacbd\uc6b0, \uc911\ubcf5\ub418\ub294 \uad00\uacc4\ub97c \ub2e8\uc21c\ud55c \uad00\uacc4\ub85c \ud45c\ud604\uac00\ub2a5(IS-PART-OF \uad00\uacc4)              |\\n\\n## EER \ubaa8\ub378\ub9c1 \uc0ac\ub840\\n\\n<Drawio content={simpleGraph3} resize={true} />\\n\\n## \ub17c\ub9ac\uc801 \ubaa8\ub378\ub9c1\uc758 \uc815\uc758\\n\\n- \uac1c\ub150\uc801 \ubaa8\ub378\ub9c1 \ub2e8\uacc4\uc5d0\uc11c \uc815\uc758\ub41c \uac1c\uccb4, \uc18d\uc131, \uad00\uacc4\ub97c \ubaa9\ud45c DBMS \uc5d0 \ub9de\uac8c \ub17c\ub9ac\uc801 \ub370\uc774\ud130 \uad6c\uc870\ub85c \ubcc0\ud658\ud558\ub294 \uac83\\n\\n## \ub17c\ub9ac\uc801 \ubaa8\ub378\ub9c1\uc758 \ubaa9\uc801\\n\\n- \ub17c\ub9ac\uc801 \ubaa8\ub378\uc5d0\uc11c \uc0ac\uc6a9\uac00\ub2a5\ud55c \ub370\uc774\ud130\uc758 \uad6c\uc870\uc640\uc758 \uc81c\uc57d\uc0ac\ud56d \ubaa8\ub378\ud654\ub97c \uc704\ud55c \uae30\ub2a5\ub4e4\uc744 \ud6a8\uacfc\uc801\uc73c\ub85c \ud45c\ud604\\n- \ud574\ub2f9 \ube44\uc9c0\ub2c8\uc2a4\uc5d0 \ub300\ud55c \ub370\uc774\ud130 \uad00\uc810\uc5d0\uc11c\uc758 \uba85\ud655\uc548 \uc774\ud574\ud558\uace0 \uc0ac\uc6a9\uc790\uc640\uc758 \uba85\ud655\ud55c \uc758\uc0ac\uc18c\ud1b5\uc744 \uc704\ud55c \uc218\ub2e8\uc73c\ub85c \ud65c\uc6a9\\n- \uc804\uc0ac \ub370\uc774\ud130\uc5d0 \ub300\ud55c \uad6c\uc870\ub97c \uccb4\uacc4\ud654\ud558\uace0 \uc774\ub97c \ud1b5\ud574 \ud1b5\ud569 \ub370\uc774\ud130 Architecture \uc218\ub9bd\\n- \ub370\uc774\ud130\uc758 \uc77c\uad00\uc131 \ubc0f \uc815\ud655\uc131 \uc720\uc9c0\ub97c \uc704\ud55c \uaddc\uce59 \ub3c4\ucd9c\\n\\n## \ub17c\ub9ac\uc801 \ubaa8\ub378\ub9c1 \uc131\uacf5 \uc694\uac74\\n\\n- \uc5c5\ubb34\uc5d0 \ub2a5\ud1b5\ud55c \ud604\uc5c5 \uc0c1\uc694\uc790\uc640 \ud568\uaf10 \ub370\uc774\ud130 \ubaa8\ub378\ub9c1 \uc9c4\ud589\\n- \ub370\uc774\ud130\uc5d0 \ucd08\uc810\uc744 \ub450\uace0 \ubaa8\ub378\ub9c1\uc744 \uc9c4\ud589\\n- \ub370\uc774\ud130\uc758 \uad6c\uc870\uc640 \ubb34\uacb0\uc131(Intergrity)\uc744 \ud544\uc218 \uace0\ub824 \ud544\uc694\\n- \uc815\uaddc\ud654(Nomalization) \uae30\ubc95\uc744 \uc774\uc6a9\ud55c \uc774\uc0c1\ud604\uc0c1 \ubc29\uc9c0 \ud544\uc694\\n- \ub370\uc774\ud130 \ubaa8\ub378\ub9c1\uc744 \uc9c0\uc6d0\ud558\ub294 \ub370\uc774\ud130 \uc0ac\uc804\uc758 \uad6c\ucd95\\n\\n## \ub17c\ub9ac\uc801 \ubaa8\ub378\ub9c1 \uc8fc\uc694 \ud65c\ub3d9\\n\\n<table>\\n  <thead>\\n    <tr>\\n      <th colSpan={2}>\uc8fc\uc694 \ud65c\ub3d9</th>\\n      <th>\ub0b4\uc6a9</th>\\n    </tr>\\n  </thead>\\n  <tbody>\\n    <tr>\\n      <td colSpan={2}>\uc18d\uc131 \uc0c1\uc138\ud654</td>\\n      <td>\\n        <ul>\\n          <li>\uac1c\ub150 \ub370\uc774\ud130 \ubaa8\ub378\ub9c1\uc5d0\uc11c \ucd94\ucd9c\ub41c Entity \uc18d\uc131\uc758 \uac80\uc99d \ubc0f \ud655\uc815</li>\\n          <li>\uc18d\uc131\uc774 \uac00\uc9c8\uc218 \uc788\ub294 \ub3d9\uc548 \uac12\uc5d0 \ub300\ud55c \ub098\uc5f4</li>\\n        </ul>\\n      </td>\\n    </tr>\\n    <tr>\\n      <td rowSpan={4}>\uac1c\uccb4 \uc0c1\uc138\ud654</td>\\n      <td>\uc2dd\ubcc4\uc790 \ud655\uc815</td>\\n      <td>\\n        <ul>\\n          <li>\\n            Entity\uc5d0 \ub300\ud574 \uc720\uc77c\uc131\uc744 \uc2dd\ubcc4\ud560\uc218 \uc788\ub294 \uc8fc \uc2dd\ubcc4 \ud655\uc7a5(Primary Key)\\n          </li>\\n          <li>\\n            \ubd80 \uc2dd\ubcc4(Alternate identifer), \uc678\ubd80 \uc2dd\ubcc4(Foreign identifer) \ub4f1 \ud655\uc7a5\\n          </li>\\n        </ul>\\n      </td>\\n    </tr>\\n    <tr>\\n      <td>\uc815\uaddc\ud654</td>\\n      <td>\\n        \ub370\uc774\ud130 \uad6c\uc870\uc0c1\uc758 \uc788\uc5b4 \uc0bd\uc785, \uc0ad\uc81c, \uac31\uc2e0\uc758 \uc774\uc0c1\ud604\uc0c1 \ubb38\uc81c\uc810 \ud574\uacb0\uc744 \uc704\ud574\\n        \uc815\uaddc\ud654\ub97c \uc218\ud589(1~5\ucc28 \uc815\uaddc\ud654 BCNF)\\n      </td>\\n    </tr>\\n    <tr>\\n      <td>M:M \uad00\uacc4</td>\\n      <td>M:M \uad00\uacc4\uc778 Entity \uc758 \uad00\uacc4\ub97c 1:M \uad00\uacc4\ub85c \ud574\uc18c</td>\\n    </tr>\\n    <tr>\\n      <td>\uac8c\uccb4 \ubb34\uacb0\uc131 \uaddc\uce59 \uc815\uc758</td>\\n      <td>\\n        <ul>\\n          <li>\uac1c\uccb4 \ubb34\uacb0\uc131: \uc5b4\ub5a4 \uae30\ubcf8\ud0a4 \uac12\uc774 NULL \uac12\uc774 \ub420\uc218 \uc5c6\uc74c</li>\\n          <li>\\n            \ucc38\uc870 \ubb34\uacb0\uc131: \uad00\uacc4 \ud14c\uc774\ube14\uc758 \uc678\ubd80 \uc2dd\ubcc4\uc790(FK)\ub294 \uad00\uacc4\ud14c\uc774\ube14\uc758 \uc8fc \uc2dd\ubcc4\uc790\\n            \uc774\uc5b4\uc57c \ud568\\n          </li>\\n        </ul>\\n      </td>\\n    </tr>\\n  </tbody>\\n</table>\\n\\n## \ub17c\ub9ac\uc801 \ubaa8\ub378\ub9c1\uc758 \uc804\ud658\uacfc\uc815\\n\\n|         \ub2e8\uacc4         | \ub0b4\uc6a9                                                                                                                    |\\n| :------------------: | :---------------------------------------------------------------------------------------------------------------------- |\\n| Entity(\ud14c\uc774\ube14) \uc804\ud658  | \uac1c\ub150 \ubaa8\ub378\ub9c1\uc5d0\uc11c Entity\ub97c \uc720\uc77c\uc131\uc73c\ub85c \ubcf4\uc7a5\ud558\ub294 \uae30\ubcf8 \ud0a4\ub97c \uc9c0\uc815\ud558\uc5ec \uc2e4\uc81c \ub370\uc774\ud130\uac00 \uc800\uc7a5\ub420 \ub17c\ub9ac\uc801 Relation \uc73c\ub85c \uc804\ud658          |\\n|    Relation(\uad00\uacc4)    | Entity \uac04\uc5d0 \uc5f0\uacb0\uace0\ub9ac\ub97c 1:1 \ub610\ub294 1:M \ud615\ud0dc\ub85c \uc804\ud658                                                                         |\\n|     \uc815\uaddc\ud654 \uc218\ud589      | \ub370\uc774\ud130 \uc911\ubcf5 \uc800\uc7a5, \uc774\uc0c1\ud604\uc0c1 \ubc29\uc9c0\ub97c \uc704\ud574 1NF ~ BCNF \uc815\uaddc\ud615\uae4c\uc9c0 \ubaa8\ub450 \ub9cc\uc871\ud558\ub294\uc9c0\ub97c \uac80\uc0ac\ud558\uace0, \uc624\ub958 \ubc1c\uc0dd\uc2dc \uc774\uc804 \ub2e8\uacc4\ub97c \uc7ac\uac80\ud1a0 |\\n| \uc0ac\uc6a9\uc790 \ud2b8\ub79c\uc7ad\uc158 \uac80\uc99d | \ub3c4\ucd9c\ub41c \ub17c\ub9ac\uc801 \ub370\uc774\ud130 \ubaa8\ub378\uc774 \uc0ac\uc6a9\uc790\uac00 \uc6d0\ud558\ub294 \ud2b8\ub79c\uc7ad\uc158\uc744 \ubaa8\ub450 \ub9cc\uc871\uc2dc\ud0a4\ub294\uc9c0\ub97c \ud655\uc778\ud568                                       |\\n|       ERD \uac80\uc99d       | \uc218\uc815 \ubc0f \ubcf4\uc644 \ud574\uc57c \ud560 \uc0ac\ud56d\uc5d0 \ub300\ud558\uc5ec ERD \uc5d0 \uc7ac\ubc18\uc601                                                                        |\\n|   \ubb34\uacb0\uc131 \uc81c\uc57d \uc815\uc758   | \uad00\uacc4\ud615 DB \uc5d0\uc11c \ubb34\uacb0\uc131\uc744 \ub9cc\uc871\ud558\uae30 \uc704\ud55c \uc81c\uc57d \uc870\uac74\uc744 \uc124\uc815                                                                  |\\n\\n## E-R \ubaa8\ub378\uc758 \uad00\uacc4\ud615 \ubaa8\ub378\ub85c\uc758 \ubcc0\ud658(\uc0ac\ub840)\\n\\n<Drawio content={simpleGraph4} resize={true} />\\n\\n## \uad00\uacc4\ud615 \ub370\uc774\ud130 \ubaa8\ub378\\n\\n- \ubaa8\ub378\uc758 \uae30\ubcf8\uad6c\uc131\uc694\uc18c\uac00 \ub9b4\ub808\uc774\uc158(relation) \uc778 \ubaa8\ub378\ub85c 1970\ub144 IBM\uc758 Codd \uc5d0 \uc758\ud574 \ucc98\uc74c\uc81c\uc548\ub428\\n- \ub9b4\ub808\uc774\uc158\uc758 \uc605\uc740 \uc18d\uc131\ub4e4\uc744 \ub098\ud0c0\ub0b4\uba70 \ud29c\ud50c(tuple)\uc774\ub77c \ubd88\ub9ac\ub294 \ub9b4\ub808\uc774\uc158\uc758 \ud589\ub4e4\uc744 \uac01 \uc18d\uc131\uc774 \uac00\uc9c0\ub294 \uac12\ub4e4\uc758 \ubaa8\uc784\uc784\\n- \ub370\uc774\ud130 \uc911\ubcf5\uc758 \ucd5c\uc18c\ud654\uc640 \ubb34\uacb0\uc131(Data Intergrity) \ubcf4\uc7a5\\n\\n![](./relation-element.png)\\n\\n## \uc18d\uc131\uacfc \ub3c4\uba54\uc778\\n\\n|         \uad6c\ubd84          | \ub0b4\uc6a9                                                                                                                       | \uc608                                                           |\\n| :-------------------: | :------------------------------------------------------------------------------------------------------------------------- | :----------------------------------------------------------- |\\n| \uc18d\uc131<br/>(atturibute) | - \ub370\uc774\ud130\uc758 \uac00\uc7a5 \uc791\uc740 \ub17c\ub9ac\uc801 \ub2e8\uc704 <br/> - \ub354 \ubd84\ud574\ub418\uc9c0 \uc54a\ub294 \uc6d0\uc790 \uac12 (Atomic Value) \ub9cc \ud5c8\uc6a9                                   | - \ud559\ubc88 (SNO)<br/> - \uc774\ub984(SNAME)                              |\\n|  \ub3c4\uba54\uc778<br/>(Domain)  | - \uc18d\uc131\uc774 \ucde8\ud55c\uc218 \uc788\ub294 \uac19\uc740 \ud0c0\uc785\uc758 \ubaa8\ub4e0 \uc6d0\uc790\uac12\uc758 \uc9d1\ud569<br/> - \uc815\uc758\ub41c \uc18d\uc131\uc740 \ubc18\ub4dc\uc2dc \uadf8 \ud574\ub2f9 \ub3c4\uba54\uc778\uc73c\ub85c\ubd80\ud130\ub9cc \uac12\uc744 \ucde8 \ud560\uc218 \uc788\uc74c | - \ud559\ubc88(SNO): 3\uc790\ub9ac \uc218 \uc815\uc218<br/> - \uc774\ub984(SNAME): 10\uc790\ub9ac \ubb38\uc790\uc5f4 |\\n\\n## \ub9b4\ub808\uc774\uc158(Relation)\uc758 \uad6c\uc131\\n\\n|                   \uad6c\ubd84                    | \ub0b4\uc6a9                                                                                                | \ube44\uace0                                 |\\n| :---------------------------------------: | :-------------------------------------------------------------------------------------------------- | :----------------------------------- |\\n|    \ub9b4\ub808\uc774\uc158 \uc2a4\ud0b4<br/>(Relation Scheme)    | - \ub9b4\ub808\uc774\uc158\uc758 \ub17c\ub9ac\uc801 \uad6c\uc870                                                                            | - \ub9b4\ub808\uc774\uc158 \ub0b4\ud3ec(Relation intension)  |\\n| \ub9b4\ub808\uc774\uc158 \uc778\uc2a4\ud134\uc2a4<br/>(Relation Instance) | - \uc5b4\ub290 \ud55c \uc2dc\uc810\uc758 \ub9b4\ub808\uc774\uc158\uc758 \ub0b4\uc6a9(\uc0c1\ud0dc)<br/> - \uc5b4\ub290 \ud55c \uc2dc\uc810\uc5d0 \ub9b4\ub808\uc774\uc158 R\uc5d0 \ud3ec\ud568\ub418\uc5b4 \uc788\ub294 \ud22c\ud50c\uc758 \uc9d1\ud569 | - \ub9b4\ub808\uc774\uc158 \uc678\uba74 (Relation Entension) |\\n\\n## E-R \ubaa8\ub378\uc758 \uad00\uacc4\ud615 \ubaa8\ub378\ub85c\uc758 \ubcc0\ud658 \uaddc\uce59\\n\\n<table>\\n  <thead>\\n    <tr>\\n      <th colSpan=\\"2\\">E-R \ubaa8\ub378</th>\\n      <th>\uad00\uacc4\ud615 \ubaa8\ub378</th>\\n    </tr>\\n  </thead>\\n  <tbody>\\n    <tr>\\n      <td colSpan=\\"2\\">\uac1c\uccb4</td>\\n      <td>\ub9b4\ub808\uc774\uc158</td>\\n    </tr>\\n    <tr>\\n      <td rowSpan=\\"3\\">\uc18d\uc131</td>\\n      <td>\ub2e8\uc77c \uc18d\uc131</td>\\n      <td>\uc18d\uc131</td>\\n    </tr>\\n    <tr>\\n      <td>\ubcf5\ud569 \uc18d\uc131</td>\\n      <td>\\n        \ubcf5\ud569 \uc18d\uc131\uc758 \ubaa8\ub4e0 \uc131\ubd84\ub4e4\uc744 \uac1c \uac1c\uc758 \uc18d\uc131\uc73c\ub85c \uace0\ub824 \ub610\ub294 \uc804\ucc44 \ubcf5\ud569 \uc18d\uc131\uc744\\n        \ub2e8\uc77c \uc18d\uc131\uc73c\ub85c \uace0\ub824\ud558\ub294 \ubc29\ubc95\\n      </td>\\n    </tr>\\n    <tr>\\n      <td>\ud0a4 \uc18d\uc131</td>\\n      <td>\uae30\ubcf8\ud0a4 \ub610\ub294 \ubcf4\uc870\ud0a4</td>\\n    </tr>\\n    <tr>\\n      <td rowSpan=\\"5\\">\uad00\uacc4</td>\\n      <td>1:1 \uc591\ubc29\ud5a5 \uc644\uc804\uad00\uacc4</td>\\n      <td>\ub450 \ub9b4\ub808\uc774\uc158\uc744 \ud558\ub098\uc758 \ub9b4\ub808\uc774\uc158\uc73c\ub85c \ud1b5\ud569</td>\\n    </tr>\\n    <tr>\\n      <td>1:1 \ud55c\ubc29\ud5a5 \uc644\uc804\uad00\uacc4</td>\\n      <td>\ubd80\ubd84\uad00\uacc4 \ub9b4\ub808\uc774\uc158\uc758 PK\ub97c \uc644\uc804\uad00\uacc4 \ub9b4\ub808\uc774\uc158\uc758 FK\ub85c \uc801\uc6a9</td>\\n    </tr>\\n    <tr>\\n      <td>1:1 \uc591\ubc29\ud5a5 \ubd80\ubd84\uad00\uacc4</td>\\n      <td>\ub450 \ub9b4\ub808\uc774\uc158\uc758 PK\ub9cc\uc73c\ub85c \uc218\uc131\ub41c \ub9b4\ub808\uc774\uc158\uc744 \uc0dd\uc131</td>\\n    </tr>\\n    <tr>\\n      <td>1:N N\uce21 \uc644\uc804\uad00\uacc4</td>\\n      <td>1\uce21 \ub9b4\ub808\uc774\uc158\uc758 PK\ub97c N\uce21 \ub9b4\ub808\uc774\uc158\uc758 FK \ub85c \uc801\uc6a9</td>\\n    </tr>\\n    <tr>\\n      <td>M:N \uad00\uacc4</td>\\n      <td>\\n        \ub450 \ub9b4\ub808\uc774\uc158\uc758 PK\ub85c \uad6c\uc131\ub41c \ub9b4\ub808\uc774\uc158\uc744 \uc0dd\uc131, \uc0c8\ub85c\uc6b4 \ub9b4\ub808\uc774\uc158\uc758 PK\ub294 \ub450\\n        \ub9b4\ub808\uc774\uc158\uc758 PK\ub97c \uacb0\ud569\ud558\uc5ec \uc0ac\uc6a9\\n      </td>\\n    </tr>\\n  </tbody>\\n</table>"},{"id":"/2023/02/16/DB\uac1c\uc6945","metadata":{"permalink":"/blog/second/2023/02/16/DB\uac1c\uc6945","source":"@site/second-blog/2023-02-16-DB\uac1c\uc6945/index.mdx","title":"DataBase \uac1c\uc694 5","description":"\ud0a4 (Key)","date":"2023-02-16T01:04:00.000Z","formattedDate":"2023\ub144 2\uc6d4 16\uc77c","tags":[{"label":"database","permalink":"/blog/second/tags/database"},{"label":"\uc815\ubcf4\uad00\ub9ac","permalink":"/blog/second/tags/\uc815\ubcf4\uad00\ub9ac"},{"label":"DB-Key","permalink":"/blog/second/tags/db-key"}],"readingTime":4.23,"hasTruncateMarker":false,"authors":[{"name":"100milliongold","title":"developer","url":"https://sebastienlorber.com","imageURL":"https://avatars.githubusercontent.com/u/13652711?s=400&u=0a1092b8b1170f71ae883d098f8721c12ce39e9e&v=4","key":"100milliongold"}],"frontMatter":{"title":"DataBase \uac1c\uc694 5","date":"2023-02-16T01:04:00.000Z","authors":["100milliongold"],"tags":["database","\uc815\ubcf4\uad00\ub9ac","DB-Key"],"password":"1q2w3e4r"},"unlisted":false,"prevItem":{"title":"DataBase \uac1c\uc694 7","permalink":"/blog/second/2023/02/16/DB\uac1c\uc6947"},"nextItem":{"title":"DataBase \uac1c\uc694 4","permalink":"/blog/second/2023/02/15/DB\uac1c\uc6944"}},"content":"import Drawio from \\"@theme/Drawio\\";\\nimport simpleGraph from \\"!!raw-loader!./cart.drawio\\";\\n\\n## \ud0a4 (Key)\\n\\n- \ud0a4\uc758 \uc815\uc758\\n\\n  - \ub9b4\ub808\uc774\uc158\uc758 \ud29c\ud50c\uc744 \uc720\uc77c\ud558\uac8c \uc2dd\ubcc4\ud560\uc218 \uc788\ub294 \uc18d\uc131\ub4e4\uc758 \uc9d1\ud569\\n  - \uc720\uc77c\ud558\uac8c \ud0a4\ub97c \uaebc\ub0bc\uc218 \uc788\uc5b4\uc57c \ud55c\ub2e4.\\n\\n- \ud0a4\uc758 \ud2b9\uc131\\n\\n  - **_\uc720\uc77c\uc131_**: \ub9b4\ub808\uc774\uc158 \ub0b4\uc5d0\uc11c \uc720\uc77c\\n  - **_\ucd5c\uc18c\uc131_**: \ud0a4\uac00 \ub458 \uc774\uc0c1\uc758 \uc18d\uc131\uc73c\ub85c \uad6c\uc131 \ub418\uc5c8\uc744 \uacbd\uc6b0 \ud558\ub098\uc758 \uc18d\uc131\uc744 \uc81c\uac70\ud558\uba74 \uc720\uc77c\uc131 \ud30c\uad34<br/> -> \ud0a4\ub294 \ubaa8\ub4e0 \ud29c\ud50c\uc744 \uc720\uc77c\ud558\uac8c \uc2dd\ubcc4\ud560 \uc218 \uc788\ub294 \ucd5c\uc18c\uc758 \uc18d\uc131\ub4e4\ub85c \uad6c\uc131\\n\\n- \ud0a4\uc758 \uc720\ud615 (\uc2dd\ubcc4\uc790)\\n\\n  |  \uc720\ud615  | \uac1c\ub150                                                         | \uc608\uc2dc                                            |\\n  | :----: | :----------------------------------------------------------- | :---------------------------------------------- |\\n  | \uc288\ud37c\ud0a4 | \uc720\uc77c\uc131\uc740 \uc788\uc73c\ub098 \ucd5c\uc18c\uc131\uc740 \uc5c6\uc74c                                | [\ud559\ubc88,\uc774\ub984,\ud559\uacfc]                                |\\n  | \ud6c4\ubcf4\ud0a4 | \uc720\uc77c\uc131\uacfc \ucd5c\uc18c\uc131 \ub9cc\uc871                                         | [\ud559\ubc88], [\uc774\ub984, \ud559\uacfc]                            |\\n  | \uae30\ubcf8\ud0a4 | \uc5ec\ub7ec \ud6c4\ubcf4\ud0a4 \uc911 \ud558\ub098 \uc120\uc810                                     | [\ud559\ubc88],[\uc774\ub984, \ud559\uacfc] \uc900 \uc120\ud0dd                     |\\n  | \ub300\uccb4\ud0a4 | \ud6c4\ubcf4\ud0a4\uc758 \uae30\ubcf8\ud0a4 \uc774\uc678\uc5d0 \ub0a8\uc740 \ud0a4 <br/>\uae30\ubcf8\ud0a4 \ub300\uccb4 \uac00\ub219         | \uae30\ubcf8\ud0a4 - [\ud559\ubc88]<br/>\ub300\uccb4\uae30 - [\uc774\ub984, \ud559\uacfc]       |\\n  | \uc678\ub798\ud0a4 | \uc5b4\ub290 \ud55c \ub9b4\ub808\uc774\uc158 \uc18d\uc131\uc758 \uc9d1\ud559\uc774 \ub2e4\ub978 \ub9b4\ub808\uc774\uc158\uc758 \uae30\ubcf8\ud0a4\ub85c \uc0ac\uc6a9 | [\ud559\uacfc\ucf54\ub4dc], [\uc774\ub984, \ud655\uac00\ucf54\ub3c4, \ud559\ub144]\uc5d0\uc11c \ud559\uacfc\ucf54\ub4dc |\\n\\n- DB \uc124\uacc4\uc2dc \ucd5c\ucd08\ub294 \ud0a4\ub97c \uc124\uacc4\ud558\ub294 \uac83\uc774\ub2e4\\n\\n---\\n\\n## \uc2a4\ud0a4\ub9c8\uc5d0 \ub300\ud55c Key \uacb0\uc815 \ubc0f \ub3c4\ucd9c \uacfc\uc815\\n\\n<Drawio content={simpleGraph} />\\n\\n---\\n\\n## \ub370\uc774\ud130 \ubb34\uacb0\uc131\\n\\n### \ub370\uc774\ud130 \ubb34\uacb0\uc131\uc758 \uc815\uc758\\n\\n<ul>\\n  <li>\\n    <span style={{ color: \\"blue\\" }}>\ubb34\ud6a8 \uac31\uc2e0\uc73c\ub85c \ubd80\ud130 \ub370\uc774\ud130\ub97c \ubcf4\ud638</span> \ud558\uc5ec\\n    <span style={{ fontWeight: \\"bold\\" }}>\uc815\ud655\uc131,\uc720\ud6a8\uc131, \uc77c\uad00\uc131, \uc548\uc815\uc131</span>\uc744 \uc720\uc9c0\ud558\uc5ec\ub294\\n    \uc131\uc9c8 : \uc815\uc720\uc77c\uc548\\n  </li>\\n</ul>\\n\\n---\\n\\n### \ub370\uc774\ud130\ubca0\uc774\uc2a4 \ubb34\uacb0\uc131\uc758 \uc885\ub958\\n\\n|    \uc885\ub958     | \uad00\ub828\ud0a4 | \uc124\uba85                                                                                        |\\n| :---------: | :----: | :------------------------------------------------------------------------------------------ |\\n| \uac8c\uccb4 \ubb34\uacb0\uc131 | \uae30\ubcf8\ud0a4 | - \ubaa8\ub4e0 \uac8c\uccb4\ub294 \uc2dd\ubcc4\uc790\ub97c \uc815\uc758\ud558\uace0 \uadf8\uc2e4\ubcc4\uc790 \uac12\uc740 NULL \uac12\uc774 \uc5c6\uc774 \uac8c\uccb4 \ub0b4\uc5d0\uc11c \uc720\uc77c\ud55c \uac12\uc774\uc5b4\uc57c \ud568 |\\n| \ucc38\uc870 \ubb34\uacb0\uc131 | \uc678\ub798\ud0a4 | - \uc678\ub798\ud0a4 \uac12\uc740 \ud0a4\ubcf8\ud0a4\ub85c \uc0ac\uc6a9\ub41c Relation \uc758 \uae30\ubcf8\ud0a4 \uac12\uc774\uac70\ub098 \ub110(NULL) \uac12\uc77c \uac83                  |\\n| \uc18d\uc131 \ubb34\uacb0\uc131 |  \uceec\ub7fc  | - \uc9c0\uc815\ub41c \ub370\uc774\ud130 \ud615\uc2dd \ub9cc\uc871, CHAR, DATE                                                       |\\n| \uc0ac\uc6a9\uc790 \uc815\uc758 | \ub370\uc774\ud130 | - \uc0ac\uc6a9\uc790 \uc5c5\ubb34 \uaddc\uce59(Business Rule) \uc815\uc758<br/>- CHECK, DEFAULT, \ub370\uc774\ud130\ubca0\uc774\uc2a4 \ud2b8\ub9ac\uac70 \ub4f1         |\\n\\n---\\n\\n### \ub370\uc774\ud130 \ubb34\uacb0\uc131 \ubcf4\uc7a5 \ubc29\ubc95\\n\\n|     \ubd84\ub958      | \uc124\uba85                                                               |\\n| :-----------: | :----------------------------------------------------------------- |\\n| \uc751\uc6a9 \ud504\ub85c\uadf8\ub7a8 | \ub370\uc774\ud130\ub97c \uc800\uc791\ud558\ub294 \ud504\ub85c\uadf8\ub7a8 \ub0b4\uc5d0 \ubb34\uacb0\uc131 \uc800\uac74\uc744 \uac80\uc99d\ud558\ub294 \ucf54\ub4dc\ub97c \ucd94\uac00 |\\n|    \ud2b8\ub9ac\uac70     | \ud2b8\ub9ac\uac70(Trigger) \uc774\ubca4\ud2b8\uc2dc Procedure \ub97c \uc2e4\ud589\ud558\uc5ec \ubb34\uacb0\uc131 \uc870\uac74 \uc2e4\ud589    |\\n|   \uc81c\uc57d\uc870\uac74    | \ub370\uc774\ud130 \ubca0\uc774\uc2a4 \uc81c\uc57d \uc870\uac74 \uae30\ub2a5\uc744 \uc120\uc5b8\ud558\uace0 \ubb34\uacb0\uc131\uc744 \uc720\uc9c0              |\\n\\n---\\n\\n### \ub370\uc774\ud130 \ubb34\uacb0\uc131 \ubcf4\uc7a5 \ubc29\ubc95\uac04\uc758 \uc7a5\ub2e8\uc810 \ube44\uad50\\n\\n|        \ubd84\ub958         | \uc7a5\uc810                                                                                                               | \ub2e8\uc810                                                                            |\\n| :-----------------: | :----------------------------------------------------------------------------------------------------------------- | :------------------------------------------------------------------------------ |\\n| \uc751\uc6a9 \ud504\ub85c\uadf8\ub7a8 \ucf54\ub4dc  | - \uc0ac\uc6a9\uc790 \uc815\uc758 \uac19\uc740 \ubcf5\uc7a1\ud55c \ubb34\uacb0\uc131 \uc870\uac74\uc758 \uad6c\ud604 \uac00\ub2a5                                                                  | - \uc18c\uc2a4 \ucf54\ub4dc\uc5d0 \ubd84\uc0b0\ub418\uc5b4 \uad00\ub9ac\uac00 \uc5b4\ub824\uc6c0 <br/> - \uac1c\ubcc4\uc801 \uad00\ub9ac\ub85c \uc801\uc815\uc131 \uac80\ud1a0\uac00 \uc5b4\ub824\uc6c0 |\\n| \ub370\uc774\ud130\ubca0\uc774\uc2a4 \ud2b8\ub9ac\uac70 | - \ud1b5\ud569 \uad00\ub9ac\uac00 \uac00\ub2a5\ud568 <br/> - \ubcf5\uc7a1\ud55c \uc694\uac74 \uad6c\ud604 \uac00\ub2a5                                                                 | \uc6b4\uc601 \uc911 \ubcc0\uacbd\uc774 \uc5b4\ub824\uc6c0                                                           |\\n|      \uc81c\uc57d\uc870\uac74       | - \ud1b5\ud569\uad00\ub9ac\uac00 \uac00\ub2a5\ud568 <br/> - \uac04\ub2e8\ud55c \uc120\uc5b8\uc73c\ub85c \uad6c\ud604 \uac00\ub2a5<br/> - \ubcc0\uacbd\uc774 \uc6a9\uc774\ud568<br/> - \uc6d0\uce59\uc801\uc73c\ub85c \uc624\ub958 \ub370\uc774\ud130 \ubc1c\uc0dd \uc5b5\uc81c | - \ubcf5\uc7a1\ud55c \uc81c\uc57d \uc870\uac74 \uad6c\ud604\uc774 \ubd88\uac00\ub2a5\ud568<br/> - \uc608\uc678\uc801\uc778 \ucc98\ub9ac\uac00 \ubd88\uac00\ub2a5                |"},{"id":"/2023/02/15/DB\uac1c\uc6944","metadata":{"permalink":"/blog/second/2023/02/15/DB\uac1c\uc6944","source":"@site/second-blog/2023-02-15-DB\uac1c\uc6944/index.mdx","title":"DataBase \uac1c\uc694 4","description":"\uad00\uacc4\ud615(RDB), \uac8d\uccb4\uc9c0\ud5a5\ud615(OODBMS)","date":"2023-02-15T00:29:00.000Z","formattedDate":"2023\ub144 2\uc6d4 15\uc77c","tags":[{"label":"database","permalink":"/blog/second/tags/database"},{"label":"\uc815\ubcf4\uad00\ub9ac","permalink":"/blog/second/tags/\uc815\ubcf4\uad00\ub9ac"}],"readingTime":10.325,"hasTruncateMarker":false,"authors":[{"name":"100milliongold","title":"developer","url":"https://sebastienlorber.com","imageURL":"https://avatars.githubusercontent.com/u/13652711?s=400&u=0a1092b8b1170f71ae883d098f8721c12ce39e9e&v=4","key":"100milliongold"}],"frontMatter":{"title":"DataBase \uac1c\uc694 4","date":"2023-02-15T00:29:00.000Z","authors":["100milliongold"],"tags":["database","\uc815\ubcf4\uad00\ub9ac"],"password":"1q2w3e4r"},"unlisted":false,"prevItem":{"title":"DataBase \uac1c\uc694 5","permalink":"/blog/second/2023/02/16/DB\uac1c\uc6945"},"nextItem":{"title":"DataBase \uac1c\uc694 3","permalink":"/blog/second/2023/02/14/DB\uac1c\uc6943"}},"content":"## \uad00\uacc4\ud615(RDB), \uac8d\uccb4\uc9c0\ud5a5\ud615(OODBMS)\\n\\n| \uad6c\ubd84           | RDBMS                                       | OODBMS                                              | ORDBMS                                                |\\n| -------------- | ------------------------------------------- | --------------------------------------------------- | ----------------------------------------------------- |\\n| \uc800\uc7a5\ud30c\uc77c       | \ubb38\uc790\uc5f4 \ub370\uc774\ud130 \uc704\uc8fc                          | **_\ub370\uc774\ud130\uc640 \uc5f0\uad00 \ud504\ub85c\uadf8\ub7a8_** <br/> (\uba54\uc18c\ub4dc) \uc800\uc7a5    | \ub370\uc774\ud130\uc640 \uc5f0\uad00 \ud504\ub85c\uadf8\ub7a8 <br/> (\uba54\uc18c\ub4dc) \uc800\uc7a5            |\\n| \uc790\ub8cc\ubaa8\ub378       | \ud14c\uc774\ube14 \uad6c\uc870 \uad00\uacc4 <br/> \uae30\ubcf8\ud0a4 (Primary Key) | \uc5d4\ud2f0\ud2f0\uac04 \ud3ec\uc778\ud305 \ubc29\uc2dd<br/> \uac8d\uccb4\uc9c0\ud5a5(OID)             | RDBMS, OODBMS                                         |\\n| \uc790\uc6d0 \uc790\ub8cc\ud615    | \ubbf8\ub9ac \uc815\uc758\ub41c \uc77c\ubc18\uc815\ubcf4 \ud0c0\uc785\ub9cc \uc9c0\uc6d0            | **_\ube44\uc815\ud615 \uac8d\uccb4\ud0c0\uc785_** \uc9c0\uc6d0                          | \uc0ac\uc6a9\uc790 \uc815\uc758 \ubc0f \ube44\uc815\ud615 \uac8d\uccb4\ud0c0\uc785 \uc9c0\uc6d0                   |\\n| DB \uc5d1\uc138\uc2a4 \ubc29\uc2dd | SQL \uc9c8\uc758\uc5b4 \uc0ac\uc6a9                             | OQL(Object Query Lang)                              | SQL \ud655\uc7a5 \uc9c8\uc758\uc5b4 \uc0ac\uc6a9(SQL3)                            |\\n| \uc7a5\uc810           | \uc2dc\uc2a4\ud15c \uc548\uc815\uc131\uacfc \ub300\uaddc\ubaa8 \ud2b8\ub79c\uc81d\uc158 \ucc98\ub9ac        | \ubcf5\uc7a1 \ube44\uc815\ud615 \ubaa8\ub378 \uc801\uc6a9                               | \uad00\uacc4\ud615\uc758 \uc548\uc815\uc131\uacfc \uac8d\uccb4\uc9c0\ud5a5\ubaa8\ub378\uc758 \ubcf5\ud569\uc801 \uc694\uc18c \ubaa8\ub378\uc801\uc6a9 |\\n| \ub2e8\uc810           | \ubcf5\uc7a1\ud55c \uc815\ubcf4\uc218\uc6a9\uc744 \uc704\ud55c \ubaa8\ub378\uc801\uc6a9\uc774 \uc81c\ud55c\uc801\uc784  | \ub370\uc774\ud130 \ubca0\uc774\uc2a4 \uae30\ubcf8\uae30\ub2a5\uc774 \ubbf8\uc57d\ud558\uc5ec \uc548\uc815\uc131, \uc131\ub2a5 \ubbf8\ud761 | **_\ud45c\uc900\ud654\uac00 \ub418\uc5b4\uc788\uc9c0 \uc54a\uc74c_**                          |\\n\\n---\\n\\n## \uc2dc\uc2a4\ud15c \uce74\ud0c8\ub85c\uadf8 / \ub370\uc774\ud130 \uc0ac\uc804\uc758 \uac1c\ub150\\n\\n- \ub370\uc774\ud130 \ubca0\uc774\uc2a4\uc5d0 \ud3ec\ud568\ub418\uc5b4 \uc788\ub294 \ubaa8\ub4e0 \ub370\uc774\ud130 \uac8d\uccb4\uc5d0 \ub300\ud55c \uc815\uc758\ub098 \uba85\uc138\uc5d0 \uad00\ud55c \uc815\ubcf4\ub97c \uc800\uc7a5\ud558\ub294 \uc790\ub8cc \uc800\uc7a5\uc18c, \uc790\ub8cc \uc0ac\uc804\\n- \uad11\uc758\uc801 \uc758\ubbf8\ub85c\ub294 \uba54\ud0c0\uc815\ubcf4\uc758 \uc131\uaca9\uc744 \uc9c0\ub2c8\uba70, \ud611\uc758\uc801 \uc758\ubbf8\ub85c\ub294 \ub370\uc774\ud130\ubca0\uc774\uc2a4 \uc2dc\uc2a4\ud15c \uce74\ud0c8\ub85c\uadf8\\n- \uc815\ubcf4\uc790\uc6d0\uad00\ub9ac\ub098 \uc2dc\uc2a4\ud15c H/W, S/W, \ubb38\uc11c, \uc0ac\uc6a9\uc790\ub4e4\uc5d0 \ub300\ud55c \uc815\ubcf4\uad00\ub9ac \ub4f1\uc744 \uc704\ud574 \uc124\uacc4\uc790, \uc0ac\uc6a9\uc790, \uad00\ub9ac\uc790\uac00 \uc8fc\ub85c \uc0ac\uc6a9\ud568\\n- \ubc94\uc6a9 DBMS\uc758 \ud575\uc2ec\uc778 \uba54\ud0c0\ub370\uc774\ud130\ub85c\uc11c \ub370\uc774\ud130\ubca0\uc774\uc2a4\uc758 \uc2a4\ud0a4\ub9c8 \uc815\ubcf4, \uc2a4\ud0a4\ub9c8\ub4e4\uac04\uc758 \uc0ac\uc0c1\uc815\ubcf4, DBMS\uc758 \ud2b9\uc815 \ubaa8\ub4c8 (\uc9c8\uc758 \ucd5c\uc801\ud654\uae30, \uad8c\ud55c\uad00\ub9ac \ubaa8\ub378\ub4f1)\ub4e4\uc774 \ud544\uc694\ub85c \ud558\ub294 \uc815\ubcf4\ub97c \uc800\uc7a5\\n\\n## \uc2dc\uc2a4\ud15c \uce74\ud0c8\ub85c\uadf8\uc758 \ud2b9\uc9d5\\n\\n- \uce74\ud0c8\ub85c\uadf8 \uc790\uccb4\ub3c4 \uc2dc\uc2a4\ud15c \ud14c\uc774\ube14\ub85c \uad6c\uc131\ub418\uc5b4 \uc788\uc5b4 \uc77c\ubc18 \uc774\uc6a9\uc790\ub4e4\ub3c4 \uad8c\ud55c\uc5d0 \ub530\ub77c SQL \uc744 \uc774\uc6a9\ud558\uc5ec \ub0b4\uc6a9 \uac80\uc0c9 \uac00\ub2a5\\n- Insert, Update, Delete \ubb38\uc73c\ub85c \uac31\uc2e0\ud558\ub294 \uac83\uc740 \ud5c8\uc6a9\ub418\uc9c0 \uc54a\uc74c\\n- DBMS\uac00 \uc2a4\uc2a4\ub85c \uc0dd\uc131\ud558\uace0 \uc720\uc9c0\ud568\\n\\n---\\n\\n## \uc2dc\uc2a4\ud15c \uce74\ud0c8\ub85c\uadf8 / \ub370\uc774\ud130 \uc0ac\uc815\uc758 \ud544\uc694 \uc774\uc720 \ubc0f \uc8fc\uc694 \uad6c\uc131\uc694\uc18c\\n\\n| \uad6c\uc131\uc694\uc18c        | \ub0b4\uc6a9                                                                                   |\\n| --------------- | -------------------------------------------------------------------------------------- |\\n| \uc2a4\ud0a4\ub9c8 \uad6c\uc870     | - \uc2a4\ud0a4\ub9c8\uc758 \ud14c\uc774\ube14\uba85, \uc778\ud14d\uc2a4\uba85, \uce7c\ub7fc\uba85, \ubdf0, \ucc38\uc870\uad00\uacc4\uc5d0 \ub300\ud55c \ub0b4\uc6a9                        |\\n| \uac10\uc0ac \ucd94\uc801       | - \ub370\uc774\ud130\ubca0\uc774\uc2a4\uc5d0\uc11c \uc791\uc5c5\uc744 \uc218\ud589\ud55c \uc774\ub825, \ud2b8\ub79c\uc7ad\uc158 \uc815\ubcf4 \uc138\uc158\uc815\ubcf4 \ub4f1                       |\\n| \uc0ac\uc6a9\uc790 \uad8c\ud55c     | - \ub370\uc774\ud130\ubca0\uc774\uc2a4 \uc624\ube0c\uc81d\ud2b8\uc5d0 \ub300\ud574 \uc811\uadfc\ud558\uae30 \uc704\ud55c \uc811\uadfc, \uc785\ub825, \uc218\uc815, \uc0ad\uc81c \ub4f1\uc5d0 \ub300\ud55c \uad8c\ud55c\uc815\ubcf4 |\\n| \uc9c8\uc758 \ucd5c\uc801\ud654\ud558\uae30 | - Optimizer \uac00 \ucd5c\uc801\ud654\ub41c \uacbd\ub85c\ub97c \ucc3e\uae30 \uc704\ud55c \ud1b5\uacc4\uc815\ubcf4\ub97c \uc0dd\uc131\ud558\uc5ec \uc800\uc7a5                      |\\n| \ucef4\ud30c\uc77c\ub7ec        | - \uace0\uc218\uc900\uc758 \uc9c8\uc758\uc640 \ub370\uc774\ud130 \uc870\uc791\uc5b4 \uc601\ud5a5\ub4e4\uc744 \uc800\uc218\uc900\uc758 \ud30c\uc77c\uc811\uadfc \uba85\ub839\uc73c\ub85c \ubcc0\ud658               |\\n\\n![](./image.png)\\n\\n---\\n\\n## \ub370\uc774\ud130\ubca0\uc774\uc2a4\uc758 \uc5b8\uc5b4\uc758 \uc815\uc758\\n\\n- \ub370\uc774\ud130\ubca0\uc774\uc2a4\ub97c \uc815\uc758\ud558\uace0 \uc811\uadfc\ud558\uae30 \uc704\ud55c \ubc29\ubc95\uc73c\ub85c \uc0ac\uc6a9\uc790\uac00 DBMS\ub97c \ud65c\uc6a9\ud558\uae30 \uc704\ud55c \ub300\ud654\ub3c4\uad6c\uc784\\n\\n| \uc885\ub958 | \uc124\uba85                                                                                                                                                                                                                                                             | \ube44\uad50                                               |\\n| ---- | ---------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------- | -------------------------------------------------- |\\n| DML  | - Data Manipluation Language <br/> - \ub370\uc774\ud130\ubca0\uc774\uc2a4\uc758 \uc6d0\ud558\ub294 **_\ub370\uc774\ud130\ub97c \uc870\uc791_**(**_\uac80\uc0c9_**\ud558\uace0, **_\uc218\uc815_**\ud558\uace0, **_\uc0bd\uc785_**\ud558\uace0, **_\uc0ad\uc81c_** ) \ud558\ub294 \uc5b8\uc5b4<br/> - \uc0ac\uc6a9\uc790\uc640 \ub370\uc774\ud130\ubca0\uc774\uc2a4 \uc0ac\uc774\uc758 \uc778\ud130\ubca0\uc774\uc2a4 \uc81c\uacf5<br/> - \uc808\ucc28\uc801 DML(\uc751\uc6a9\ud504\ub85c\uadf8\ub7a8), \ube44\uc808\ucc28\uc801 DML (\uc9c8\uc758\uc5b4) | - SELECT<br/> - INSERT<br/> - UPDATE<br/> - DELETE |\\n| DDL  | - Data Definition Language <br/> - \ub370\uc774\ud130\ubca0\uc774\uc2a4\uc758 **_\uc2a4\ud0a4\ub9c8\ub97c \uc815\uc758\ud558\ub294 \uc5b8\uc5b4_**\ub85c DBA\uac00 \uc0ac\uc6a9<br/> - \uba54\ud0c0 \ub370\uc774\ud130 \ud615\ud0dc\ub85c \ub370\uc774\ud130 \uc0ac\uc804(Data Dictionary) \uc774\ub77c\ub294 \ud2b9\ubcc4\ud55c \ud30c\uc77c\uc5d0 \uae30\ub85d                                                                                     | - CREATE <br/> - ALTER <br/> - DROP                |\\n| DCL  | - Data Control Language <br/> - \ub370\uc774\ud130\ubca0\uc774\uc2a4\uc758 **_\uaddc\uc815\uc774\ub098 \uae30\ubc95\uc744 \uc815\uc758\ud558\uace0 \uc81c\uc5b4\ud558\ub294_** \uc5b8\uc5b4<br/> - \uad8c\ud55c \ubd80\uc5ec/\ud68c\uc218, \uc138\uc158 \ud0ac \ub4f1                                                                                                                                    | - GRANT<br/> - REVOLKE                             |\\n\\n![](./image1.gif)\\n\\n---\\n\\n## \ub370\uc774\ud130\ud488\uc9c8\uad00\ub9ac \ud504\ub808\uc784\uc6cc\ud06c\\n\\n|       \uc870\uc9c1(1) \\\\ \ub300\uc0c1       |  \ub370\uc774\ud130 \uac12 (2)   |            \ub370\uc774\ud130 \uad6c\uc870 (3)            |       \ub370\uc774\ud130 \uad00\ub9ac \ud504\ub85c\uc138\uc2a4 (4)        |\\n| :------------------------: | :--------------: | :-----------------------------------: | :-----------------------------------: |\\n| CIO/EDA <br/>(\uac1c\uad04\uc801 \uad00\uc810) | \ub370\uc774\ud130 \uad00\ub9ac \uc815\ucc45 |           \ub370\uc774\ud130 \uad00\ub9ac \uc815\ucc45            |           \ub370\uc774\ud130 \uad00\ub9ac \uc815\ucc45            |\\n|    DA<br/>(\uac1c\ub150\uc801 \uad00\uc810)    |   \ud45c\uc900 \ub370\uc774\ud130    | \uac1c\ub150 \ub370\uc774\ud130 \ubaa8\ub378<br/>\ub370\uc774\ud130 \ucc38\uc870 \ubaa8\ub378 |  \ub370\uc774\ud130 \ud45c\uc900 \uad00\ub9ac<br/>\uc694\uad6c\uc0ac\ud56d \uad00\ub9ac   |\\n| Modeler<br/>(\ub17c\ub9ac\uc801 \uad00\uc810)  |   \ubaa8\ub378 \ub370\uc774\ud130    |           \ub17c\ub9ac \ub370\uc774\ud130 \ubaa8\ub378            | \ub370\uc774\ud130 \ubaa8\ub378 \uad00\ub9ac<br/>\ub370\uc774\ud130 \ud750\ub984 \uad00\ub9ac |\\n|   DBA<br/>(\ubb3c\ub9ac\uc801 \uad00\uc810)    |   \uad00\ub9ac \ub370\uc774\ud130    |   \ubb3c\ub9ac \ub370\uc774\ud130 \ubaa8\ub378<br/>\ub370\uc774\ud130\ubca0\uc774\uc2a4   |       DB \uad00\ub9ac<br/>DB \ubcf4\uc548 \uad00\ub9ac        |\\n|   User<br/>(\uc6b4\uc6a9\uc801 \uad00\uc810)   |   \uc5c5\ubb34 \ub370\uc774\ud130    |              \uc0ac\uc6a9\uc790 View              |           \ub370\uc774\ud130 \ud65c\uc6a9 \uad00\ub9ac            |\\n\\n- CIO: \ucd5c\uace0\uc815\ubcf4\ucc45\uc784\uc790(\u6700\u9ad8\u60c5\u5831\u8cac\u4efb\u8005, Chief Information Officer)\ub294 \uc815\ubcf4\ub098 \uc815\ubcf4\uae30\uc220\uc5d0 \uad00\ud55c \ucd5c\uace0\uc758 \uc784\uc6d0\uc744 \ub9d0\ud55c\ub2e4. CIO \ub4f1\uc73c\ub85c \ubd80\ub978\ub2e4.\\n- EDA : EDA(Enterprise data architecture)\ub294 IT \ud504\ub85c\uadf8\ub7a8 \ubc0f \uc815\ubcf4 \uc790\uc0b0\uc744 \ube44\uc988\ub2c8\uc2a4 \uc804\ub7b5\uc5d0 \ub9de\ucd94\ub3c4\ub85d \uc124\uacc4\ub41c \ub9c8\uc2a4\ud130 \uccad\uc0ac\uc9c4 \ubaa8\uc74c\uc744 \ub9d0\ud569\ub2c8\ub2e4. EDA\ub294 \ud1b5\ud569, \ud488\uc9c8 \ud5a5\uc0c1 \ubc0f \uc131\uacf5\uc801\uc778 \ub370\uc774\ud130 \uc804\ub2ec\uc744 \uc548\ub0b4\ud558\ub294 \ub370 \uc0ac\uc6a9\ub429\ub2c8\ub2e4.\\n- DA : \ub370\uc774\ud130 \ubd84\uc11d \uc124\uacc4\uc790, \uc624\ub77c\ud074, DB2, MySQL ..\ub4f1\ub4f1\uacfc \uac19\uc774 \ud2b9\uc815 DB\ub9cc\uc744 \ucd94\uad6c\ud558\uc9c0 \uc54a\uc73c\uba70 \ub370\uc774\ud130 \uc544\ud0a4\ud14d\uccd0\ub97c \ud65c\uc6a9\ud558\uc5ec \ub370\uc774\ud130\ub97c \ubd84\uc11d\ud558\uace0 \uc124\uacc4\ub97c \ud558\ub294 \uc5ed\ud560, \uc5b4\ub5a4 \uceec\ub7fc\uc774 \uc778\ub371\uc2a4\ub85c \uc801\ud569\ud55c\uc9c0 \ud655\uc778\ud558\ub294 \uc5ed\ud560\\n\\n### (1) \uc870\uc9c1\\n\\n- \ub370\uc774\ud130 \ud488\uc9c8\uad00\ub9ac \ubc0f \uc720\uc9c0\ub97c \uc704\ud55c \uc870\uc9c1\uc758 \uc5ed\ud65c\uacfc \ucc45\uc784 \uc815\uc758\\n\\n#### \uc870\uc9c1 \ubc0f R&R \uc218\ub9bd\ubc29\ud5a5\\n\\n- \ub370\uc774\ud130, \ub370\uc774\ud130 \uad6c\uc870\uc758 \ud488\uc9c8\uad00\ub9ac \ubc0f \ub370\uc774\ud130 \ubcf4\uc548\uc744 \uc704\ud55c \uad00\ub9ac\uc870\uc9c1\uc758 \uba85\ud655\ud55c \uc5ed\ud65c\uacfc \ucc45\uc784 \uad00\ub9ac\\n- \ub370\uc774\ud130 \uad00\ub9ac \uc774\uc288 \uc870\uc815 \ubc0f \ud574\uacb0\uc744 \uc704\ud55c \ub370\uc774\ud130 \uad00\ub9ac\uc704\uc6d0\ud68c \uad6c\uc131\\n- \uc804\uc0ac\uc801 \ub370\uc774\ud130 \ud1b5\ud569 \uad00\ub9ac\ub97c \uc704\ud55c \uc804\ub2f4\uc778\uc5ed \ubc30\uc815\\n\\n### (2) \ub370\uc774\ud130\\n\\n- \uae30\uad00 \ubc0f \uae30\uc5c5\uc758 \ube44\uc83c \ubc0f \ubaa9\ud45c\ub2ec\uc131 \uc704\ud55c \uc804\uc0b0\ud654\ub41c \ub610\ub294 \uc804\uc0b0\ud654\uac00 \ud544\uc694\ud55c \ub370\uc774\ud130\\n\\n### (3) \ub370\uc774\ud130 \uad6c\uc870\\n\\n- \ub370\uc774\ud130\uac00 \ub2f4\uaca8\uc9c4 \ubaa8\uc591 / \ud2c0\\n- \ub370\uc774\ud130\ub97c \ucde8\uae09\ud558\ub294 \uad00\uc810 (View)\uc5d0 \ub530\ub77c \uad6c\uc870\uac00 \ub2e4\ub984\\n\\n### (4) \ub370\uc774\ud130 \uad00\ub9ac \ud504\ub85c\uc138\uc2a4\\n\\n- \ub370\uc774\ud130 \ubc0f \ub370\uc774\ud130 \uad6c\uc870\uc758 \ud488\uc9c8\uc744 \uc548\uc815\uc801\uc73c\ub85c \uc720\uc9c0 \uac1c\uc120\ud558\uae30 \uc704\ud55c \ud65c\uc6a9\uc73c\ub85c \ub370\uc774\ud130 \uad00\uc810 \uc808\ucc28\uc640 \uc870\uc9c1\uc758 \uc5ed\ud65c\uc744 \ud3ec\ud568\ud568\\n\\n#### \uad00\ub9ac\ud504\ub85c\uc138\uc2a4 \uc218\ub9bd\ubc29\ud5a5\\n\\n- \ub370\uc774\ud130 \ubc0f \ub370\uc774\ud130 \uad6c\uc870\ub97c \uc77c\uad00\uc131 \uc788\uac8c \uad00\ub9ac\ud558\uace0 \uc5c5\ubb34\uc0dd\uc0b0\uc131\uc744 \ub192\uc77c \uc218 \uc788\ub3c4\ub85d \ud504\ub85c\uc138\uc2a4 \uc124\uacc4\\n- \uc815\ubcf4 \uc694\uad6c\uc0ac\ud56d\uc758 \ucd94\uc801\uad00\ub9ac\ub97c \uc704\ud574 \uc694\uad6c\uc0ac\ud56d \ubc1c\uc0dd \ubd80\ud130 DB \ubc18\uc601 \ubc0f \ub370\uc774\ud130 \uac80\uc99d \ud504\ub85c\uc138\uc2a4\uae4c\uc9c0 \uace0\ub824\ud558\uc5ec \uc124\uacc4\\n- \uc548\uc815\uc801 \uc2dc\uc2a4\ud15c \uc6b4\uc601\uc744 \uc704\ud55c \ub370\uc774\ud130 \ubc0f \ub370\uc774\ud130 \uad6c\uc870\ubcc0\uacbd\uc5d0 \ub530\ub978 \uc601\ud5a5\ub3c4 \ubd84\uc11d\uc744 \ubc18\ub4dc\uc2dc \uc218\ud589\ud558\ub3c4\ub85d \ud504\ub85c\uc138\uc2a4 \uc124\uacc4\\n\\n---\\n\\n## \ud488\uc9c8\uad00\ub9ac \uad00\uc810\uc758 \uc0ac\uc6a9\uc790 \ubcc4 R&R\\n\\n|  \uc5ed\ud65c   | \ucc45\uc784                                                                                                                                                   |\\n| :-----: | :----------------------------------------------------------------------------------------------------------------------------------------------------- |\\n| CIO/EDA | - \ub370\uc774\ud130 \uad00\ub9ac \ucd1d\uad04 <br/> - \ub370\uc774\ud130 \uad00\ub9ac \uc815\ucc45 \uc218\ub9bd <br/> - \ub370\uc774\ud130 \uad00\ub9ac\uc790\uac04 \uc774\uc288\uc0ac\ud56d \uc870\uc815                                                                 |\\n|   DA    | - \uc804\uc0ac \ub370\uc774\ud130 \ubcc0\uacbd\ucc98\ub9ac \ucd1d\uad04 <br/> - \uc804\uc0ac \ub370\uc774\ud130 \ud1b5\ud569 \ubaa8\ub35c \uad00\ub9ac <br/> - \ub370\uc774\ud130 \ud45c\uc900 \uac1c\ubc1c \ubc0f \uc870\uc815                                                        |\\n| Modeler | - \ud574\ub2f9\uae30\ub2a5 \uc601\uc5ed\uc758 \ub370\uc774\ud130 \uc694\uad6c\uc0ac\ub791 \ubc0f \uc774\uc288\uc0ac\ud56d \uc870\uc815\uacfc \ud1b5\ud569 <br/> - \ud574\ub2f9 \uae30\ub2a5 \uc601\uc5ed\uc758 \ube44\uc9c0\ub2c8\uc2a4 \uc694\uac74\uc744 \ud1a0\ub300\ub85c \ub370\uc774\ud130 \ubaa8\ub378\ub9c1 \uc218\ud589 <br/> - \ud45c\uc900 \ud655\uc778 \ubc0f \uc801\uc6a9 |\\n|   DBA   | - DB \ub514\uc790\uc778 <br/> - DB \uc640 \ub370\uc774\ud130\uc758 \ud615\uc0c1 \uad00\ub9ac \uc218\ud589 <br/> - DB\uc758 \ub370\uc774\ud130 \ubaa8\ub378\ub9c1 \ubc0f \ud29c\ub9c1, \ubcf4\uc548 \uad00\ub9ac                                                        |\\n|  User   | - \uc11c\ube44\uc2a4\ub418\ub294 \ub370\uc774\ud130 \ubc0f \uc6b4\uc601,\ubd84\uc11d \ub370\uc774\ud130\uc5d0 \ub300\ud55c \ud65c\uc6a9 <br/> - \ub370\uc774\ud130\uc5d0 \ub300\ud55c \ucd94\uac00 \uc694\uac74 \uc694\uccad                                                               |"},{"id":"/2023/02/14/DB\uac1c\uc6943","metadata":{"permalink":"/blog/second/2023/02/14/DB\uac1c\uc6943","source":"@site/second-blog/2023-02-14-DB\uac1c\uc6943/index.mdx","title":"DataBase \uac1c\uc694 3","description":"DBMS \uc758 \uc815\uc758","date":"2023-02-14T23:59:00.000Z","formattedDate":"2023\ub144 2\uc6d4 14\uc77c","tags":[{"label":"database","permalink":"/blog/second/tags/database"},{"label":"\uc815\ubcf4\uad00\ub9ac","permalink":"/blog/second/tags/\uc815\ubcf4\uad00\ub9ac"},{"label":"DBMS","permalink":"/blog/second/tags/dbms"}],"readingTime":0.84,"hasTruncateMarker":false,"authors":[{"name":"100milliongold","title":"developer","url":"https://sebastienlorber.com","imageURL":"https://avatars.githubusercontent.com/u/13652711?s=400&u=0a1092b8b1170f71ae883d098f8721c12ce39e9e&v=4","key":"100milliongold"}],"frontMatter":{"title":"DataBase \uac1c\uc694 3","date":"2023-02-14T23:59:00.000Z","authors":["100milliongold"],"tags":["database","\uc815\ubcf4\uad00\ub9ac","DBMS"],"password":"1q2w3e4r"},"unlisted":false,"prevItem":{"title":"DataBase \uac1c\uc694 4","permalink":"/blog/second/2023/02/15/DB\uac1c\uc6944"},"nextItem":{"title":"DataBase \uac1c\uc694 2","permalink":"/blog/second/2023/02/14/DB\uac1c\uc6942"}},"content":"## DBMS \uc758 \uc815\uc758\\n\\n- \ub2e4\uc218\uc758 \uc0ac\uc6a9\uc790\ub4e4\uc774 \ub370\uc774\ud130 \ubca0\uc774\uc2a4 \ub0b4\uc758 \ub370\uc774\ud130\ub97c \uc811\uadfc\ud560 \uc218 \uc788\ub3c4\ub85d \ud574 \uc8fc\ub294 \uad00\ub9ac \uc18c\ud504\ud2b8\uc6e8\uc5b4 \ub3c4\uad6c\ub4e4\uc758 \uc9d1\ud569\\n\\n## DBMS \uc758 \uae30\ub2a5\\n\\n- \ub370\uc774\ud130 \uc800\uc7a5\uacfc \uac1c\ubc1c \ubc0f \uc720\uc9c0\ubcf4\uad6c \uce21\uba74\uc5d0\uc11c **_\uc911\ubcf5\uc131\uc758 \ud1b5\uc81c_**\\n- \ub9ce\uc740 \uc0ac\uc6a9\uc790 \uac04\uc758 **_\ub370\uc774\ud130 \uacf5\uc720_**\\n- \uad8c\ud55c\uc5c6\ub294 \uc0ac\uc6a9\uc790\uc758 \ub370\uc774\ud130 **_\uc811\uadfc\uc744 \ud1b5\uc81c_**\\n- \ub2e4\uc591\ud55c \uc0ac\uc6a9\uc790\uc5d0\uac8c **_\ub2e4\uc591\ud55c \ud615\ud0dc\uc758 \uc778\ud130\ud398\uc774\uc2a4\ub97c_** \uc81c\uacf5\\n- \ub370\uc774\ud130 \uc0ac\uc591\uc5d0 \uc874\uc7ac\ud558\ub294 \ubcf5\uc7a1\ud55c \uad00\ub828\uc131\uc744 \ud45c\ud604\\n- \ub370\uc774\ud130\ubca0\uc774\uc2a4\uc758 **_\ubb34\uacb0\uc131\uc744 \ubcf4\uc7a5_**\\n- **_\ubc31\uc5c5\uacfc \ubcf5\uad6c_** \uae30\ub2a5\uc744 \uc81c\uacf5\\n\\n![](./Database-Evolution-1200x675.webp)"},{"id":"/2023/02/14/DB\uac1c\uc6942","metadata":{"permalink":"/blog/second/2023/02/14/DB\uac1c\uc6942","source":"@site/second-blog/2023-02-14-DB\uac1c\uc6942/index.mdx","title":"DataBase \uac1c\uc694 2","description":"\ub370\uc774\ud130 \ubca0\uc774\uc2a4 3\ub2e8\uacc4 \uc544\ud0a4\ud14d\uccd0 ANSI/SPARC \ubaa8\ub378","date":"2023-02-14T12:52:00.000Z","formattedDate":"2023\ub144 2\uc6d4 14\uc77c","tags":[{"label":"database","permalink":"/blog/second/tags/database"},{"label":"\uc815\ubcf4\uad00\ub9ac","permalink":"/blog/second/tags/\uc815\ubcf4\uad00\ub9ac"},{"label":"ANSI/SPARC \ubaa8\ub378","permalink":"/blog/second/tags/ansi-sparc-\ubaa8\ub378"},{"label":"\ub370\uc774\ud130 \ub3c5\ub9bd\uc131","permalink":"/blog/second/tags/\ub370\uc774\ud130-\ub3c5\ub9bd\uc131"}],"readingTime":4.435,"hasTruncateMarker":false,"authors":[{"name":"100milliongold","title":"developer","url":"https://sebastienlorber.com","imageURL":"https://avatars.githubusercontent.com/u/13652711?s=400&u=0a1092b8b1170f71ae883d098f8721c12ce39e9e&v=4","key":"100milliongold"}],"frontMatter":{"title":"DataBase \uac1c\uc694 2","date":"2023-02-14T12:52:00.000Z","authors":["100milliongold"],"tags":["database","\uc815\ubcf4\uad00\ub9ac","ANSI/SPARC \ubaa8\ub378","\ub370\uc774\ud130 \ub3c5\ub9bd\uc131"],"password":"1q2w3e4r"},"unlisted":false,"prevItem":{"title":"DataBase \uac1c\uc694 3","permalink":"/blog/second/2023/02/14/DB\uac1c\uc6943"},"nextItem":{"title":"DataBase \uac1c\uc694 1","permalink":"/blog/second/2023/02/14/DB\uac1c\uc6941"}},"content":"## \ub370\uc774\ud130 \ubca0\uc774\uc2a4 3\ub2e8\uacc4 \uc544\ud0a4\ud14d\uccd0 ANSI/SPARC \ubaa8\ub378\\n\\n- ANSI/SPARC (American National Standards Institute / System Planning And Requirements Commitree) \ubaa8\ub378\\n- \uc0ac\uc6a9\uc790 \uad00\uc810, \ud1b5\ud569 \ubaa8\ub378 \uad00\uc810, \uc800\uc7a5\uc7a5\uce58\uc758 \uc138\uac00\uc9c0 \uad00\uc810\uc5d0\uc11c Architecture \uc815\uc758 \ubc0f \uc774\ub4e4 \uac04\uc5d0 \uc0c1\ud638 \uc5f0\uad00\uad00\uacc4\ub97c \uc815\uc758\ud568\\n- \ubbf8\uad6d \ud45c\uc900\ud611\ud68c(ANSI) \uc0b0\ud558\uc758 X3 \uc704\uc6d0\ud68c (\ucef4\ud4e8\ud130 \ubc0f \uc815\ubcf4\ucc98\ub9ac)\uc758 \ud2b9\ubcc4 \uc5f0\uad6c\ubd84\uacfc \uc704\uc6d0\ud68c\uc5d0\uc11c DBMS \uc640 \uadf8 \uc778\ud130\ud398\uc774\uc2a4\ub97c \uc704\ud574 \uc81c\uc548\ud55c Three-Schema Architeture (1978\ub144)\\n\\n| \ub2e8\uacc4        | \uc124\uba85                                                                                                                                                                                                                                    | \ube44\uace0     |\\n| ----------- | --------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------- | -------- |\\n| \uc678\ubd80 \uc2a4\ud0a4\ub9c8 | - \uc0ac\uc6a9\uc790\ub098 \uc751\uc6a9 \ud504\ub85c\uadf8\ub798\uba38\uac00 \uc811\uadfc\ud55c\uc218 \uc788\ub294 \ub370\uc774\ud130\ubca0\uc774\ub97c \uc815\uc758 <br/> - \uc804\uccb4 \ub370\uc774\ud130\ubca0\uc774\uc2a4\uc758 \ud55c \ub17c\ub9ac\uc801\uc778 \ubd80\ubd84\uc73c\ub85c \ubcfc\uc218 \uc788\uc73c\ubbc0\ub85c \uc11c\ube0c \uc2a4\ud0a4\ub9c8\ub77c\uace0 \ud568                                                                                          | View     |\\n| \uac1c\ub150 \uc2a4\ud0a4\ub9c8 | - \ubc94 \uae30\uad00\uc801 \uc785\uc7a5\uc5d0\uc11c \ubcf8 \ub370\uc774\ud130\ubca0\uc774\uc2a4\uc758 \uc815\uc758\ub97c \uae30\uc220<br/> - \ubaa8\ub4e0\uc751\uc6a9\uc5d0 \ub300\ud55c \uc804\uccb4\uc801\uc73c\ub85c \ud1b5\ud569\ub41c \ub370\uc774\ud130 \uad6c\uc870\ub85c\uc11c \ub2e8\uc21c\ud788 \uc2a4\ud0a4\ub9c8\ub77c\uace0 \ud568 <br/> (\uc2a4\ud0a4\ub9c8\ub77c\uace0 \ud558\uba74 \uc77c\ubc18\uc801\uc73c\ub85c \uac1c\ub150\uc2a4\ud0a4\ub9c8\ub97c \uc758\ubbf8\ud568) <br/> - \uc811\uadfc\uad8c\ud55c, \ubcf4\uc548\uc815\ucc44\uadf8 \ubb34\uacb0\uc131 \uaddc\uce59\uc744 \uba85\uc138 | \ud1b5\ud569 ERD |\\n| \ub0b4\ubd80 \uc2a4\ud0a4\ub9c8 | - \ubb3c\ub9ac\uc801 \uc800\uc7a5 \uc7a5\uce58\uc758 \uad00\uc810\uc5d0\uc11c \ubcf8 \uc804\uccb4 \ub370\uc774\ud130\ubca0\uc774\uc2a4\uc758 \uba85\uc138\uc758\ubbf8 <br/> - \uac1c\ub150\uc2a4\ud0a4\ub9c8\uc758 \ubb3c\ub9ac\uc801 \uc800\uc7a5\uad6c\uc870\uc5d0 \ub300\ud55c \uc815\uc758\ub97c \uae30\uc220                                                                                                                   | \ubb3c\ub9acDB   |\\n\\n---\\n\\n## ANSI/SPARC \uc544\ud0a4\ud14d\uccd0 \uc2a4\ud0a4\ub9c8\uc640 \uc778\uc2a4\ud134\uc2a4\\n\\n| \uad6c\ubd84 | \uc2a4\ud0a4\ub9c8                                                                                                                                       | \uc778\uc2a4\ud134\uc2a4                                                     |\\n| ---- | -------------------------------------------------------------------------------------------------------------------------------------------- | ------------------------------------------------------------ |\\n| \uc815\uc758 | - \ub370\uc774\ud130\ubca0\uc774\uc2a4\uc5d0 \uc800\uc7a5\ub418\ub294 \ub370\uc774\ud130 \uad6c\uc870\ubc0f \uc720\ud615\uc744 \uc815\uc758\ud55c\uac83<br/> - \ub370\uc774\ud130\ubca0\uc774\uc2a4\uc758 \uc804\uccb4\uc801\uc778 \uc815\uc758\ub97c \ub098\ud0c0\ub0b4\uba70 \uc77c\ubc18\uc801\uc73c\ub85c \uac1c\ub150(\ub17c\ub9ac) \uc2a4\ud0a4\ub9c8\ub97c \uc9c0\uc815\ud568 | - \ub370\uc774\ud130\ubca0\uc774\uc2a4\uc5d0 \uc2e4\uc81c \uc800\uc7a5\ub418\uc5b4 \uc788\ub294 \uac12\ub4e4\uc744 \ub098\ud0c0\ub0c4            |\\n| \ud2b9\uc9d5 | - \ud55c\ubc88 \uc815\uc758\ub418\uba74 \uc798 \ubcc0\uacbd\ub418\uc9c0 \uc54a\uc74c                                                                                                             | - \uacc4\uc18d\uc801\uc73c\ub85c \ubcc0\ud654\ud558\ub294 \ub370\uc774\ud130\ubca0\uc774\uc2a4 \ud2b9\uc131\uc73c\ub85c \uc778\ub798 \uc790\uc8fc \ubcc0\uacbd\ub428 |\\n| \uc5b8\uc5b4 | - DDL(Data Definition Language)                                                                                                              | - DML(Data Multiplation Language)                            |\\n| \uc0ac\ub840 | ![](./data1.jpeg)                                                                                                                            | ![](./data2.png)                                             |\\n\\n---\\n\\n## 3\ub2e8\uacc4 Student \ub370\uc774\ud130\ubca0\uc774\uc2a4 \uc608\\n\\n![](./data3.png)\\n\\n---\\n\\n## \ub370\uc774\ud130 \ub3c5\ub9bd\uc131\\n\\n### \ub370\uc774\ud130 \ub3c5\ub9bd\uc131\uc758 \uc815\uc758\\n\\n- \uc0c1\uc704 \ub2e8\uacc4\uc758 \uc2a4\ud0a4\ub9c8 \uc815\uc758\uc5d0 \uc601\ud5a5\uc744 \uc8fc\uc9c0 \uc54a\uc73c\uba74\uc11c \uc5b4\ub5a4 \ub2e8\uacc4\uc758 \uc2a4\ud0a4\ub9c8\ub97c \ubcc0\uacbd\ud560\uc218 \uc788\ub294\uac83\\n\\n### \ub370\uc774\ud130 \ub3c5\ub9bd\uc131\uc758 \ubaa9\uc801\\n\\n- DB \uc5d0 \ub300\ud55c \uc0ac\uc6a9\uc790\uc758 View \uc640 DB \uac00 \uc2e4\uc81c \ud45c\ud604\ub418\ub294 View\ub97c \ubd84\ub9ac\ud558\uc5ec **_\ubcc0\uacbd \uac04\uc12d \ucd5c\uc18c\ud654_**\\n- \uac01 View \uc758 \ub3c5\ub9bd\uc131 \uc720\uc9c0, **_\uacc4\uce35\ubcc4 View \uc5d0 \uc601\ud5a5\uc744 \uc8fc\uc9c0 \uc54a\uace0 \ubcc0\uacbd_**\uc774 \uac00\ub2a5\\n\\n| \uad6c\ubd84                | \ub0b4\uc6a9                                                                                                                                                                                                                                                                                            |\\n| ------------------- | ----------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------- |\\n| **_\ub17c\ub9ac\uc801 \ub3c5\ub9bd\uc131_** | - **_\uc678\ubd80 \uc2a4\ud0a4\ub9c8\ub098 \uc751\uc6a9 \ud504\ub85c\uadf8\ub7a8\uc744 \ubcc0\uacbd\ud558\uc9c0 \uc54a\uc73c\uba74\uc11c \uac1c\ub150 \uc2a4\ud0a4\ub9c8\ub97c \ubcc0\uacbd_**\ud560\uc218 \uc788\ub294 \ub2a5\ub825(\uc131\uc9c8)<br/> - \uac1c\ub150 \uc2a4\ud0a4\ub9c8 \uad6c\uc870\uc758 \ubcc0\uacbd\uc774\ub098 \uc18d\uc131\uc758 \uc0ad\uc81c \ub4f1\ub3c4 \uc774\uac83\uc744 \uc2e4\uc81c\ub85c \uc774\uc6a9\ud558\uc9c0 \uc54a\ub294 \uc678\ubd80 \uc2a4\ud0a4\ub9c8\ub294 \uc601\ud5a5\uc744 \uc8fc\uc9c0\uc54a\uc74c                                                                                    |\\n| **_\ubb3c\ub9ac\uc801 \ub3c5\ub9bd\uc131_** | - **_\uac1c\ub150 \uc2a4\ud0a4\ub9c8\ub97c \ubcc0\uacbd\ud558\uc9c0 \uc54a\uc73c\uba74\uc11c \ub0b4\ubd80 \uc2a4\ud0a4\ub9c8\ub97c \ubcc0\uacbd_**\ud560\uc218 \uc788\ub294 \ub2a5\ub825(\uc131\uc9c8) <br/> - \uac1c\ub150 \uc2a4\ud0a4\ub9c8\uac00 \ub514\uc2a4\ud06c \ub0b4\uc758 \ub0b4\ubd80 \ud544\ub4dc\uc640 \uc5b4\ub5bb\uac8c \ub300\uc751\ud558\ub294\uac00\ub97c \uc815\uc758<br/> - \ub2e4\ub978 \ub514\uc2a4\ud06c\ub85c\uc758 \ub370\uc774\ud130 \uc774\ub3d9\uc774\ub098 \ud30c\uc77c \uad6c\uc870\uc758 \ubcc0\uacbd\uacfc \uac19\uc740 \ubb3c\ub9ac\uc801\uc778 \ubcc0\uacbd \ud544\uc694\uc2dc \ub0b4\ubd80\uc2a4\ud0a4\ub9c8\ub97c \ubcc0\uacbd\ud574\uc8fc\uba74 \uac1c\ub150\uc2a4\ud0a4\ub9c8\uc5d0 \uc601\ud5a5\uc744 \uc8fc\uc9c0 \uc54a\uc74c |\\n\\n---\\n\\n## 3\ub2e8\uacc4 \uc2a4\ud0a4\ub9c8 \uac04\uc758 \uc0ac\uc0c1\\n\\n![](./data4.jpeg)"},{"id":"/2023/02/14/DB\uac1c\uc6941","metadata":{"permalink":"/blog/second/2023/02/14/DB\uac1c\uc6941","source":"@site/second-blog/2023-02-14-DB\uac1c\uc6941/index.mdx","title":"DataBase \uac1c\uc694 1","description":"\ub370\uc774\ud130 / \uc815\ubcf4 / \uc9c0\uc2dd / \uc9c0\ud61c","date":"2023-02-14T12:07:00.000Z","formattedDate":"2023\ub144 2\uc6d4 14\uc77c","tags":[{"label":"database","permalink":"/blog/second/tags/database"},{"label":"\uc815\ubcf4\uad00\ub9ac","permalink":"/blog/second/tags/\uc815\ubcf4\uad00\ub9ac"},{"label":"DIKW","permalink":"/blog/second/tags/dikw"}],"readingTime":4.675,"hasTruncateMarker":false,"authors":[{"name":"100milliongold","title":"developer","url":"https://sebastienlorber.com","imageURL":"https://avatars.githubusercontent.com/u/13652711?s=400&u=0a1092b8b1170f71ae883d098f8721c12ce39e9e&v=4","key":"100milliongold"}],"frontMatter":{"title":"DataBase \uac1c\uc694 1","date":"2023-02-14T12:07:00.000Z","authors":["100milliongold"],"tags":["database","\uc815\ubcf4\uad00\ub9ac","DIKW"],"password":"1q2w3e4r"},"unlisted":false,"prevItem":{"title":"DataBase \uac1c\uc694 2","permalink":"/blog/second/2023/02/14/DB\uac1c\uc6942"}},"content":"## \ub370\uc774\ud130 / \uc815\ubcf4 / \uc9c0\uc2dd / \uc9c0\ud61c\\n\\n![](./DIKW_\ud53c\ub77c\ubbf8\ub4dc.jpeg)\\n\\n| \uad6c\ubd84        | \ud2b9\uc9d5                                                                              | \ube44\uad50                                                                            | \uad00\ub828 \uc2dc\uc2a4\ud15c                                  |\\n| ----------- | --------------------------------------------------------------------------------- | ------------------------------------------------------------------------------- | -------------------------------------------- |\\n| Data        | - \uadf8\uc790\uccb4\ub85c\ub294 \uc758\ubbf8\ub97c \uac16\uc9c0\uc54a\uace0 \uc874\uc7ac\ud558\ub294\uac83 <br/> - \uc2dd\ubcc4 \ubc0f \uae30\ub85d\uc774 \uac00\ub2a5\ud55c \ub2e8\uc21c\ud55c \uc0ac\uc2e4 | - \uc601\uc218\uc99d \ub0b4 \uae30\ub85d\ub370\uc774\ud130                                                          | \ub370\uc774\ud130\ubca0\uc774\uc2a4, OLTP, CDC, ETL, \ub370\uc774\ud130 \ub808\uc774\ud06c  |\\n| Information | - \uad00\uacc4\uc5d0 \ub530\ub77c \uc758\ubbf8\uac00 \ubd80\uc5ec\ub41c Data <br/> - \uc758\ubbf8\uc788\uac8c \uad6c\uc870\ud654\ub41c Data \uc758 \uc9d1\ud569           | - \uc5b4\uc81c\ub9e4\ucd9c : 70\ub9cc\uc6d0 <br/> - \uc624\ub298\ub9e4\ucd9c : 40\ub9cc\uc6d0 <br/> -> \ud3c9\uade0 60\ub9cc\uc6d0              | \ub370\uc774\ud130 \uc6e8\uc5b4\ud558\uc6b0\uc2a4, OLAP                      |\\n| Knowledge   | - \uc758\uc0ac \uacb0\uc815\uc5d0 \uc774\uc6a9\ub418\ub294 \uc815\ubcf4 <br/> - \ubd80\uac00 \uac00\uce58 \ucc3d\ucd9c <br/> - \uc815\ubcf4\uc758 \ucd94\uc0c1\ud654 , \uc77c\ubc18\ud654 | - \ub9e4\ucd9c\uc99d\uac00\uc758 \uc6d0\uc778\ubd84\uc11d \ub208\uc774 \uc624\uba74 \uac00\uae4c\uc6b4 \uacf3\uc5d0\uc11c \uc2dc\ucf1c\uba39\uc73c\ub824\ub294 \uacbd\ud5a5                 | \uc9c0\uc2dd \uad00\ub9ac \uc2dc\uc2a4\ud15c(KMS), \uc804\uc0ac\uc801 \uc9c0\uc2dd \ud3ec\ud0c8(EKP) |\\n| Wisdom      | - \uc62c\ubc14\ub978 \uacb0\uc815\uc774\ub098 \ud310\ub2e8\uc744 \uc704\ud55c \uc9c0\uc2dd\uacfc \uacbd\ud5d8\uc774 \ucd95\uc801\ub41c \uc0c1\ud0dc / \ub2a5\ub825                    | - \ucc3d\uc758\uc801\uc778 \uc544\uc774\ub514\uc5b4 \uc751\uc6a9 (\ub208\uc608\ubcf4\uc2dc \ubc30\ub2ec 10% \ud560\uc778 \ud310\ucd09\ud589\uc0ac -> \ub9e4\ucd9c 100\ub9cc\uc6d0 \uc99d\uac00) | \ube44\uc988\ub2c8\uc2a4 \uc778\ud154\ub9ac\uc804\uc2a4(BI)                      |\\n\\n---\\n\\n## \ub370\uc774\ud130 \ubca0\uc774\uc2a4\uc758 \uc815\uc758\\n\\n\ud55c \uc870\uc9c1\uc758 \uc5ec\ub7ec \uc751\uc6a9 \uc2dc\uc2a4\ud15c\uc774 **_\uacf5\uc6a9_** \ud558\uae30 \uc704\ud574 \ucd5c\uc18c\uc758 \uc911\ubcf5\uc73c\ub85c **_\ud1b5\ud569_**, **_\uc800\uc7a5_**\ub41c **_\uc6b4\uc601_**\ub41c \ub370\uc774\ud130\uc758 \uc9d1\ud569\\n\\n- \ud1b5\ud569 \ub370\uc774\ud130 : \ucd5c\uc18c\uc758 \uc911\ubcf5 / \ud1b5\uc81c\ub41c \uc911\ubcf5\\n- \uc800\uc7a5 \ub370\uc774\ud130 : \ucef4\ud4e8\ud130\uac00 \uc811\uadfc \uac00\ub2a5\ud55c \ub9e4\uccb4\uc5d0 \uc800\uc7a5\\n- \uc6b4\uc601 \ub370\uc774\ud130 : \uc870\uc9c1\uc758 \uc6b4\uc601\uc5d0 \uaf2d \ud544\uc694\ud55c \ud544\uc218\uc801\uc778 \ub370\uc774\ud130\ubca0\uc774\uc2a4\\n- \uacf5\uc720 \ub370\uc774\ud130 : \uc5ec\ub7ec \uc751\uc6a9 \ud504\ub85c\uadf8\ub7a8\uc774 \uacf5\ud1b5\uc73c\ub85c \ud5c8\uc6a9\\n\\n\ud558\ub098 \uc774\uc0c1\uc758 \uc11c\ub85c \uad00\ub828\uc774 \ub418\ub294 \uc870\uc9c1\uccb4\ub4e4\uc758 \ud65c\ub3d9\uc744 \uae30\uc220\ud558\ub294 \ub370\uc774\ud130 \ub4e4\uc758 \ubaa8\uc784\\n\\n| \ud2b9\uc9d5             | \ub0b4\uc6a9                                                                                                                     |\\n| ---------------- | ------------------------------------------------------------------------------------------------------------------------ |\\n| \uc2e4\uc2dc\uac04 \uc811\uadfc      | \uc800\uc7a5\ub418\ub294 \ub370\uc774\ud130\ub294 \uc2e4\uc2dc\uac04 \uc811\uadfc\uc131\uc774 \ubcf4\uc7a5\ub41c                                                                                 |\\n| \uacc4\uc18d\uc801\uc778 \ubcc0\ud654    | \uac31\uc2e0 \uc0bd\uc785 \uc0ad\uc81c\ub4f1\uc758 \uc5f0\uc0b0\uc5d0 \uc758\ud574 \ub0b4\uc6a9\uc774 \ub3d9\uc801\uc73c\ub85c \ubcc0\uacbd\ub428                                                                    |\\n| \ub3d9\uc2dc \uacf5\uc720        | \uc800\uc7a5\ub41c \ub370\uc774\ud130\ub294 \uc790\uc218\uc758 \uc0ac\uc6a9\uc790\uc5d0 \uc758\ud574 \ub3d9\uc2dc\uc5d0 \uacf5\uc720\ud560\uc218 \uc788\uc74c                                                                |\\n| \ub0b4\uc6a9\uc5d0 \uc758\ud55c \ucc38\uc870 | \uc0ac\uc6a9\uc790\ub294 \ucc38\uc870\ud558\uae30\ub97c \uc6d0\ud558\ub294 \ub370\uc774\ud130\uc758 \ub0b4\uc6a9\uc744 \uc774\uc6a9\ud558\uc5ec \ub370\uc774\ud130\uc758 \uc800\uc7a5 \uc704\ucc28\ub098 \uc218\uc5d0 \uc0c1\uad00\uc5c6\uc774 \ub370\uc774\ud130\uc5d0 \ub17c\ub9ac\uc801\uc73c\ub85c \uc811\uadfc\ud560\uc218 \uc788\uc74c |\\n\\n---\\n\\n## \ub370\uc774\ud130\ubca0\uc774\uc2a4 \uc2dc\uc2a4\ud15c\uc758 \uc815\uc758\\n\\n- \ub370\uc774\ud130\ub97c \uc800\uc7a5 \uc720\uc9c0 \uad00\ub9ac\ud558\uc5ec \uc815\ubcf4\ub97c \uc0dd\uc131\ud558\ub294 \uc2dc\uc2a4\ud15c\\n- \ub370\uc774\ud130\ubca0\uc774\uc2a4\ub97c \ud3ec\ud568\ud558\uc5ec \uc774\ub97c \uad6c\ucd95, \uad00\ub9ac\ud558\uace0 \uc0ac\uc6a9\ud558\uae30 \uc704\ud55c \ucd1d\uccb4\uc801\uc778 \uc2dc\uc2a4\ud15c\\n\\n![](./dbsystem.png)\\n\\n---\\n\\n## \ub370\uc774\ud130\ubca0\uc774\uc2a4 \uc2dc\uc2a4\ud15c\uc758 \uae30\ubcf8 \uad6c\uc870\\n\\n![](./\uad00\uc810.jpeg)\\n\\n| \ub2e8\uacc4     | \uc124\uba85                                                                                                                                                                                              |\\n| -------- | ------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------- |\\n| \uc678\ubd80\ub2e8\uacc4 | - \uc0ac\uc6a9\uc790\uc5d0\uc11c \ub370\uc774\ud130\ub97c \ubc14\ub77c\ubcf4\ub294 \uad00\uc810\uc744 \ubaa8\ub378\ub9c1\ud55c\uac83 <br/> - \uc5ec\ub7ec\uac1c\uc758 \uc678\ubd80 \uc2a4\ud0a4\ub9c8\ub97c \uac00\uc9c8\uc218 \uc788\uc74c <br/> ex)\uc790\ub3d9\ucc28\uc5d0 \ub300\ud55c \uc0ac\uc6a9\uc790\uc758 \uad00\uc810 : \uc774\ub3d9\uc218\ub2e8(\uc6b4\uc804\uc790), \uc218\ub9ac\ub300\uc0c1(\uc815\ube44\uc0ac)                             |\\n| \uac1c\ub150\ub2e8\uacc4 | - DB \uad00\ub9ac\uc790\uac00 \ub370\uc774\ud130\ub97c \ubc14\ub77c\ubcf4\ub294 \uad00\uc810\uc744 \ubaa8\ub378\ub9c1\ud55c\uac83 <br/> - \ub370\uc774\ud130\ubca0\uc774\uc2a4\uc758 \uad6c\uc870\uc640 \ub370\uc774\ud130\uac04\uc758 \uad00\uacc4\ub97c \ub17c\ub9ac\uc801\uc73c\ub85c \ud45c\ud604<br/> - \ub370\uc774\ud130 \ubca0\uc774\uc2a4 \uad00\ub9ac\uc790\uc5d0 \uc758\ud558\uc5ec \uc0ac\uc6a9\ub418\uba70, \ud558\ub098\uc758 \uac1c\ub150\uc2a4\ud06c\ub9c8\ub97c \uac00\uc9c0\uace0 \uc788\uc74c  |\\n| \ub0b4\ubd80\ub2e8\uacc4 | - \ubb3c\ub9ac\uc801\uc740 \uc800\uc7a5\uad6c\uc870\ub97c \ud45c\ud604, \ub370\uc774\ud130\ub97c \uc2e4\uc81c\ub85c \uc5b4\ub5bb\uac8c \uc800\uc7a5\ud560\uc9c0\uc5d0 \ub300\ud558\uc5ec \ub370\uc774\ud130\ubca0\uc774\uc2a4 \uad00\ub9ac\uc790\uac00 \uae30\uc220<br/> - \uc2dc\uc2a4\ud15c \uc124\uacc4\uc790\ub098 \uc2dc\uc2a4\ud15c \ud504\ub85c\uadf8\ub798\uba38\uac00 \ub370\uc774\ud130\ub97c \ubc14\ub77c\ubcf4\ub294 \uad00\uc810<br/> - \ub77c\ub098\uc758 \ub0b4\ubd80\uc2a4\ud0a4\ub9c8\ub97c \uac00\uc9d0 |"}]}')}}]);